INSERT INTO `txt` VALUES ('41', '2', 'bill发给我的.sql(我的职位列表（已开始）).txt', 'select top 1000 * from ( select jd.MODIFIEDDATETIME , jd.JDID,jd.CUSTPOSITIONNAME,jd.COUNTYID,\r\n(case when county.NAME is null then \'\' else county.NAME end) as CountyName,\r\njd.HC,jd.StatusId,jd.CUSTACCOUNT,\r\n(case when cust.NAME is null then \'\' else cust.NAME end) as CustName,\r\n(case when cp.CONTACTPERSONID is null then \'\' else cp.CONTACTPERSONID end) as CONTACTPERSONID,\r\n(case when cp.CONTACTPERSONID is null then \'\'else cp.NAME + \' | \' + cp.PHONE + \' | \' + cp.CELLULARPHONE + \'<br>\' + cp.EMAIL end) as ContactPersonName ,\r\n(select count(RECRUITMENTID) from HRMRECRUITMENTTABLE as recr where recr.DATAAREAID=jd.DATAAREAID and recr.JDID=jd.JDID) as RecruitmentCount ,\r\n(select count(RECRUITMENTID) from HRMRECRUITMENTTABLE as recr where recr.DATAAREAID=jd.DATAAREAID and recr.JDID=jd.JDID and recr.STATUSID=\'Ready\') as ReadyCount ,\r\n(select count(RECRUITMENTID) from HRMRECRUITMENTTABLE as recr where recr.DATAAREAID=jd.DATAAREAID and recr.JDID=jd.JDID and recr.STATUSID=\'SendReport\') as SendReportCount ,\r\n(select count(RECRUITMENTID) from HRMRECRUITMENTTABLE as recr where recr.DATAAREAID=jd.DATAAREAID and recr.JDID=jd.JDID and recr.STATUSID=\'Interview\') as InterviewCount ,\r\n(select count(RECRUITMENTID) from HRMRECRUITMENTTABLE as recr where recr.DATAAREAID=jd.DATAAREAID and recr.JDID=jd.JDID and recr.STATUSID=\'Survey\') as SurveyCount ,\r\n(select count(RECRUITMENTID) from HRMRECRUITMENTTABLE as recr where recr.DATAAREAID=jd.DATAAREAID and recr.JDID=jd.JDID and recr.STATUSID=\'NegoOffer\') as NegoOfferCount ,\r\n(select count(RECRUITMENTID) from HRMRECRUITMENTTABLE as recr where recr.DATAAREAID=jd.DATAAREAID and recr.JDID=jd.JDID and recr.STATUSID=\'Offer\') as OfferCount , \r\n(select count(RECRUITMENTID) from HRMRECRUITMENTTABLE as recr where recr.DATAAREAID=jd.DATAAREAID and recr.JDID=jd.JDID and recr.STATUSID=\'Onboard\') as OnboardCount ,\r\n(select count(RECRUITMENTID) from HRMRECRUITMENTTABLE as recr where recr.DATAAREAID=jd.DATAAREAID and recr.JDID=jd.JDID and recr.STATUSID like \'TurnDown%\') as TurnDownCount \r\nfrom HRMJDCONTACTINFO as jd \r\nleft join ADDRESSCOUNTY as county \r\non county.DATAAREAID=\'CV1\' and county.COUNTRYREGIONID=jd.COUNTRYREGIONID and county.STATEID=jd.STATEID and county.COUNTYID=jd.COUNTYID\r\nleft join CUSTTABLE as cust on cust.DATAAREAID=\'CV1\' and cust.ACCOUNTNUM=jd.CUSTACCOUNT\r\nleft join CONTACTPERSON as cp  on cp.DATAAREAID=jd.DATAAREAID and cp.CONTACTPERSONID=jd.CUSTPERSONID\r\nleft join EMPLTABLE as resp on resp.DATAAREAID=\'CV1\' and resp.EMPLID=jd.RESPONSIBLE \r\nleft join EMPLTABLE as cpe on cpe.DATAAREAID=\'CV1\' and cpe.EMPLID=jd.CONTACTPERSONEMPLID \r\nleft join EMPLTABLE as cpa on cpa.DATAAREAID=\'CV1\'   and cpa.EMPLID=jd.CONTACTPERSONALTERNATEID \r\ninner join HRMJDRECRUITER as recruiter on recruiter.DATAAREAID=recruiter.DATAAREAID   and recruiter.JDID=jd.JDID \r\nwhere jd.DATAAREAID=\'CI\' and recruiter.EMPLID=\'CI1606\' and jd.STATUSID in (\'�ѿ�ʼ\')\r\nand jd.JDID   not in (select top (15 * 0) jd.JDID from HRMJDCONTACTINFO as jd \r\nleft join ADDRESSCOUNTY as county on county.DATAAREAID=\'CV1\' and  \r\ncounty.COUNTRYREGIONID=jd.COUNTRYREGIONID and county.STATEID=jd.STATEID and county.COUNTYID=jd.COUNTYID\r\nleft join CUSTTABLE as cust    on cust.DATAAREAID=\'CV1\' and cust.ACCOUNTNUM=jd.CUSTACCOUNT\r\nleft join CONTACTPERSON as cp on cp.DATAAREAID=jd.DATAAREAID   and cp.CONTACTPERSONID=jd.CUSTPERSONID \r\nleft join EMPLTABLE as resp on resp.DATAAREAID=\'CV1\' and resp.EMPLID=jd.RESPONSIBLE  \r\nleft join EMPLTABLE as cpe on cpe.DATAAREAID=\'CV1\' and cpe.EMPLID=jd.CONTACTPERSONEMPLID \r\nleft join EMPLTABLE as cpa on cpa.DATAAREAID=\'CV1\'   and cpa.EMPLID=jd.CONTACTPERSONALTERNATEID \r\ninner join HRMJDRECRUITER as recruiter on recruiter.DATAAREAID=recruiter.DATAAREAID    and recruiter.JDID=jd.JDID\r\nwhere jd.DATAAREAID=\'CI\' and recruiter.EMPLID=\'CI1606\' and jd.STATUSID in (\'�ѿ�ʼ\')   \r\norder by jd.MODIFIEDDATETIME desc,jd.JDID desc)     ) \r\nf where 1=1  \r\norder by f.MODIFIEDDATETIME desc,f.JDID desc \r\n', '0', '2017-09-08 09:48:19', null, 'bill发给我的.sql(我的职位列表（已开始）).txt');
INSERT INTO `txt` VALUES ('42', '2', 'bill发给我的.候选人状态.txt', 'select top 10 jd.JDID,jd.CUSTACCOUNT,(case when cust.NAME is null then \'\' else cust.NAME end) as CustName ,\r\n(case when cust.IsKa is null then 0 else cust.IsKa end) as IsKa ,jd.CUSTPOSITIONNAME as PositionName ,\r\njd.COUNTYID,(case when county.NAME is null then \'\' else county.NAME end) as CountyName ,jd.HC,\r\njd.Responsible,empl.EMPLENG as ResponsibleName ,recr.CREATEDDATE,recr.STATUSID,rs.DESCRIPTION as StatusName,recrcreatedemp.EmplEng as CreatedEmplName,\r\nrecr.StatusDate from HRMJDCONTACTINFO as jd left join CUSTTABLE as cust on cust.DATAAREAID=\'CV1\' and cust.ACCOUNTNUM=jd.CUSTACCOUNT \r\nleft join ADDRESSCOUNTY as county on county.DATAAREAID=\'CV1\' and county.COUNTRYREGIONID=jd.COUNTRYREGIONID and county.STATEID=jd.STATEID \r\nand county.COUNTYID=jd.COUNTYID left join EMPLTABLE as empl on empl.DATAAREAID=\'CV1\' and empl.EMPLID=jd.RESPONSIBLE \r\nleft join HRMRECRUITMENTTABLE as recr on recr.DATAAREAID=jd.DATAAREAID and recr.JDID=jd.JDID \r\nleft join HRMRECRUITMENTSTATUS as rs on rs.DATAAREAID=recr.DATAAREAID and rs.STATUSID=recr.STATUSID \r\nleft join EMPLTABLE as recrcreatedemp on recrcreatedemp.DATAAREAID=\'CV1\' and recrcreatedemp.EmplId=recr.CreatedEmplId \r\nwhere jd.DATAAREAID=\'CI\' and recr.CIID in (\'CI1509291726414353\') and jd.JDID not in (select top 0 jd.JDID from HRMJDCONTACTINFO as jd \r\nleft join CUSTTABLE as cust on cust.DATAAREAID=\'CV1\' and cust.ACCOUNTNUM=jd.CUSTACCOUNT left join ADDRESSCOUNTY as county on county.DATAAREAID=\'CV1\' \r\nand county.COUNTRYREGIONID=jd.COUNTRYREGIONID and county.STATEID=jd.STATEID and county.COUNTYID=jd.COUNTYID \r\nleft join EMPLTABLE as empl on empl.DATAAREAID=\'CV1\' and empl.EMPLID=jd.RESPONSIBLE\r\n left join HRMRECRUITMENTTABLE as recr on recr.DATAAREAID=jd.DATAAREAID and recr.JDID=jd.JDID \r\n left join HRMRECRUITMENTSTATUS as rs on rs.DATAAREAID=recr.DATAAREAID and rs.STATUSID=recr.STATUSID \r\n left join EMPLTABLE as recrcreatedemp on recrcreatedemp.DATAAREAID=\'CV1\' and recrcreatedemp.EmplId=recr.CreatedEmplId where jd.DATAAREAID=\'CI\'\r\n  and recr.CIID in (\'CI1509291726414353\') order by jd.MODIFIEDDATETIME\r\n desc,cust.NAME,county.NAME,jd.CUSTPOSITIONNAME) \r\n order by jd.MODIFIEDDATETIME desc,cust.NAME,county.NAME,jd.CUSTPOSITIONNAME \r\n', '0', '2017-09-08 09:48:19', null, 'bill发给我的.候选人状态.txt');
INSERT INTO `txt` VALUES ('43', '2', 'bill发给我的.同步ERP.Employees和职位信息.txt', 'INSERT INTO [dbo].[Employees]\r\n([EmpID] ,[UserName] ,[EmpName],[PwdMD5],[IsDeleted],[CreatedDate], [UpdatedDate])\r\nselect EMPLID,EMPLENG,EMPLENG,\'\',1,GETDATE(),GETDATE() FROM EMPLTABLE\r\nwhere EMPLID not in  ( select EmpID from  [Employees] )\r\n\r\n\r\nINSERT INTO [114.215.139.111].[CareerCMS_New].[dbo].[Organization]\r\n           ([CompanyID]\r\n           ,[CompanyName]\r\n           ,[OrgID]\r\n           ,[OrgPID]\r\n           ,[OrgName]\r\n           ,IsDeleted)\r\nselect \'01\',\'CI\',ORGLV4,(case when ORGLV3=ORGLV4 then \'\' else ORGLV3 end),ORGLV4NAME,0 from HRMORGTREE\r\nwhere  ORGLV4 not  in  (select [OrgID] from [Organization] )\r\n--ͬ�� �û�ְλ/��֯����\r\n\r\n--delete [EmployeesPosition]\r\nINSERT INTO  [dbo].[EmployeesPosition]\r\n           ([EmpID]\r\n           ,[HRMEMPLOYMENTFACTOR]\r\n           ,[PositionID]\r\n           ,[PositionName]\r\n           ,[BUID]\r\n           ,[TeamID]\r\n           ,[SectorID]\r\n           ,[BUName]\r\n           ,[TeamName]\r\n           ,[SectorName]\r\n           ,[CompanyID]\r\n           ,[CompanyName]\r\n           ,[ReportTo]\r\n           ,[ReportToEmplName])\r\nselect \r\npos.REFERENCE,\r\npos.HRMEMPLOYMENTFACTOR,\r\npos.DESCRIPTION as PositionName,\r\njob.DESCRIPTION as JobName,\r\ntree.ORGLV2 as BUID,\r\ntree.ORGLV3 as TeamID,\r\ntree.ORGLV4 as SectorID,\r\ntree.ORGLV2NAME as BUName,\r\ntree.ORGLV3NAME as TeamName,\r\ntree.ORGLV4NAME as SectorName,\r\n\'CI\',\r\n\'01\',\r\nemp.EMPLID as ReportTo,\r\nemp.EMPLENG as ReportToEmplName\r\nfrom HRPPARTYPOSITIONTABLERELAT2226 as pos\r\ninner join HRMORGTREE as tree on tree.DATAAREAID=pos.DATAAREAID and tree.ORGLV4=pos.ORGANIZATIONUNITID \r\nleft join HRPPARTYJOBTABLERELATIONSHIP as job on job.DATAAREAID=pos.DATAAREAID and job.JOBID=pos.JOBID\r\nleft join HRPPARTYPOSITIONTABLERELAT2226 as rep on rep.DATAAREAID=pos.DATAAREAID and rep.POSITIONID=pos.REPORTSTOPOSITION \r\nleft join EMPLTABLE as emp on emp.DATAAREAID=\'cv1\' and emp.EMPLID=rep.REFERENCE \r\nleft join EMPLTABLE as emp1 on emp.DATAAREAID=\'cv1\' and emp1.EMPLID=pos.REFERENCE \r\nleft join DIRPARTYINTERNALORGANIZATI2216 as ss on \r\nss.ORGANIZATIONUNITID=tree.ORGLV4\r\nwhere pos.DATAAREAID=\'ci\' \r\nand pos.VALIDTODATETIME>=getdate() and rep.VALIDTODATETIME>=getdate()\r\ngroup by ss.HRMRESPONSIBLE,pos.REFERENCE,emp1.EMPLENG,pos.DESCRIPTION,pos.ValidFromDateTime,\r\ntree.ORGLV2 ,\r\ntree.ORGLV3 ,\r\ntree.ORGLV4 ,\r\npos.VALIDTODATETIME,pos.HRMEMPLOYMENTFACTOR,\r\ntree.ORGLV2NAME,tree.ORGLV3NAME,tree.ORGLV4NAME,emp.EMPLENG,rep.DESCRIPTION,job.DESCRIPTION,emp.EMPLID\r\n\r\n\r\nselect * from dbo.EmployeesEMail\r\ndelete dbo.EmployeesEMail\r\ninsert EmployeesEMail (EMPLID,DATAAREAID,recid,EMAIL)\r\nselect EMPLID,DATAAREAID,0,EMAIL from \r\n(\r\nSELECT     A.EMPLID, A.DATAAREAID, A.RECID, B.DATAAREAID AS DATAAREAID#2, C.DATAAREAID AS DATAAREAID#3, C.EMAIL, C.PHONE, C.PHONELOCAL, C.TELEX, \r\n					C.URL\r\n					FROM         (SELECT     (CASE WHEN VIRT.ID IS NULL THEN A.DATAAREAID ELSE VIRT.ID END) AS DATAAREAID, A.EMPLID, A.PARTYID, A.RECID\r\n					FROM          dbo.EMPLTABLE AS A LEFT OUTER JOIN\r\n					dbo.VIRTUALDATAAREALIST AS VIRT ON A.DATAAREAID = VIRT.VIRTUALDATAAREA) AS A INNER JOIN\r\n					(SELECT     (CASE WHEN VIRT.ID IS NULL THEN B.DATAAREAID ELSE VIRT.ID END) AS DATAAREAID, B.PARTYID, B.VALUESRECID\r\n					FROM          dbo.DIRPARTYECOMMUNICATIONRELA2608 AS B LEFT OUTER JOIN\r\n					dbo.VIRTUALDATAAREALIST AS VIRT ON B.DATAAREAID = VIRT.VIRTUALDATAAREA) AS B ON A.PARTYID = B.PARTYID AND \r\n					A.DATAAREAID = B.DATAAREAID INNER JOIN\r\n					(SELECT     (CASE WHEN VIRT.ID IS NULL THEN C.DATAAREAID ELSE VIRT.ID END) AS DATAAREAID, C.ECOMMUNICATIONTYPEID, C.URL, C.EMAIL, C.TELEX, \r\n					C.PHONELOCAL, C.PHONE, C.RECID\r\n					FROM          dbo.DIRECOMMUNICATIONADDRESS AS C LEFT OUTER JOIN\r\n					dbo.VIRTUALDATAAREALIST AS VIRT ON C.DATAAREAID = VIRT.VIRTUALDATAAREA) AS C ON B.VALUESRECID = C.RECID AND \r\n					B.DATAAREAID = C.DATAAREAID\r\n					WHERE     (C.ECOMMUNICATIONTYPEID = N\'Email\') and a.DATAAREAID=\'CI\') a\r\n', '0', '2017-09-08 09:48:19', null, 'bill发给我的.同步ERP.Employees和职位信息.txt');
