INSERT INTO `txt` VALUES ('56', '2', 'Button(httpwww.17sucai.com).css.txt', '\r\n.Button {\r\n    border: 1px solid transparent;\r\n    border-radius: 0.3em;\r\n    box-shadow: 0 1px rgba(255, 255, 255, 0.8), 0 1px rgba(255, 255, 255, 0.35) inset;\r\n    cursor: pointer;\r\n    display: inline-block;\r\n    line-height: 1em;\r\n    padding: 0.45em 0.825em;\r\n    position: relative;\r\n    text-align: center;\r\n    transition-duration: 0.05s;\r\n    transition-property: color, -moz-box-shadow, text-shadow;\r\n    transition-timing-function: ease-in-out;\r\n}\r\n.Button:hover {\r\n    text-decoration: none;\r\n}\r\n.Button span {\r\n    border: 1px solid;\r\n    border-radius: 0.3em;\r\n    bottom: -1px;\r\n    box-shadow: 0 1px rgba(255, 255, 255, 0.35) inset;\r\n    display: block;\r\n    left: -1px;\r\n    opacity: 1;\r\n    position: absolute;\r\n    right: -1px;\r\n    top: -1px;\r\n    transition-duration: 0.5s;\r\n    transition-property: opacity;\r\n    transition-timing-function: ease-in-out;\r\n    z-index: 1;\r\n}\r\n.Button:hover span {\r\n    transition-duration: 0.05s;\r\n    transition-property: opacity;\r\n    transition-timing-function: linear;\r\n}\r\n.Button:active span {\r\n    transition: none 0s ease 0s ;\r\n}\r\n.Button strong {\r\n    font-weight: 800;\r\n    position: relative;\r\n    z-index: 2;\r\n}\r\n.Button2 {\r\n    border: 1px solid transparent;\r\n    border-radius: 0.3em;\r\n    box-shadow: 0 1px rgba(255, 255, 255, 0.8), 0 1px rgba(255, 255, 255, 0.35) inset;\r\n    cursor: pointer;\r\n    display: inline-block;\r\n    font-family: \"helvetica neue\",arial,sans-serif;\r\n    font-weight: bold;\r\n    line-height: 1em;\r\n    margin: 0;\r\n    padding: 0.45em 0.825em;\r\n    text-align: center;\r\n    transition: all 0.05s ease-in-out 0s;\r\n}\r\n.Button2:focus {\r\n    outline: medium none;\r\n}\r\n.Button2:hover {\r\n    text-decoration: none;\r\n}\r\n.WhiteButton {\r\n    color: #524d4d;\r\n    text-shadow: 0 1px rgba(255, 255, 2