rder-color: #015e91;\r\n}\r\n.RedButton:hover {\r\n    background: -moz-linear-gradient(center top , #5eb4ea, #61a1ee 50%, #59a5eb 50%, #3691e6) repeat scroll 0 0 rgba(0, 0, 0, 0);\r\n    border-color: #0366ad;\r\n}\r\n.RedButton:active {\r\n    background: -moz-linear-gradient(center top , #4b9cdd, #4189d5 50%, #3d8bd3 50%, #3093c8) repeat scroll 0 0 rgba(0, 0, 0, 0);\r\n    border-color: #013b6a;\r\n}\r\n.RedButton.Button18:hover span {\r\n    box-shadow: 0 1px rgba(255, 255, 255, 0.8), 0 1px rgba(255, 255, 255, 0.35) inset, 0 0 10px rgba(82, 162, 235, 0.25);\r\n}\r\n.RedButton.Button18:active span {\r\n    box-shadow: 0 1px 2px rgba(34, 25, 25, 0.25) inset, 0 0 3px rgba(82, 202, 235, 0.35);\r\n}\r\n.blue-button, .blue-button:hover, .blue-button:active {\r\n    -moz-border-bottom-colors: none;\r\n    -moz-border-left-colors: none;\r\n    -moz-border-right-colors: none;\r\n    -moz-border-top-colors: none;\r\n    background: -moz-linear-gradient(center top , #52a8e8 20%, #2e76cf 100%) repeat scroll 0 0 rgba(0, 0, 0, 0);\r\n    border-color: #4081af #4081af #20559a;\r\n    border-image: none;\r\n    border-radius: 15px;\r\n    border-style: solid;\r\n    border-width: 1px;\r\n    box-shadow: 0 1px 0 hsla(0, 100%, 100%, 0.3) inset, 0 0 2px hsla(0, 100%, 100%, 0.3) inset, 0 1px 2px hsla(0, 0%, 0%, 0.29);\r\n    color: #fff;\r\n    cursor: pointer;\r\n    font-size: 14px;\r\n    line-height: 1em;\r\n    margin: 1.5em 0 0;\r\n    padding: 8px 18px;\r\n    text-align: center;\r\n    text-shadow: 0 -1px 0 hsla(0, 0%, 0%, 0.3);\r\n}\r\n.login-button {\r\n    display: inline-block;\r\n    float: left;\r\n    height: 51px;\r\n    margin: 0 10px 0 0;\r\n    width: 130px;\r\n}\r\n.login-button:hover {\r\n    cursor: pointer;\r\n    text-decoration: none;\r\n}\r\n.login-button span {\r\n    color: #fff;\r\n    display: inline-block;\r\n    float: left;\r\n    font-size: 15px;\r\n    font-weight: 500;\r\n    margin-left: 40px;\r\n    margin-top: 7px;\r\n    min-height: 20px;\r\n