INSERT INTO `txt` VALUES ('57', '2', 'C# byte[]和文件FileStream相互转化.txt', 'C# byte[]和文件FileStream相互转化\r\nusing System.IO;\r\n\r\n \r\n        /// <summary>\r\n        /// 读filename到byte[]\r\n        /// </summary>\r\n        /// <param name=\"fileName\"></param>\r\n        /// <returns></returns>\r\n        private byte[] ReadFile(string fileName)\r\n        {\r\n            FileStream pFileStream = null;\r\n            byte[] pReadByte = new byte[0];\r\n            try\r\n            {\r\n                pFileStream = new FileStream(fileName, FileMode.Open, FileAccess.Read);\r\n                BinaryReader r = new BinaryReader(pFileStream);\r\n                r.BaseStream.Seek(0, SeekOrigin.Begin);    //将文件指针设置到文件开\r\n                pReadByte = r.ReadBytes((int)r.BaseStream.Length);\r\n                return pReadByte;\r\n            }\r\n            catch\r\n            {\r\n                return pReadByte;\r\n            }\r\n            finally\r\n            {\r\n                if (pFileStream != null)\r\n                    pFileStream.Close();\r\n            }\r\n        }\r\n        //写byte[]到fileName\r\n        private bool writeFile(byte[] pReadByte, string fileName)\r\n        {\r\n            FileStream pFileStream = null;\r\n            try\r\n            {\r\n                pFileStream = new FileStream(fileName, FileMode.OpenOrCreate);\r\n                pFileStream.Write(pReadByte, 0, pReadByte.Length);\r\n            }\r\n            catch\r\n            {\r\n                return false;\r\n            }\r\n            finally\r\n            {\r\n                if (pFileStream != null)\r\n                    pFileStream.Close();\r\n            }\r\n            return true;\r\n        }\r\n\r\n        // 测试\r\n        private void button6_Click(object sender, EventArgs e)\r\n        {\r\n            byte[] b = ReadFile(@\"c:\\u.jpg\");\r\n            if (writeFile(b, @\"c:\\u1.jpg\"))\r\n            {\r\n                //MessageBox.Show(\"成功\");\r\n            }\r\n            else\r\n            {\r\n                //MessageBox.Show(\"失败\"); \r\n            }\r\n        }\r\n', '0', '2017-09-08 09:48:19', null, 'C# byte[]和文件FileStream相互转化.txt');
INSERT INTO `txt` VALUES ('58', '2', 'C# Byte[]数组读取和写入文件.txt', 'protected void ByteToString_Click(object sender, EventArgs e)\r\n        {\r\n            string content = this.txtContent.Text.ToString();\r\n            if (string.IsNullOrEmpty(content))\r\n            {\r\n             return;\r\n         }\r\n         //string תΪbyte����\r\n         byte[] array = Encoding.UTF8.GetBytes(content);\r\n         //��byte����תΪstring\r\n         string result = Encoding.UTF8.GetString(array);\r\n         Response.Write(result);\r\n       }\r\n       //����byte[]����д���ļ�\r\n       protected void writerFile_Click(object sender, EventArgs e)\r\n       {\r\n           string content = this.txtContent.Text.ToString();\r\n           if (string.IsNullOrEmpty(content))\r\n           {\r\n              return;\r\n           }\r\n           //��stringתΪbyte����\r\n           byte[] array = Encoding.UTF8.GetBytes(content);\r\n           string path = Server.MapPath(\"/test.txt\");\r\n           //����һ���ļ���\r\n           FileStream fs = new FileStream(path, FileMode.Create);\r\n          //��byte����д���ļ���\r\n           fs.Write(array, 0, array.Length);\r\n           //���������Ͷ�Ҫ�ر��������������ڴ�й¶����\r\n           fs.Close();\r\n         Response.Write(\"�����ļ��ɹ�\");\r\n     }\r\n     //����byte[]�����ȡ�ļ�\r\n    protected void readFile_Click(object sender, EventArgs e)\r\n     {\r\n         string path = Server.MapPath(\"/test.txt\");\r\n         FileStream fs = new FileStream(path, FileMode.Open);\r\n         //��ȡ�ļ���С\r\n         long size = fs.Length;\r\n         byte[] array = new byte[size];\r\n         //���ļ�����byte������\r\n         fs.Read(array, 0, array.Length);\r\n         fs.Close();\r\n         //��byte����תΪstring\r\n         string result = Encoding.UTF8.GetString(array);\r\n        Response.Write(result);\r\n     }\r\n', '0', '2017-09-08 09:48:19', null, 'C# Byte[]数组读取和写入文件.txt');
INSERT INTO `txt` VALUES ('59', '2', 'C# Post数据和接收简单示例 .juns.txt', ' C# Post数据和接收简单示例\r\n //from:  http://blog.csdn.net/zhihu008/article/details/8523230\r\n \r\n protected void Page_Load(object sender, EventArgs e)\r\n    {\r\n\r\n        string type = \"\";\r\n        string Re = \"\";\r\n        Re += \"数据传送方式：\";\r\n        if (Request.RequestType.ToUpper() == \"POST\")\r\n        {\r\n            type = \"POST\";\r\n            Re += type + \"<br/>参数分别是：<br/>\";\r\n            SortedList table = Param();\r\n            if (table != null)\r\n            {\r\n                foreach (DictionaryEntry De in table) { Re += \"参数名：\" + De.Key + \" 值：\" + De.Value + \"<br/>\"; }\r\n            }\r\n            else\r\n            { Re = \"你没有传递任何参数过来！\"; }\r\n        }\r\n        else\r\n        {\r\n            type = \"GET\";\r\n            Re += type + \"<br/>参数分别是：<br/>\";\r\n            NameValueCollection nvc = GETInput();\r\n            if (nvc.Count != 0)\r\n            {\r\n                for (int i = 0; i < nvc.Count; i++) { Re += \"参数名：\" + nvc.GetKey(i) + \" 值：\" + nvc.GetValues(i)[0] + \"<br/>\"; }\r\n            }\r\n            else\r\n            { Re = \"你没有传递任何参数过来！\"; }\r\n        }\r\n        Response.Write(Re);\r\n\r\n    }\r\n\r\n    //获取GET返回来的数据\r\n    private NameValueCollection GETInput()\r\n    { return Request.QueryString; }\r\n    // 获取POST返回来的数据\r\n    private string PostInput()\r\n    {\r\n        try\r\n        {\r\n            System.IO.Stream s = Request.InputStream;\r\n            int count = 0;\r\n            byte[] buffer = new byte[1024];\r\n            StringBuilder builder = new StringBuilder();\r\n            while ((count = s.Read(buffer, 0, 1024)) > 0)\r\n            {\r\n                builder.Append(Encoding.UTF8.GetString(buffer, 0, count));\r\n            }\r\n            s.Flush();\r\n            s.Close();\r\n            s.Dispose();\r\n            return builder.ToString();\r\n        }\r\n        catch (Exception ex)\r\n        { throw ex; }\r\n    }\r\n    private SortedList Param()\r\n    {\r\n        string POSTStr = PostInput();\r\n        SortedList SortList = new SortedList();\r\n        int index = POSTStr.IndexOf(\"&\");\r\n        string[] Arr = { };\r\n        if (index != -1) //参数传递不只一项\r\n        {\r\n            Arr = POSTStr.Split(\'&\');\r\n            for (int i = 0; i < Arr.Length; i++)\r\n            {\r\n                int equalindex = Arr[i].IndexOf(\'=\');\r\n                string paramN = Arr[i].Substring(0, equalindex);\r\n                string paramV = Arr[i].Substring(equalindex + 1);\r\n                if (!SortList.ContainsKey(paramN)) //避免用户传递相同参数\r\n                { SortList.Add(paramN, paramV); }\r\n                else //如果有相同的，一直删除取最后一个值为准\r\n                { SortList.Remove(paramN); SortList.Add(paramN, paramV); }\r\n            }\r\n        }\r\n        else //参数少于或等于1项\r\n        {\r\n            int equalindex = POSTStr.IndexOf(\'=\');\r\n            if (equalindex != -1)\r\n            { //参数是1项\r\n                string paramN = POSTStr.Substring(0, equalindex);\r\n                string paramV = POSTStr.Substring(equalindex + 1);\r\n                SortList.Add(paramN, paramV);\r\n\r\n            }\r\n            else //没有传递参数过来\r\n            { SortList = null; }\r\n        }\r\n        return SortList;\r\n    }\r\n', '0', '2017-09-08 09:48:19', null, 'C# Post数据和接收简单示例 .juns.txt');
INSERT INTO `txt` VALUES ('60', '2', 'C# XML转义字符与特殊字符进行替换.txt', 'C# XML转义字符与特殊字符进行替换\r\nXML转义字符必须以\"&\"开头,以符号\";\"结尾。\r\n&lt;<小于号&gt;>大于号&amp;&和&apos;\'单引号&quot;\"双引号\r\n            C#在引用XML文件时时只需要将读出的字符串进行替换就行了.现给出源码以备将来快速引用.\r\npublic string replaceSpecialChar(string strSource) {\r\n             var strTemp = strSource;\r\n            strTemp = strTemp.Replace(\"&amp;\",\"&\" );\r\n            strTemp = strTemp.Replace(\"&lt;\",\"<\" );\r\n            strTemp = strTemp.Replace(\"&gt;\",\">\" );\r\n            strTemp = strTemp.Replace(\"&apos;\",\"\\\'\" );\r\n            strTemp = strTemp.Replace(\"&quot;\",\"\\\"\" );\r\n            return strTemp;\r\n}\r\n', '0', '2017-09-08 09:48:19', null, 'C# XML转义字符与特殊字符进行替换.txt');
INSERT INTO `txt` VALUES ('61', '2', 'C# 使用TimeSpan计算两个时间差 .txt', '\r\nC# 使用TimeSpan计算两个时间差【转】\r\n(2013-03-10 11:59:16)\r\n转载▼\r\n标签：\r\nc\r\ndatetime\r\ntimespan\r\n时间差\r\n计算\r\nit\r\n	分类： C#.NET\r\n\r\n可以加两个日期之间任何一个时间单位。\r\nprivate string DateDiff(DateTime DateTime1, DateTime DateTime2)\r\n {string dateDiff = null; \r\n  TimeSpan ts = DateTime1.Subtract(DateTime2).Duration();\r\n dateDiff = ts.Days.ToString()+\"天\"+ ts.Hours.ToString()+\"小时\"+ ts.Minutes.ToString()+\"分钟\"+ ts.Seconds.ToString()+\"秒\";\r\n return dateDiff;\r\n }\r\n\r\n\r\n说明：\r\n1.DateTime值类型代表了一个从公元0001年1月1日0点0分0秒到公元9999年12月31日23点59分59秒之间的具体日期时刻。因此，你可以用DateTime值类型来描述任何在想象范围之内的时间。一个DateTime值代表了一个具体的时刻\r\n2.TimeSpan值包含了许多属性与方法，用于访问或处理一个TimeSpan值\r\n下面的列表涵盖了其中的一部分：\r\nAdd：与另一个TimeSpan值相加。 \r\nDays:返回用天数计算的TimeSpan值。 \r\nDuration:获取TimeSpan的绝对值。 \r\nHours:返回用小时计算的TimeSpan值 \r\nMilliseconds:返回用毫秒计算的TimeSpan值。 \r\nMinutes:返回用分钟计算的TimeSpan值。 \r\nNegate:返回当前实例的相反数。 \r\nSeconds:返回用秒计算的TimeSpan值。 \r\nSubtract:从中减去另一个TimeSpan值。 \r\nTicks:返回TimeSpan值的tick数。 \r\nTotalDays:返回TimeSpan值表示的天数。 \r\nTotalHours:返回TimeSpan值表示的小时数。 \r\nTotalMilliseconds:返回TimeSpan值表示的毫秒数。 \r\nTotalMinutes:返回TimeSpan值表示的分钟数。 \r\nTotalSeconds:返回TimeSpan值表示的秒数。\r\n\r\n\r\n\r\nlong seconddiff =d.Ticks/10000000;//tick和秒之间和换算\r\n', '0', '2017-09-08 09:48:19', null, 'C# 使用TimeSpan计算两个时间差 .txt');
INSERT INTO `txt` VALUES ('62', '2', 'C# 动态增加CSS连接 js文件连接2.txt', 'C# ��̬����CSS���� js�ļ�����\r\n����������������ֻҪ�������js,css�ļ�·���Ϳ�����\r\nusing System.Web.UI.HtmlControls;\r\npublic void AddScript(string scriptUrl)\r\n    {\r\n        script_js script = new script_js();\r\n        script.src = scriptUrl;\r\n\r\n        HtmlHead.Controls.Add(script);\r\n    }\r\n\r\n    public void AddStyle(string styleUrl)\r\n    {\r\n        HtmlLink link = new HtmlLink();\r\n        link.Href = styleUrl;\r\n        link.Attributes[\"text\"] = \"text/css\";\r\n        link.Attributes[\"rel\"] = \"stylesheet\";\r\n\r\n        HtmlHead.Controls.Add(link);\r\n    }\r\n\r\n����\r\n\r\npublic void AddStyle(string styleUrl)\r\n    {\r\n        HtmlLink link = new HtmlLink();\r\n        link.Href = styleUrl;\r\n        link.Attributes[\"text\"] = \"text/css\";\r\n        link.Attributes[\"rel\"] = \"stylesheet\";\r\n\r\n        Page.Controls.Add(link);\r\n    }\r\n', '0', '2017-09-08 09:48:19', null, 'C# 动态增加CSS连接 js文件连接2.txt');
INSERT INTO `txt` VALUES ('63', '2', 'C# 各种数据类型的最大值和最小值常数.txt', 'using System;\r\nusing System.Collections.Generic;\r\nusing System.Linq;\r\nusing System.Text;\r\n\r\nnamespace TypeMaxMinValue\r\n{\r\n    class Program\r\n    {\r\n        static void Main(string[] args)\r\n        {\r\n            PrintDataRange();        \r\n        }\r\n        static void PrintDataRange() {\r\n            System.Console.WriteLine(\"int类型的最大值:  {0},最小值:  {1}\", int.MaxValue, int.MinValue);\r\n            System.Console.WriteLine(\"uint类型的最大值:  {0},最小值:  {1}\", uint.MaxValue, uint.MinValue);\r\n            System.Console.WriteLine(\"byte类型的最大值:  {0},最小值:  {1}\", byte.MaxValue, byte.MinValue);\r\n            System.Console.WriteLine(\"sbyte类型的最大值:  {0},最小值:  {1}\", sbyte.MaxValue, sbyte.MinValue);\r\n            System.Console.WriteLine(\"short类型的最大值:  {0},最小值:  {1}\", short.MaxValue, short.MinValue);\r\n            System.Console.WriteLine(\"ushort类型的最大值:  {0},最小值:  {1}\",ushort.MaxValue, ushort.MinValue);\r\n            System.Console.WriteLine(\"long类型的最大值:  {0},最小值:  {1}\", long.MaxValue, long.MinValue);\r\n            System.Console.WriteLine(\"ulong类型的最大值:  {0},最小值:  {1}\", ulong.MaxValue, ulong.MinValue);\r\n            System.Console.WriteLine(\"float类型的最大值:  {0},最小值:  {1}\", float.MaxValue, float.MinValue);\r\n            System.Console.WriteLine(\"double类型的最大值:  {0},最小值:  {1}\", double.MaxValue, double.MinValue);\r\n            System.Console.WriteLine(\"decimal类型的最大值:  {0},最小值:  {1}\", decimal.MaxValue, decimal.MinValue);\r\n            System.Console.ReadLine();\r\n        }       \r\n    }\r\n}\r\n\r\n运行结果如下：\r\n\r\nint类型的最大值:  2147483647,最小值:  -2147483648\r\nuint类型的最大值:  4294967295,最小值:  0\r\nbyte类型的最大值:  255,最小值:  0\r\nsbyte类型的最大值:  127,最小值:  -128\r\nshort类型的最大值:  32767,最小值:  -32768\r\nushort类型的最大值:  65535,最小值:  0\r\nlong类型的最大值:  9223372036854775807,最小值:  -9223372036854775808\r\nulong类型的最大值:  18446744073709551615,最小值:  0\r\nfloat类型的最大值:  3.402823E+38,最小值:  -3.402823E+38\r\ndouble类型的最大值:  1.79769313486232E+308,最小值:  -1.79769313486232E+308\r\ndecimal类型的最大值:  79228162514264337593543950335,最小值:  -792281625142643375 93543950335\r\n', '0', '2017-09-08 09:48:19', null, 'C# 各种数据类型的最大值和最小值常数.txt');
