INSERT INTO `txt` VALUES ('81', '2', 'C#.Download.byte[].Save.File.ToLocalDisk.下载远程路径.字节数组.保存文件到本地磁盘.txt', '//============C#����һ��Զ�̵��ļ����ֽ����飬���浽����============ \r\n\r\n   WebClient wcClient = new WebClient();\r\n   //��Զ���ļ�����һ������\r\n   WebRequest webReq = WebRequest.Create(fullPath);\r\n   //����Զ��WEB���������ص���Ӧ\r\n   WebResponse webRes = webReq.GetResponse();\r\n   //��ȡ�ļ�����\r\n   long fileLength = webRes.ContentLength;\r\n   //����һ���ļ��������շ��ص�����Ϣ\r\n   Stream srm = webRes.GetResponseStream();\r\n   //ʹ����������ȡ����Ϣ\r\n   StreamReader srmReader = new StreamReader(srm);\r\n   //���建����\r\n   byte[] bufferbyte = new byte[fileLength];\r\n   //����������\r\n   int allByte = (int)bufferbyte.Length;\r\n   int startByte = 0;\r\n   //��ȡ����������\r\n   while (fileLength > 0)\r\n   {\r\n       int downByte = srm.Read(bufferbyte, startByte, allByte);\r\n       if (downByte == 0) { break; };\r\n       startByte += downByte;\r\n       allByte -= downByte;\r\n   }\r\n   \r\n   //���浽����\r\n   var exten = Path.GetExtension(fullPath);//�ļ���չ��,����\r\n     string savePath = \"D://test//\" + DateTime.Now.ToString(\"yyyyMMddHHmmssffffff\") + exten;//���ؾ���·��\r\n     //����һ���ļ�������������ļ���д�����\r\n     FileStream fs = new FileStream(savePath, FileMode.OpenOrCreate, FileAccess.ReadWrite);\r\n     fs.Write(bufferbyte, 0, bufferbyte.Length);\r\n     srm.Close();\r\n     srmReader.Close();\r\n     fs.Close();\r\n	//д��������\r\n', '0', '2017-09-08 09:48:20', null, 'C#.Download.byte[].Save.File.ToLocalDisk.下载远程路径.字节数组.保存文件到本地磁盘.txt');
INSERT INTO `txt` VALUES ('82', '2', 'C#.Enum.获取枚举类型的名字字符串的方法.jun.txt', ' //获取枚举类型的名字字符串的方法.enum.\r\n		public enum TimeOfDay\r\n        {\r\n            Morning = 0,\r\n            Afternoon = 1,\r\n            Evening\r\n        }\r\n\r\n        public void EnumTest()\r\n        {\r\n            TimeOfDay time = TimeOfDay.Afternoon;\r\n            //将枚举字符串打印，即枚举类型转换（解析）成字符串类型\r\n            string str = TimeOfDay.Afternoon.ToString();//返回Afternoon\r\n            int intStr = Convert.ToInt32(TimeOfDay.Afternoon);//返回1\r\n            \r\n            //获取枚举字符串的值，即将一个表示枚举成员的字符串，解析为对应枚举类型\r\n            TimeOfDay time2 = (TimeOfDay)Enum.Parse(typeof(TimeOfDay), \"Evening\", true);\r\n\r\n            //枚举类型可以强制转换成整型，整型也可以强制转换成枚举型\r\n            Console.WriteLine((int)time2);//输出：2\r\n            // 遍历所有的枚举元素            ;\r\n            foreach (string s in Enum.GetNames(typeof(TimeOfDay)))\r\n            {\r\n                Console.WriteLine(s);\r\n            }\r\n            //输出： Morning\r\n            //       Afternoon\r\n            //       Evening\r\n        }\r\n', '0', '2017-09-08 09:48:20', null, 'C#.Enum.获取枚举类型的名字字符串的方法.jun.txt');
