INSERT INTO `txt` VALUES ('84', '2', 'C#.Get.Post请求.txt', 'from:https://stackoverflow.com/questions/4015324/http-request-with-post\r\n\r\nThere are several ways to perform HTTP GET and POST requests:\r\n\r\n================================Method A: HttpClient================================\r\n\r\nAvailable in: .NET Framework 4.5+, .NET Standard 1.1+, .NET Core 1.0+\r\n\r\nCurrently the preferred approach. Asynchronous. Portable version for other platforms available via NuGet.\r\n\r\nusing System.Net.Http;\r\nSetup\r\n\r\nIt is recommended to instantiate one HttpClient for your application\'s lifetime and share it.\r\n\r\nprivate static readonly HttpClient client = new HttpClient();\r\nPOST\r\n\r\nvar values = new Dictionary<string, string>\r\n{\r\n   { \"thing1\", \"hello\" },\r\n   { \"thing2\", \"world\" }\r\n};\r\n\r\nvar content = new FormUrlEncodedContent(values);\r\n\r\nvar response = await client.PostAsync(\"http://www.example.com/recepticle.aspx\", content);\r\n\r\nvar responseString = await response.Content.ReadAsStringAsync();\r\nGET\r\n\r\nvar responseString = await client.GetStringAsync(\"http://www.example.com/recepticle.aspx\");\r\n\r\n================================Method B: 3rd-Party Libraries================================\r\n\r\nRestSharp\r\n\r\nTried and tested library for interacting with REST APIs. Portable. Available via NuGet.\r\n\r\nFlurl.Http\r\n\r\nNewer library sporting a fluent API and testing helpers. HttpClient under the hood. Portable. Available via NuGet.\r\n\r\nusing Flurl.Http;\r\nPOST\r\n\r\nvar responseString = await \"http://www.example.com/recepticle.aspx\"\r\n    .PostUrlEncodedAsync(new { thing1 = \"hello\", thing2 = \"world\" })\r\n    .ReceiveString();\r\nGET\r\n\r\nvar responseString = await \"http://www.example.com/recepticle.aspx\"\r\n    .GetStringAsync();\r\n================================Method C: Legacy================================\r\nAvailable in: .NET Framework 1.1+, .NET Standard 2.0+, .NET Core 1.0+\r\n\r\nusing System.Net;\r\nusing System.Text;  // for class Encoding\r\nPOST\r\n\r\nvar request = (HttpWebRequest)WebRequest.Create(\"http://www.example.com/recepticle.aspx\");\r\n\r\nvar postData = \"thing1=hello\";\r\n    postData += \"&thing2=world\";\r\nvar data = Encoding.ASCII.GetBytes(postData);\r\n\r\nrequest.Method = \"POST\";\r\nrequest.ContentType = \"application/x-www-form-urlencoded\";\r\nrequest.ContentLength = data.Length;\r\n\r\nusing (var stream = request.GetRequestStream())\r\n{\r\n    stream.Write(data, 0, data.Length);\r\n}\r\n\r\nvar response = (HttpWebResponse)request.GetResponse();\r\n\r\nvar responseString = new StreamReader(response.GetResponseStream()).ReadToEnd();\r\nGET\r\n\r\nvar request = (HttpWebRequest)WebRequest.Create(\"http://www.example.com/recepticle.aspx\");\r\n\r\nvar response = (HttpWebResponse)request.GetResponse();\r\n\r\nvar responseString = new StreamReader(response.GetResponseStream()).ReadToEnd();\r\n\r\n================================Method D: WebClient (Also now legacy)================================\r\n\r\nAvailable in: .NET Framework 1.1+, .NET Standard 2.0+, .NET Core 2.0+\r\n\r\nusing System.Net;\r\nusing System.Collections.Specialized;\r\nPOST\r\n\r\nusing (var client = new WebClient())\r\n{\r\n    var values = new NameValueCollection();\r\n    values[\"thing1\"] = \"hello\";\r\n    values[\"thing2\"] = \"world\";\r\n\r\n    var response = client.UploadValues(\"http://www.example.com/recepticle.aspx\", values);\r\n\r\n    var responseString = Encoding.Default.GetString(response);\r\n}\r\nGET\r\n\r\nusing (var client = new WebClient())\r\n{\r\n    var responseString = client.DownloadString(\"http://www.example.com/recepticle.aspx\");\r\n}\r\n', '0', '2017-09-08 09:48:20', null, 'C#.Get.Post请求.txt');
INSERT INTO `txt` VALUES ('85', '2', 'C#.getCurDateTimeWithMillion.根据时间.随机数.txt', 'public string getCurDateTimeWithMillion()\r\n        {\r\n            DateTime currentDate = DateTime.Now;\r\n\r\n            int year = currentDate.Year;\r\n            int month = currentDate.Month;\r\n            int day = currentDate.Day;\r\n            int hour = currentDate.Hour;\r\n            int minute = currentDate.Minute;\r\n            int second = currentDate.Second;\r\n            int million = currentDate.Millisecond;\r\n            String currentTime = \"\";\r\n            currentTime = currentTime + year + (month < 10 ? \"0\" + month : \"\" + month)\r\n                                      + (day < 10 ? \"0\" + day : \"\" + day)\r\n                                      + (hour < 10 ? \"0\" + hour : \"\" + hour)\r\n                                      + (minute < 10 ? \"0\" + minute : \"\" + minute)\r\n                                      + (second < 10 ? \"0\" + second : \"\" + second);\r\n\r\n            if (million < 10)\r\n            {\r\n                currentTime = currentTime + \"00\" + million;\r\n            }\r\n            if (million > 10 && million < 100)\r\n            {\r\n                currentTime = currentTime + \"0\" + million;\r\n            }\r\n            else\r\n            {\r\n                currentTime = currentTime + million;\r\n            }\r\n            return currentTime;\r\n        }\r\n    \r\n', '0', '2017-09-08 09:48:20', null, 'C#.getCurDateTimeWithMillion.根据时间.随机数.txt');
