INSERT INTO `txt` VALUES ('94', '2', 'C#.SBC.DBC.case.全角.半角.转化.jun.quanjiao.banjiao.txt', '   /// <summary>\r\n        /// 转全角的函数(SBC case)\r\n        /// </summary>\r\n        /// <param name=\"input\">任意字符串</param>\r\n        /// <returns>全角字符串</returns>\r\n        /// <remarks>全角空格为12288，半角空格为32;其他字符半角(33-126)与全角(65281-65374)的对应关系是：均相差65248</remarks>\r\n        public static string ToSBC(this string input)\r\n        {\r\n            // 半角转全角：\r\n            char[] array = input.ToCharArray();\r\n            for (int i = 0; i < array.Length; i++)\r\n            {\r\n                if (array[i] == 32)\r\n                {\r\n                    array[i] = (char)12288;\r\n                    continue;\r\n                }\r\n                if (array[i] < 127)\r\n                {\r\n                    array[i] = (char)(array[i] + 65248);\r\n                }\r\n            }\r\n            return new string(array);\r\n        }\r\n\r\n        /// <summary>\r\n        /// 转半角的函数(DBC case) \r\n        /// </summary>\r\n        /// <param name=\"input\">任意字符串</param>\r\n        /// <returns>半角字符串</returns>\r\n        /// <remarks>全角空格为12288，半角空格为32;其他字符半角(33-126)与全角(65281-65374)的对应关系是：均相差65248</remarks>\r\n        public static string ToDBC(this string input)\r\n        {\r\n            char[] array = input.ToCharArray();\r\n            for (int i = 0; i < array.Length; i++)\r\n            {\r\n                if (array[i] == 12288)\r\n                {\r\n                    array[i] = (char)32;\r\n                    continue;\r\n                }\r\n                if (array[i] > 65280 && array[i] < 65375)\r\n                {\r\n                    array[i] = (char)(array[i] - 65248);\r\n                }\r\n            }\r\n            return new string(array);\r\n        }\r\n', '0', '2017-09-08 09:48:20', null, 'C#.SBC.DBC.case.全角.半角.转化.jun.quanjiao.banjiao.txt');
