INSERT INTO `txt` VALUES ('114', '2', 'c#.日期时间.DateTime.txt', 'DateTime.Now.AddDays(-14).ToString(\"dd/MM/yyyy\");//������ǰ��14��\r\ntring time = DateTime.Now.AddMonths(1).ToString(\"yyyy-MM-dd HH:mm\");//����������һ����\r\n\r\n\r\n������ת��Ϊָ����ʽ������ʱ���ַ�����yyyy-MM-dd HH:mm:ss��\r\n\r\n        /// <summary>\r\n        /// ��ȡ��ǰ��ʱ��\r\n        /// </summary>\r\n        public static string Now\r\n        {\r\n            get\r\n            {\r\n                return DateTime.Now.ToString(\"yyyy-MM-dd HH:mm:ss\");\r\n            }\r\n        }\r\n		/// <summary>\r\n        /// �����벿�ֵĵ�ǰʱ�䣬ʾ��Ϊ2003-07-22 10:02:52.136\r\n        /// </summary>\r\n        public static string NowWithMilliSeconds\r\n        {\r\n            get\r\n            {\r\n                DateTime dtNow = DateTime.Now;\r\n                int nMilliSeconds = dtNow.Millisecond;\r\n                return dtNow.ToString(\"yyyy-MM-dd HH:mm:ss\") + \".\" + nMilliSeconds.ToString(\"000\");\r\n            }\r\n        }\r\n		/// <summary>\r\n        /// ��ȡ��ǰ��ʱ����һ���µ�ʱ��\r\n        /// </summary>\r\n        public static string LastMonth\r\n        {\r\n            get\r\n            {\r\n                return DateTime.Now.AddMonths(-1).ToString(\"yyyy-MM-dd HH:mm:ss\");\r\n            }\r\n        }\r\n		\r\n		/// <summary>\r\n        /// ��ȡ���ڵ����\r\n        /// </summary>\r\n        /// <param name=\"dtString\">����ʱ�䴮</param>\r\n        /// <returns>���</returns>\r\n        public static int Year(string dtString)\r\n        {\r\n            int nYear;\r\n\r\n            nYear = Convert.ToInt32(dtString.Substring(0, 4));\r\n            Debug.Assert(nYear > 1900 && nYear < 2100);\r\n\r\n            return nYear;\r\n        }\r\n		\r\n        /// <summary>\r\n        /// ��ȡ���ڵ��·�\r\n        /// </summary>\r\n        /// <param name=\"dtString\">����ʱ�䴮</param>\r\n        /// <returns>�·�</returns>\r\n        public static int Month(string dtString)\r\n        {\r\n            int nMonth;\r\n\r\n            nMonth = Convert.ToInt32(dtString.Substring(5, 2));\r\n            Debug.Assert(nMonth >= 1 && nMonth <= 12);\r\n\r\n            return nMonth;\r\n        }\r\n		\r\n        /// <summary>\r\n        /// ��ȡ�����и��µĵڼ���\r\n        /// </summary>\r\n        /// <param name=\"dtString\">����ʱ�䴮</param>\r\n        /// <returns>���µĵڼ���</returns>\r\n        public static int Day(string dtString)\r\n        {\r\n            int nDay;\r\n\r\n            nDay = Convert.ToInt32(dtString.Substring(8, 2));\r\n            Debug.Assert(nDay >= 1 && nDay <= 31);\r\n\r\n            return nDay;\r\n        }\r\n        /// <summary>\r\n        /// ��ȡ�����е�Сʱ����\r\n        /// </summary>\r\n        /// <param name=\"dtString\">����ʱ�䴮</param>\r\n        /// <returns>Сʱ����</returns>\r\n        public static int Hour(string dtString)\r\n        {\r\n            int nHour;\r\n\r\n            if (dtString.Length != 19)\r\n                return 0;\r\n\r\n            nHour = Convert.ToInt32(dtString.Substring(11, 2));\r\n            Debug.Assert(nHour >= 0 && nHour <= 24);\r\n\r\n            return nHour;\r\n        }\r\n\r\n        /// <summary>\r\n        /// ��ȡ�����еķ��Ӳ���\r\n        /// </summary>\r\n        /// <param name=\"dtString\">����ʱ�䴮</param>\r\n        /// <returns>���Ӳ���</returns>\r\n        public static int Minute(string dtString)\r\n        {\r\n            int nMinute;\r\n\r\n            if (dtString.Length != 19)\r\n                return 0;\r\n\r\n            nMinute = Convert.ToInt32(dtString.Substring(14, 2));\r\n            Debug.Assert(nMinute >= 0 && nMinute <= 60);\r\n\r\n            return nMinute;\r\n        }\r\n\r\n        /// <summary>\r\n        /// ��ȡ�����е��벿��\r\n        /// </summary>\r\n        /// <param name=\"dtString\">����ʱ�䴮</param>\r\n        /// <returns>�벿��</returns>\r\n        public static int Second(string dtString)\r\n        {\r\n            int nSecond;\r\n\r\n            if (dtString.Length != 19)\r\n                return 0;\r\n\r\n            nSecond = Convert.ToInt32(dtString.Substring(17, 2));\r\n            Debug.Assert(nSecond >= 0 && nSecond <= 60);\r\n\r\n            return nSecond;\r\n        }\r\n\r\n        /// <summary>\r\n        /// ��ȡ��ʾ�����������ڼ�\r\n        /// </summary>\r\n        /// <param name=\"dtString\">����ʱ�䴮</param>\r\n        /// <returns>���ڼ�</returns>\r\n        /// <remarks>����ֵ��0��6��������Ϊ0��������Ϊ6</remarks>\r\n        public static int DayOfWeek(string dtString)\r\n        {\r\n            DateTime dt = DateTime.Parse(dtString);\r\n            return (int)dt.DayOfWeek;\r\n        }\r\n\r\n        /// <summary>\r\n        /// ��ȡ��ʾ��������һ���еĵڼ���\r\n        /// </summary>\r\n        /// <param name=\"dtString\">����ʱ�䴮</param>\r\n        /// <returns>һ���еĵڼ���</returns>\r\n        public static int DayOfYear(string dtString)\r\n        {\r\n            DateTime dt = DateTime.Parse(dtString);\r\n            return dt.DayOfYear;\r\n        }\r\n\r\n        /// <summary>\r\n        /// ��ָ�����·����ӵ�����ֵ��\r\n        /// </summary>\r\n        /// <param name=\"dtString\">ԭ��������</param>\r\n        /// <param name=\"nOffset\">ָ�����·���</param>\r\n        /// <returns>�õ�������</returns>\r\n        public static string AddMonths(string dtString, int nOffset)\r\n        {\r\n            DateTime dt = DateTime.Parse(dtString);\r\n            DateTime dtRes = dt.AddMonths(nOffset);\r\n            return dtRes.ToString(\"yyyy-MM-dd HH:mm:ss\");\r\n        }\r\n\r\n        /// <summary>\r\n        /// ��ָ���������ӵ�����ֵ��\r\n        /// </summary>\r\n        /// <param name=\"dtString\">ԭ��������</param>\r\n        /// <param name=\"offset\">ָ��������</param>\r\n        /// <returns>�õ�������</returns>\r\n        /// <remarks>��������Ϊ����</remarks>\r\n        public static string AddDays(string dtString, int offset)\r\n        {\r\n            DateTime dt = DateTime.Parse(dtString);\r\n            DateTime dtRes = dt.AddDays(offset);\r\n            return dtRes.ToString(\"yyyy-MM-dd HH:mm:ss\");\r\n        }\r\n\r\n        /// <summary>\r\n        /// ��ָ���������ӵ�����ֵ��\r\n        /// </summary>\r\n        /// <param name=\"dtString\">ԭ��������ʱ��</param>\r\n        /// <param name=\"offset\">ָ��������</param>\r\n        /// <returns>�õ�������ʱ��</returns>\r\n        /// <remarks>��������Ϊ����</remarks>\r\n        public static string AddSeconds(string dtString, int offset)\r\n        {\r\n            DateTime dt = DateTime.Parse(dtString);\r\n            DateTime dtRes = dt.AddSeconds(offset);\r\n            return dtRes.ToString(\"yyyy-MM-dd HH:mm:ss\");\r\n        }\r\n\r\n        /// <summary>\r\n        /// �õ���������ʱ��֮����������\r\n        /// </summary>\r\n        /// <param name=\"dtFromString\">��ʼ����</param>\r\n        /// <param name=\"dtToString\">��ֹ����</param>\r\n        /// <returns>��������</returns>\r\n        /// <remarks>\r\n        /// ���ж�ʱ��������ֻ�ж������Ƿ��л�����ʹʱ�����������ӣ�\r\n        /// ֻҪ�л������ڣ�Ҳ�����������������磬2005-05-05 23:58:58��\r\n        /// 2005-05-06 00:02:03֮�����������Ҳ��1��\r\n        /// </remarks>\r\n        public static int DaysAfter(string dtFromString, string dtToString)\r\n        {\r\n            DateTime dtFrom = DateTime.Parse(dtFromString).Date;\r\n            DateTime dtTo = DateTime.Parse(dtToString).Date;\r\n            TimeSpan timeSpan = dtTo - dtFrom;\r\n            return (int)timeSpan.TotalDays;\r\n        }\r\n\r\n        /// <summary>\r\n        /// �õ���������ʱ��֮����������\r\n        /// </summary>\r\n        /// <param name=\"dtFromString\">��ʼ����ʱ��</param>\r\n        /// <param name=\"dtToString\">��ֹ����ʱ��</param>\r\n        /// <returns>��������</returns>\r\n        public static int SecondsAfter(string dtFromString, string dtToString)\r\n        {\r\n            DateTime dtFrom = DateTime.Parse(dtFromString);\r\n            DateTime dtTo = DateTime.Parse(dtToString);\r\n            TimeSpan timeSpan = dtTo - dtFrom;\r\n            return (int)timeSpan.TotalSeconds;\r\n        }\r\n\r\n        /// <summary>\r\n        /// �õ���������ʱ��֮�����ĺ�����\r\n        /// </summary>\r\n        /// <param name=\"dtFromString\">��ʼ����ʱ��</param>\r\n        /// <param name=\"dtToString\">��ֹ����ʱ��</param>\r\n        /// <returns>���ĺ�����</returns>\r\n        public static long MilliSecondsAfter(string dtFromString, string dtToString)\r\n        {\r\n            DateTime dtFrom = DateTime.Parse(dtFromString);\r\n            DateTime dtTo = DateTime.Parse(dtToString);\r\n            TimeSpan timeSpan = dtTo - dtFrom;\r\n            return (long)timeSpan.TotalMilliseconds;\r\n        }\r\n\r\n		 /// <summary>\r\n        /// �����Ѻõ�ʱ���ʽdd \r\n        /// </summary>\r\n        /// <param name=\"dtFromString\">19λ��ʼ����ʱ��</param>\r\n        /// <param name=\"dtToString\">19λ��ֹ����ʱ��</param>\r\n        /// <returns></returns>\r\n        public static string StrDateTime(string dtFromString, string dtToString)\r\n        {\r\n            string sDateString = string.Empty;\r\n            long nMilliSeconds = MilliSecondsAfter(dtFromString, dtToString);\r\n            long nSeconds = nMilliSeconds / 1000;//�õ� ��\r\n            long nMinutes = nSeconds / 60;//�õ� ��\r\n            long nHours = nMinutes / 60;//�õ� Сʱ\r\n            long nDay = nHours / 24;//�õ� ��\r\n\r\n            if (nSeconds <= 60)\r\n            {\r\n                sDateString = nSeconds + \"��ǰ\";\r\n            }\r\n            else\r\n            {\r\n                if (nMinutes <= 60)\r\n                {\r\n                    sDateString = nMinutes + \"��ǰ\";\r\n                }\r\n                else\r\n                {\r\n                    if (nHours <= 24)\r\n                    {\r\n                        sDateString = nHours + \"Сʱǰ\";\r\n                    }\r\n                    else\r\n                    {\r\n                        if (nDay < 7)\r\n                        {\r\n                            sDateString = nDay + \"��ǰ\";\r\n                        }\r\n                        else\r\n                        {\r\n                            sDateString = GetDatePart(dtFromString);\r\n                        }\r\n                    }\r\n                }\r\n            }\r\n            return sDateString;\r\n        }\r\n\r\n		\r\n        /// <summary>\r\n        /// �õ�ʱ����ʱ���ʽ��dd hh:mm:ss��\r\n        /// </summary>\r\n        /// <param name=\"dtFromString\">19λ��ʼ����ʱ��</param>\r\n        /// <param name=\"dtToString\">19λ��ֹ����ʱ��</param>\r\n        /// <returns></returns>\r\n        public static string DateTimeAfter(string dtFromString, string dtToString)\r\n        {\r\n            string sDateString = string.Empty;\r\n            long nMilliSeconds = MilliSecondsAfter(dtFromString, dtToString);\r\n            long nSeconds = nMilliSeconds / 1000;//�õ� ��\r\n            long nMinutes = nSeconds / 60;//�õ� ��\r\n            long nHours = nMinutes / 60;//�õ� Сʱ\r\n            long nDay = nHours / 24;//�õ� ��\r\n\r\n            if (nDay > 0)\r\n            {\r\n                sDateString = nDay + \" \";\r\n                nHours = nHours % 24;//ȡ�� Сʱ\r\n            }\r\n            if (nHours > 0)\r\n            {\r\n                sDateString += nHours + \":\";\r\n                nMinutes = nMinutes % 60;//ȡ��� \r\n            }\r\n            if (nMinutes > 0)\r\n            {\r\n                sDateString += nMinutes + \":\";//�õ� ��\r\n                nSeconds = nSeconds % 60;//ȡ�� ��\r\n            }\r\n            sDateString += nSeconds;//�õ� �� \r\n            return sDateString;\r\n        }\r\n\r\n		\r\n		/// <summary>\r\n        /// ͨ��ʱ���֡���õ�ʱ�䴮\r\n        /// </summary>\r\n        /// <param name=\"nHour\">Сʱ</param>\r\n        /// <param name=\"nMinute\">����</param>\r\n        /// <param name=\"nSecond\">��</param>\r\n        /// <returns>ʱ�䲿��</returns>\r\n        public static string EncodeTime(int nHour, int nMinute, int nSecond)\r\n        {\r\n            string sResult = nHour.ToString(\"00\") + \":\" + nMinute.ToString(\"00\")\r\n                    + \":\" + nSecond.ToString(\"00\");\r\n            return sResult;\r\n        }\r\n\r\n        /// <summary>\r\n        /// ͨ���ꡢ�¡��յõ����ڲ���\r\n        /// </summary>\r\n        /// <param name=\"nYear\">���</param>\r\n        /// <param name=\"nMonth\">�·�</param>\r\n        /// <param name=\"nDay\">��</param>\r\n        /// <returns>���ڲ���</returns>\r\n        public static string EncodeDate(int nYear, int nMonth, int nDay)\r\n        {\r\n            string sResult = nYear.ToString(\"0000\") + \"-\" + nMonth.ToString(\"00\")\r\n                    + \"-\" + nDay.ToString(\"00\");\r\n            return sResult;\r\n        }\r\n\r\n        /// <summary>\r\n        /// �õ����ڲ���(yyyy-MM-dd)\r\n        /// </summary>\r\n        /// <param name=\"sDatetime\">����ʱ��</param>\r\n        /// <returns>���ڲ���</returns>\r\n        public static string GetDatePart(string sDatetime)\r\n        {\r\n            if (sDatetime.Length < 10)\r\n                return \"\";\r\n            return sDatetime.Substring(0, 10);\r\n        }\r\n\r\n        /// <summary>\r\n        /// ���r�g�D����8λ����(yyyyMMdd)\r\n        /// </summary>\r\n        /// <param name=\"sDatetime\">����ʱ��</param>\r\n        /// <returns>���ڲ���</returns>\r\n        public static string ToDatePart8(string sDatetime)\r\n        {\r\n            if (sDatetime.Length > 10)\r\n            {\r\n                sDatetime = sDatetime.Substring(0, 10);\r\n            }\r\n            if (sDatetime.Length < 10)\r\n                return \"\";\r\n            return sDatetime.Replace(\"-\", \"\");\r\n        }\r\n\r\n\r\n        /// <summary>\r\n        /// �����˜�(2011-1-1)�������ַ���ʽ���ɘ˜�����(yyyy-MM-dd)\r\n        /// </summary>\r\n        /// <param name=\"sDatetime\">���˜ʵ������ַ�(2011-1-1)</param>\r\n        /// <returns>�˜�����(yyyy-MM-dd)</returns>\r\n        public static string FormatDate(string sDatetime)\r\n        {\r\n            string[] sList = StringUtil.Split(sDatetime, \"-\");\r\n            string sDate = sList[0];\r\n            if (sList.Length != 3)\r\n            {\r\n                return sDatetime;\r\n            }\r\n            if (sList[1].Length != 2)\r\n            {\r\n                sDate += \"-0\" + sList[1];\r\n            }\r\n            else\r\n            {\r\n                sDate += \"-\" + sList[1];\r\n            }\r\n            if (sList[2].Length != 2)\r\n            {\r\n                sDate += \"-0\" + sList[2];\r\n            }\r\n            else\r\n            {\r\n                sDate += \"-\" + sList[2];\r\n            }\r\n            return sDate;\r\n        }\r\n\r\n        /// <summary>\r\n        /// ��8λ(yyyyMMdd)�����D����10����(yyyy-MM-dd) \r\n        /// </summary>\r\n        /// <param name=\"sDatetime\">����ʱ��</param>\r\n        /// <returns>���ڲ���</returns>\r\n        public static string FromDatePart8(string sDatetime8)\r\n        {\r\n            sDatetime8 = sDatetime8.Replace(\"-\", \"\");\r\n            sDatetime8 = sDatetime8.Replace(\" \", \"\");\r\n            if (sDatetime8.Length > 8)\r\n            {\r\n                sDatetime8 = sDatetime8.Substring(0, 8);\r\n            }\r\n            if (sDatetime8.Length < 8)\r\n                return \"\";\r\n            if (sDatetime8.Length == 8)\r\n            {\r\n                sDatetime8 = sDatetime8.Insert(4, \"-\");\r\n                sDatetime8 = sDatetime8.Insert(7, \"-\");\r\n                return sDatetime8;\r\n            }\r\n            return sDatetime8;\r\n        }\r\n\r\n        /// <summary>\r\n        /// �õ�ʱ�䲿��\r\n        /// </summary>\r\n        /// <param name=\"sDatetime\">����ʱ��</param>\r\n        /// <returns>ʱ�䲿��</returns>\r\n        public static string GetTimePart(string sDatetime)\r\n        {\r\n            if (sDatetime.Length == 19)\r\n                return sDatetime.Substring(11, 8);\r\n            else\r\n                return \"00:00:00\";\r\n        }\r\n\r\n        /// <summary>\r\n        /// �õ�һ�����ʼʱ��\r\n        /// </summary>\r\n        /// <param name=\"sDate\">����</param>\r\n        /// <returns>��ʼʱ��</returns>\r\n        public static string BeginTimeOfDay(string sDate)\r\n        {\r\n            if (sDate.Length > 10)\r\n                sDate = sDate.Substring(0, 10);\r\n            return sDate + \" 00:00:00\";\r\n        }\r\n\r\n        /// <summary>\r\n        /// �õ�һ��Ľ���ʱ��\r\n        /// </summary>\r\n        /// <param name=\"sDate\">����</param>\r\n        /// <returns>����ʱ��</returns>\r\n        public static string EndTimeOfDay(string sDate)\r\n        {\r\n            if (sDate.Length > 10)\r\n                sDate = sDate.Substring(0, 10);\r\n            return sDate + \" 23:59:59.999\";\r\n        }\r\n		\r\n		 /// <summary>\r\n        /// ��14λ������ʱ��ת��19λ������ʱ��\r\n        /// </summary>\r\n        /// <param name=\"sDT14\">14λ������ʱ��</param>\r\n        /// <returns>19λ������ʱ��</returns>\r\n        public static string FromDateTime14Str(string sDT14)\r\n        {\r\n            if (sDT14 == \"\")\r\n                return \"\";\r\n            if (sDT14.Length != 14)\r\n                throw new Exception(\"DateTimeUtil.FromDateTime14Str() error : ��������14λ\");\r\n\r\n            string sRet = sDT14.Substring(0, 4) + \"-\" + sDT14.Substring(4, 2)\r\n                    + \"-\" + sDT14.Substring(6, 2)\r\n                    + \" \" + sDT14.Substring(8, 2) + \":\" + sDT14.Substring(10, 2)\r\n                    + \":\" + sDT14.Substring(12, 2);\r\n            return sRet;\r\n        }\r\n', '0', '2017-09-08 09:48:20', null, 'c#.日期时间.DateTime.txt');
INSERT INTO `txt` VALUES ('115', '2', 'C#.替换.字符.C#特殊字符过滤函数.txt', 'C#�����ַ����˺���\r\n\r\nusing System;\r\nusing System.Collections.Generic;\r\nusing System.Text;\r\nusing System.Text.RegularExpressions;\r\n\r\n            /// <summary>\r\n            /// ���ַ������м����滻���е������ַ�\r\n            /// </summary>\r\n            /// <param name=\"strHtml\"></param>\r\n            /// <returns></returns>\r\n            public static string HtmlToTxt(string strHtml)\r\n            {\r\n                string[] aryReg ={\r\n                        @\"<script[^>]*?>.*?</script>\",\r\n                        @\"<(\\/\\s*)?!?((\\w+:)?\\w+)(\\w+(\\s*=?\\s*(([\"\"\'])(\\\\[\"\"\'tbnr]|[^\\7])*?\\7|\\w+)|.{0})|\\s)*?(\\/\\s*)?>\",\r\n                        @\"([\\r\\n])[\\s]+\",\r\n                        @\"&(quot|#34);\",\r\n                        @\"&(amp|#38);\",\r\n                        @\"&(lt|#60);\",\r\n                        @\"&(gt|#62);\",\r\n                        @\"&(nbsp|#160);\",\r\n                        @\"&(iexcl|#161);\",\r\n                        @\"&(cent|#162);\",\r\n                        @\"&(pound|#163);\",\r\n                        @\"&(copy|#169);\",\r\n                        @\"&#(\\d+);\",\r\n                        @\"-->\",\r\n                        @\"<!--.*\\n\"\r\n                        };\r\n\r\n                string newReg = aryReg[0];\r\n                string strOutput = strHtml;\r\n                for (int i = 0; i < aryReg.Length; i++)\r\n                {\r\n                    Regex regex = new Regex(aryReg[i], RegexOptions.IgnoreCase);\r\n                    strOutput = regex.Replace(strOutput, string.Empty);\r\n                }\r\n\r\n                strOutput.Replace(\"<\", \"\");\r\n                strOutput.Replace(\">\", \"\");\r\n                strOutput.Replace(\"\\r\\n\", \"\");\r\n\r\n\r\n                return strOutput;\r\n            }\r\n\r\n\r\n=================������������=====================\r\n    /// <summary>\r\n    /// �滻html�е������ַ�\r\n    /// </summary>\r\n    /// <param name=\"theString\">��Ҫ�����滻���ı���</param>\r\n    /// <returns>�滻����ı���</returns>\r\n    public string HtmlEncode(string theString)\r\n    {\r\n        theString = theString.Replace(\">\", \"&gt;\");\r\n        theString = theString.Replace(\"<\", \"&lt;\");\r\n        theString = theString.Replace(\" \", \"&nbsp;\");\r\n        theString = theString.Replace(\" \", \"&nbsp;\");\r\n        theString = theString.Replace(\"\\\"\", \"&quot;\");\r\n        theString = theString.Replace(\"\\\'\", \"\'\");\r\n        theString = theString.Replace(\"\\n\", \"<br/> \");\r\n        return theString;\r\n    }\r\n\r\n    /// <summary>\r\n    /// �ָ�html�е������ַ�\r\n    /// </summary>\r\n    /// <param name=\"theString\">��Ҫ�ָ����ı���</param>\r\n    /// <returns>�ָ��õ��ı���</returns>\r\n    public string HtmlDiscode(string theString)\r\n    {\r\n        theString = theString.Replace(\"&gt;\", \">\");\r\n        theString = theString.Replace(\"&lt;\", \"<\");\r\n        theString = theString.Replace(\"&nbsp;\", \" \");\r\n        theString = theString.Replace(\"&nbsp;\", \" \");\r\n        theString = theString.Replace(\"&quot;\", \"\\\"\");\r\n        theString = theString.Replace(\"\'\", \"\\\'\");\r\n        theString = theString.Replace(\"<br/> \", \"\\n\");\r\n        return theString;\r\n    }\r\n', '0', '2017-09-08 09:48:20', null, 'C#.替换.字符.C#特殊字符过滤函数.txt');
