INSERT INTO `txt` VALUES ('122', '2', 'C#.特殊字符转义方法.转义字符串中所有正则特殊字符.txt', '         /// <summary>\r\n        /// ת���ַ������������������ַ�\r\n        /// </summary>\r\n        /// <param name=\"input\">�����ַ���</param>\r\n        /// <returns></returns>\r\n       public  string FilterString(string input)\r\n        {\r\n            input = input.Replace(\"\\\\\", \"\\\\\\\\\");//���滻��\\������Ȼ�������Ϊ�滻���������ġ�\\��\r\n\r\n            Regex r = new Regex(\"[\\\\*\\\\.\\\\?\\\\+\\\\$\\\\^\\\\[\\\\]\\\\(\\\\)\\\\{\\\\}\\\\|\\\\/]\");\r\n            MatchCollection ms = r.Matches(input);\r\n            List<string> list = new List<string>();\r\n            foreach (Match item in ms)\r\n            {\r\n                if (list.Contains(item.Value))\r\n                    continue;\r\n                input = input.Replace(item.Value, \"\\\\\" + item.Value);\r\n                list.Add(item.Value);\r\n            }\r\n            return input;\r\n        }\r\n', '0', '2017-09-08 09:48:20', null, 'C#.特殊字符转义方法.转义字符串中所有正则特殊字符.txt');
