INSERT INTO `txt` VALUES ('135', '2', 'c#.随机码Random.Next.Validate.Check.验证.噪音线.干扰线.jun.cs.txt', '==========================Ҫ��֤��ҳ��===========================   \r\n   <img src=\"CheckCode.aspx\" alt=\"ˢ��\" id=\"imgs\" onclick=\"Check()\" />\r\n    <script type=\"text/javascript\">\r\n        function Check() {\r\n            document.getElementById(\"imgs\").src = \"RandomcodeImage.aspx?id=\" + Math.random();\r\n        }\r\n        Check();\r\n    </script>\r\n	/////////////��֤�Աȵ�ʱ���Session[\"ValidCode\"]��ȡֵ�Ƚ�\r\n=======================���������ͼƬ��ҳ�棨RandomcodeImage.aspx��=================\r\nusing System;\r\nusing System.Collections.Generic;\r\nusing System.Linq;\r\nusing System.Web;\r\nusing System.Web.UI;\r\nusing System.Web.UI.WebControls;\r\n\r\npublic partial class _RandomcodeImage : System.Web.UI.Page\r\n{\r\n    protected void Page_Load(object sender, EventArgs e)\r\n    {\r\n        createImage(createRandomCode(6));\r\n    }\r\n\r\n    /// <summary>\r\n    /// �����������\r\n    /// </summary>\r\n    /// <param name=\"piLength\"></param>\r\n    /// <returns></returns>\r\n    private string createRandomCode(int piLength)\r\n    {\r\n        //�˴�����������Ϊ��֤�ַ���Ҳ��ʹ���ַ�������������\r\n        string strWords = \"ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789\";\r\n        //string strWords = \"��һ���ڲ����к������д�Ϊ�ϸ�������Ҫ��ʱ���������������ڳ��ͷֶԳɻ�������궯ͬ��Ҳ���¹���˵�����������ඨ��ѧ������þ�ʮ��֮���ŵȲ��ȼҵ�������ˮ�����Զ�����С����ʵ�����������ƻ���ʹ���ҵ��ȥ���Ժ�Ӧ�����ϻ�������ЩȻǰ������������������ƽ����ȫ�������ظ��������������ķ�������ԭ��ô���Ȼ�������������˱���ֻû������⽨�¹���ϵ������������������ͨ����ֱ�⵳��չ�������Ա��λ�볣���ܴ�Ʒʽ���輰���ؼ�������ͷ���ʱ���·����ͼɽͳ��֪�Ͻ�����Ʊ����ֽ��ڸ�����ũָ������ǿ�ž�����������ս�Ȼ�����ȡ�ݴ����ϸ�ɫ���ż����α���ٹ������ߺ��ڶ�����ѹ־���������ý���˼����������ʲ������Ȩ��֤���强���ٲ�ת�������д�׽��ٻ�������������������ÿĿ�����߻�ʾ��������������뻪��ȷ�ſ�������ڻ�������Ԫ�����´������Ⱥ��ʯ������н������ɽ��Ҿ���Խ֯װӰ��ͳ������鲼���ݶ�����̷����������ѽ���ǧ��ί�ؼ��������ʡ��ϰ��Լ֧��ʷ���ͱ����������п˺γ���������̫׼��ֵ������ά��ѡ��д���ë�׿�Ч˹Ժ�齭�����������������ɲ�Ƭʼȴר״������ʶ����Բ����ס�����ؾ��ղκ�ϸ������������\";\r\n        char[] chrWord = strWords.ToCharArray();\r\n\r\n        System.Text.StringBuilder sbResult = new System.Text.StringBuilder();\r\n\r\n        //�����\r\n        Random rnd = new Random();\r\n        for (int i = 0; i < piLength; i++)\r\n        {\r\n            sbResult.Append(chrWord[rnd.Next(chrWord.Length)].ToString());\r\n        }\r\n\r\n        return sbResult.ToString();\r\n    }\r\n\r\n    /// <summary>\r\n    /// ����ͼƬ\r\n    /// </summary>\r\n    /// <param name=\"pstrCode\">�û�����ͼƬ�Ĵ���</param>\r\n    private void createImage(string pstrCode)\r\n    {\r\n        //�������Ϊ�������\r\n        if (string.IsNullOrEmpty(pstrCode.Trim()))\r\n            return;\r\n        //������ŵ�����session�У��Ա�������֤\r\n        Session[\"ValidCode\"] = pstrCode;\r\n        //����ͼƬ��С�����ݴ��볤��������\r\n        //�˴�Ҳ����ʹ��Image����\r\n        System.Drawing.Bitmap bmp = new System.Drawing.Bitmap(Convert.ToInt32(pstrCode.Length * 16), 20);\r\n        //��ͼ����\r\n        System.Drawing.Graphics g = System.Drawing.Graphics.FromImage(bmp);\r\n        //�����\r\n        Random rnd = new Random();\r\n        //����ͼƬΪ��ɫ\r\n        g.Clear(System.Drawing.Color.White);\r\n        //��ͼƬ�ı���������()\r\n        int lineCount = 10;// ����������\r\n        for (int i = 0; i < lineCount; i++)\r\n        {\r\n            int x1 = rnd.Next(bmp.Width);\r\n            int x2 = rnd.Next(bmp.Width);\r\n            int y1 = rnd.Next(bmp.Height);\r\n            int y2 = rnd.Next(bmp.Height);\r\n            g.DrawLine(new System.Drawing.Pen(System.Drawing.Color.FromArgb(rnd.Next(255), rnd.Next(255), rnd.Next(255)), (float)(rnd.Next(3))), x1, x2, y1, y2);\r\n        }\r\n\r\n        System.Drawing.Font fnt = new System.Drawing.Font(\"Arial\", 13, getFontStyle(rnd.Next(1, 5)));\r\n        System.Drawing.Drawing2D.LinearGradientBrush brsh = new System.Drawing.Drawing2D.LinearGradientBrush(new System.Drawing.Rectangle(0, 0, bmp.Width, bmp.Height), System.Drawing.Color.Blue, System.Drawing.Color.Red, 1.2f, true);\r\n        g.DrawString(pstrCode, fnt, brsh, 5, 2);\r\n\r\n        //��ͼƬ��ǰ�������\r\n        g.DrawRectangle(new System.Drawing.Pen(System.Drawing.Color.FromArgb(rnd.Next(255), rnd.Next(255), rnd.Next(255))), 0, 0, bmp.Width - 1, bmp.Height - 1);\r\n\r\n        System.IO.MemoryStream ms = new System.IO.MemoryStream();\r\n        bmp.Save(ms, System.Drawing.Imaging.ImageFormat.Gif);\r\n        Response.ClearContent();\r\n        Response.ContentType = \"bmp/Gif\";\r\n        Response.BinaryWrite(ms.ToArray());\r\n    }\r\n\r\n    /// <summary>\r\n    /// ��ȡFontStyle\r\n    /// </summary>\r\n    /// <param name=\"piFontStyle\">1~5֮���һ������</param>\r\n    /// <returns></returns>\r\n    private System.Drawing.FontStyle getFontStyle(int piFontStyle)\r\n    {\r\n        switch (piFontStyle)\r\n        {\r\n            case 1:\r\n                return System.Drawing.FontStyle.Bold;\r\n            case 2:\r\n                return System.Drawing.FontStyle.Italic;\r\n            case 3:\r\n                return System.Drawing.FontStyle.Regular;\r\n            case 4:\r\n                return System.Drawing.FontStyle.Strikeout;\r\n            case 5:\r\n                return System.Drawing.FontStyle.Underline;\r\n            default:\r\n                return System.Drawing.FontStyle.Regular;\r\n        }\r\n    }\r\n}\r\n', '0', '2017-09-08 09:48:20', null, 'c#.随机码Random.Next.Validate.Check.验证.噪音线.干扰线.jun.cs.txt');
