INSERT INTO `txt` VALUES ('153', '2', 'C#时间.年月日时分秒.生产随机码.txt', '		/// <summary>\r\n        /// �������������\r\n        /// </summary>\r\n        /// <returns></returns>\r\n        public static string GetRamCode()\r\n        {\r\n            return (DateTime.Now.ToString(\"yyyyMMddHHmmss\", CultureInfo.CurrentCulture) + MakeRandomString(\"0123456789\", 4));\r\n        }\r\n		\r\n		 /// <summary>\r\n        /// ȡ������ַ���\r\n        /// </summary>\r\n        /// <param name=\"pwdchars\"></param>\r\n        /// <param name=\"pwdlen\"></param>\r\n        /// <returns></returns>\r\n        public static string MakeRandomString(string pwdchars, int pwdlen)\r\n        {\r\n            StringBuilder builder = new StringBuilder();\r\n            Random random = new Random();\r\n            for (int i = 0; i < pwdlen; i++)\r\n            {\r\n                int num = random.Next(pwdchars.Length);\r\n                builder.Append(pwdchars[num]);\r\n            }\r\n            return builder.ToString();\r\n        }\r\n', '0', '2017-09-08 09:48:21', null, 'C#时间.年月日时分秒.生产随机码.txt');
INSERT INTO `txt` VALUES ('154', '2', 'C#根据当前时间获取，本周，本月，本季度等时间段.txt', 'C#���ݵ�ǰʱ���ȡ�����ܣ����£������ȵ�ʱ���\r\n    DateTime dt = DateTime.Now;  //��ǰʱ��  \r\n       \r\n    DateTime startWeek = dt.AddDays(1 - Convert.ToInt32(dt.DayOfWeek.ToString(\"d\")));  //������һ  \r\n    DateTime endWeek = startWeek.AddDays(6);  //��������  \r\n       \r\n    DateTime startMonth = dt.AddDays(1 - dt.Day);  //�����³�  \r\n    DateTime endMonth = startMonth.AddMonths(1).AddDays(-1);  //������ĩ  \r\n    //DateTime endMonth = startMonth.AddDays((dt.AddMonths(1) - dt).Days - 1);  //������ĩ  \r\n       \r\n    DateTime startQuarter = dt.AddMonths(0 - (dt.Month - 1) % 3).AddDays(1 - dt.Day);  //�����ȳ�  \r\n    DateTime endQuarter = startQuarter.AddMonths(3).AddDays(-1);  //������ĩ  \r\n       \r\n    DateTime startYear = new DateTime(dt.Year, 1, 1);  //�������  \r\n    DateTime endYear = new DateTime(dt.Year, 12, 31);  //������ĩ  \r\n       \r\n    �������졢���졢���ܡ����¡��ϼ��ȡ�����ȵȵȣ�ֻҪAddDays()��AddMonths()��AddYears()�⼸�ַ������һ�¾Ϳ����ˡ�  \r\n    C#��datetime��ʹ��  \r\n    //����㻹�����ף��ٿ�һ��������ʾ���ڼ��ķ�����Ӧ�ö���  \r\n    //����DayOfWeek���ص������ֵ����ڼ�������Ҫ����ת���ɺ��ַ��������Ķ�����Щ�˿��ܻ���switch��һ��һ���ض��գ���ʵ������ô�鷳��  \r\n    string[] Day = new string[] { \"������\", \"����һ\", \"���ڶ�\", \"������\", \"������\", \"������\", \"������\" };  \r\n    string week = Day[Convert.ToInt32(DateTime.Now.DayOfWeek.ToString(\"d\"))].ToString();  \r\n       \r\n    //���ܣ�ͬ���һ������7�죬���ܾ��Ǳ����ټ�ȥ7�죬����Ҳ��һ��  \r\n    DateTime.Now.AddDays(Convert.ToInt32 (1 - Convert.ToInt32(DateTime.Now.DayOfWeek)) - 7);        //����һ  \r\n    DateTime.Now.AddDays(Convert.ToInt32 (1 - Convert.ToInt32(DateTime.Now.DayOfWeek)) - 7).AddDays(6);     //����ĩ�������գ�  \r\n    //����  \r\n    DateTime.Now.AddDays(Convert.ToInt32 (1 - Convert.ToInt32(DateTime.Now.DayOfWeek)) + 7);        //����һ  \r\n    DateTime.Now.AddDays(Convert.ToInt32(1 - Convert.ToInt32(DateTime.Now.DayOfWeek)) + 7).AddDays(6);      //����ĩ  \r\n    //����,�ܶ��˶���˵���µĵ�һ����϶���1�ţ����һ������¸���һ���ټ�һ�졣��Ȼ���ǶԵ�  \r\n    //һ���д��  \r\n    DateTime.Now.Year.ToString() + DateTime.Now.Month.ToString() + \"1\"; //��һ��  \r\n    DateTime.Parse(DateTime.Now.Year.ToString() + DateTime.Now.Month.ToString() + \"1\").AddMonths(1).AddDays(-1).ToShortDateString();//���һ��  \r\n       \r\n    //����C#��ToString���ַ���ʽ�������  \r\n    DateTime.Now.ToString(\"yyyy-MM-01\");  \r\n    DateTime.Parse(DateTime.Now.ToString(\"yyyy-MM-01\")).AddMonths(1).AddDays(-1).ToShortDateString();  \r\n       \r\n    //�ϸ��£���ȥһ���·�  \r\n    DateTime.Parse(DateTime.Now.ToString(\"yyyy-MM-01\")).AddMonths(-1).ToShortDateString();  \r\n    DateTime.Parse(DateTime.Now.ToString(\"yyyy-MM-01\")).AddDays(-1).ToShortDateString();  \r\n    //�¸��£���ȥһ���·�  \r\n    DateTime.Parse(DateTime.Now.ToString(\"yyyy-MM-01\")).AddMonths(1).ToShortDateString();  \r\n    DateTime.Parse(DateTime.Now.ToString(\"yyyy-MM-01\")).AddMonths(2).AddDays(-1).ToShortDateString();  \r\n    //7���  \r\n    DateTime.Now.Date.ToShortDateString();  \r\n    DateTime.Now.AddDays(7).ToShortDateString();  \r\n    //7��ǰ  \r\n    DateTime.Now.AddDays(-7).ToShortDateString();  \r\n    DateTime.Now.Date.ToShortDateString();  \r\n       \r\n    //����ȣ���ToString���ַ���ʽ������Ҳ�����׵��������ȵĵ�һ������һ��  \r\n    DateTime.Parse(DateTime.Now.ToString(\"yyyy-01-01\")).ToShortDateString();  \r\n    DateTime.Parse(DateTime.Now.ToString(\"yyyy-01-01\")).AddYears(1).AddDays(-1).ToShortDateString();  \r\n    //����ȣ������ٽ����˰�  \r\n    DateTime.Parse(DateTime.Now.ToString(\"yyyy-01-01\")).AddYears(-1).ToShortDateString();  \r\n    DateTime.Parse(DateTime.Now.ToString(\"yyyy-01-01\")).AddDays(-1).ToShortDateString();  \r\n    //�����  \r\n    DateTime.Parse(DateTime.Now.ToString(\"yyyy-01-01\")).AddYears(1).ToShortDateString();  \r\n    DateTime.Parse(DateTime.Now.ToString(\"yyyy-01-01\")).AddYears(2).AddDays(-1).ToShortDateString();  \r\n       \r\n    /