INSERT INTO `txt` VALUES ('155', '2', 'C#格式化Json数据.Format.jun.txt', '��Ҫ���� namespace  Newtonsoft.Json \r\n\r\n\r\n\r\n\r\n\r\n public string ConvertJsonString(string str)\r\n        {\r\n            //��ʽ��json�ַ���\r\n            JsonSerializer serializer = new JsonSerializer();\r\n            TextReader tr = new StringReader(str);\r\n            JsonTextReader jtr = new JsonTextReader(tr);\r\n            object obj = serializer.Deserialize(jtr);\r\n            if (obj != null)\r\n            {\r\n                StringWriter textWriter = new StringWriter();\r\n                JsonTextWriter jsonWriter = new JsonTextWriter(textWriter)\r\n                {\r\n                    Formatting = Formatting.Indented,\r\n                    Indentation = 4,\r\n                    IndentChar = \' \'\r\n                };\r\n                serializer.Serialize(jsonWriter, obj);\r\n                return textWriter.ToString();\r\n            }\r\n            else\r\n            {\r\n                return str;\r\n            }\r\n        }\r\n', '0', '2017-09-08 09:48:21', null, 'C#格式化Json数据.Format.jun.txt');
INSERT INTO `txt` VALUES ('156', '2', 'C#模拟http 发送post或get请求.txt', 'C#模拟http 发送post或get请求\r\nfrom：http://www.cnblogs.com/xssxss/archive/2012/07/03/2574554.html\r\nprivate string HttpPost(string Url, string postDataStr)\r\n        {\r\n            HttpWebRequest request = (HttpWebRequest)WebRequest.Create(Url);\r\n            request.Method = \"POST\";\r\n            request.ContentType = \"application/x-www-form-urlencoded\";\r\n            request.ContentLength = Encoding.UTF8.GetByteCount(postDataStr);\r\n            request.CookieContainer = cookie;\r\n            Stream myRequestStream = request.GetRequestStream();\r\n            StreamWriter myStreamWriter = new StreamWriter(myRequestStream, Encoding.GetEncoding(\"gb2312\"));\r\n            myStreamWriter.Write(postDataStr);\r\n            myStreamWriter.Close();\r\n \r\n            HttpWebResponse response = (HttpWebResponse)request.GetResponse();\r\n \r\n            response.Cookies = cookie.GetCookies(response.ResponseUri);\r\n            Stream myResponseStream = response.GetResponseStream();\r\n            StreamReader myStreamReader = new StreamReader(myResponseStream, Encoding.GetEncoding(\"utf-8\"));\r\n            string retString = myStreamReader.ReadToEnd();\r\n            myStreamReader.Close();\r\n            myResponseStream.Close();\r\n \r\n            return retString;\r\n        }\r\n \r\n        public string HttpGet(string Url, string postDataStr)\r\n        {\r\n            HttpWebRequest request = (HttpWebRequest)WebRequest.Create(Url + (postDataStr == \"\" ? \"\" : \"?\") + postDataStr);\r\n            request.Method = \"GET\";\r\n            request.ContentType = \"text/html;charset=UTF-8\";\r\n \r\n            HttpWebResponse response = (HttpWebResponse)request.GetResponse();\r\n            Stream myResponseStream = response.GetResponseStream();\r\n            StreamReader myStreamReader = new StreamReader(myResponseStream, Encoding.GetEncoding(\"utf-8\"));\r\n            string retString = myStreamReader.ReadToEnd();\r\n            myStreamReader.Close();\r\n            myResponseStream.Close();\r\n \r\n            return retString;\r\n        }\r\n\r\n　　\r\n\r\n在post的时候有时也用的到cookie，像登录163发邮件时候就需要发送cookie，所以在外部一个cookie属性随时保存 CookieContainer cookie = new CookieContainer();\r\n\r\n！注意：有时候请求会重定向，但我们就需要从重定向url获取东西，像QQ登录成功后获取sid，但上面的会自动根据重定向地址跳转。我们可以用:\r\nrequest.AllowAutoRedirect = false;设置重定向禁用，你就可以从headers的Location属性中获取重定向地址\r\n', '0', '2017-09-08 09:48:21', null, 'C#模拟http 发送post或get请求.txt');
INSERT INTO `txt` VALUES ('157', '2', 'C#生成随机验证码.txt', 'asp.net(C#) ���������֤��Ĵ��� .\r\n���ࣺ �¼��� 2007-08-02 16:50 992���Ķ� ����(0) �ղ� �ٱ� \r\n  ���õ�������֤����� ��ͼƬЧ�����£�\r\n     \r\n\r\n\r\nusing System;  \r\nusing System.IO;  \r\nusing System.Drawing;  \r\nusing System.Drawing.Imaging;  \r\nusing System.Text;  \r\nusing System.Collections;  \r\nusing System.Web;  \r\nusing System.Web.UI;  \r\nusing System.Web.UI.WebControls;  \r\nusing System.Web.UI.WebControls.WebParts;  \r\nusing System.Web.UI.HtmlControls;  \r\n/**//// <summary>  \r\n///   \r\n/// ** asp.net(C#) ������֤�� **  \r\n///   \r\n///  File: GenerateCheckCode.aspx.cs  \r\n///   \r\n///  Author: ������ (Zxjay Ʈң)  \r\n///   \r\n///  E-Mail: tda7264@163.com  \r\n///   \r\n///  Date: 07-04-10  \r\n///   \r\n/// </summary>  \r\npublic partial class GenerateCheckCode : System.Web.UI.Page  \r\n...{  \r\n    protected void Page_Load(object sender, EventArgs e)  \r\n    ...{  \r\n        string chkCode = string.Empty;  \r\n        //��ɫ�б��������֤�롢���ߡ����  \r\n        Color[] color =...{ Color.Black, Color.Red, Color.Blue, Color.Green, Color.Orange, Color.Brown, Color.Brown, Color.DarkBlue };  \r\n        //�����б��������֤��  \r\n        string[] font =...{ \"Times New Roman\", \"MS Mincho\", \"Book Antiqua\", \"Gungsuh\", \"PMingLiU\", \"Impact\" };  \r\n        //��֤����ַ�����ȥ����һЩ���׻������ַ�  \r\n        char[] character =...{ \'2\', \'3\', \'4\', \'5\', \'6\', \'8\', \'9\', \'A\', \'B\', \'C\', \'D\', \'E\', \'F\', \'G\', \'H\', \'J\', \'K\', \'L\', \'M\', \'N\', \'P\', \'R\', \'S\', \'T\', \'W\', \'X\', \'Y\' };  \r\n        Random rnd = new Random();  \r\n        //������֤���ַ���  \r\n        for (int i = 0; i < 4; i++)  \r\n        ...{  \r\n            chkCode += character[rnd.Next(character.Length)];  \r\n        }  \r\n        Bitmap bmp = new Bitmap(100, 40);  \r\n        Graphics g = Graphics.FromImage(bmp);  \r\n        g.Clear(Color.White);  \r\n        //������  \r\n        for (int i = 0; i < 10; i++)  \r\n        ...{  \r\n            int x1 = rnd.Next(100);  \r\n            int y1 = rnd.Next(40);  \r\n            int x2 = rnd.Next(100);  \r\n            int y2 = rnd.Next(40);  \r\n            Color clr = color[rnd.Next(color.Length)];  \r\n            g.DrawLine(new Pen(clr), x1, y1, x2, y2);  \r\n        }  \r\n        //����֤���ַ���  \r\n        for (int i = 0; i < chkCode.Length; i++)  \r\n        ...{  \r\n            string fnt = font[rnd.Next(font.Length)];  \r\n            Font ft = new Font(fnt, 18);  \r\n            Color clr = color[rnd.Next(color.Length)];  \r\n            g.DrawString(chkCode[i].ToString(), ft, new SolidBrush(clr), (float)i * 20 + 8, (float)8);  \r\n        }  \r\n        //�����  \r\n        for (int i = 0; i < 100; i++)  \r\n        ...{  \r\n            int x = rnd.Next(bmp.Width);  \r\n            int y = rnd.Next(bmp.Height);  \r\n            Color clr = color[rnd.Next(color.Length)];  \r\n            bmp.SetPixel(x, y, clr);  \r\n        }  \r\n        //�����ҳ������棬���ø�ҳ�޻���  \r\n        Response.Buffer = true;  \r\n        Response.ExpiresAbsolute = System.DateTime.Now.AddMilliseconds(0);  \r\n        Response.Expires = 0;  \r\n        Response.CacheControl = \"no-cache\";  \r\n        Response.AppendHeader(\"Pragma\", \"No-Cache\");  \r\n        //����֤��ͼƬд���ڴ������������� \"image/Png\" ��ʽ���  \r\n        MemoryStream ms = new MemoryStream();  \r\n        try  \r\n        ...{  \r\n            bmp.Save(ms, ImageFormat.Png);  \r\n            Response.ClearContent();  \r\n            Response.ContentType = \"image/Png\";  \r\n            Response.BinaryWrite(ms.ToArray());  \r\n        }  \r\n        finally  \r\n        ...{  \r\n            //��ʽ�ͷ���Դ  \r\n            bmp.Dispose();  \r\n            g.Dispose();  \r\n        }  \r\n    }  \r\n} \r\n\r\nʹ�÷������£� \r\n�½���Ϊ GenerateCheckCode.aspx ���ļ������������뿽���������ļ� GenerateCheckCode.aspx.cs  \r\n����Ҫ��֤��ĵط�������� <asp:Image ID=\"img1\" runat=\"server\" ImageUrl=\"~/GenerateCheckCode.aspx\" /> ���ɡ�\r\n������ת���Խű�֮�ң�http://www.jb51.net/html/200704/76/9492.htm\r\n', '0', '2017-09-08 09:48:21', null, 'C#生成随机验证码.txt');
