INSERT INTO `txt` VALUES ('188', '2', 'ConfigBase.cs.txt', '\r\nusing System;\r\nusing System.Collections.Generic;\r\nusing System.Text;\r\nusing System.Collections;\r\n\r\nusing System.Xml;\r\n\r\nnamespace BJHouse.Common\r\n{\r\n    /// <summary>\r\n    /// 配置的基类。配置基于XML文件的格式，按相对固定的格式组织。\r\n    /// 如果需要针对一个特定文件进行处理时，一般需要继承该类。\r\n    /// </summary>\r\n    public class ConfigBase\r\n    {\r\n        /// <summary>\r\n        /// 主結點configRoot下的子結點\r\n        /// </summary>\r\n        private Hashtable _htSections;\r\n        /// <summary>\r\n        /// xml文檔\r\n        /// </summary>\r\n        private XmlDocument _xmlDoc = null;\r\n        /// <summary>\r\n        /// xml文檔configRoot根結點\r\n        /// </summary>\r\n        private XmlNode _nodeRoot = null;\r\n        /// <summary>\r\n        /// xml文檔路徑\r\n        /// </summary>\r\n        private string _filePath = \"\";\r\n\r\n        private void LoadSectionNode(XmlNode sectionNode)\r\n        {\r\n            Hashtable htKeys = new Hashtable();\r\n\r\n            foreach (XmlNode keyNode in sectionNode.ChildNodes)\r\n            {\r\n                if (keyNode.NodeType != XmlNodeType.Comment)\r\n                {\r\n                    foreach (XmlAttribute attr in keyNode.Attributes)\r\n                    {\r\n                        if (!htKeys.ContainsKey(keyNode.Name) && attr.Name == \"value\")\r\n                            htKeys.Add(keyNode.Name, attr.Value);\r\n                    }\r\n                }\r\n            }\r\n\r\n            if (sectionNode.NodeType != XmlNodeType.Comment)\r\n                _htSections.Add(sectionNode.Name, htKeys);\r\n        }\r\n\r\n        /// <summary>\r\n        /// 构造函数\r\n        /// </summary>\r\n        public ConfigBase()\r\n        {\r\n            _htSections = new Hashtable();\r\n        }\r\n\r\n        /// <summary>\r\n        /// 构造函数\r\n        /// </summary>\r\n        /// <param name=\"sFileName\">配置文件名</param>\r\n        public ConfigBase(string sFileName)\r\n        {\r\n            _htSections = new Hashtable();\r\n            LoadFromFile(sFileName);\r\n        }\r\n\r\n        /// <summary>\r\n        /// 从文件中加载配置项\r\n        /// </summary>\r\n        /// <param name=\"sFileName\">配置文件名</param>\r\n        public void LoadFromFile(string sFileName)\r\n        {\r\n            //========== 1. 打开文件 ============\r\n            _filePath = sFileName;\r\n            _xmlDoc = new XmlDocument();\r\n            _xmlDoc.Load(_filePath);\r\n            _nodeRoot = _xmlDoc.DocumentElement;\r\n\r\n            //============= 2. 处理每一个子节点 ========\r\n            foreach (XmlNode node in _nodeRoot.ChildNodes)\r\n                LoadSectionNode(node);\r\n        }\r\n\r\n\r\n        /// <summary>\r\n        /// 更新整個XML文件(保存)\r\n        /// </summary>\r\n        public void RefreshNode()\r\n        {\r\n            _xmlDoc.Save(_filePath);\r\n        }\r\n\r\n        /// <summary>\r\n        /// 在指定結點上添加一個新結點（不寫入）\r\n        /// </summary>\r\n        /// <param name=\"noteSection\">父級節點</param>\r\n        /// <param name=\"sOldNodeName\">修改原結點名</param>\r\n        /// <param name=\"sNewNodeName\">修改後結點名</param>\r\n        /// <param name=\"sNewNodeValue\">新結點值</param>\r\n        /// <returns>新結點</returns>\r\n        private XmlElement UpdateChildNode(XmlElement noteSection, string sOldNodeName, string sNewNodeName, string sNewNodeValue)\r\n        {\r\n            //=============== 1.0 查找是否存在 ==============\r\n            XmlElement element = FindNode(noteSection, sOldNodeName);\r\n            if (element == null)\r\n            {\r\n                return element;\r\n            }\r\n\r\n            //=============== 2.0 修改節點名 ==============\r\n            if (element.Name != sNewNodeName)\r\n            {\r\n                element = InsertChildNode(noteSection, sNewNodeName, sNewNodeValue);\r\n                noteSection.RemoveChild(element);\r\n                return element;\r\n            }\r\n\r\n            //=============== 3.0 修改節點屬性值 ==============\r\n            UpdateNode(element, sNewNodeValue);\r\n            return element;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 在指定結點上添加一個新結點（不寫入）\r\n        /// </summary>\r\n        /// <param name=\"noteSection\">父級節點</param>\r\n        /// <param name=\"sNodeName\">修改結點名</param>\r\n        /// <param name=\"sNodeValue\">新結點值</param>\r\n        /// <returns>新結點</returns>\r\n        private XmlElement UpdateChildNode(XmlElement noteSection, string sNodeName, string sNodeValue)\r\n        {\r\n            return UpdateChildNode(noteSection, sNodeName, sNodeName, sNodeValue);\r\n        }\r\n\r\n        /// <summary>\r\n        /// 修改當前節點（不寫入）\r\n        /// </summary>\r\n        /// <param name=\"noteSection\">修改的節點</param>  \r\n        /// <param name=\"sNewNodeValue\">新結點值</param>\r\n        /// <returns>是否修改</returns>\r\n        private bool UpdateNode(XmlElement xNote, string sNewNodeValue)\r\n        {\r\n            bool isUpdate = false;\r\n            //=============== 3.0 是否修改節點值 ==============\r\n            if (xNote.Attributes.Count > 0 && xNote.Attributes[0].Value != sNewNodeValue)\r\n            {\r\n                xNote.Attributes[0].Value = sNewNodeValue;\r\n                isUpdate = true;\r\n            }\r\n            return isUpdate;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 在指定結點上添加一個新結點(沒有屬性)\r\n        /// </summary> \r\n        /// <param name=\"sNodeName\">新結點名</param> \r\n        /// <returns>新結點</returns>\r\n        private XmlElement InsertSectionNode(string sNodeName)\r\n        {\r\n            XmlElement noteSection = _xmlDoc.CreateElement(sNodeName);\r\n            _nodeRoot.AppendChild(noteSection);\r\n            return noteSection;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 從Section 上刪除一個結點(更新)\r\n        /// </summary> \r\n        /// <param name=\"sSectionName\">新結點名</param> \r\n        /// <returns>新結點</returns>\r\n        public bool DeleteSectionNode(string sSectionName)\r\n        {\r\n            XmlElement noteSection = FindSectionNode(sSectionName);\r\n            if (noteSection != null)\r\n            {\r\n                DeleteSectionNode(noteSection);\r\n                return true;\r\n            }\r\n            return false;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 從Section 上刪除一個結點(更新)\r\n        /// </summary> \r\n        /// <param name=\"noteSection\">Section結點</param> \r\n        /// <returns>新結點</returns>\r\n        public void DeleteSectionNode(XmlElement noteSection)\r\n        {\r\n            _nodeRoot.RemoveChild(noteSection);\r\n            _htSections.Remove(noteSection.Name);\r\n            RefreshNode();\r\n        }\r\n\r\n        /// <summary>\r\n        /// 刪除一個結點\r\n        /// </summary> \r\n        /// <param name=\"xNode\">主結點</param>  \r\n        /// <param name=\"xNode\">要刪除的結點名</param>  \r\n        /// <returns>新結點</returns>\r\n        public bool DeleteNode(XmlElement noteSection, string sNoteName)\r\n        {\r\n            XmlElement xNote = FindNode(noteSection, sNoteName);\r\n            if (xNote == null)\r\n            {\r\n                return false;\r\n            }\r\n\r\n            ((Hashtable)_htSections[noteSection.Name]).Remove(sNoteName);\r\n            noteSection.RemoveChild(xNote);\r\n            RefreshNode();\r\n            return true;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 從Section 上刪除一個結點\r\n        /// </summary> \r\n        /// <param name=\"sSectionName\">主結點名</param> \r\n        /// <param name=\"sNodeName\">要刪除的結點名</param> \r\n        /// <returns>新結點</returns>\r\n        public bool DeleteNode(string sSectionName, string sNodeName)\r\n        {\r\n            XmlElement noteSection = FindSectionNode(sSectionName);\r\n            if (noteSection != null)\r\n            {\r\n                return DeleteNode(noteSection, sNodeName);\r\n            }\r\n            return false;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 在指定結點上添加一個新結點\r\n        /// </summary>\r\n        /// <param name=\"noteSection\">父級節點</param>\r\n        /// <param name=\"sNodeName\">新結點名</param>\r\n        /// <param name=\"sNodeValue\">新結點值</param>\r\n        /// <returns>新結點</returns>\r\n        private XmlElement InsertChildNode(XmlElement noteSection, string sNodeName, string sNodeValue)\r\n        {\r\n            XmlElement element = _xmlDoc.CreateElement(sNodeName);\r\n            XmlAttribute att = _xmlDoc.CreateAttribute(\"value\");\r\n            att.Value = sNodeValue;\r\n            element.Attributes.Append(att);\r\n            noteSection.AppendChild(element);\r\n            return element;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 根據SectionNode結點名查找(快)\r\n        /// </summary>\r\n        /// <param name=\"sSectionName\">結點名</param>\r\n        /// <returns>XmlElement結點</returns>\r\n        private XmlElement FindSectionNode(string sSectionName)\r\n        {\r\n            //============= 1. 处理configRoot同級节点 ========\r\n            foreach ( XmlNode sectionNode in _nodeRoot.ChildNodes)\r\n            {\r\n                if (sectionNode is XmlElement && sectionNode.Name == sSectionName)\r\n                {\r\n                    return (XmlElement)sectionNode;\r\n                }\r\n            }\r\n            return null;\r\n        }\r\n\r\n\r\n        /// <summary>\r\n        /// 查找XmlNode結點下是否存在sNodeName(所有)\r\n        /// </summary>\r\n        /// <param name=\"sNodeName\">結點名</param>\r\n        /// <returns>XmlElement結點</returns>\r\n        private XmlElement FindNode(XmlElement xNode, string sNodeName)\r\n        {\r\n            //============= 1. 处理configRoot同級节点 ========\r\n            foreach (XmlNode sectionNode in xNode.ChildNodes)\r\n            {\r\n                if (sectionNode is XmlElement && sectionNode.Name == sNodeName)\r\n                {\r\n                    return (XmlElement)sectionNode;\r\n                }\r\n                if (sectionNode.ChildNodes.Count > 0)\r\n                {\r\n                    return FindNode((XmlElement)sectionNode, sNodeName);\r\n                }\r\n            }\r\n            return null;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 設置  个  里的配  Hash表〃\r\n        /// </summary>\r\n        /// <param name=\"sSectionName\">父級名</param> \r\n        /// <param name=\"sNodeName\">名</param>  \r\n        /// <param name=\"sNodeValue\"> 值</param>  \r\n        public void SetString(string sSectionName, string sNodeName, string sNodeValue)\r\n        {\r\n            SetString(sSectionName, sNodeName, sNodeValue, false);\r\n        }\r\n\r\n        /// <summary>\r\n        /// 設置  个  里的配  Hash表〃\r\n        /// </summary>\r\n        /// <param name=\"sSectionName\">父級名</param> \r\n        /// <param name=\"sNodeName\">名</param>  \r\n        /// <param name=\"sNodeValue\"> 值</param>  \r\n        /// <param name=\"isSave\"> 是否保存</param>  \r\n        public void SetString(string sSectionName, string sNodeName, string sNodeValue, bool isSave)\r\n        {\r\n            if (!_htSections.ContainsKey(sSectionName))\r\n            {\r\n                //================= 1.0 添加節點 =================\r\n                XmlElement noteSection = InsertSectionNode(sSectionName);\r\n                InsertChildNode(noteSection, sNodeName, sNodeValue);\r\n\r\n                //================= 2.0 添加鍵值 =================\r\n                Hashtable hsk = new Hashtable();\r\n                hsk.Add(sNodeName, sNodeValue);\r\n                _htSections.Add(sSectionName, hsk);\r\n            }\r\n            else\r\n            {\r\n                //================= 1.0 添加/修改節點 =================\r\n                XmlElement noteSection = FindSectionNode(sSectionName);\r\n                if (noteSection == null)\r\n                {\r\n                    noteSection = InsertSectionNode(sSectionName);\r\n                }\r\n                XmlElement xNote = FindNode(noteSection, sNodeName);\r\n                if (xNote == null)\r\n                {\r\n                    InsertChildNode(noteSection, sNodeName, sNodeValue);\r\n                }\r\n                else\r\n                {\r\n                    UpdateNode(xNote, sNodeValue);\r\n                }\r\n\r\n                //================= 2.0 添加鍵值 =================\r\n                Hashtable hsk = GetSection(sSectionName);\r\n                hsk[sNodeName] = sNodeValue;\r\n            }\r\n            if (isSave)\r\n            {\r\n                _xmlDoc.Save(_filePath);\r\n            }\r\n        }\r\n\r\n\r\n        /// <summary>\r\n        /// 得到配置字符串\r\n        /// </summary>\r\n        /// <param name=\"sSectionName\">段名</param>\r\n        /// <param name=\"sKeyName\">关键字名</param>\r\n        /// <returns>配置的字符串</returns>\r\n        public string GetString(string sSectionName, string sKeyName)\r\n        {\r\n            return GetString(sSectionName, sKeyName, \"\");\r\n        }\r\n\r\n        /// <summary>\r\n        /// 得到配置字符串\r\n        /// </summary>\r\n        /// <param name=\"sSectionName\">段名</param>\r\n        /// <param name=\"sKeyName\">关键字名</param>\r\n        /// <param name=\"sDefault\">缺省值</param>\r\n        /// <returns>配置的字符串</returns>\r\n        public string GetString(string sSectionName, string sKeyName, string sDefault)\r\n        {\r\n            string sRet;\r\n            try\r\n            {\r\n                sRet = (string)((Hashtable)_htSections[sSectionName])[sKeyName];\r\n            }\r\n            catch\r\n            {\r\n                return sDefault;\r\n            }\r\n            if (sRet == null)\r\n                return sDefault;\r\n            else\r\n                return sRet;\r\n        }\r\n\r\n\r\n\r\n        /// 得到配置的整数\r\n        /// </summary>\r\n        /// <param name=\"sSectionName\">段名</param>\r\n        /// <param name=\"sKeyName\">关键字名</param>\r\n        /// <returns>配置的整数</returns>\r\n        public int GetInt(string sSectionName, string sKeyName)\r\n        {\r\n            return GetInt(sSectionName, sKeyName, 0);\r\n        }\r\n\r\n        /// <summary>\r\n        /// 得到配置的整数\r\n        /// </summary>\r\n        /// <param name=\"sSectionName\">段名</param>\r\n        /// <param name=\"sKeyName\">关键字名</param>\r\n        /// <param name=\"nDefault\">缺省值</param>\r\n        /// <returns>配置的整数</returns>\r\n        public int GetInt(string sSectionName, string sKeyName, int nDefault)\r\n        {\r\n            Object objRet;\r\n            int nRet;\r\n            try\r\n            {\r\n                objRet = ((Hashtable)_htSections[sSectionName])[sKeyName];\r\n            }\r\n            catch\r\n            {\r\n                return nDefault;\r\n            }\r\n\r\n            if (objRet == null)\r\n                return nDefault;\r\n\r\n            try\r\n            {\r\n                nRet = Convert.ToInt32(objRet);\r\n                return nRet;\r\n            }\r\n            catch\r\n            {\r\n                return nDefault;\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 得到配置的布尔值\r\n        /// </summary>\r\n        /// <param name=\"sSectionName\">段名</param>\r\n        /// <param name=\"sKeyName\">关键字名</param>\r\n        /// <returns>配置的布尔值</returns>\r\n        /// <remarks>缺省返回false</remarks>\r\n        public bool GetBool(string sSectionName, string sKeyName)\r\n        {\r\n            return GetBool(sSectionName, sKeyName, false);\r\n        }\r\n\r\n        /// <summary>\r\n        /// 得到配置的布尔值\r\n        /// </summary>\r\n        /// <param name=\"sSectionName\">段名</param>\r\n        /// <param name=\"sKeyName\">关键字名</param>\r\n        /// <param name=\"bDefault\">缺省值</param>\r\n        /// <returns>配置的布尔值</returns>\r\n        /// <remarks>\r\n        /// 判断配置项的第一个字符，为1,T,t,Y,y则返回真，\r\n        /// 为0,F,f,N,n返回假。这样，对于true, false, yes,\r\n        /// no的处理也能够一网打尽。\r\n        /// </remarks>\r\n        public bool GetBool(string sSectionName, string sKeyName, bool bDefault)\r\n        {\r\n            string sRet;\r\n            sRet = GetString(sSectionName, sKeyName, \"HlThoz\");\r\n            if (sRet == \"HlThoz\")\r\n                return bDefault;\r\n\r\n            char cFirstChar = sRet[0];\r\n            switch (cFirstChar)\r\n            {\r\n                case \'1\':\r\n                case \'T\':\r\n                case \'t\':\r\n                case \'Y\':\r\n                case \'y\':\r\n                    return true;\r\n                case \'0\':\r\n                case \'F\':\r\n                case \'f\':\r\n                case \'N\':\r\n                case \'n\':\r\n                    return false;\r\n                default:\r\n                    return bDefault;\r\n            }\r\n        }\r\n         \r\n\r\n        /// <summary>\r\n        /// 得到一个段里的配置Hash表。\r\n        /// </summary>\r\n        /// <param name=\"sSectionName\">段名</param>\r\n        /// <returns>段的配置Hash表</returns>\r\n        public Hashtable GetSection(string sSectionName)\r\n        {\r\n            Hashtable htRet;\r\n            htRet = (Hashtable)_htSections[sSectionName];\r\n            return htRet;\r\n        }\r\n\r\n\r\n        /// <summary>\r\n        /// 返回所有子節點\r\n        /// </summary>                            \r\n        /// <returns>所有子節點</returns>\r\n        public Hashtable GetSection()\r\n        {\r\n            return this._htSections;\r\n        }\r\n    }\r\n}\r\n', '0', '2017-09-08 09:48:22', null, 'ConfigBase.cs.txt');
INSERT INTO `txt` VALUES ('189', '2', 'ConfigHelper.cs.配置管理.txt', '/**************************************\r\n* ���ã�ת�������ַ�����\r\n**************************************/\r\nusing System;\r\nusing System.Configuration;\r\nusing System.Collections.Generic;\r\nusing System.Text;\r\n\r\nnamespace Permission.Lib\r\n{\r\n    public sealed class ConfigHelper\r\n    {\r\n        /// <summary>\r\n        /// �õ�AppSettings�е������ַ�����Ϣ\r\n        /// </summary>\r\n        /// <param name=\"key\"></param>\r\n        /// <returns></returns>\r\n        public static string GetConfigString(string key)\r\n        {\r\n            string CacheKey = \"AppSettings-\" + key;\r\n            object objModel = DataCache.GetCache(CacheKey);\r\n            if (objModel == null)\r\n            {\r\n                try\r\n                {\r\n                    objModel = ConfigurationManager.AppSettings[key];\r\n                    if (objModel != null)\r\n                    {\r\n                        DataCache.SetCache(CacheKey, objModel, DateTime.Now.AddMinutes(180), TimeSpan.Zero);\r\n                    }\r\n                }\r\n                catch\r\n                { }\r\n            }\r\n            return objModel.ToString();\r\n        }\r\n\r\n        /// <summary>\r\n        /// �õ�AppSettings�е�����Bool��Ϣ\r\n        /// </summary>\r\n        /// <param name=\"key\"></param>\r\n        /// <returns></returns>\r\n        public static bool GetConfigBool(string key)\r\n        {\r\n            bool result = false;\r\n            string cfgVal = GetConfigString(key);\r\n            if (null != cfgVal && string.Empty != cfgVal)\r\n            {\r\n                try\r\n                {\r\n                    result = bool.Parse(cfgVal);\r\n                }\r\n                catch (FormatException)\r\n                {\r\n                    // Ignore format exceptions.\r\n                }\r\n            }\r\n            return result;\r\n        }\r\n        /// <summary>\r\n        /// �õ�AppSettings�е�����Decimal��Ϣ\r\n        /// </summary>\r\n        /// <param name=\"key\"></param>\r\n        /// <returns></returns>\r\n        public static decimal GetConfigDecimal(string key)\r\n        {\r\n            decimal result = 0;\r\n            string cfgVal = GetConfigString(key);\r\n            if (null != cfgVal && string.Empty != cfgVal)\r\n            {\r\n                try\r\n                {\r\n                    result = decimal.Parse(cfgVal);\r\n                }\r\n                catch (FormatException)\r\n                {\r\n                    // Ignore format exceptions.\r\n                }\r\n            }\r\n\r\n            return result;\r\n        }\r\n        /// <summary>\r\n        /// �õ�AppSettings�е�����int��Ϣ\r\n        /// </summary>\r\n        /// <param name=\"key\"></param>\r\n        /// <returns></returns>\r\n        public static int GetConfigInt(string key)\r\n        {\r\n            int result = 0;\r\n            string cfgVal = GetConfigString(key);\r\n            if (null != cfgVal && string.Empty != cfgVal)\r\n            {\r\n                try\r\n                {\r\n                    result = int.Parse(cfgVal);\r\n                }\r\n                catch (FormatException)\r\n                {\r\n                    // Ignore format exceptions.\r\n                }\r\n            }\r\n\r\n            return result;\r\n        }\r\n    }\r\n}\r\n', '0', '2017-09-08 09:48:22', null, 'ConfigHelper.cs.配置管理.txt');
INSERT INTO `txt` VALUES ('190', '2', 'ConvertUtil.cs.txt', '\r\nusing System;\r\nusing System.Collections.Generic;\r\nusing System.Text;\r\n\r\nusing System.Text.RegularExpressions;\r\n\r\nnamespace BJHouse.Common\r\n{\r\n    /// <summary>\r\n    /// 处理各个类型之间的转换\r\n    /// </summary>\r\n    public class ConvertUtil\r\n    {\r\n        /// <summary>\r\n        /// 构造函数\r\n        /// </summary>\r\n        private ConvertUtil()\r\n        {\r\n        }\r\n\r\n        #region NotNullStr\r\n\r\n        /// <summary>\r\n        /// 返回非空(null)字符串\r\n        /// </summary>\r\n        /// <param name=\"canNullStr\">待转换的对象</param>\r\n        /// <param name=\"defaultStr\">缺省字符串</param>\r\n        /// <returns>得到字符串</returns>\r\n        public static string NotNullStr(object canNullStr, string defaultStr)\r\n        {\r\n            if (canNullStr == null)\r\n            {\r\n                if (defaultStr != null)\r\n                {\r\n                    return defaultStr;\r\n                }\r\n                else\r\n                {\r\n                    return \"\";\r\n                }\r\n            }\r\n            else\r\n            {\r\n                return Convert.ToString(canNullStr);\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 返回非空(null)字符串\r\n        /// </summary>\r\n        /// <param name=\"canNullStr\">待转换的对象</param>\r\n        /// <returns>得到的字符串。如果为对象null，则返回\"\"</returns>\r\n        public static string NotNullStr(object canNullStr)\r\n        {\r\n            return NotNullStr(canNullStr, \"\");\r\n        }\r\n\r\n        #endregion NotNullStr\r\n\r\n\r\n        /// <summary>\r\n        /// 将对象转换为 byte\r\n        /// </summary>\r\n        /// <param name=\"objInt\">对象</param> \r\n        /// <returns>得到的整数</returns>\r\n        public static byte ToByte(object objByte)\r\n        {\r\n            try\r\n            {\r\n                return Convert.ToByte(objByte);\r\n            }\r\n            catch\r\n            {\r\n                return new byte();\r\n            }\r\n        }\r\n\r\n\r\n\r\n        #region ToLong\r\n\r\n        /// <summary>\r\n        /// 将对象转换为整型\r\n        /// </summary>\r\n        /// <param name=\"objInt\">对象</param>\r\n        /// <param name=\"defaultValue\">缺省值</param>\r\n        /// <returns>得到的整数</returns>\r\n        public static long ToLong(object objInt, long defaultValue)\r\n        {\r\n            if (objInt == null)\r\n            {\r\n                return defaultValue;\r\n            }\r\n            else if (objInt is string)\r\n            {\r\n                string sString = (string)objInt;\r\n                return ToLong__StringOnly(sString, defaultValue);\r\n            }\r\n            else if (objInt is long)\r\n            {\r\n                return (long)objInt;\r\n            }\r\n            else\r\n            {\r\n                try\r\n                {\r\n                    return Convert.ToInt64(objInt);\r\n                }\r\n                catch\r\n                {\r\n                    return defaultValue;\r\n                }\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 字符串转换为整数\r\n        /// </summary>\r\n        /// <param name=\"sToConvertString\"></param>\r\n        /// <param name=\"nDefaultValue\"></param>\r\n        /// <returns></returns>\r\n        public static long ToLong__StringOnly(string sToConvertString, long nDefaultValue)\r\n        {\r\n            Regex rex = new Regex(@\"^[-\\+]?\\d+$\");\r\n            long nResult = nDefaultValue;\r\n            if (rex.IsMatch(sToConvertString))\r\n            {\r\n                nResult = long.Parse(sToConvertString);\r\n                return nResult;\r\n            }\r\n            else\r\n                return nDefaultValue;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 将对象转换为整型\r\n        /// </summary>\r\n        /// <param name=\"objInt\">对象</param>\r\n        /// <returns>得到的整数。缺省为0</returns>\r\n        public static long ToLong(object objInt)\r\n        {\r\n            return ToLong(objInt, 0);\r\n        }\r\n\r\n        #endregion ToLong\r\n\r\n\r\n        #region ToInt\r\n\r\n        /// <summary>\r\n        /// 将对象转换为整型\r\n        /// </summary>\r\n        /// <param name=\"objInt\">对象</param>\r\n        /// <param name=\"defaultValue\">缺省值</param>\r\n        /// <returns>得到的整数</returns>\r\n        public static int ToInt(object objInt, int defaultValue)\r\n        {\r\n            try\r\n            {\r\n                if (objInt == null)\r\n                {\r\n                    return defaultValue;\r\n                }\r\n                else if (objInt is string)\r\n                {\r\n                    string sString = (string)objInt;\r\n                    if (sString.Length > 10)\r\n                    {\r\n                        return defaultValue;\r\n                    }\r\n                    else\r\n                    {\r\n                        return ToInt__StringOnly(sString, defaultValue);\r\n                    }\r\n\r\n                }\r\n                else if (objInt is int)\r\n                {\r\n                    return (int)objInt;\r\n                }\r\n                else\r\n                {\r\n                    return Convert.ToInt32(objInt); \r\n                }\r\n            }\r\n            catch\r\n            {\r\n                return defaultValue;\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 字符串转换为整数\r\n        /// </summary>\r\n        /// <param name=\"sToConvertString\"></param>\r\n        /// <param name=\"nDefaultValue\"></param>\r\n        /// <returns></returns>\r\n        public static int ToInt__StringOnly(string sToConvertString, int nDefaultValue)\r\n        {\r\n            Regex rex = new Regex(@\"^[-\\+]?\\d+$\");\r\n            int nResult = nDefaultValue;\r\n            if (rex.IsMatch(sToConvertString))\r\n            {\r\n                nResult = int.Parse(sToConvertString);\r\n                return nResult;\r\n            }\r\n            else\r\n                return nDefaultValue;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 将对象转换为整型\r\n        /// </summary>\r\n        /// <param name=\"objInt\">对象</param>\r\n        /// <returns>得到的整数。缺省为0</returns>\r\n        public static int ToInt(object objInt)\r\n        {\r\n            return ToInt(objInt, 0);\r\n        }\r\n\r\n        #endregion ToInt\r\n\r\n        #region ToBool\r\n        /// <summary>\r\n        /// 将对象转换为布尔型(1,Y:true --- 0,N:false)\r\n        /// </summary>\r\n        /// <param name=\"objBool\">待转换的对象</param>\r\n        /// <param name=\"defaultValue\">缺省值</param>\r\n        /// <returns>得到的布尔型</returns>\r\n        public static bool ToBool(object objBool, bool defaultValue)\r\n        {\r\n            if (objBool == null || objBool == DBNull.Value)\r\n            {\r\n                return defaultValue;\r\n            }\r\n            else\r\n            {\r\n                try\r\n                {\r\n                    if (objBool.ToString() == \"1\" || objBool.ToString().ToUpper() == \"Y\")\r\n                    {\r\n                        return true;\r\n                    }\r\n                    else if (objBool.ToString() == \"0\" || objBool.ToString().ToUpper() == \"N\")\r\n                    {\r\n                        return false;\r\n                    }\r\n                    return Convert.ToBoolean(objBool);\r\n                }\r\n                catch\r\n                {\r\n                    return defaultValue;\r\n                }\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 将对象转换为布尔型(1 :true --- 0 :false)\r\n        /// </summary>\r\n        /// <param name=\"objBool\">待转换的对象</param> \r\n        /// <returns>得到的布尔型</returns>\r\n        public static int ToBoolInt(bool objBool)\r\n        {\r\n            if (objBool)\r\n            {\r\n                return 1;\r\n            }\r\n            return 0;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 将对象转换为布尔型( Y:true ---  N:false)\r\n        /// </summary>\r\n        /// <param name=\"objBool\">待转换的对象</param> \r\n        /// <returns>得到的布尔型</returns>\r\n        public static string ToBoolString(bool objBool)\r\n        {\r\n            if (objBool)\r\n            {\r\n                return \"Y\";\r\n            }\r\n            return \"N\";\r\n        }\r\n\r\n\r\n        #endregion\r\n\r\n\r\n        #region ToDecimal\r\n\r\n        /// <summary>\r\n        /// 将对象转换为Decimal类型\r\n        /// </summary>\r\n        /// <param name=\"objDecimal\">待转换的对象</param>\r\n        /// <param name=\"defaultValue\">缺省值</param>\r\n        /// <returns>得到的Decimal类型</returns>\r\n        public static decimal ToDecimal(object objDecimal, decimal defaultValue)\r\n        {\r\n            if (objDecimal == null)\r\n            {\r\n                return defaultValue;\r\n            }\r\n            else\r\n            {\r\n                try\r\n                {\r\n                    return Convert.ToDecimal(objDecimal);\r\n                }\r\n                catch\r\n                {\r\n                    return defaultValue;\r\n                }\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 将对象转换为Decimal类型\r\n        /// </summary>\r\n        /// <param name=\"objDecimal\">待转换的对象</param>\r\n        /// <returns>得到的Decimal类型。缺省为0</returns>\r\n        public static decimal ToDecimal(object objDecimal)\r\n        {\r\n            return ToDecimal(objDecimal, 0);\r\n        }\r\n\r\n        #endregion ToDecimal\r\n\r\n        #region ToFloat\r\n\r\n        /// <summary>\r\n        /// 将对象转换为Float类型\r\n        /// </summary>\r\n        /// <param name=\"objFloat\">待转换的对象</param>\r\n        /// <param name=\"defaultValue\">缺省值</param>\r\n        /// <returns>得到的Float类型</returns>\r\n        public static double ToFloat(object objFloat, double defaultValue)\r\n        {\r\n            if (objFloat == null)\r\n            {\r\n                return defaultValue;\r\n            }\r\n            else\r\n            {\r\n                try\r\n                {\r\n                    return Convert.ToDouble(objFloat);\r\n                }\r\n                catch\r\n                {\r\n                    return defaultValue;\r\n                }\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 将对象转换为Float类型\r\n        /// </summary>\r\n        /// <param name=\"objFloat\">待转换的对象</param>\r\n        /// <returns>得到的Float类型。缺省为0</returns>\r\n        public static double ToFloat(object objFloat)\r\n        {\r\n            return ToFloat(objFloat, 0);\r\n        }\r\n\r\n        #endregion ToFloat\r\n\r\n        #region ToString\r\n\r\n        /// <summary>\r\n        /// 将对象转换为字符串类型\r\n        /// </summary>\r\n        /// <param name=\"objString\">待转换为对象</param>\r\n        /// <param name=\"defaultValue\">缺省值</param>\r\n        /// <returns>得到的字符串类型</returns>\r\n        public static string ToString(object objString, string defaultValue)\r\n        {\r\n            if (objString == null || objString == DBNull.Value)\r\n            {\r\n                return defaultValue;\r\n            }\r\n            else\r\n            {\r\n                try\r\n                {\r\n                    return Convert.ToString(objString);\r\n                }\r\n                catch\r\n                {\r\n                    return defaultValue;\r\n                }\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 将对象转换为字符串类型\r\n        /// </summary>\r\n        /// <param name=\"objString\"></param>\r\n        /// <returns></returns>\r\n        public static string ToString(object objString)\r\n        {\r\n            return ToString(objString, \"\");\r\n        }\r\n\r\n        #endregion\r\n\r\n        #region ExtractNumberFromString\r\n        /// <summary>\r\n        /// 将字符串解析为数字\r\n        /// </summary>\r\n        /// <param name=\"sString\">字符串</param>\r\n        /// <param name=\"fDefault\">缺省值</param>\r\n        /// <returns>数字</returns>\r\n        public static double ExtractNumberFromString(string sString, double fDefault)\r\n        {\r\n            //========= 1. 如果有数字，则只取数字部分进行转换 =========\r\n            for (int i = 0; i < sString.Length; i++)\r\n            {\r\n                char cChar = sString[i];\r\n                if ((cChar >= \'0\' && cChar <= \'9\') || cChar == \'-\')\r\n                {\r\n                    string sRearString = sString.Substring(i);\r\n                    sRearString = ExtractNumberFromString__CutRearString(sRearString);\r\n                    double fNumber = ConvertUtil.ToFloat(sRearString, fDefault);\r\n                    return fNumber;\r\n                }\r\n            }\r\n\r\n            return fDefault;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 取出可以转换为数字的字符串\r\n        /// </summary>\r\n        /// <param name=\"sRearString\">第一位是数字或负数符号的字符串</param>\r\n        /// <returns>可转换为数字的字符串</returns>\r\n        private static string ExtractNumberFromString__CutRearString(string sRearString)\r\n        {\r\n            int nFinalPos = 0;\r\n            bool bHasMeetPoint = false;\r\n\r\n            for (int i = 1; i < sRearString.Length; i++)\r\n            {\r\n                char ch = sRearString[i];\r\n                if (ch >= \'0\' && ch <= \'9\')\r\n                    nFinalPos = i;\r\n                else if (ch == \'.\')\r\n                {\r\n                    if (bHasMeetPoint)\r\n                        break;\r\n                    else\r\n                    {\r\n                        bHasMeetPoint = true;\r\n                        nFinalPos = i;\r\n                    }\r\n                }\r\n                else\r\n                    break;\r\n            }\r\n\r\n            return sRearString.Substring(0, nFinalPos + 1);\r\n        }\r\n\r\n        #endregion ExtractNumberFromString\r\n\r\n    }\r\n}\r\n', '0', '2017-09-08 09:48:22', null, 'ConvertUtil.cs.txt');
