INSERT INTO `txt` VALUES ('234', '2', 'CVOwner新加了一个表.sql.txt', 'USE [CareerCandidate]\r\nGO\r\n\r\n/****** Object:  Table [dbo].[CVOwnerShow]    Script Date: 2015/3/18 16:07:40 ******/\r\nSET ANSI_NULLS ON\r\nGO\r\n\r\nSET QUOTED_IDENTIFIER ON\r\nGO\r\n\r\nCREATE TABLE [dbo].[CVOwnerShow](\r\n	[ID] [nvarchar](50) NOT NULL,\r\n	[CVID] [nvarchar](50) NULL,\r\n	[UserID] [nvarchar](50) NULL,\r\n	[RelationType] [int] NULL,\r\n	[createDate] [datetime] NULL,\r\n	[EnableState] [int] NULL,\r\n	[updateTime] [datetime] NULL,\r\n CONSTRAINT [PK_CVOwnerShow] PRIMARY KEY CLUSTERED \r\n(\r\n	[ID] ASC\r\n)WITH (PAD_INDEX = OFF, STATISTICS_NORECOMPUTE = OFF, IGNORE_DUP_KEY = OFF, ALLOW_ROW_LOCKS = ON, ALLOW_PAGE_LOCKS = ON) ON [PRIMARY]\r\n) ON [PRIMARY]\r\n\r\nGO\r\n\r\nALTER TABLE [dbo].[CVOwnerShow] ADD  CONSTRAINT [DF_CVOwnerShow_createDate]  DEFAULT (getdate()) FOR [createDate]\r\nGO\r\n\r\nALTER TABLE [dbo].[CVOwnerShow] ADD  CONSTRAINT [DF_CVOwnerShow_EnableState]  DEFAULT ((1)) FOR [EnableState]\r\nGO\r\n\r\nEXEC sys.sp_addextendedproperty @name=N\'MS_Description\', @value=N\'1�����ĺ�ѡ�ˣ������Լ����������˸����ǩ�ģ�2�����ǩ�ĺ�ѡ�ˣ����������Լ������ģ�3����ϵ���ĺ�ѡ��4������Ȩ���ĺ�ѡ��\r\n5.��������\r\n6.�ղصĺ�ѡ��(ERP��������)\r\n\' , @level0type=N\'SCHEMA\',@level0name=N\'dbo\', @level1type=N\'TABLE\',@level1name=N\'CVOwnerShow\', @level2type=N\'COLUMN\',@level2name=N\'RelationType\'\r\nGO\r\n\r\n\r\n\r\n--��CVOwner������Copy��CVOwnerShow����\r\ninsert into CVOwnerShow \r\nselect * from CVOwner\r\ngo\r\n--ɾ��CVOwnerShow�ظ�������\r\ndelete from CVOwnerShow where ID not in ( \r\n   select ID from \r\n    (select CVID,UserID ,max(ID) ID from CVOwnerShow group by CVID,UserID ) a\r\n)\r\n', '0', '2017-09-08 09:48:24', null, 'CVOwner新加了一个表.sql.txt');
