INSERT INTO `txt` VALUES ('236', '2', 'Dapper.SqlMapperUtil.junstested(crud.增删改查).轻量级.ORM.基于ado.net - 副本.txt', '     //Dapper.SqlMapperUtil   增删改查\r\n	 //需要:SqlMapper.cs         (namespace Dapper)\r\n	 //需要:SqlMapperUtil.cs   (namespace Dapper)\r\n	 \r\n	 ===================插入多条数据=====================\r\n	 int flag = Dapper.SqlMapperUtil.InsertUpdateOrDeleteSql(\"insert CVInformation(ChineseName, Birthday) values (@ChineseName, @Birthday)\",\r\n                   new[]{\r\n                    new {ChineseName=\"杨利伟2\",Birthday=DateTime.Now.AddDays(-50)},\r\n                    new {ChineseName=\"杨丽萍2\",Birthday=DateTime.Now.AddDays(-200)}\r\n                },\r\n                   null);\r\n=========================插入多条数据方法2=============================\r\n  List<CVInformation> listInfo = new List<CVInformation>();\r\n            listInfo.Add(new CVInformation { ChineseName = \"我是中国人\", Birthday = DateTime.Now });\r\n            listInfo.Add(new CVInformation { ChineseName = \"他是美国人\", Birthday = DateTime.Now.AddYears(-10) });\r\n            string CreateStatement =\r\n     @\"INSERT dbo.CVInformation (ChineseName, Birthday) SELECT @ChineseName, @Birthday\";\r\n            Dapper.SqlMapperUtil.InsertMultiple<CVInformation>(CreateStatement, listInfo, null);\r\n================查询List列表====================\r\n  string ReadStatement = @\"SELECT top 500  * FROM dbo.CVInformation order by id desc\";\r\n\r\n            var list = Dapper.SqlMapperUtil.SqlWithParams<CVInformation>(ReadStatement, null, null);\r\n=====================带参数的查询List(等于)=============================\r\n            string ReadStatement = @\"SELECT top 500  * FROM dbo.CVInformation where ChineseName = @ChineseNames     order by id desc\";\r\n            var list = Dapper.SqlMapperUtil.SqlWithParams<CVInformation>(ReadStatement,\r\n                    new { ChineseNames = new string[] { \"杨利伟\" } },\r\n                null);\r\n--------------------------------------------------------------------------