INSERT INTO `txt` VALUES ('245', '2', 'datatable.to.json.lyn给我的.mofiy写的.txt', 'private string DataTableToJSON(DataTable dt)\r\n        {\r\n            StringBuilder jsonBuilder = new StringBuilder();\r\n            //jsonBuilder.Append(\"\\\"table\\\":[\");\r\n            jsonBuilder.Append(\"[\");\r\n            for (int i = 0; i < dt.Rows.Count; i++)\r\n            {\r\n                if (i > 0)\r\n                    jsonBuilder.Append(\",\");\r\n                jsonBuilder.Append(\"{\");\r\n                for (int j = 0; j < dt.Columns.Count; j++)\r\n                {\r\n                    if (j > 0)\r\n                        jsonBuilder.Append(\",\");\r\n                    jsonBuilder.Append(\"\\\"\" + dt.Columns[j].ColumnName.ToLower() + \"\\\":\\\"\" + dt.Rows[i][j].ToString().Replace(\"\\t\", \"        \").Replace(\"\\r\", \"<br>\").Replace(\"\\n\", \"<p>\").Replace(\"\\\"\", \"\\\\\\\"\") + \"\\\"\");\r\n                    //jsonBuilder.Append(\"\\\"\" + dt.Columns[j].ColumnName.ToLower() + \"\\\":\\\"\" + dt.Rows[i][j].ToString().Replace(\"\\\"\", \"\\\\\\\"\") + \"\\\"\");\r\n                }\r\n                jsonBuilder.Append(\"}\");\r\n            }\r\n            jsonBuilder.Append(\"]\");\r\n            return jsonBuilder.ToString();\r\n        }\r\n', '0', '2017-09-08 09:48:24', null, 'datatable.to.json.lyn给我的.mofiy写的.txt');
