INSERT INTO `txt` VALUES ('249', '2', 'DateTimeFormat.日期格式化.将2011092012格式化成标准日期格式.riqigeshihua.txt', '        /// <summary>\r\n        /// ��2011092012��ʽ����2011/09/20 12:00:00\r\n        /// </summary>\r\n        /// <param name=\"str\">2011092012</param>\r\n        /// <returns>2011/09/20 12:00:00</returns>\r\n        public string FormatDateTimeString(string str)\r\n        {\r\n            string strFormat = string.Empty;\r\n            switch (str.Length)\r\n            {\r\n                case 8:\r\n                    strFormat = str.Substring(0, 4) + \"/\" + str.Substring(4, 2) + \"/\" + str.Substring(6, 2) + \" 00:00:00\";\r\n                    break;\r\n                case 10:\r\n                    strFormat = str.Substring(0, 4) + \"/\" + str.Substring(4, 2) + \"/\" + str.Substring(6, 2) + \" \" + str.Substring(8, 2) + \":00:00\";\r\n                    break;\r\n                case 12:\r\n                    strFormat = str.Substring(0, 4) + \"/\" + str.Substring(4, 2) + \"/\" + str.Substring(6, 2) + \" \" + str.Substring(8, 2) + \":\" + str.Substring(10, 2) + \":00\";\r\n                    break;\r\n                case 14:\r\n                    strFormat = str.Substring(0, 4) + \"/\" + str.Substring(4, 2) + \"/\" + str.Substring(6, 2) + \" \" + str.Substring(8, 2) + \":\" + str.Substring(10, 2) + str.Substring(12, 2);\r\n                    break;\r\n                default:\r\n                    strFormat = str;\r\n                    break;\r\n            }\r\n            return strFormat;\r\n        }\r\n', '0', '2017-09-08 09:48:24', null, 'DateTimeFormat.日期格式化.将2011092012格式化成标准日期格式.riqigeshihua.txt');
INSERT INTO `txt` VALUES ('250', '2', 'DateTimeUtil.cs.txt', '\r\nusing System;\r\nusing System.Collections;\r\n\r\nusing System.Diagnostics;\r\n\r\nnamespace BJHouse.Common\r\n{\r\n    /// <summary>\r\n    /// 对时间的处理，尤其是字符串时间格式的处理提供支持\r\n    /// </summary>\r\n    /// <remarks>\r\n    /// 在DateTimeUtil中，对日期、时间统一按特定的字符串格式进行\r\n    /// 处理，时间采用19位的字符串，编码为“yyyy-mm-dd hh:mi:ss”，\r\n    /// 日期采用10位的字符串，编码为“yyyy-mm-dd”。\r\n    /// </remarks>\r\n    public sealed class DateTimeUtil\r\n    {\r\n        #region 日期时间支持一（格式转换）\r\n        /// <summary>\r\n        /// 判断给定的日期时间字符串是否符合相应的格式\r\n        /// </summary>\r\n        /// <param name=\"strDate\">日期时间字符串</param>\r\n        /// <returns>是否符合格式</returns>\r\n        public static bool IsDateTime(string strDate)\r\n        {\r\n            if (strDate.Length != 19)\r\n                return false;\r\n\r\n            try\r\n            {\r\n                DateTime dt = DateTime.Parse(strDate);\r\n                return true;\r\n            }\r\n            catch\r\n            {\r\n                return false;\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 得到当前月的第一天（yyyy-MM-dd HH:mm:ss）\r\n        /// </summary>\r\n        /// <returns>当前月的第一天</returns>\r\n        public static string ThisMonthFirstDate\r\n        {\r\n            get\r\n            {\r\n                DateTime dt = new DateTime(DateTime.Today.Year, DateTime.Today.Month, 1);\r\n                return dt.ToString(\"yyyy-MM-dd HH:mm:ss\");\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 得到当前月的最后一天（yyyy-MM-dd HH:mm:ss）\r\n        /// </summary>\r\n        /// <returns>当前月的最后一天</returns>\r\n        public static string ThisMonthLastDate\r\n        {\r\n            get\r\n            {\r\n                DateTime dt = new DateTime(DateTime.Today.Year, DateTime.Today.Month + 1, 1);\r\n                dt = dt.AddDays(-1);\r\n                return dt.ToString(\"yyyy-MM-dd HH:mm:ss\");\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 指定日期的该月第一天（yyyy-MM-dd 00:00:00）\r\n        /// </summary>\r\n        /// <param name=\"sThisDate\">指定的日期</param>\r\n        /// <returns>该月第一天</returns>\r\n        public static string MonthFirstDate(string sThisDate)\r\n        {\r\n            int nYear = Year(sThisDate);\r\n            int nMonth = Month(sThisDate);\r\n            int nDay = 1;\r\n\r\n            return EncodeDate(nYear, nMonth, nDay) + \" 00:00:00\";\r\n        }\r\n\r\n        /// <summary>\r\n        /// 指定日期的该月最后一天（yyyy-MM-dd HH:mm:ss）\r\n        /// </summary>\r\n        /// <param name=\"sThisDate\">指定的日期</param>\r\n        /// <returns>该月最后一天</returns>\r\n        public static string MonthLastDate(string sThisDate)\r\n        {\r\n            int nYear = Year(sThisDate);\r\n            int nMonth = Month(sThisDate);\r\n\r\n            nMonth++;\r\n            if (nMonth == 13)\r\n            {\r\n                nYear++;\r\n                nMonth = 1;\r\n            }\r\n\r\n            DateTime dt = new DateTime(nYear, nMonth, 1);\r\n            dt = dt.AddDays(-1);\r\n            return dt.ToString(\"yyyy-MM-dd HH:mm:ss\");\r\n        }\r\n\r\n        /// <summary>\r\n        /// 转换字符串类型的时间至日期时间类型（DateTime）\r\n        /// </summary>\r\n        /// <param name=\"strDateTime\">时间字符串</param>\r\n        /// <returns>日期时间类型的值</returns>\r\n        /// <remarks>如果格式不正确，则返回当前的时间</remarks>\r\n        public static DateTime ToDateTime(string strDateTime)\r\n        {\r\n            try\r\n            {\r\n                return DateTime.Parse(strDateTime);\r\n            }\r\n            catch\r\n            {\r\n                return DateTime.Now;\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 转换时间类型的字符串至日期时间类型（DateTime）\r\n        /// </summary>\r\n        /// <param name=\"strTime\">时间字符串</param>\r\n        /// <returns>时间值</returns>\r\n        /// <remarks>如果格式不正确，则返回的是\"00:00:00\"的时间值</remarks>\r\n        public static DateTime ToTime(string strTime)\r\n        {\r\n            try\r\n            {\r\n                return DateTime.Parse(strTime);\r\n            }\r\n            catch\r\n            {\r\n                return DateTime.Parse(\"00:00:00\");\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 将时间值转换至时间字符串（HH:mm:ss）\r\n        /// </summary>\r\n        /// <param name=\"time\">时间值</param>\r\n        /// <returns>时间字符串</returns>\r\n        public static string ToTimeStr(DateTime time)\r\n        {\r\n            return time.ToString(\"HH:mm:ss\");\r\n        }\r\n\r\n        /// <summary>\r\n        /// 将对象转换为指定格式的字符串（yyyy-MM-dd HH:mm:ss）\r\n        /// </summary>\r\n        /// <param name=\"time\">待转换的对象</param>\r\n        /// <param name=\"sFormat\">指定的字符串格式</param>\r\n        /// <returns>转换后的字符串</returns>\r\n        /// <remarks>如果为空值，或非日期时间对象，则返回\"\"</remarks>\r\n        public static string ToTimeStr(object time, string sFormat)\r\n        {\r\n            if (time == null || time == DBNull.Value)\r\n                return \"\";\r\n            else\r\n            {\r\n                try\r\n                {\r\n                    return ((DateTime)time).ToString(sFormat);\r\n                }\r\n                catch\r\n                {\r\n                    return \"\";\r\n                }\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 将对象转换为字符串，格式为\"HH:mm:ss\"\r\n        /// </summary>\r\n        /// <param name=\"time\">待转换的对象</param>\r\n        /// <returns>转换后的字符串</returns>\r\n        public static string ToTimeStr(object time)\r\n        {\r\n            return ToTimeStr(time, \"HH:mm:ss\");\r\n        }\r\n\r\n        /// <summary>\r\n        /// 从秒数转换为时间字符串（dd天 HH:mm:ss）\r\n        /// </summary>\r\n        /// <param name=\"Second\">秒数</param>\r\n        /// <returns>时间字符串</returns>\r\n        public static string ToTimeStrFromSecond1(int Second)\r\n        {\r\n            //=========== 1. 得到小时、分钟和秒数 ===========\r\n            string sTimeStr = \"\";\r\n\r\n            int nNewSecond = 0;\r\n            int hour = Math.DivRem(Second, 3600, out nNewSecond);    //小时\r\n            //============ 得到天数 ==========\r\n            int nNewHour = 0;\r\n            int nNewDay = Math.DivRem(hour, 24, out nNewHour);    //小时\r\n\r\n            Second = nNewSecond;\r\n            nNewSecond = 0;\r\n            int minute = Math.DivRem(Second, 60, out nNewSecond);    //分钟\r\n\r\n            //============ 2. 得到返回的字符串 ============\r\n            if (nNewDay > 0)\r\n                sTimeStr = sTimeStr + nNewDay.ToString() + \"天 \";\r\n\r\n            if (nNewHour < 10)\r\n                sTimeStr = sTimeStr + \"0\" + nNewHour.ToString() + \":\";\r\n            else\r\n                sTimeStr = sTimeStr + nNewHour.ToString() + \":\";\r\n\r\n            if (minute < 10)\r\n                sTimeStr = sTimeStr + \"0\" + minute.ToString() + \":\";\r\n            else\r\n                sTimeStr = sTimeStr + minute.ToString() + \":\";\r\n\r\n            if (nNewSecond < 10)\r\n                sTimeStr = sTimeStr + \"0\" + nNewSecond.ToString();\r\n            else\r\n                sTimeStr = sTimeStr + nNewSecond.ToString();\r\n\r\n            return sTimeStr;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 从秒数转换为时间字符串（HH:mm:ss）\r\n        /// </summary>\r\n        /// <param name=\"Second\">秒数</param>\r\n        /// <returns>时间字符串</returns>\r\n        public static string ToTimeStrFromSecond(int Second)\r\n        {\r\n            //=========== 1. 得到小时、分钟和秒数 ===========\r\n            string sTimeStr = \"\";\r\n\r\n            int nNewSecond = 0;\r\n            int hour = Math.DivRem(Second, 3600, out nNewSecond);    //小时 \r\n\r\n            Second = nNewSecond;\r\n            nNewSecond = 0;\r\n            int minute = Math.DivRem(Second, 60, out nNewSecond);    //分钟\r\n\r\n            //============ 2. 得到返回的字符串 ============ \r\n\r\n            if (hour < 10)\r\n                sTimeStr = sTimeStr + \"0\" + hour.ToString() + \":\";\r\n            else\r\n                sTimeStr = sTimeStr + hour.ToString() + \":\";\r\n\r\n            if (minute < 10)\r\n                sTimeStr = sTimeStr + \"0\" + minute.ToString() + \":\";\r\n            else\r\n                sTimeStr = sTimeStr + minute.ToString() + \":\";\r\n\r\n            if (nNewSecond < 10)\r\n                sTimeStr = sTimeStr + \"0\" + nNewSecond.ToString();\r\n            else\r\n                sTimeStr = sTimeStr + nNewSecond.ToString();\r\n\r\n            return sTimeStr;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 将时间字符串转换为秒数\r\n        /// </summary>\r\n        /// <param name=\"timeStr\">时间字符串</param>\r\n        /// <returns>转换后的秒数</returns>\r\n        public static int ToSecondsFromTimeStr(string timeStr)\r\n        {\r\n            DateTime dt = ToTime(timeStr);\r\n            return dt.Hour * 3600 + dt.Minute * 60 + dt.Second;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 将日期时间值转换为日期字符串（yyyy-MM-dd）\r\n        /// </summary>\r\n        /// <param name=\"dt\">日期时间值</param>\r\n        /// <returns>日期字符串</returns>\r\n        public static string ToDateStr(DateTime dt)\r\n        {\r\n            return dt.ToString(\"yyyy-MM-dd\");\r\n        }\r\n\r\n        /// <summary>\r\n        /// 将对象转换为日期字符串（yyyy-MM-dd）\r\n        /// </summary>\r\n        /// <param name=\"dt\">对象</param>\r\n        /// <param name=\"defaultStr\">缺省字符串</param>\r\n        /// <returns>转换后的字符串。如果为空值，则按缺省值返回</returns>\r\n        public static string ToDateStr(object dt, string defaultStr)\r\n        {\r\n            if (dt == null || dt is System.DBNull)\r\n                return defaultStr;\r\n            else\r\n                return ((DateTime)dt).ToString(\"yyyy-MM-dd\");\r\n        }\r\n\r\n        /// <summary>\r\n        /// 将对象转换为日期字符串（yyyy-MM-dd）\r\n        /// </summary>\r\n        /// <param name=\"dt\">对象</param>\r\n        /// <returns>转换后的字符串。如果转换不成功，则返回\"\"</returns>\r\n        public static string ToDateStr(object dt)\r\n        {\r\n            return ToDateStr(dt, \"\");\r\n        }\r\n\r\n        /// <summary>\r\n        /// 将日期时间值转换为字符串（yyyy-MM-dd HH:mm:ss）\r\n        /// </summary>\r\n        /// <param name=\"dt\">日期时间值</param>\r\n        /// <returns>字符串(len=19)</returns>\r\n        public static string ToDateTimeStr(DateTime dt)\r\n        {\r\n            return dt.ToString(\"yyyy-MM-dd HH:mm:ss\");\r\n        }\r\n\r\n        /// <summary>\r\n        /// 将对象转换为日期时间字符串（yyyy-MM-dd HH:mm:ss）\r\n        /// </summary>\r\n        /// <param name=\"dt\">对象</param>\r\n        /// <param name=\"defaultStr\">转换不成功时所取的缺省字符串</param>\r\n        /// <returns>转换后的日期时间字符串(len=19)</returns>\r\n        public static string ToDateTimeStr(object dt, string defaultStr)\r\n        {\r\n            return ToDateTimeStr(dt, defaultStr, \"yyyy-MM-dd HH:mm:ss\");\r\n        }\r\n\r\n        /// <summary>\r\n        /// 将对象转换为日期时间字符串（yyyy-MM-dd HH:mm:ss）\r\n        /// </summary>\r\n        /// <param name=\"dt\">对象</param>\r\n        /// <returns>转换后的日期时间字符串(len=19)</returns>\r\n        /// <remarks>如果转换不成功，则返回\"\"</remarks>\r\n        public static string ToDateTimeStr(object dt)\r\n        {\r\n            return ToDateTimeStr(dt, \"\", \"yyyy-MM-dd HH:mm:ss\");\r\n        }\r\n\r\n        /// <summary>\r\n        /// 将对象转换为指定格式的日期时间字符串（yyyy-MM-dd HH:mm:ss）\r\n        /// </summary>\r\n        /// <param name=\"dt\">对象</param>\r\n        /// <param name=\"defaultStr\">缺省字符串</param>\r\n        /// <param name=\"sFormat\">格式</param>\r\n        /// <returns>转换后的字符串</returns>\r\n        public static string ToDateTimeStr(object dt, string defaultStr, string sFormat)\r\n        {\r\n            if (dt == null || dt is System.DBNull || dt.ToString() == \"\")\r\n                return defaultStr;\r\n            else\r\n                return ((DateTime)dt).ToString(sFormat);\r\n        }\r\n\r\n        #endregion\r\n\r\n        #region 日期时间支持二（实用例程）\r\n\r\n        /// <summary>\r\n        /// 获取当前的时间\r\n        /// </summary>\r\n        public static string Now\r\n        {\r\n            get\r\n            {\r\n                return DateTime.Now.ToString(\"yyyy-MM-dd HH:mm:ss\");\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 带毫秒部分的当前时间，示例为2003.07.22 10:02:52.136\r\n        /// </summary>\r\n        public static string NowWithMilliSeconds\r\n        {\r\n            get\r\n            {\r\n                DateTime dtNow = DateTime.Now;\r\n                int nMilliSeconds = dtNow.Millisecond;\r\n                return dtNow.ToString(\"yyyy-MM-dd HH:mm:ss\") + \".\" + nMilliSeconds.ToString(\"000\");\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 获取当前的时间\r\n        /// </summary>\r\n        public static string Today\r\n        {\r\n            get\r\n            {\r\n                return DateTime.Today.ToString(\"yyyy-MM-dd HH:mm:ss\");\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 获取当前的时间上一个月的时间\r\n        /// </summary>\r\n        public static string LastMonth\r\n        {\r\n            get\r\n            {\r\n                return DateTime.Now.AddMonths(-1).ToString(\"yyyy-MM-dd HH:mm:ss\");\r\n            }\r\n        }\r\n        /// <summary>\r\n        /// 获取日期的年份\r\n        /// </summary>\r\n        /// <param name=\"dtString\">日期时间串</param>\r\n        /// <returns>年份</returns>\r\n        public static int Year(string dtString)\r\n        {\r\n            int nYear;\r\n\r\n            nYear = Convert.ToInt32(dtString.Substring(0, 4));\r\n            Debug.Assert(nYear > 1900 && nYear < 2100);\r\n\r\n            return nYear;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 获取日期的月份\r\n        /// </summary>\r\n        /// <param name=\"dtString\">日期时间串</param>\r\n        /// <returns>月份</returns>\r\n        public static int Month(string dtString)\r\n        {\r\n            int nMonth;\r\n\r\n            nMonth = Convert.ToInt32(dtString.Substring(5, 2));\r\n            Debug.Assert(nMonth >= 1 && nMonth <= 12);\r\n\r\n            return nMonth;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 获取日期中该月的第几天\r\n        /// </summary>\r\n        /// <param name=\"dtString\">日期时间串</param>\r\n        /// <returns>该月的第几天</returns>\r\n        public static int Day(string dtString)\r\n        {\r\n            int nDay;\r\n\r\n            nDay = Convert.ToInt32(dtString.Substring(8, 2));\r\n            Debug.Assert(nDay >= 1 && nDay <= 31);\r\n\r\n            return nDay;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 获取日期中的小时部分\r\n        /// </summary>\r\n        /// <param name=\"dtString\">日期时间串</param>\r\n        /// <returns>小时部分</returns>\r\n        public static int Hour(string dtString)\r\n        {\r\n            int nHour;\r\n\r\n            if (dtString.Length != 19)\r\n                return 0;\r\n\r\n            nHour = Convert.ToInt32(dtString.Substring(11, 2));\r\n            Debug.Assert(nHour >= 0 && nHour <= 24);\r\n\r\n            return nHour;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 获取日期中的分钟部分\r\n        /// </summary>\r\n        /// <param name=\"dtString\">日期时间串</param>\r\n        /// <returns>分钟部分</returns>\r\n        public static int Minute(string dtString)\r\n        {\r\n            int nMinute;\r\n\r\n            if (dtString.Length != 19)\r\n                return 0;\r\n\r\n            nMinute = Convert.ToInt32(dtString.Substring(14, 2));\r\n            Debug.Assert(nMinute >= 0 && nMinute <= 60);\r\n\r\n            return nMinute;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 获取日期中的秒部分\r\n        /// </summary>\r\n        /// <param name=\"dtString\">日期时间串</param>\r\n        /// <returns>秒部分</returns>\r\n        public static int Second(string dtString)\r\n        {\r\n            int nSecond;\r\n\r\n            if (dtString.Length != 19)\r\n                return 0;\r\n\r\n            nSecond = Convert.ToInt32(dtString.Substring(17, 2));\r\n            Debug.Assert(nSecond >= 0 && nSecond <= 60);\r\n\r\n            return nSecond;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 获取表示的日期是星期几\r\n        /// </summary>\r\n        /// <param name=\"dtString\">日期时间串</param>\r\n        /// <returns>星期几</returns>\r\n        /// <remarks>返回值从0至6；星期日为0，星期六为6</remarks>\r\n        public static int DayOfWeek(string dtString)\r\n        {\r\n            DateTime dt = DateTime.Parse(dtString);\r\n            return (int)dt.DayOfWeek;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 获取表示的日期是一年中的第几天\r\n        /// </summary>\r\n        /// <param name=\"dtString\">日期时间串</param>\r\n        /// <returns>一年中的第几天</returns>\r\n        public static int DayOfYear(string dtString)\r\n        {\r\n            DateTime dt = DateTime.Parse(dtString);\r\n            return dt.DayOfYear;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 将指定的月份数加到日期值上\r\n        /// </summary>\r\n        /// <param name=\"dtString\">原来的日期</param>\r\n        /// <param name=\"nOffset\">指定的月份数</param>\r\n        /// <returns>得到的日期</returns>\r\n        public static string AddMonths(string dtString, int nOffset)\r\n        {\r\n            DateTime dt = DateTime.Parse(dtString);\r\n            DateTime dtRes = dt.AddMonths(nOffset);\r\n            return dtRes.ToString(\"yyyy-MM-dd HH:mm:ss\");\r\n        }\r\n\r\n        /// <summary>\r\n        /// 将指定的天数加到日期值上\r\n        /// </summary>\r\n        /// <param name=\"dtString\">原来的日期</param>\r\n        /// <param name=\"offset\">指定的天数</param>\r\n        /// <returns>得到的日期</returns>\r\n        /// <remarks>天数可以为负数</remarks>\r\n        public static string AddDays(string dtString, int offset)\r\n        {\r\n            DateTime dt = DateTime.Parse(dtString);\r\n            DateTime dtRes = dt.AddDays(offset);\r\n            return dtRes.ToString(\"yyyy-MM-dd HH:mm:ss\");\r\n        }\r\n\r\n        /// <summary>\r\n        /// 将指定的秒数加到日期值上\r\n        /// </summary>\r\n        /// <param name=\"dtString\">原来的日期时间</param>\r\n        /// <param name=\"offset\">指定的秒数</param>\r\n        /// <returns>得到的日期时间</returns>\r\n        /// <remarks>秒数可以为负数</remarks>\r\n        public static string AddSeconds(string dtString, int offset)\r\n        {\r\n            DateTime dt = DateTime.Parse(dtString);\r\n            DateTime dtRes = dt.AddSeconds(offset);\r\n            return dtRes.ToString(\"yyyy-MM-dd HH:mm:ss\");\r\n        }\r\n\r\n        /// <summary>\r\n        /// 得到两个日期时间之间相差的天数\r\n        /// </summary>\r\n        /// <param name=\"dtFromString\">起始日期</param>\r\n        /// <param name=\"dtToString\">终止日期</param>\r\n        /// <returns>相差的天数</returns>\r\n        /// <remarks>\r\n        /// 不判断时间的情况。只判断日期是否切换，即使时间相差仅数分钟，\r\n        /// 只要切换了日期，也计算相差的天数。例如，2005-05-05 23:58:58和\r\n        /// 2005-05-06 00:02:03之间所差的天数也是1。\r\n        /// </remarks>\r\n        public static int DaysAfter(string dtFromString, string dtToString)\r\n        {\r\n            DateTime dtFrom = DateTime.Parse(dtFromString).Date;\r\n            DateTime dtTo = DateTime.Parse(dtToString).Date;\r\n            TimeSpan timeSpan = dtTo - dtFrom;\r\n            return (int)timeSpan.TotalDays;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 得到两个日期时间之间相差的秒数\r\n        /// </summary>\r\n        /// <param name=\"dtFromString\">起始日期时间</param>\r\n        /// <param name=\"dtToString\">终止日期时间</param>\r\n        /// <returns>相差的秒数</returns>\r\n        public static int SecondsAfter(string dtFromString, string dtToString)\r\n        {\r\n            DateTime dtFrom = DateTime.Parse(dtFromString);\r\n            DateTime dtTo = DateTime.Parse(dtToString);\r\n            TimeSpan timeSpan = dtTo - dtFrom;\r\n            return (int)timeSpan.TotalSeconds;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 得到两个日期时间之间相差的毫秒数\r\n        /// </summary>\r\n        /// <param name=\"dtFromString\">起始日期时间</param>\r\n        /// <param name=\"dtToString\">终止日期时间</param>\r\n        /// <returns>相差的毫秒数</returns>\r\n        public static long MilliSecondsAfter(string dtFromString, string dtToString)\r\n        {\r\n            DateTime dtFrom = DateTime.Parse(dtFromString);\r\n            DateTime dtTo = DateTime.Parse(dtToString);\r\n            TimeSpan timeSpan = dtTo - dtFrom;\r\n            return (long)timeSpan.TotalMilliseconds;\r\n        }\r\n\r\n\r\n        #region 返回友好的时间格式\r\n        /// <summary>\r\n        /// 返回友好的时间格式dd \r\n        /// </summary>\r\n        /// <param name=\"dtFromString\">19位起始日期时间</param> \r\n        /// <returns></returns>\r\n        public static string StrDateTime(string dtFromString)\r\n        {\r\n            return StrDateTime(dtFromString, DateTimeUtil.Now);\r\n        }\r\n\r\n        /// <summary>\r\n        /// 返回友好的时间格式dd \r\n        /// </summary>\r\n        /// <param name=\"dtFromString\">19位起始日期时间</param>\r\n        /// <param name=\"dtToString\">19位终止日期时间</param>\r\n        /// <returns></returns>\r\n        public static string StrDateTime(string dtFromString, string dtToString)\r\n        {\r\n            string sDateString = string.Empty;\r\n            long nMilliSeconds = MilliSecondsAfter(dtFromString, dtToString);\r\n            long nSeconds = nMilliSeconds / 1000;//得到 秒\r\n            long nMinutes = nSeconds / 60;//得到 分\r\n            long nHours = nMinutes / 60;//得到 小时\r\n            long nDay = nHours / 24;//得到 天\r\n\r\n            if (nSeconds <= 60)\r\n            {\r\n                sDateString = nSeconds + \"秒前\";\r\n            }\r\n            else\r\n            {\r\n                if (nMinutes <= 60)\r\n                {\r\n                    sDateString = nMinutes + \"分前\";\r\n                }\r\n                else\r\n                {\r\n                    if (nHours <= 24)\r\n                    {\r\n                        sDateString = nHours + \"小时前\";\r\n                    }\r\n                    else\r\n                    {\r\n                        if (nDay < 7)\r\n                        {\r\n                            sDateString = nDay + \"天前\";\r\n                        }\r\n                        else\r\n                        {\r\n                            sDateString = GetDatePart(dtFromString);\r\n                        }\r\n                    }\r\n                }\r\n            }\r\n            return sDateString;\r\n        }\r\n\r\n\r\n        /// <summary>\r\n        /// 得到时间差，的时间格式（dd hh:mm:ss）\r\n        /// </summary>\r\n        /// <param name=\"dtFromString\">19位起始日期时间</param>\r\n        /// <param name=\"dtToString\">19位终止日期时间</param>\r\n        /// <returns></returns>\r\n        public static string DateTimeAfter(string dtFromString, string dtToString)\r\n        {\r\n            string sDateString = string.Empty;\r\n            long nMilliSeconds = MilliSecondsAfter(dtFromString, dtToString);\r\n            long nSeconds = nMilliSeconds / 1000;//得到 秒\r\n            long nMinutes = nSeconds / 60;//得到 分\r\n            long nHours = nMinutes / 60;//得到 小时\r\n            long nDay = nHours / 24;//得到 天\r\n\r\n            if (nDay > 0)\r\n            {\r\n                sDateString = nDay + \" \";\r\n                nHours = nHours % 24;//取余 小时\r\n            }\r\n            if (nHours > 0)\r\n            {\r\n                sDateString += nHours + \":\";\r\n                nMinutes = nMinutes % 60;//取余分 \r\n            }\r\n            if (nMinutes > 0)\r\n            {\r\n                sDateString += nMinutes + \":\";//得到 秒\r\n                nSeconds = nSeconds % 60;//取余 秒\r\n            }\r\n            sDateString += nSeconds;//得到 秒 \r\n            return sDateString;\r\n        }\r\n        #endregion\r\n\r\n        #endregion\r\n\r\n        #region 日期时间支持三（日期和时间部分分解组合）\r\n        /// <summary>\r\n        /// 通过时、分、秒得到时间串\r\n        /// </summary>\r\n        /// <param name=\"nHour\">小时</param>\r\n        /// <param name=\"nMinute\">分钟</param>\r\n        /// <param name=\"nSecond\">秒</param>\r\n        /// <returns>时间部分</returns>\r\n        public static string EncodeTime(int nHour, int nMinute, int nSecond)\r\n        {\r\n            string sResult = nHour.ToString(\"00\") + \":\" + nMinute.ToString(\"00\")\r\n                    + \":\" + nSecond.ToString(\"00\");\r\n            return sResult;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 通过年、月、日得到日期部分\r\n        /// </summary>\r\n        /// <param name=\"nYear\">年份</param>\r\n        /// <param name=\"nMonth\">月份</param>\r\n        /// <param name=\"nDay\">日</param>\r\n        /// <returns>日期部分</returns>\r\n        public static string EncodeDate(int nYear, int nMonth, int nDay)\r\n        {\r\n            string sResult = nYear.ToString(\"0000\") + \"-\" + nMonth.ToString(\"00\")\r\n                    + \"-\" + nDay.ToString(\"00\");\r\n            return sResult;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 得到日期部分(yyyy-MM-dd)\r\n        /// </summary>\r\n        /// <param name=\"sDatetime\">日期时间</param>\r\n        /// <returns>日期部分</returns>\r\n        public static string GetDatePart(string sDatetime)\r\n        {\r\n            if (sDatetime.Length < 10)\r\n                return \"\";\r\n            return sDatetime.Substring(0, 10);\r\n        }\r\n\r\n        /// <summary>\r\n        /// 將時間轉化成8位日期(yyyyMMdd)\r\n        /// </summary>\r\n        /// <param name=\"sDatetime\">日期时间</param>\r\n        /// <returns>日期部分</returns>\r\n        public static string ToDatePart8(string sDatetime)\r\n        {\r\n            if (sDatetime.Length > 10)\r\n            {\r\n                sDatetime = sDatetime.Substring(0, 10);\r\n            }\r\n            if (sDatetime.Length < 10)\r\n                return \"\";\r\n            return sDatetime.Replace(\"-\", \"\");\r\n        }\r\n\r\n\r\n        /// <summary>\r\n        /// 將不標準(2011-1-1)的日期字符格式化成標準日期(yyyy-MM-dd)\r\n        /// </summary>\r\n        /// <param name=\"sDatetime\">不標準的日期字符(2011-1-1)</param>\r\n        /// <returns>標準日期(yyyy-MM-dd)</returns>\r\n        public static string FormatDate(string sDatetime)\r\n        {\r\n            string[] sList = StringUtil.Split(sDatetime, \"-\");\r\n            string sDate = sList[0];\r\n            if (sList.Length != 3)\r\n            {\r\n                return sDatetime;\r\n            }\r\n            if (sList[1].Length != 2)\r\n            {\r\n                sDate += \"-0\" + sList[1];\r\n            }\r\n            else\r\n            {\r\n                sDate += \"-\" + sList[1];\r\n            }\r\n            if (sList[2].Length != 2)\r\n            {\r\n                sDate += \"-0\" + sList[2];\r\n            }\r\n            else\r\n            {\r\n                sDate += \"-\" + sList[2];\r\n            }\r\n            return sDate;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 將8位(yyyyMMdd)日期轉化成10日期(yyyy-MM-dd) \r\n        /// </summary>\r\n        /// <param name=\"sDatetime\">日期时间</param>\r\n        /// <returns>日期部分</returns>\r\n        public static string FromDatePart8(string sDatetime8)\r\n        {\r\n            sDatetime8 = sDatetime8.Replace(\"-\", \"\");\r\n            sDatetime8 = sDatetime8.Replace(\" \", \"\");\r\n            if (sDatetime8.Length > 8)\r\n            {\r\n                sDatetime8 = sDatetime8.Substring(0, 8);\r\n            }\r\n            if (sDatetime8.Length < 8)\r\n                return \"\";\r\n            if (sDatetime8.Length == 8)\r\n            {\r\n                sDatetime8 = sDatetime8.Insert(4, \"-\");\r\n                sDatetime8 = sDatetime8.Insert(7, \"-\");\r\n                return sDatetime8;\r\n            }\r\n            return sDatetime8;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 得到时间部分\r\n        /// </summary>\r\n        /// <param name=\"sDatetime\">日期时间</param>\r\n        /// <returns>时间部分</returns>\r\n        public static string GetTimePart(string sDatetime)\r\n        {\r\n            if (sDatetime.Length == 19)\r\n                return sDatetime.Substring(11, 8);\r\n            else\r\n                return \"00:00:00\";\r\n        }\r\n\r\n        /// <summary>\r\n        /// 得到一天的起始时间\r\n        /// </summary>\r\n        /// <param name=\"sDate\">日期</param>\r\n        /// <returns>起始时间</returns>\r\n        public static string BeginTimeOfDay(string sDate)\r\n        {\r\n            if (sDate.Length > 10)\r\n                sDate = sDate.Substring(0, 10);\r\n            return sDate + \" 00:00:00\";\r\n        }\r\n\r\n        /// <summary>\r\n        /// 得到一天的结束时间\r\n        /// </summary>\r\n        /// <param name=\"sDate\">日期</param>\r\n        /// <returns>结束时间</returns>\r\n        public static string EndTimeOfDay(string sDate)\r\n        {\r\n            if (sDate.Length > 10)\r\n                sDate = sDate.Substring(0, 10);\r\n            return sDate + \" 23:59:59.999\";\r\n        }\r\n        #endregion\r\n\r\n        #region 长度14的日期时间\r\n        /// <summary>\r\n        /// 由19位的日期时间转到14位的日期时间\r\n        /// </summary>\r\n        /// <param name=\"sDT\">19位的日期时间</param>\r\n        /// <returns>14位的日期时间</returns>\r\n        public static string ToDateTime14Str(string sDT)\r\n        {\r\n            if (sDT == \"\")\r\n                return \"\";\r\n\r\n            if (sDT.Length != 19)\r\n                throw new Exception(\"DateTimeUtil.ToDateTime14Str() error : 参数不是19位\");\r\n\r\n            string sRet = sDT.Replace(\":\", \"\").Replace(\"-\", \"\").Replace(\" \", \"\");\r\n            if (sRet.Length != 14)\r\n                throw new Exception(\"DateTimeUtil.ToDateTime14Str() error : 返回值不是14位\");\r\n\r\n            return sRet;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 由14位的日期时间转到19位的日期时间\r\n        /// </summary>\r\n        /// <param name=\"sDT14\">14位的日期时间</param>\r\n        /// <returns>19位的日期时间</returns>\r\n        public static string FromDateTime14Str(string sDT14)\r\n        {\r\n            if (sDT14 == \"\")\r\n                return \"\";\r\n            if (sDT14.Length != 14)\r\n                throw new Exception(\"DateTimeUtil.FromDateTime14Str() error : 参数不是14位\");\r\n\r\n            string sRet = sDT14.Substring(0, 4) + \"-\" + sDT14.Substring(4, 2)\r\n                    + \"-\" + sDT14.Substring(6, 2)\r\n                    + \" \" + sDT14.Substring(8, 2) + \":\" + sDT14.Substring(10, 2)\r\n                    + \":\" + sDT14.Substring(12, 2);\r\n            return sRet;\r\n        }\r\n        #endregion 长度14的日期时间\r\n    }\r\n}\r\n', '0', '2017-09-08 09:48:24', null, 'DateTimeUtil.cs.txt');
INSERT INTO `txt` VALUES ('251', '2', 'DaxtraSmartParse(CV简历解析为xml调用程序).asp.txt', '<!-- ============================================= -->\r\n<!-- DAXTRA Technologies smart parsing integration -->\r\n<!--                                               -->\r\n<% \r\n\r\n\r\nFunction DaxtraSessionFill( byval FileName, byval AccountName, byVal DAXTRA_SERVER )\r\n\r\n    if AccountName <> \"TALENTBANK\" and AccountName <> \"MWA\" then\r\n      DaxtraSessionFill = \"<Error>Unregistered Account \" & AccountName & \"</Error>\"\r\n      Exit Function\r\n    End If\r\n\r\n    code_to_exec = DaxtraSmartParse( FileName, AccountName, DAXTRA_SERVER )\r\n\r\n    if code_to_exec = \"\" or Left(code_to_exec, 7) = \"<Error>\" then \r\n       DaxtraSessionFill = code_to_exec\r\n       Exit Function\r\n    End if\r\n\r\n    On Error Resume Next\r\n \r\n    DEBUG_FLAG=0\r\n\r\n    if DEBUG_FLAG=1 then response.write(code_to_exec & \"<br>\") \r\n\r\n    Set objXML = Server.CreateObject(\"Microsoft.XMLDOM\") \r\n    objXML.async = False\r\n    call objXML.setProperty(\"SelectionLanguage\", \"XPath\")\r\n\r\n    objXML.loadXML(code_to_exec)\r\n    If objXML.parseError.errorCode <> 0 Then\r\n           DaxtraSessionFill = \"XML parse error\"\r\n           Exit Function\r\n    End If\r\n \r\n    hrxml = getCharacterData(objXML, \"*/hrxml\")\r\n    if hrxml = \"\" then\r\n         DaxtraSessionFill = \"<Error>no profile returned</Error>\"\r\n         Exit Function\r\n    End If \r\n    if InStr(hrxml, \"<QSERROR\") or InStr(hrxml, \"<QS-ERROR\") then \r\n        DaxtraSessionFill = \"<Error>\" & hrxml & \"</Error>\"\r\n        Exit Function\r\n    End If \r\n\r\n    objXML.loadXML(hrxml)\r\n    If objXML.parseError.errorCode <> 0 Then\r\n           DaxtraSessionFill = \"<Error>HRXML parse error</Error>\"\r\n           Exit Function\r\n    End If\r\n \r\n\r\n\r\n\r\n    \'-------------------  Personal / Contact ----------------------------------------------\r\n    \'\r\n    GivenName    = getCharacterData(objXML, \"Resume/StructuredXMLResume/ContactInfo/PersonName/GivenName\")\r\n    FamilyName   = getCharacterData(objXML, \"Resume/StructuredXMLResume/ContactInfo/PersonName/FamilyName\")  \r\n \r\n    AddressLine  = getCharacterData(objXML, \"Resume/StructuredXMLResume/ContactInfo/ContactMethod/PostalAddress/DeliveryAddress/AddressLine\")\r\n    Municipality = getCharacterData(objXML, \"Resume/StructuredXMLResume/ContactInfo/ContactMethod/PostalAddress/Municipality\")\r\n    PostalCode   = getCharacterData(objXML, \"Resume/StructuredXMLResume/ContactInfo/ContactMethod/PostalAddress/PostalCode\")\r\n    Region       = getCharacterData(objXML, \"Resume/StructuredXMLResume/ContactInfo/ContactMethod/PostalAddress/Region\")   \r\n\r\n    homeTel = getCharacterData(objXML, \"Resume/StructuredXMLResume/ContactInfo/ContactMethod/Telephone[@type=\'home\']/FormattedNumber\")\r\n    workTel = getCharacterData(objXML, \"Resume/StructuredXMLResume/ContactInfo/ContactMethod/Telephone[@type=\'work\']/FormattedNumber\")\r\n    mobile  = getCharacterData(objXML, \"Resume/StructuredXMLResume/ContactInfo/ContactMethod/Mobile/FormattedNumber\")\r\n    email   = getCharacterData(objXML, \"Resume/StructuredXMLResume/ContactInfo/ContactMethod/InternetEmailAddress\")\r\n\r\n    \'----------------------------- work histories ------------------------------------------------\r\n    \'\r\n    Dim whist_sd_Array(15)   \r\n    Dim whist_ed_Array(15)\r\n    Dim whist_org_Array(15)\r\n    Dim whist_title_Array(15)\r\n    for wh_num=0 to 14\r\n       whist_sd_Array(wh_num) = getPatternDataComplex(objXML, \"Resume/StructuredXMLResume/EmploymentHistory/EmployerOrg\", wh_num, \"PositionHistory/StartDate\", 0, \"#\")\r\n       whist_ed_Array(wh_num) = getPatternDataComplex(objXML, \"Resume/StructuredXMLResume/EmploymentHistory/EmployerOrg\", wh_num, \"PositionHistory/EndDate\",   0, \"#\")\r\n       whist_org_Array(wh_num)= getPatternDataComplex(objXML, \"Resume/StructuredXMLResume/EmploymentHistory/EmployerOrg\", wh_num, \"EmployerOrgName\",           0, \"#\") \r\n       title1                 = getPatternDataComplex(objXML, \"Resume/StructuredXMLResume/EmploymentHistory/EmployerOrg\", wh_num, \"PositionHistory/Title\",     0, \"#\")\r\n       title2                 = getPatternDataComplex(objXML, \"Resume/StructuredXMLResume/EmploymentHistory/EmployerOrg\", wh_num, \"PositionHistory/Title\",     1, \"#\")\r\n       if title2 <> \"\" then title_1 = title_1 & \" / \" & title2 \r\n       whist_title_Array(wh_num) = title1\r\n    Next\r\n\r\n  \r\n    \'----------------------- LANGUAGES and Levels -------------------------- \r\n    Dim lang_Array(5)\r\n    Dim levl_Array(5)\r\n    lang_count = 0\r\n    For i = 0 to 10\r\n       lang_tt    = getPatternDataComplex(objXML, \"*/Competency[@description=\'Skill > Language\']\", i, \"CompetencyId\", 0, \"id\")\r\n       if lang_tt <> \"\" then\r\n         set_lang = set_lang + 1\r\n         level_tt = getPatternDataComplex(objXML, \"*/Competency[@description=\'Skill > Language\']\", i, \"CompetencyWeight[@type=\'skillProficiency\']/StringValue\", 0, \"#\")\r\n         lang_Array(lang_count) = lang_tt\r\n         levl_Array(lang_count) = level_tt\r\n         lang_count = lang_count +1 \r\n       End If\r\n    Next\r\n \r\n\r\n    \'-------------------- Quals   ---------------------------------------\r\n    Dim qual_Array(10)\r\n    qual_count = 0\r\n    For i = 0 to 10\r\n       qual_new    = getPatternDataComplex(objXML, \"*/Competency[@description=\'Degree/Qualification\']\", i, \"CompetencyId\", 0, \"id\")\r\n       if qual_new <> \"\" then \r\n           qual_Array(qual_count) = qual_new\r\n           qual_count =  qual_count + 1\r\n       end If\r\n    Next\r\n\r\n    \'--------------------- JOBTITLEs  ------------------------------\r\n    Dim job_Array(50)\r\n    job_count = 0\r\n    For i = 0 to 50\r\n         next_job = getPatternData(objXML, \"*/Competency[@description=\'Held Position\']\", i, \"name\")\r\n         if next_job = \"\" then  Exit For\r\n         next_job = getPatternDataComplex(objXML, \"*/Competency[@description=\'Held Position\']\", i, \"CompetencyId\", 0, \"id\")\r\n         if next_job <> \"\" then  \r\n               job_Array(job_count) = next_job\r\n               job_count =  job_count + 1\r\n         End If\r\n    Next\r\n\r\n   \'--------------------- SKILLS  ------------------------------\r\n    Dim skill_Array(150)\r\n    skill_count = 0\r\n    For i = 0 to 150\r\n         next_skill = getPatternData(objXML, \"*/Competency[@description=\'Skill\']\", i, \"name\")\r\n         if next_skill = \"\" then  Exit For\r\n         next_skill = getPatternDataComplex(objXML, \"*/Competency[@description=\'Skill\']\", i, \"CompetencyId\", 0, \"id\")\r\n         if next_skill <> \"\" then  \r\n               skill_Array(skill_count) = next_skill\r\n               skill_count = skill_count + 1\r\n         End If\r\n   Next\r\n   For i = 0 to 150\r\n         next_skill = getPatternData(objXML, \"*/Competency[@description=\'Skill > IT\']\", i, \"name\")\r\n         if next_skill = \"\" then  Exit For\r\n         next_skill = getPatternDataComplex(objXML, \"*/Competency[@description=\'Skill > IT\']\", i, \"CompetencyId\", 0, \"id\")\r\n         if next_skill <> \"\" then  \r\n               skill_Array(skill_count) = next_skill\r\n               skill_count = skill_count + 1\r\n         End If\r\n   Next\r\n\r\n\r\nif DEBUG_FLAG=1 then\r\nresponse.write(\"SD1=\"    & whist_sd_Array(0)  & \"<br>\")\r\nresponse.write(\"SD2=\"    & whist_sd_Array(1) & \"<br>\")\r\nresponse.write(\"ED1=\"    & whist_ed_Array(0)   & \"<br>\")\r\nresponse.write(\"ED2=\"    & whist_rd_Array(1)   & \"<br>\")\r\nresponse.write(\"ORG1=\"   & whist_org_Array(0)  & \"<br>\")\r\nresponse.write(\"ORG2=\"   & whist_org_Array(1)  & \"<br>\")\r\nresponse.write(\"TITLE1=\" & whist_title_Array(0)  & \"<br>\")\r\nresponse.write(\"TITLE2=\" & whist_title_Array(1)  & \"<br>\")\r\n\r\nresponse.write(\"Lang_1=\" & lang_Array(0) & \"<br>\")\r\nresponse.write(\"Lev_1=\"  & levl_Array(0)  & \"<br>\")\r\nresponse.write(\"Lang_2=\" & lang_Array(1)  & \"<br>\")\r\nresponse.write(\"Lev_2=\"  & levl_Array(1)  & \"<br>\")\r\nresponse.write(\"Quals1=\" & qual_Array(0) & \"<br>\")\r\nresponse.write(\"Quals2=\" & qual_Array(1) & \"<br>\")\r\nresponse.write(\"JOBTITLE1=\" & job_Array(0) & \"<br>\")\r\nresponse.write(\"JOBTITLE2=\" & job_Array(1) & \"<br>\")\r\nresponse.write(\"SKILLS1=\" & skill_Array(0) & \"<br>\")\r\nresponse.write(\"SKILLS2=\" & skill_Array(1) & \"<br>\")\r\nEnd If\r\n \r\n     objXML = Nothing\r\n\r\n\r\n\r\n     \'=============================================================\r\n     \'\r\n     \'          MAPPING BIT\r\n     \'-------------------------------------------------------------\r\n  \r\n     session.Contents.removeall\r\n     session(\"CANDNAME\") = GivenName & \" \" & FamilyName \r\n     session(\"ADDRESS1\") = AddressLine\r\n     session(\"ADDRESS2\") = \"\"\r\n     session(\"TOWN\") = Municipality \r\n     session(\"POSTCODE\") = PostalCode\r\n     session(\"County\") = Region\r\n     session(\"TelNo\") = homeTel\r\n     session(\"Mobile\") = mobile\r\n     session(\"Email\") = email\r\n\r\n     for wh_num=1 to 15\r\n        ExecuteGlobal ( \"session(\" + Chr(34) + \"workhistdatefrom\" & wh_num & Chr(34) & \") = \" &  Chr(34) & whist_sd_Array(wh_num-1)     & Chr(34) )\r\n        ExecuteGlobal ( \"session(\" + Chr(34) + \"workhistdateto\"   & wh_num & Chr(34) & \") = \" &  Chr(34) & whist_ed_Array(wh_num-1)     & Chr(34) )\r\n        ExecuteGlobal ( \"session(\" + Chr(34) + \"workhistcompname\" & wh_num & Chr(34) & \") = \" &  Chr(34) & whist_org_Array(wh_num-1)    & Chr(34) )\r\n        ExecuteGlobal ( \"session(\" + Chr(34) + \"workhistdesc\"     & wh_num & Chr(34) & \") = \" &  Chr(34) & whist_title_Array(wh_num-1)  & Chr(34) )\r\n     Next\r\n \r\n     for lang_num=1 to 5\r\n       level_tt = \"\"\r\n       if levl_Array(lang_num-1) = \"NATIVE\"       or levl_Array(lang_num-1) = \"EXCELLENT\" then level_tt =\"1\"\r\n       if levl_Array(lang_num-1) = \"ADVANCED\"     or levl_Array(lang_num-1) = \"GOOD\"      then level_tt =\"2\"\r\n       if levl_Array(lang_num-1) = \"INTERMEDIATE\" or levl_Array(lang_num-1) = \"WORKING\"   then level_tt =\"2\"\r\n       if levl_Array(lang_num-1) = \"BASIC\"        or levl_Array(lang_num-1) = \"BASIC\"     then level_tt =\"3\"\r\n       ExecuteGlobal ( \"session(\" + Chr(34) + \"language\" & lang_num & Chr(34) & \") = \" &  Chr(34) &  lang_Array(lang_num-1)  & Chr(34) )\r\n       ExecuteGlobal ( \"session(\" + Chr(34) + \"level\"    & lang_num & Chr(34) & \") = \" &  Chr(34) &  level_tt                & Chr(34) )\r\n     Next\r\n\r\n     Quals = \"\"   \'-------------------- Quals comma delimed going to NewDropDown() in multidelect\r\n     For i = 0 to 10\r\n       qual_new    = qual_Array(i)  \r\n       if qual_new = \"\" then Exit For    \r\n       if Quals <> \"\" then Quals = Quals & \", \"\r\n       Quals = Quals & qual_new\r\n     Next\r\n     session(\"Qualifications\")  = Quals\r\n\r\n     JOBTITLE = \"\"  \'-------------- newline delimeted going to textarea\r\n     For i = 0 to 100\r\n         next_job =  job_Array(i)\r\n         if next_job = \"\" then  Exit For\r\n         if JOBTITLE <> \"\" then JOBTITLE = JOBTITLE + Chr(10)  \r\n         JOBTITLE = JOBTITLE + next_job\r\n     Next\r\n     session(\"JOBTITLE\") = JOBTITLE\r\n\r\n     SKILLS = \"\"  \'--------- newline delimeted going to textarea\r\n     For i = 0 to 150\r\n         next_skill =  skill_Array(i)\r\n         if next_skill = \"\" then  Exit For\r\n         if SKILLS <> \"\" then SKILLS = SKILLS + Chr(10)  \r\n         SKILLS = SKILLS + next_skill\r\n     Next\r\n     session(\"SKILLS\") = SKILLS\r\n \r\n     DaxtraSessionFill = \"SUCCESS\"\r\n \r\nEnd Function\r\n\r\n\r\n\r\n\r\n\'======================================================================\r\n\' \r\n\'                       XML Parsing\r\n\'\r\n\'--------------------------------------------------------------------\r\n\r\nFunction getCharacterData(ByRef xml_doc, ByVal query)\r\n   getCharacterData = getPatternData(xml_doc, query, 0, \"#\")\r\nEnd Function\r\n\r\nFunction getPatternData(ByRef xml_doc, ByVal query, ByVal item_number, ByVal pattern)\r\n  On Error Resume Next\r\n  Dim objList: Set objLst = xml_doc.getElementsByTagName(query)\r\n \r\n  For i = 0 to (objLst.length)\r\n    if i=item_number then \r\n       getPatternData =  getPatternDatafromNode(objLst.item(i), pattern) \r\n      Exit For\r\n    End If\r\n  Next\r\nEnd Function\r\n\r\n\r\nFunction getPatternDataComplex(ByRef xml_doc, ByVal query, ByVal nnumber, ByVal query1, ByVal nnumber1, ByVal pattern)\r\n  On Error Resume Next\r\n  if query1 = \"\" then\r\n     getPatternDataComplex = getPatternData(xml_doc, query, nnumber, pattern)\r\n     Exit Function\r\n  End If\r\n\r\n\r\n  Dim objList: Set objLst = xml_doc.getElementsByTagName(query)\r\n  Dim node\r\n  For i = 0 to (objLst.length)\r\n    if i=nnumber then \r\n      Set node = objLst.item(i)\r\n      Exit For\r\n    End If\r\n  Next\r\n\r\n  If node Is Nothing Then\r\n      getPatternDataComplex = \"\" \' MAGIC_NO_MATCH_STRING\r\n      Exit Function\r\n  End If\r\n  \r\n \r\n  Set objLst = node.selectNodes(query1)\r\n  For i = 0 to (objLst.length)\r\n    if i=nnumber1 then \r\n      getPatternDataComplex = getPatternDatafromNode(objLst.item(i), pattern)\r\n      Exit For\r\n    End If\r\n\r\n  Next\r\nEnd Function\r\n\r\n\r\nFunction getPatternDatafromNode(ByRef node, ByVal pattern)\r\n      On Error Resume Next\r\n      if pattern = \"#\" or pattern = \"{#}\" or pattern = \"\" then\r\n           getPatternDatafromNode = node.text\r\n      Else \r\n          Dim AttrArray : Set AttrArray = node.attributes \' .getNamedItem(pattern)\r\n           For Each aElem In AttrArray\r\n              if aElem.name = pattern then\r\n                  getPatternDatafromNode = aElem.value\r\n                  Exit For \r\n              End If\r\n           Next\r\n      End if\r\n   \r\n      \'getPatternDatafromNode = UtilsForm.RTrimComplete(getCharacterData)\r\nEnd Function\r\n\'-------------------------------------------------------------------------\r\n\'\r\n\'==========================================================================\r\n\r\n\r\n\r\n\r\n\r\n\'===========================================================================\r\n\'============================================================================\r\n\'\r\n\'                  GETTING HRXML\r\n\'\r\n\'-------------------------------------------------------------------------------\r\nFunction DaxtraSmartParse( byval FileName, byval AccountName, byVal DAXTRA_SERVER )\r\n     On Error Resume Next\r\n\r\n     Err.Number= 0\r\n                                        \r\n     if FileName = \"\" then\r\n        DaxtraSmartParse = \"<Error>FileName argument is empty</Error>\"\r\n        Exit Function\r\n     End If\r\n     if AccountName = \"\" then\r\n        DaxtraSmartParse = \"<Error>AccountName argument is empty</Error>\"\r\n        Exit Function\r\n     End If\r\n\r\n   \' cv_file_base_64 = \"UGF1bCBTbWl0aCAgNjUgQ29ybyBTdHJlZXQsIGVkaW5idXJnaCBFSDE1IDNMWg==\"\r\n\r\n     cv_file_base_64 = base64_encode_fromfile(FileName)\r\n     if  Left(cv_file_base_64, 7) = \"<Error>\" then \r\n        DaxtraSmartParse = cv_file_base_64  \r\n        Exit Function\r\n     End If\r\n     if cv_file_base_64 = \"\"  then \r\n        DaxtraSmartParse = \"<Error>Could not read \" + FileName + \"</Error>\"\r\n        Exit Function\r\n     End If\r\n  \r\n     \'response.write(cv_file_base_64)\r\n \r\n     strEnvelope = strEnvelope + \"<soap:Envelope\" + Chr(10)\r\n     strEnvelope = strEnvelope + \"xmlns:soap=\'http://schemas.xmlsoap.org/soap/envelope/\' \" + Chr(10)\r\n     strEnvelope = strEnvelope + \"xmlns:xsi=\'http://www.w3.org/2001/XMLSchema-instance\'  \" + Chr(10)\r\n     strEnvelope = strEnvelope + \"xmlns:xsd=\'http://www.w3.org/2001/XMLSchema\'>  \" + Chr(10)   \r\n     strEnvelope = strEnvelope + \"<soap:Body> \" + Chr(10)   \r\n     strEnvelope = strEnvelope + \"<ProcessCV xmlns=\'CVXtractorService\'> \" + Chr(10)\r\n     strEnvelope = strEnvelope + \"<document_url xsi:type=\'xsd:base64Binary\'>\" + cv_file_base_64 + \"</document_url>\" + Chr(10)\r\n     strEnvelope = strEnvelope + \"<account>\" + AccountName + \"</account>\" + Chr(10)\r\n     strEnvelope = strEnvelope + \"</ProcessCV>\" + Chr(10)\r\n     strEnvelope = strEnvelope + \"</soap:Body>\" + Chr(10)\r\n     strEnvelope = strEnvelope + \"</soap:Envelope>\" + Chr(10)\r\n\r\n     Dim objHTTP:  set objHTTP = CreateObject(\"Microsoft.XMLHTTP\")\r\n     \'Dim objHTTP:  set objHTTP = CreateObject(\"Msxml2.XMLHTTP.4.0\")\r\n     if Err.Number <> 0 then \r\n          DaxtraSmartParse  = \"<Error>Failed to initiate HTTP:\" & Err.Description & \"</Error>\"\r\n          Exit function\r\n     End If\r\n\r\n     objHTTP.open \"post\", \"http://\" & DAXTRA_SERVER & \"/cvvalid-bin/CVXtractorServiceSOAP.pl\", False\r\n     if Err.Number <> 0 then goto err_handle\r\n     objHTTP.setRequestHeader \"Content-Type\", \"text/xml\" \r\n     objHTTP.setRequestHeader \"Connection\", \"close\"\r\n     objHTTP.send strEnvelope\r\nerr_handle:\r\n     if Err.Number <> 0 then \r\n          objHTTP = Nothing\r\n          DaxtraSmartParse  = \"<Error>Failed to post to the server:\" & Err.Description & \"</Error>\"\r\n          Exit function\r\n     End If\r\n\r\n     If (objHTTP.Status >= 200 And objHTTP.Status < 300) Then  \' -- SUCCESS\r\n       txtResponseHeaders = objHTTP.getAllResponseHeaders\r\n       txtResponse = objHTTP.responseText\r\n       DaxtraSmartParse = txtResponse\r\n     Else\r\n       DaxtraSmartParse = \"<Error>Request Failed with status \" & xmlReq.Status & vbCrLf\r\n       DaxtraSmartParse = DaxtraSmartParse & xmlReq.responseText & \"</Error>\"\r\n     End If\r\n\r\n    objHTTP = Nothing\r\nEnd Function \'----------------------------------------------------------------------\r\n\r\n\r\nFunction base64_encode_fromfile( byval sFilename )\r\n	On Error Resume Next\r\n\r\n	Dim objStream: Set objStream = Server.CreateObject(\"ADODB.Stream\")\r\n        if Err.Number <> 0 then \r\n          base64_encode_fromfile = \"<Error>Could not initialize ADODB</Error>\"\r\n          Exit function\r\n        End If\r\n\r\n	objStream.Type = 1\r\n	objStream.Open\r\n	objStream.LoadFromFile sFilename \' Server.MapPath(sFilename)\r\n       \r\n        if Err.Number <> 0 then \r\n          base64_encode_fromfile = \"<Error>Could not open file \" & sFilename & \" : \" & Err.Description & \"</Error>\"\r\n          Exit function\r\n        End If\r\n\r\n	Dim objXMLDoc: Set objXMLDoc = Server.CreateObject(\"MSXML2.DOMDocument\")\r\n        if Err.Number <> 0 then \r\n          base64_encode_fromfile = \"<Error>Could not initialize MSXML2.DOMDocument</Error>\"\r\n          Exit function\r\n        End If\r\n\r\n        objXMLDoc.async = False\r\n        objXMLDoc.validateOnParse = False\r\n        objXMLDoc.loadXML \"<Base64Data />\"\r\n\r\n	Dim objDocElem: Set objDocElem = objXMLDoc.documentElement\r\n	objDocElem.dataType = \"bin.base64\"\r\n	objDocElem.nodeTypedValue = objStream.Read\r\n\r\n	sBase64String = objDocElem.text\r\n\r\n	objStream.Close\r\n\r\n	Set objStream = Nothing\r\n	Set objDocElem = Nothing\r\n	Set objXMLDoc = Nothing\r\n\r\n	base64_encode_fromfile = sBase64String\r\nEnd Function  \'-------------------------------------------------\r\n\r\n%>\r\n\r\n<!-- ========= END OF DAXTRA Technologies smart parsing integration -->\r\n<!-- ============================================================== -->\r\n\r\n', '0', '2017-09-08 09:48:24', null, 'DaxtraSmartParse(CV简历解析为xml调用程序).asp.txt');
