INSERT INTO `txt` VALUES ('255', '2', 'db.AccessHelper.txt', 'using System;\r\nusing System.Data;\r\nusing System.Data.OleDb;\r\n\r\n\r\n/// <summary>\r\n///Access���ݿ������\r\n///����ʱ�䣺2010��10��1��\r\n///���ߣ����Ѿ\r\n///QQ: 20444465\r\n/// </summary>\r\npublic class AccessHelper\r\n{\r\n    private OleDbConnection conn = null;\r\n    private OleDbCommand cmd = null;\r\n    private OleDbDataReader sdr = null;\r\n    \r\n    public AccessHelper()\r\n    {\r\n        //  string connStr = WebConfigurationManager.ConnectionStrings[\"connStr\"].ToString();\r\n        string connStr = \"Provider=Microsoft.Jet.OLEDB.4.0;Data Source=|DataDirectory|db.mdb\";\r\n        conn = new OleDbConnection(connStr);\r\n    }\r\n    /// <summary>\r\n    /// ����Command����\r\n    /// </summary>\r\n    /// <param name=\"sql\">SQL���</param>\r\n    public void CreateCommand(string sql)\r\n    {\r\n        conn.Open();\r\n        cmd = new OleDbCommand(sql, conn);\r\n    }\r\n\r\n\r\n    /// <summary>\r\n    /// ��Ӳ���\r\n    /// </summary>\r\n    /// <param name=\"paramName\">��������</param>\r\n    /// <param name=\"value\">ֵ</param>\r\n    public void AddParameter(string paramName, object value)\r\n    {\r\n        cmd.Parameters.Add(new OleDbParameter(paramName, value));\r\n    }\r\n\r\n\r\n    /// <summary>\r\n    ///  ִ�в�����������ɾ��SQL���\r\n    /// </summary>\r\n    /// <param name=\"cmdText\">��ɾ��SQL���</param>\r\n    /// <param name=\"ct\">��������</param>\r\n    /// <returns>bool</returns>\r\n    public bool ExecuteNonQuery()\r\n    {\r\n        int res;\r\n        try\r\n        {\r\n            res = cmd.ExecuteNonQuery();\r\n            if (res > 0)\r\n            {\r\n                return true;\r\n            }\r\n        }\r\n        catch (Exception ex)\r\n        {\r\n            throw ex;\r\n        }\r\n        finally\r\n        {\r\n            if (conn.State == ConnectionState.Open)\r\n            {\r\n                conn.Close();\r\n            }\r\n        }\r\n        return false;\r\n    }\r\n\r\n\r\n    /// <summary>\r\n    ///  ִ�в�ѯSQL���\r\n    /// </summary>\r\n    /// <param name=\"cmdText\">��ѯSQL���</param>\r\n    /// <returns>DataTable</returns>\r\n    public DataTable ExecuteQuery()\r\n    {\r\n        DataTable dt = new DataTable();\r\n        using (sdr = cmd.ExecuteReader(CommandBehavior.CloseConnection))\r\n        {\r\n            dt.Load(sdr);\r\n        }\r\n        return dt;\r\n    }\r\n\r\n\r\n    /// <summary>\r\n    /// ��ҳ\r\n    /// </summary>\r\n    /// <param name=\"tableName\">����</param>\r\n    /// <param name=\"fieldName\">�ֶ���</param>\r\n    /// <param name=\"OrderfieldName\">�����ֶ���</param>\r\n    /// <param name=\"OrderType\">����ʽ��asc����desc</param>\r\n    /// <param name=\"strWhere\">���������ü�where</param>\r\n    /// <param name=\"PageSize\">ҳ��С</param>\r\n    /// <param name=\"PageIndex\">ҳ����</param>\r\n    /// <returns>DataTable</returns>\r\n    public DataTable Paging(string tableName, string fieldName, string OrderfieldName, string OrderType, string strWhere, int PageSize, int PageIndex)\r\n    {\r\n        DataTable dt = new DataTable();\r\n        string strSQL = \"\"; // ����� \r\n        string strTemp = \"\"; // ��ʱ���� \r\n        string strOrder = \"\"; // ��������\r\n\r\n\r\n        if (OrderType == \"desc\")\r\n        {\r\n            // ����\r\n            strTemp = \"<(select min\";\r\n            strOrder = \" order by \" + OrderfieldName + \" desc\";\r\n        }\r\n        else\r\n        {\r\n            // ����\r\n            strTemp = \">(select max\";\r\n            strOrder = \" order by \" + OrderfieldName + \" asc\";\r\n        }\r\n\r\n\r\n        #region ��һҳ\r\n        if (PageIndex == 1)\r\n        {\r\n            strTemp = string.IsNullOrEmpty(strWhere) ? \"\" : \" where \" + strWhere;\r\n            strSQL = \"select top \" + PageSize + \" \" + fieldName + \" from \" + tableName + strTemp + \" \" + strOrder;\r\n            CreateCommand(strSQL);\r\n            dt = ExecuteQuery();\r\n            return dt;\r\n        }\r\n        #endregion\r\n\r\n\r\n        #region ���ǵ�һҳ\r\n        if (string.IsNullOrEmpty(strWhere))\r\n        {\r\n            // ����Ϊ��\r\n            strSQL = string.Format(\"select top {0} {1} from {2} where {3}{4}({5}) from (select top {6} {7} from {8} {9}) as tblTmp) {10}\", PageSize, fieldName, tableName, OrderfieldName, strTemp, OrderfieldName, (PageIndex - 1) * PageSize, OrderfieldName, tableName, strOrder, strOrder);\r\n            CreateCommand(strSQL);\r\n            dt = ExecuteQuery();\r\n        }\r\n        else\r\n        {\r\n            // ������Ϊ��\r\n            strSQL =string.Format( \"select top {0} {1} from {2} where  {3}{4}({5}) from (select top {6} {7} from {8} where {9} {10}) as tblTmp) and {11} {12}\",PageSize,fieldName,tableName,OrderfieldName,strTemp,OrderfieldName,(PageIndex-1)*PageSize,OrderfieldName,tableName,strWhere,strOrder,strWhere,strOrder);\r\n            CreateCommand(strSQL);\r\n            dt = ExecuteQuery();\r\n        }\r\n        #endregion\r\n\r\n\r\n        return dt;\r\n    }\r\n\r\n\r\n    /// <summary>\r\n    /// ���ز�ѯSQL����ѯ���Ľ���ĵ�һ�е�һ�е�ֵ\r\n    /// </summary>\r\n    /// <returns></returns>\r\n    public string ExecuteScalar()\r\n    {\r\n        string res = \"\";\r\n        try\r\n        {\r\n            object obj = cmd.ExecuteScalar();\r\n            if (obj != null)\r\n            {\r\n                res = obj.ToString();\r\n            }\r\n        }\r\n        catch (Exception ex)\r\n        {\r\n            throw ex;\r\n        }\r\n        finally\r\n        {\r\n            if (conn.State == ConnectionState.Open)\r\n            {\r\n                conn.Close();\r\n            }\r\n        }\r\n        return res;\r\n    }\r\n\r\n\r\n}\r\n', '0', '2017-09-08 09:48:24', null, 'db.AccessHelper.txt');
