INSERT INTO `txt` VALUES ('284', '2', 'DBHelper.cs.txt', 'using System;\r\nusing System.Data;\r\nusing System.Data.SqlClient;\r\nusing System.Configuration;\r\n\r\n/// <summary>\r\n///DBHelpers 的摘要说明\r\n/// </summary>\r\npublic class DBHelper\r\n{\r\n    public DBHelper()\r\n    {\r\n        //\r\n        //TODO: 在此处添加构造函数逻辑\r\n        //\r\n    }\r\n    private static readonly string connectionString = ConfigurationManager.ConnectionStrings[\"connectionString\"].ConnectionString;\r\n    private static SqlConnection con;\r\n    public static SqlConnection GetConnection()\r\n    {\r\n        if (con == null)\r\n        {\r\n            con = new SqlConnection(connectionString);\r\n            con.Open();\r\n        }\r\n        if (con != null && con.State == ConnectionState.Closed)\r\n        {\r\n            con.Open();\r\n        }\r\n        if (con != null && con.State == ConnectionState.Broken)\r\n        {\r\n            con.Close();\r\n            con.Open();\r\n        }\r\n        return con;\r\n    }\r\n\r\n    /// <summary>\r\n    /// Close Connection\r\n    /// </summary>\r\n    private static void CloseCon()\r\n    {\r\n        if (con != null && con.State == ConnectionState.Open)\r\n        {\r\n            con.Close();\r\n            con = null;\r\n        }\r\n    }\r\n\r\n    /// <summary>\r\n    /// Execute Query To DataSet.\r\n    /// </summary>\r\n    /// <param name=\"commandType\">The commandType.\"text\"&\"sp\"/\"storedprocedure\"</param>\r\n    /// <param name=\"commandText\">The commandText.</param>\r\n    /// <param name=\"sqlParameter\">The params.</param>\r\n    /// <returns>The DataSet.</returns>\r\n    public static DataSet ExecuteQueryToDataSet(string commandType, string commandText, params SqlParameter[] sqlParameter)\r\n    {\r\n        try\r\n        {\r\n            SqlCommand cmd = new SqlCommand();\r\n            cmd.Connection = GetConnection();\r\n            if (commandType == \"text\")\r\n            {\r\n                cmd.CommandType = CommandType.Text;\r\n            }\r\n            if (commandType == \"sp\" || commandType == \"storedprocedure\")\r\n            {\r\n                cmd.CommandType = CommandType.StoredProcedure;\r\n            }\r\n            cmd.CommandText = commandText;\r\n            if (sqlParameter != null)\r\n            {\r\n                cmd.Parameters.AddRange(sqlParameter);\r\n            }\r\n            DataSet dataSet = new DataSet();\r\n            SqlDataAdapter dataAdapter = new SqlDataAdapter(cmd);\r\n            dataAdapter.Fill(dataSet);\r\n            return dataSet;\r\n        }\r\n        catch (Exception e)\r\n        {\r\n            throw e;\r\n        }\r\n        finally\r\n        {\r\n            CloseCon();\r\n        }\r\n    }\r\n\r\n\r\n\r\n    /// <summary>\r\n    /// Execute NonQuery.\r\n    /// </summary>\r\n    /// <param name=\"sqlConnection\">The sqlConnection.</param>\r\n    /// <param name=\"commandType\">The commandType.</param>\r\n    /// <param name=\"commandText\">The commandText.</param>\r\n    /// <param name=\"sqlParameter\"></param>\r\n    /// <returns>sqlParameter</returns>\r\n    public static int ExecuteNonQuery(string commandType, string commandText, params SqlParameter[] sqlParameter)\r\n    {\r\n        try\r\n        {\r\n            SqlCommand cmd = new SqlCommand();\r\n            cmd.Connection = GetConnection();\r\n            if (commandType == \"text\")\r\n            {\r\n                cmd.CommandType = CommandType.Text;\r\n            }\r\n            if (commandType == \"sp\" || commandType == \"storedprocedure\")\r\n            {\r\n                cmd.CommandType = CommandType.StoredProcedure;\r\n            }\r\n            cmd.CommandText = commandText;\r\n            if (sqlParameter != null)\r\n            {\r\n                cmd.Parameters.AddRange(sqlParameter);\r\n            }\r\n            var index = cmd.ExecuteNonQuery();\r\n            return index;\r\n        }\r\n        catch (Exception ex)\r\n        {\r\n            throw ex;\r\n        }\r\n    }\r\n}\r\n', '0', '2017-09-08 09:48:25', null, 'DBHelper.cs.txt');
INSERT INTO `txt` VALUES ('285', '2', 'DBHelper最简单版.txt', 'using System;\r\nusing System.Collections.Generic;\r\nusing System.Linq;\r\nusing System.Text;\r\nusing System.Data;\r\nusing System.Data.SqlClient;\r\nusing MyBookShop.Models;\r\nusing System.Configuration;\r\n\r\nnamespace MyBookShop.DAL\r\n{\r\n    public static class DBHelper\r\n    {\r\n\r\n        //private static readonly string conStr = ConfigurationManager.ConnectionStrings[\"conStr\"].ConnectionString;\r\n        private static readonly string conStr = @\"server=.\\sql2005;uid=sa;pwd=sa;database=MyBookShop\";\r\n        private static SqlConnection con = null;\r\n        private static SqlCommand cmd = null;\r\n        /// <summary>\r\n        /// �����ݿ�\r\n        /// </summary>\r\n        /// <returns></returns>\r\n        public static SqlConnection GetConnection()\r\n        {\r\n            if (con == null)\r\n            {\r\n                con = new SqlConnection(conStr);\r\n                con.Open();\r\n            }\r\n            if (con != null && con.State == ConnectionState.Closed)\r\n            {\r\n                con.Open();\r\n            }\r\n            if (con != null && con.State == ConnectionState.Broken)\r\n            {\r\n                con.Close();\r\n                con.Open();\r\n            }\r\n            return con;\r\n        }\r\n        /// <summary>\r\n        /// ��ѯ�õ�Reader\r\n        /// </summary>\r\n        /// <param name=\"sql\"></param>\r\n        /// <param name=\"param\"></param>\r\n        /// <returns></returns>\r\n        public static SqlDataReader ExecuteReader(string sql, params SqlParameter[] param)\r\n        {\r\n            if (!string.IsNullOrEmpty(sql))\r\n            {\r\n                cmd = new SqlCommand(sql, con);\r\n                cmd.CommandType = CommandType.Text;\r\n                cmd.CommandText = sql;\r\n                cmd.Connection = GetConnection();\r\n            }\r\n            if (param != null && param.Length > 0)\r\n            {\r\n                cmd.Parameters.AddRange(param);\r\n            }\r\n            return cmd.ExecuteReader();\r\n        }\r\n        /// <summary>\r\n        /// ��ѯ�õ���һ�е�һ��\r\n        /// </summary>\r\n        /// <param name=\"sql\"></param>\r\n        /// <param name=\"param\"></param>\r\n        /// <returns></returns>\r\n        public static object ExecuteScalar(string sql, params SqlParameter[] param)\r\n        {\r\n            if (!string.IsNullOrEmpty(sql))\r\n            {\r\n                cmd = new SqlCommand(sql, con);\r\n                cmd.CommandType = CommandType.Text;\r\n                cmd.CommandText = sql;\r\n                cmd.Connection = GetConnection();\r\n            }\r\n            if (param != null && param.Length > 0)\r\n            {\r\n                cmd.Parameters.AddRange(param);\r\n            }\r\n            return cmd.ExecuteScalar();\r\n        }\r\n        /// <summary>\r\n        /// ��ѯ�õ�DataTable\r\n        /// </summary>\r\n        /// <param name=\"sql\"></param>\r\n        /// <param name=\"param\"></param>\r\n        /// <returns></returns>\r\n        public static DataTable ExecuteTable(string sql, params SqlParameter[] param)\r\n        {\r\n            if (!string.IsNullOrEmpty(sql))\r\n            {\r\n                cmd = new SqlCommand(sql);\r\n                cmd.CommandType = CommandType.Text;\r\n                cmd.CommandText = sql;\r\n                cmd.Connection = GetConnection();\r\n            }\r\n            if (param != null && param.Length > 0)\r\n            {\r\n                cmd.Parameters.AddRange(param);\r\n            }\r\n            SqlDataAdapter da = new SqlDataAdapter(cmd);\r\n            DataSet ds = new DataSet();\r\n            da.Fill(ds);\r\n            return ds.Tables[0];\r\n        }\r\n        /// <summary>\r\n        /// �ǲ�ѯ����\r\n        /// </summary>\r\n        /// <param name=\"sql\"></param>\r\n        /// <param name=\"param\"></param>\r\n        /// <returns></returns>\r\n        public static int ExecuteNonQuery(string sql, params SqlParameter[] param)\r\n        {\r\n            if (!string.IsNullOrEmpty(sql))\r\n            {\r\n                cmd = new SqlCommand(sql);\r\n                cmd.CommandType = CommandType.Text;\r\n                cmd.CommandText = sql;\r\n                cmd.Connection = GetConnection();\r\n            }\r\n            if (param != null && param.Length > 0)\r\n            {\r\n                cmd.Parameters.AddRange(param);\r\n            }\r\n            return cmd.ExecuteNonQuery();\r\n        }\r\n    }\r\n}\r\n', '0', '2017-09-08 09:48:25', null, 'DBHelper最简单版.txt');
INSERT INTO `txt` VALUES ('286', '2', 'DebugLogger.cs.txt', '\r\nusing System;\r\nusing System.Collections.Generic;\r\nusing System.Text;\r\n\r\nusing System.IO;\r\n\r\nnamespace BJHouse.Common\r\n{\r\n    /// <summary>\r\n    /// 诊断查错日志记录\r\n    /// </summary>\r\n    public class DebugLogger\r\n    {\r\n        /// <summary>\r\n        /// 错误记录\r\n        /// </summary>\r\n        /// <param name=\"sLogText\">错误文本</param>\r\n        public static void LogError(string sLogText)\r\n        {\r\n            try\r\n            {\r\n                if (!SigbitCommonConfig.Instance.IsLogError)\r\n                    return;\r\n\r\n                LogByDate(\"error\", sLogText);\r\n            }\r\n            catch (Exception)\r\n            {\r\n                     \r\n            }\r\n        }\r\n\r\n        \r\n        /// <summary>\r\n        /// 错误记录\r\n        /// </summary>\r\n        /// <param name=\"eException\">错误文本</param>\r\n        public static void LogError(Exception eException)\r\n        {\r\n            try\r\n            {\r\n                if (!SigbitCommonConfig.Instance.IsLogError)\r\n                    return;\r\n\r\n                string sExp = \"Message:\" + eException.Message;\r\n                sExp += \"\\r\\n Source:\" + eException.Source;\r\n                sExp += \"\\r\\n StackTrace:\" + eException.StackTrace;\r\n                sExp += \"\\r\\n \";\r\n                LogByDate(\"error\", sExp);\r\n            }\r\n            catch (Exception)\r\n            {\r\n\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 警告记录\r\n        /// </summary>\r\n        /// <param name=\"sLogText\">警告文本</param>\r\n        public static void LogWarning(string sLogText)\r\n        {\r\n            try\r\n            {\r\n                if (!SigbitCommonConfig.Instance.IsLogWarning)\r\n                    return;\r\n\r\n                LogByDate(\"warning\", sLogText);\r\n            }\r\n            catch (Exception)\r\n            {\r\n\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 记录文本信息\r\n        /// </summary>\r\n        /// <param name=\"sMessage\">文本信息</param>\r\n        /// <param name=\"sKey\">标识字</param>\r\n        public static void LogDebugMessage(string sMessage, string sKey)\r\n        {\r\n            try\r\n            {\r\n                if (!SigbitCommonConfig.Instance.IsLogDebugMessage)\r\n                    return;\r\n\r\n                LogByDate(sKey, sMessage);\r\n            }\r\n            catch (Exception)\r\n            {\r\n\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 记录文本信息\r\n        /// </summary>\r\n        /// <param name=\"sMessage\">文本信息</param>\r\n        public static void LogDebugMessage(string sMessage)\r\n        {\r\n            try\r\n            {\r\n                if (!SigbitCommonConfig.Instance.IsLogDebugMessage)\r\n                    return;\r\n\r\n                LogByDate(\"\", sMessage);\r\n            }\r\n            catch (Exception)\r\n            {\r\n\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 按日期分文件进行记录\r\n        /// </summary>\r\n        /// <param name=\"sLogType\">日志类型</param>\r\n        /// <param name=\"sLogText\">日志文本</param>\r\n        private static void LogByDate(string sLogType, string sLogText)\r\n        {\r\n            if (!SigbitCommonConfig.Instance.DebugFileEnabled)\r\n                return;\r\n\r\n            lock (typeof(DebugLogger))\r\n            {\r\n                //========== 1. 得到文件名 ============\r\n                string sLogDirectory = SigbitCommonConfig.Instance.DebugFileDirectory;\r\n                string sLogFileName = sLogDirectory + \"\\\\debuglog_\"\r\n                        + DateTimeUtil.Now.Substring(0, 10).Replace(\"-\", \"\") + \".vlog\";\r\n\r\n                //======= 2. 打开文件 ==========\r\n                FileStream fs = File.OpenWrite(sLogFileName);\r\n                fs.Seek(0, SeekOrigin.End);\r\n\r\n                //========== 3. 写入时间 ==========\r\n                string sMessage = \"========== \";\r\n                if (sLogType != \"\")\r\n                    sMessage += \"(\" + sLogType + \") \";\r\n                sMessage += DateTimeUtil.Now + \" ==========\\r\\n\";\r\n                byte[] bsLog = Encoding.UTF8.GetBytes(sMessage);\r\n                fs.Write(bsLog, 0, bsLog.Length);\r\n\r\n                bsLog = Encoding.UTF8.GetBytes(sLogText + \"\\r\\n\\r\\n\");\r\n                fs.Write(bsLog, 0, bsLog.Length);\r\n\r\n                fs.Flush();\r\n                fs.Close();\r\n            }\r\n        }\r\n    }\r\n}\r\n', '0', '2017-09-08 09:48:25', null, 'DebugLogger.cs.txt');
INSERT INTO `txt` VALUES ('287', '2', 'Drupal是使用PHP语言编写的开源内容管理框架（CMF），它由内容管理系统（CMS）.txt', 'drupal\r\n编辑\r\nDrupal是使用PHP语言编写的开源内容管理框架（CMF），它由内容管理系统（CMS）和PHP开发框架（Framework）共同构成。连续多年荣获全球最佳CMS大奖，是基于PHP语言最著名的WEB应用程序。截止2011年底，共有13,802位WEB专家参加了Drupal的开发工作；228个国家使用181种语言的729,791位网站设计工作者使用Drupal。著名案例包括：联合国、美国白宫、美国商务部、纽约时报、华纳、迪斯尼、联邦快递、索尼、美国哈佛大学、Ubuntu等。\r\n目录\r\n1简介\r\n2优点\r\n3功能\r\n4运行环境\r\n5安装\r\n6设计\r\n7入会\r\n8主要术语\r\n9网站案例\r\n10管理平台\r\n1简介\r\n编辑\r\n\r\nDrupal诞生于2000年，是一个基于PHP语言编写的开发型CMF（内容管理框架），即：CMS+ framework。其中 framework是指Drupal内核中的功能强大的PHP类库和PHP函数库，以及在此基础上抽象的Drupal API。在网站开发能力上，Drupal，Joomla和Yii、CodeIgniter、Zend、CakePHP等业界顶级PHP框架同样强大。形象的说，Drupal是一个附带CMS的PHP开发框架。Drupal创始人Dries Buytaert创办的美国Acquia公司的开发人员甚至戏称：Drupal是一个用于开发网站的“操作系统”（webOS），而且此称法被业界广泛接受。很多Drupaler（即Drupal开发者）信奉这样一条真理：既然Drupal已经为我们写好了CMS，那么我们为什么还要选择那些不含有CMS的PHP开发框架呢？\r\nDrupal是一套开源系统，全球数以万计的WEB开发专家都在为Drupal技术社区贡献代码。因此，Drupal的代码在安全性、健壮性上具有世界最高水平。这也是美国白宫、美国商务部、法国政府、纽约时报、SONY等著名政府和机构纷纷采用Drupal建设网站的最重要的原因。\r\nDrupal不等于模板建站系统，他们之间有着本质的区别。Drupal的学习曲线相当漫长和陡峭，比PHP难度大得多，要求也高的多。事实上，只有精通XHTML、CSS、Javascript、PHP、MySql的开发人员，经过长期刻苦的学习，才有可能真正的驾驭Drupal，Drupal是给经验丰富的程序员用的，而不是给一般普通用户用的。模板建站系统则不同，模板系统根本不需要任何学习，不需要任何编程知识就可以使用，只适合初级用户搭建一些简单的网站。\r\nDrupal具有业内最强的定制能力和灵活性，这也是造成Drupal学习难度太大的根本原因，这也是没有办法的事情，灵活性的提高必然使复杂程度提高。因此，Drupal技术圈内有一句名言——“Drupal有难度，学习需谨慎。”\r\nDrupal强大的定制开发能力，也使其成为众多有技术实力的网站建设公司优先选用的网站开发平台。因为，任何网站建设公司自己开发的系统，在技术上都无法达到Drupal的水平，这正是开源软件的优势所在：全世界的WEB专家都在为Drupal贡献自己的智慧，这是某个公司内部技术团队永远也无法享受到的。是否具有Drupal技术的开发能力也成为鉴别网站建设公司实力的试金石。没有技术实力的公司，根本没有能力驾驭Drupal。\r\nDrupal的架构由三大部分组成：内核、模块、主题。三者通过Hook机制紧密的联系起来。其中，内核部分由世界上多位著名的WEB开发专家组成的团队负责开发和维护。\r\nDrupal综合了强大并可自由配置的功能，能支持从个人博客（PersonalWeblog）到大型社区驱动（Community-Driven）的网站等各种不同应用的网站项目。Drupal最初是由DriesBuytaert所开发的一套社群讨论软件。之后，由于它的灵活的架构，方便的扩展等特性，使得世界上成千上万个程序员加入了Drupal的开发与应用中。今天，它已经发展成为一套强大的系统，很多大型机构都采用基于Drupal的框架建站，包括The Onion，Ain\'t ItCool News，SpreadFirefox，Ourmedia，KernelTrap，NewsBusters等等。它特别常见于社区主导的网站。\r\nDrupal目前的最高版本是8.x-dev测试版本，官方网站下载页面最近释出的版本为7.26，是官方推荐的稳定版。该版本由7.1版修复而来，修正了大量BUG，没有太多新功能，适合学习Drupal的新手安装。下载页面同时提供了Drupal 6.28等旧版本下载。\r\n\r\n2优点\r\n编辑\r\n\r\n它有以下优点\r\n+ 搜索引擎友好的URL\r\n+ Drupal提供了强大的个性化环境，每个用户可以对网站内容和表现形式进行个性化设置\r\n+ Drupal提供了基于角色的权限系统，没有必要对每个用户进行授权，只需要对角色进行授权\r\n+ Drupal提供的站内搜索系统能对站内的所有内容进行索引和搜索\r\n+ Drupal的模板系统将内容和表现分离，可以很方便地控制网站的外观\r\n+ Drupal提供内建的新闻聚合工具\r\n+ 提供完善的站点管理和分析工具\r\n+ Drupal的Caching机制能有效减少数据库查询次数，从而提高站点性能，降低服务器负荷\r\n+ 强大的多语言支持体系。能够支持很多国家的语言，让你的网站在世界上通行。\r\n+ 扩展能力强大，有丰富的第三方扩展支持。\r\n+ 内部结构以最简单的PHP特征实现丰富的钩子机制。\r\n3功能\r\n编辑\r\n\r\nDrupal是一套开源的内容管理平台，拥有多种功能，可以用来建设从个人网站到大型社区网站。Drupal包括以下的功能：\r\nBlog\r\n协同写作平台\r\n论坛\r\n电子报\r\n相册\r\n文件的上传与下载\r\n全文搜索\r\n多角色权限管理\r\n模块化\r\n主题引擎\r\n多语言支持\r\n4运行环境\r\n编辑\r\n\r\nDrupal 基于PHP脚本语言，推荐使用mysql数据库，所以要运行Drupal必须先安装好PHP支持程序，推荐apache + mysql + php的安装和配置环境。linux系统下推荐采用lamp安装套件或windows系统下推荐安装wamp套件。\r\nWeb server\r\nApache（推荐）\r\nApache 1.3 或者Apache 2.x 在 UNIX/Linux,OS X,o或 Windows上；\r\n使用apache \'mod_rewrite\' 扩展，开启简洁路径；\r\nMicrosoft IIS\r\nDrupal核心可以运用在IIS 5,IIS 6，或IIS 7，前提是PHP需要配置正确；\r\nWhen using Drupal on IIS 7 with fastcgi you must install Hotfix kb954946,or wait until the hotfix appears in a package update (recommended). KB954946 was included in Windows 2008 Server SP2\r\nDatabase server\r\nMysqL（推荐）\r\nDrupal 5.x和早期的版本支持MySQL 3.23.17 或更高版本。强力推荐MySQL 4.1或更高的；\r\nDrupal6支持MySQL 4.1或更高版本；\r\nDrupal7仅支持MySQL5.0.15或更高，而且必须在php上设置PDO database扩展；\r\nPostgreSQL\r\nDrupal6支持PostgreSQL 7.1或更高；\r\nDrupal7仅支持PostgreSQL 8.3或更高；\r\nPHP 5.2.6 for Windows has a bug in its pgsql extension. You will need to replace it with the php_pgsql.dll from version 5.2.5.\r\n5安装\r\n编辑\r\n\r\n安装步骤：\r\n1.建立数据库：用mysql命令行、phpmyadmin、mysql-front等工具新建一个数据库，并记住数据库的名称。\r\n2.将Drupal代码根目录放入apache的web服务目录（www或htdocs等），将sites/default文件夹下的default.settings.php复制一份，重命名为settings.php，并将其文件权限设置为可写。\r\n3.在浏览器地址栏输入http://你的网站域名 （如果将代码放置在网站根目录下），或http://你的网站域名/drupal （如果将代码放置在drupal目录下），在出现的页面中选择语言（默认为英语），然后按提示填入数据库的名称，用户，密码等信息。\r\n4.首次执行，可以设置相应的个性化参数，如帐户，首页，简介链接等。\r\n注：安装过程中创建的帐户为网站管理员，拥有该网站的最高权限。\r\n6设计\r\n编辑\r\n\r\nDrupal 官方提供大量模板和主题可以供用户免费下载，WEB 设计师也可以自行设计模板，和全球用户共享知识的海洋。\r\n7入会\r\n编辑\r\n\r\n2007年，时任官方专员的Jacob Redding先生开始在中国开展推广活动。2008年，由John Zhu,Jacob Redding,Amber Zhong 等人在北京组织举办了3期BootCamp推广培训，和其它各类主题推广活动。中国drupal协会是2008年11月15日由drupal活动的组织者circleoflife，chinajason，John Zhu，Amber.Zhong等人创建，协会的主旨是积极传播‘自由、参与、沟通、奉献’的开源精神，在中国推广Drupal。\r\n8主要术语\r\n编辑\r\n\r\nDrupal是一个非常优秀的PHP CMS，在使用Drupal之前，我们好好看看Drupal主要术语的介绍.Drupal术语，Block（区块），Box（盒子），Engine（引擎），Filter(过滤器),Module（模块），Node（节点），Permissions（权限），Style（样式）Taxonomy（分类）Template（模板），Theme（主题）风格\r\n# Block（区块）\r\n区块一般位于网页两边的侧栏中，用于导航或显示一些附加内容。区块是一种把数据定位到页面的方法之一。各主题风格可以通过block($subject,$content,$region = \"main\")函数来控制区块的显示风格。\r\n# Box（盒子）\r\n盒子是Drupal页面内容的容器。每一个盒子有一个标题和一些内容。各主题风格可以通过box($subject,$content,$region = \"main\")函数来控制盒子的显示风格。\r\n# Engine（引擎）\r\n引擎是一类特殊的主题，它通过模板来输出HTML内容。\r\n# Filter（过滤器）\r\n过滤器是做内容过滤的框架。\r\n# Module（模块）\r\n模块是为Drupal提供扩展功能的代码片段。有些模块是Drupal核心系统的一部分（例如taxonomy和blog模块），扩展模块（例如 weblinks和image模块）则不属于Drupal的核心系统。核心模块已包含在Drupal的下载包中，扩展模块则需要单独下载。\r\n# Node（节点）\r\n节点是Drupal中最简单又最难理解的一个概念。几乎Drupal中所有的内容都被存储为一个节点。一个节点就是Drupal的一块内容，它可以是投票、报道、书的一页、或一张图片。\r\n# Permissions（权限）\r\n权限用于控制内容的创建、修改和站点管理等访问。管理员可以将权限赋给角色，将角色赋给用户。一个用户可以属于一个或多个角色。Drupal缺省提供了两种角色：游客和注册用户\r\n# Style（样式）\r\n样式是主题或引擎提供的CSS文件，用于替换缺省的CSS。\r\n# Taxonomy（分类）\r\nDrupal通过分类来描述目录系统，用于对网站的内容进行组织和分类。\r\n# Template（模板）\r\n模板是模板引擎的，普通人可读懂的HTML文件，这些文件有一些特殊的代码，用于产生动态信息。\r\n# Theme（主题）\r\n主题是包含一些函数的PHP文件，用于提供不同的布局风格。\r\n9网站案例\r\n编辑\r\n\r\n很多全球著名的政府机构、商业公司、高等学府、技术社区和社团的网站，都使用Drupal进行搭建。下面是部分基于Drupal的典型案例：\r\n政府机构－联合国、美国白宫、美国商务部、美国总统奥巴马经济恢复网站、法国政府、纽约市长Mike Bloomberg\r\n跨国公司－联邦快递、诺基亚、Adobe、Sun\r\n娱乐公司－华纳兄弟、索尼/BMG、Fox、环球唱片、迪斯尼/ABC\r\n雅虎、Novell、BysoftChina\r\n新闻杂志－纽约时报、纽约观察报、福布斯、花花公子、The Fast Company、Now Public、The Onion\r\n高等学府－美国哈佛大学、美国麻省理工学院（MIT）\r\n技术社区－Ubuntu、Fedora、Firefox、Team Sugar\r\n个人网站－巴拉搜、思跃、思墨\r\n这些成功的合作案例证明，Drupal不但适合个人站点及网上社区类网站，而且已被国际著名大公司和组织机构所接受和采纳。相信在国内，Drupal也会很快流行起来，成为主流网站设计和开发平台。\r\n10管理平台\r\n编辑\r\n\r\n一个开源的内容管理平台，它驱动着全球数以百万计的网站和应用。一个来世界各地不同地方的人们形成的多元并且活跃的社区创建和使用Drupal，并且提供支持。人们通常将Drupal拿来和另一个著名的开源CMS，即Joomla进行比较。一个广为人知的比喻是：好比在操作系统领域的竞争，Drupal的最新版本是7.26。由于“难度”更大而中文资源较Joomla更少，Drupal在中国的普及程度较Joomla更低。但情况可能正在好转，酒香不怕巷子深，越来越多的网站创建者开始认识到Drupal的强大和它的价值。随着部分中文Drupal技术社区陆续推出一些高品质的视频和文字教程，了解和深入学习Drupal的门槛在逐步降低，Drupal在中国也拥有了越来越多坚定的支持者，未来颇可期待。\r\n但是就像上文提到的2次开发的难度Joomla不比Drupal简单多少，但因为Joomla有着长期活跃的社区，因此数以万计的模块，插件，组件可以作为2次开发最好的\"参考资料\"，Drupal的成功之处在于灵活的CCK,但这几年Joomla涌现出很多优秀的CCK组件，如入门级的K2,Zoo,Sobi,以及最强大的Cobalt 8,驾驭Cobalt 8的难度一点也不比Drupal简单，多fields,以及multi-vendor(类似于淘宝的C2C功能),小卖家可以完全定制自己的主页，有自己的产品列表页，item页等等，并且支持关注，发表评论等等，最重要的是他与社区jomsocial无缝整合，这些都是Drupal做不到的，Joomla总的来说照顾了高，中，低端所有用户，并且高度的整合了国外其它的某些CMS,或platform,如open cart,wordpress，不了解joomla的人总会说Joomla简单，因为只是缺乏了解。\r\n', '0', '2017-09-08 09:48:25', null, 'Drupal是使用PHP语言编写的开源内容管理框架（CMF），它由内容管理系统（CMS）.txt');
INSERT INTO `txt` VALUES ('288', '2', 'DTcms V1.0 在线帮助中心.txt', '\r\n模板标签说明\r\n目录导航\r\n\r\n    一、网站配置标签\r\n    二、全局通用标签(方法)\r\n    三、类别调用标签(方法)\r\n    四、资讯模块调用标签(方法)\r\n    五、图文模块调用标签(方法)\r\n    六、下载模块调用标签(方法)\r\n    七、内容模块调用标签(方法)\r\n    八、评论信息调用标签(方法)\r\n\r\n一、网站配置标签\r\n\r\n网站配置信息标签，实际上就是DTcms.Model/sys_model.cs类里面的属性，里面都有备注说明的，当然你也可以直接打开此文件对照着制作模板。如果你对sys_model.cs里的属性进行添加，直接使用{config.新增的属性}这样调用即可！\r\n标签 	备注说明\r\n{config.webname} 	站点名称\r\n{config.webcompany} 	公司名称\r\n{config.weburl} 	网站域名\r\n{config.webtel} 	联系电话\r\n{config.webfax} 	传真号码\r\n{config.webmail} 	管理员邮箱\r\n{config.webcrod} 	网站备案号\r\n{config.webtitle} 	网站首页标题\r\n{config.webkeyword} 	页面关健词\r\n{config.webdescription} 	页面描述\r\n{config.webcopyright} 	网站版权信息\r\n{config.webpath} 	网站安装目录\r\n{config.webmanagepath} 	网站管理后台目录\r\n{config.webstatus} 	是否关闭网站，是1，否0\r\n{config.webclosereason} 	关闭原因描述\r\n{config.webcountcode} 	网站统计代码\r\n{config.staticstatus} 	是否开启静态，关闭0，开启1\r\n{config.staticextension} 	静态URL后缀(未启用)\r\n{config.memberstatus} 	开启会员功能，关闭0，开启1\r\n{config.commentstatus} 	开启评论审核，关闭0，开启1\r\n{config.logstatus} 	后台管理日志，关闭0，开启1\r\n{config.emailstmp} 	STMP服务器\r\n{config.emailport} 	SMTP端口\r\n{config.emailfrom} 	发件人地址\r\n{config.emailusername} 	邮箱账号\r\n{config.emailpassword} 	邮箱密码(密文)\r\n{config.emailnickname} 	发件人昵称\r\n{config.attachpath} 	附件上传目录\r\n{config.attachextension} 	附件上传类型\r\n{config.attachsave} 	附件保存方式\r\n{config.attachfilesize} 	文件上传大小\r\n{config.attachimgsize} 	图片上传大小\r\n{config.attachimgmaxheight} 	图片最大高度(像素)，不限制0，超出自动裁剪\r\n{config.attachimgmaxwidth} 	图片最大宽度(像素)，不限制0，超出自动裁剪\r\n{config.thumbnailheight} 	生成缩略图高度(像素)\r\n{config.thumbnailwidth} 	生成缩略图宽度(像素)\r\n{config.watermarktype} 	图片水印类型，不启用0，文字1，图片2\r\n{config.watermarkposition} 	图片水印位置，取值范围1-9\r\n{config.watermarkimgquality} 	图片生成质量，取值范围0-100\r\n{config.watermarkpic} 	图片水印文件\r\n{config.watermarktransparency} 	水印透明度，取值范围1-10，10不透明\r\n{config.watermarktext} 	水印文字\r\n{config.watermarkfont} 	文字字体\r\n{config.watermarkfontsize} 	文字大小(像素)\r\n{config.templateskin} 	当前模板路径，输出：安装目录/templates/模板名称，结束不包含“/”\r\n返回目录二、全局通用标签(方法)\r\n\r\n全局通用的标签，其实就是位于DTcms.Web.UI/BasePage.cs类里面的方法，当然啦，如果你是一个程序员，随时可以根据自己的要求添加你自己的方法。\r\n1、统一链接标签：<%linkurl(key,params)%>\r\n说明：主要对DTcms.Web\\xmlconfig\\urls.config文件里的节点读取和匹配。\r\n\r\nkey：URL配置列表里面rewrite节点name属性\r\n\r\nparams：Object参数列表\r\n\r\n调用条件：直接调用即可\r\n\r\n使用示例：给关于我们做一个链接<a href=\"<%linkurl(\"content\",\"about\")%>\"></a>\r\n2、分页页码标签：<%get_page_link(pagesize, pageindex, totalcount, key, params)%>\r\n\r\npagesize：每页显示条数\r\n\r\npageindex：当前页码(从1开始)\r\n\r\ntotalcount：总记录数量(必须是已经声明的out int变量)\r\n\r\nkey：URL配置列表里面rewrite节点name属性\r\n\r\nparams：Object参数列表，其中“__id__”为页码数字，系统自动替换\r\n\r\n调用条件：必须先声明一个字符串变量接受其返回值，再显示声明的变量\r\n\r\n频道列表模板页调用示例：\r\n\r\n<%set (string){pagelist}=get_page_link(10, page, totalcount, \"article_list1\", category_id, \"__id__\")%><!--取得分页页码列表-->\r\n<div class=\"flickr\">{pagelist}</div> <!--放置页码列表，class=\"flickr\"里面的样式可以修改成不同的23种风格-->\r\n\r\n3、分页页码(重载)标签：<%get_page_link(pagesize, pageindex, totalcount, linkurl)%>\r\n\r\npagesize：每页显示条数\r\n\r\npageindex：当前页码(从1开始)\r\n\r\ntotalcount：总记录数量(必须是已经声明的out int变量)\r\n\r\nlinkurl：链接的目标页名称，其中“__id__”为页码数字，系统自动替换\r\n\r\n调用条件：必须先声明一个字符串变量接受其返回值，再显示声明的变量\r\n\r\n频道列表模板页调用示例：\r\n\r\n<%set (string){pagelist}=get_page_link(15, page, totalcount, \"search.aspx?keyword=\"+Server.UrlEncode(keyword)+\"&page=__id__\")%><!--取得分页页码列表-->\r\n<div class=\"flickr\">{pagelist}</div> <!--放置页码列表，class=\"flickr\"里面的样式可以修改成不同的23种风格-->\r\n\r\n4、插件方法调用标签：get_plugin_method(assemblyName, className, methodName, params)\r\n说明：主要用于主模板面调用插件方法，为了防止插件卸载后页面出错，建立使用该方法调用\r\n\r\nassemblyName：命名空间全局限定名\r\n\r\nclassName：类名\r\n\r\nmethodName：方法名称\r\n\r\nparams：Object参数\r\n\r\n调用条件：必须先声明一个DataTable类型变量接受其反回值，再循环显示数据\r\n\r\n使用示例：\r\n\r\n<%set (DataTable){feedback_list}=get_plugin_method(\"DTcms.Web.Plugin.Feedback\", \"feedback\", \"get_feedback_list\", 4, \"is_lock=0\")%>\r\n<%loop fdr feedback_list%>\r\n……\r\n<%/loop%>\r\n\r\n返回目录三、类别调用标签(方法)\r\n\r\n位于DTcms.Web.UI/BasePage.cs类里面的方法，当然啦，如果你是一个程序员，随时可以根据自己的要求添加你自己的方法。\r\n1、当前类别标题标签：get_category_title(category_id, default_value)\r\n说明：该方法反回的是一个String字符串\r\n\r\ncategory_id：当前类别的ID\r\n\r\ndefault_value：默认值，类别不存在时的返回值\r\n\r\n频道列表模板页调用示例：\r\n\r\n当前类别名称：<%write get_category_title(category_id, \"新闻资讯\")%>\r\n\r\n2、类别面包屑导航标签：get_category_menu(urlKey, category_id)\r\n说明：该方法反回的是一个String字符串，样式如：> 父类 > 子类 > 小类\r\n\r\nurlKey：点击导航的目标链接，URL配置列表里面rewrite节点name属性\r\n\r\ncategory_id：当前类别的ID\r\n\r\n频道列表模板页调用示例：\r\n\r\n您当前的位置：首页 > <%write get_category_menu(\"new_list1\", category_id)%>\r\n\r\n3、类别列表标签：get_category_list(channel_id, parent_id)\r\n说明：该方法返回一个DataTable，必须先声明一个DataTable类型变量接受其反回值，再循环显示数据\r\n\r\nchannel_id：该频道ID\r\n\r\nparent_id：父类别的ID，0为显示该频道下的所有类别\r\n\r\n频道列表模板页调用示例：\r\n\r\n<%set (DataTable){category_list}=get_category_list(channel_id, 0)%> <!--取得一个DataTable--> <ul>\r\n	<%loop cdr category_list%>\r\n		<li>\r\n			<%csharp%>\r\n			int layer_width=(Convert.ToInt32(cdr[\"class_layer\"])-1)*18;\r\n			<%/csharp%>\r\n			<span style=\"width:{layer_width}px;text-align:right;display:inline-block;\"></span>\r\n			<a href=\"<%linkurl(\"down_list1\",{cdr[id]},1)%>\">{cdr[title]}</a></li>\r\n		</li>\r\n	<%/loop%>\r\n</ul>\r\n\r\n返回目录四、资讯模块调用标签(方法)\r\n\r\n位于DTcms.Web.UI/BasePage.cs类里面的方法，当然啦，如果你是一个程序员，随时可以根据自己的要求添加你自己的方法。\r\n注意事项说明：\r\n\r\n1、频道页已声明的变量：①channel_id(该频道的ID)，可直接使用该变量\r\n2、频道列表页已声明的普量：①page(当前页码)、②category_id(当前类别ID)、③totalcount(数据的总记录)，可直接使用这些变量\r\n3、频道详细页已导明的变量：①id(该条信息的ID)、②model(该页面对应的Model)，可直接使用这些变量\r\n\r\n1、前几条资讯调用标签：get_article_list(channel_id, category_id, top, strwhere)\r\n\r\nchannel_id：频道ID\r\n\r\ncategory_id：类别ID\r\n\r\ntop：显示的条数\r\n\r\nstrwhere：SQL查询条件语句\r\n\r\n频道列表模板页调用示例：\r\n\r\n<%set (DataTable){article_list}=get_article_list(channel_id,category_id,5,\"is_lock=0\")%> <!--取得一个DataTable--> <ul>\r\n	<%loop dr article_list%>\r\n		<li><span><%datetostr({dr[add_time]},yyyy年MM月dd日)%></span>{dr[title]}</li>\r\n	<%/loop%>\r\n</ul>\r\n\r\n2、前几条资讯调用(重载)标签：get_article_list(channel_id, category_id, top, strwhere, orderby)\r\n\r\nchannel_id：频道ID\r\n\r\ncategory_id：类别ID\r\n\r\ntop：显示的条数\r\n\r\nstrwhere：SQL查询条件语句\r\n\r\norderby：SQL排序语句\r\n\r\n频道列表模板页调用示例：\r\n\r\n<%set (DataTable){article_list}=get_article_list(channel_id,category_id,5,\"is_lock=0\",\"add_time desc,id desc\")%> <!--取得一个DataTable--> <ul>\r\n	<%loop dr article_list%>\r\n		<li><span><%datetostr({dr[add_time]},yyyy年MM月dd日)%></span>{dr[title]}</li>\r\n	<%/loop%>\r\n</ul>\r\n\r\n3、资讯分页列表调用标签：get_article_list(channel_id, category_id, page_size, page_index, strwhere, out totalcount)\r\n\r\nchannel_id：频道ID\r\n\r\ncategory_id：类别ID\r\n\r\npage_size：每页显示大小\r\n\r\npage_index：当前页码\r\n\r\nstrwhere：SQL查询条件语句\r\n\r\ntotalcount：查询的总记录数\r\n\r\n频道列表模板页调用示例：\r\n\r\n<%set (DataTable){news_list}=get_article_list(channel_id, category_id, 10, page, \"is_lock=0\", out totalcount)%> <!--取得一个DataTable--> <%set (string){pagelist}=get_page_link(10, page, totalcount, \"article_list1\", category_id, \"__id__\")%> <!--取得分页页码列表--> <div class=\"flickr\">{pagelist}<div> <!--放置页码列表，class=\"flickr\"里面的样式可以修改成不同的23种风格--> <ul>\r\n<%loop dr news_list%>\r\n	<li><span><%datetostr({dr[add_time]},yyyy年MM月dd日)%></span>{dr[title]}</li>\r\n<%/loop%>\r\n</ul>\r\n<div class=\"flickr\">{pagelist}<div> <!--放置页码列表，class=\"flickr\"里面的样式可以修改成不同的23种风格-->\r\n\r\n返回目录五、图文模块调用标签(方法)\r\n\r\n位于DTcms.Web.UI/BasePage.cs类里面的方法，当然啦，如果你是一个程序员，随时可以根据自己的要求添加你自己的方法。\r\n注意事项说明：\r\n\r\n1、频道页已声明的变量：①channel_id(该频道的ID)，可直接使用该变量\r\n2、频道列表页已声明的普量：①page(当前页码)、②category_id(当前类别ID)、③totalcount(数据的总记录)，可直接使用这些变量\r\n3、频道详细页已导明的变量：①id(该条信息的ID)、②model(该页面对应的Model)，可直接使用这些变量\r\n\r\n1、前几条图文调用标签：get_photo_list(channel_id, category_id, top, strwhere)\r\n\r\nchannel_id：频道ID\r\n\r\ncategory_id：类别ID\r\n\r\ntop：显示的条数\r\n\r\nstrwhere：SQL查询条件语句\r\n\r\n频道列表模板页调用示例：\r\n\r\n<%set (DataTable){photo_list}=get_photo_list(channel_id,category_id,5,\"is_lock=0\")%> <!--取得一个DataTable--> <ul>\r\n	<%loop dr photo_list%>\r\n		<li><span><%datetostr({dr[add_time]},yyyy年MM月dd日)%></span>{dr[title]}</li>\r\n	<%/loop%>\r\n</ul>\r\n\r\n2、前几条图文调用(重载)标签：get_photo_list(channel_id, category_id, top, strwhere, orderby)\r\n\r\nchannel_id：频道ID\r\n\r\ncategory_id：类别ID\r\n\r\ntop：显示的条数\r\n\r\nstrwhere：SQL查询条件语句\r\n\r\norderby：SQL排序语句\r\n\r\n频道列表模板页调用示例：\r\n\r\n<%set (DataTable){photo_list}=get_photo_list(channel_id,category_id,5,\"is_lock=0\",\"add_time desc,id desc\")%> <!--取得一个DataTable--> <ul>\r\n	<%loop dr photo_list%>\r\n		<li><span><%datetostr({dr[add_time]},yyyy年MM月dd日)%></span>{dr[title]}</li>\r\n	<%/loop%>\r\n</ul>\r\n\r\n3、图文分页列表调用标签：get_photo_list(channel_id, category_id, page_size, page_index, strwhere, out totalcount)\r\n\r\nchannel_id：频道ID\r\n\r\ncategory_id：类别ID\r\n\r\npage_size：每页显示大小\r\n\r\npage_index：当前页码\r\n\r\nstrwhere：SQL查询条件语句\r\n\r\ntotalcount：查询的总记录数\r\n\r\n频道列表模板页调用示例：\r\n\r\n<%set (DataTable){photo_list}=get_photo_list(channel_id, category_id, 10, page, \"is_lock=0\", out totalcount)%> <!--取得一个DataTable--> <%set (string){pagelist}=get_page_link(10, page, totalcount, \"photo_list1\", category_id, \"__id__\")%> <!--取得分页页码列表--> <div class=\"flickr\">{pagelist}<div> <!--放置页码列表，class=\"flickr\"里面的样式可以修改成不同的23种风格--> <ul>\r\n<%loop dr photo_list%>\r\n	<li><span><%datetostr({dr[add_time]},yyyy年MM月dd日)%></span>{dr[title]}</li>\r\n<%/loop%>\r\n</ul>\r\n<div class=\"flickr\">{pagelist}<div> <!--放置页码列表，class=\"flickr\"里面的样式可以修改成不同的23种风格-->\r\n\r\n返回目录六、下载模块调用标签(方法)\r\n\r\n位于DTcms.Web.UI/BasePage.cs类里面的方法，当然啦，如果你是一个程序员，随时可以根据自己的要求添加你自己的方法。\r\n注意事项说明：\r\n\r\n1、频道页已声明的变量：①channel_id(该频道的ID)，可直接使用该变量\r\n2、频道列表页已声明的普量：①page(当前页码)、②category_id(当前类别ID)、③totalcount(数据的总记录)，可直接使用这些变量\r\n3、频道详细页已导明的变量：①id(该条信息的ID)、②model(该页面对应的Model)，可直接使用这些变量\r\n\r\n1、前几条下载调用标签：get_download_list(channel_id, category_id, top, strwhere)\r\n\r\nchannel_id：频道ID\r\n\r\ncategory_id：类别ID\r\n\r\ntop：显示的条数\r\n\r\nstrwhere：SQL查询条件语句\r\n\r\n频道列表模板页调用示例：\r\n\r\n<%set (DataTable){down_list}=get_download_list(channel_id,category_id,5,\"is_lock=0\")%> <!--取得一个DataTable--> <ul>\r\n	<%loop dr down_list%>\r\n		<li><span><%datetostr({dr[add_time]},yyyy年MM月dd日)%></span>{dr[title]}</li>\r\n	<%/loop%>\r\n</ul>\r\n\r\n2、前几条下载调用(重载)标签：get_download_list(channel_id, category_id, top, strwhere, orderby)\r\n\r\nchannel_id：频道ID\r\n\r\ncategory_id：类别ID\r\n\r\ntop：显示的条数\r\n\r\nstrwhere：SQL查询条件语句\r\n\r\norderby：SQL排序语句\r\n\r\n频道列表模板页调用示例：\r\n\r\n<%set (DataTable){down_list}=get_download_list(channel_id,category_id,5,\"is_lock=0\",\"add_time desc,id desc\")%> <!--取得一个DataTable--> <ul>\r\n	<%loop dr down_list%>\r\n		<li><span><%datetostr({dr[add_time]},yyyy年MM月dd日)%></span>{dr[title]}</li>\r\n	<%/loop%>\r\n</ul>\r\n\r\n3、下载分页列表调用标签：get_download_list(channel_id, category_id, page_size, page_index, strwhere, out totalcount)\r\n\r\nchannel_id：频道ID\r\n\r\ncategory_id：类别ID\r\n\r\npage_size：每页显示大小\r\n\r\npage_index：当前页码\r\n\r\nstrwhere：SQL查询条件语句\r\n\r\ntotalcount：查询的总记录数\r\n\r\n频道列表模板页调用示例：\r\n\r\n<%set (DataTable){down_list}=get_download_list(channel_id, category_id, 10, page, \"is_lock=0\", out totalcount)%> <!--取得一个DataTable--> <%set (string){pagelist}=get_page_link(10, page, totalcount, \"down_list1\", category_id, \"__id__\")%> <!--取得分页页码列表--> <div class=\"flickr\">{pagelist}<div> <!--放置页码列表，class=\"flickr\"里面的样式可以修改成不同的23种风格--> <ul>\r\n<%loop dr photo_list%>\r\n	<li><span><%datetostr({dr[add_time]},yyyy年MM月dd日)%></span>{dr[title]}</li>\r\n<%/loop%>\r\n</ul>\r\n<div class=\"flickr\">{pagelist}<div> <!--放置页码列表，class=\"flickr\"里面的样式可以修改成不同的23种风格-->\r\n\r\n返回目录七、内容模块调用标签(方法)\r\n\r\n位于DTcms.Web.UI/BasePage.cs类里面的方法，当然啦，如果你是一个程序员，随时可以根据自己的要求添加你自己的方法。\r\n注意事项说明：\r\n\r\n1、频道页已声明的变量：①channel_id(该频道的ID)，可直接使用该变量\r\n2、频道列表页已声明的普量：①page(当前页码)、②category_id(当前类别ID)、③totalcount(数据的总记录)，可直接使用这些变量\r\n3、频道详细页已导明的变量：①id(该条信息的ID)、②model(该页面对应的Model)，可直接使用这些变量\r\n\r\n1、单条内容调用标签：get_content(call_index)\r\n\r\ncall_index：调用标识(别名)\r\n\r\n调用示例：\r\n\r\n<%write get_content(\"about\")%><!--关于我们-->\r\n\r\n2、前几条内容调用标签：get_content_list(channel_id, category_id, top, strwhere)\r\n\r\nchannel_id：频道ID\r\n\r\ncategory_id：类别ID\r\n\r\ntop：显示的条数\r\n\r\nstrwhere：SQL查询条件语句\r\n\r\n频道列表模板页调用示例：\r\n\r\n<%set (DataTable){help_list}=get_content_list(channel_id,category_id,5,\"is_lock=0\")%> <!--取得一个DataTable--> <ul>\r\n	<%loop dr help_list%>\r\n		<li><span><%datetostr({dr[add_time]},yyyy年MM月dd日)%></span>{dr[title]}</li>\r\n	<%/loop%>\r\n</ul>\r\n\r\n3、前几条内容调用(重载)标签：get_content_list(channel_id, category_id, top, strwhere, orderby)\r\n\r\nchannel_id：频道ID\r\n\r\ncategory_id：类别ID\r\n\r\ntop：显示的条数\r\n\r\nstrwhere：SQL查询条件语句\r\n\r\norderby：SQL排序语句\r\n\r\n频道列表模板页调用示例：\r\n\r\n<%set (DataTable){help_list}=get_content_list(channel_id,category_id,5,\"is_lock=0\",\"add_time desc,id desc\")%> <!--取得一个DataTable--> <ul>\r\n	<%loop dr help_list%>\r\n		<li><span><%datetostr({dr[add_time]},yyyy年MM月dd日)%></span>{dr[title]}</li>\r\n	<%/loop%>\r\n</ul>\r\n\r\n4、内容分页列表调用标签：get_content_list(channel_id, category_id, page_size, page_index, strwhere, out totalcount)\r\n\r\nchannel_id：频道ID\r\n\r\ncategory_id：类别ID\r\n\r\npage_size：每页显示大小\r\n\r\npage_index：当前页码\r\n\r\nstrwhere：SQL查询条件语句\r\n\r\ntotalcount：查询的总记录数\r\n\r\n频道列表模板页调用示例：\r\n\r\n<%set (DataTable){help_list}=get_content_list(channel_id, category_id, 10, page, \"is_lock=0\", out totalcount)%> <!--取得一个DataTable--> <%set (string){pagelist}=get_page_link(10, page, totalcount, \"help_list1\", category_id, \"__id__\")%> <!--取得分页页码列表--> <div class=\"flickr\">{pagelist}<div> <!--放置页码列表，class=\"flickr\"里面的样式可以修改成不同的23种风格--> <ul>\r\n<%loop dr help_list%>\r\n	<li><span><%datetostr({dr[add_time]},yyyy年MM月dd日)%></span>{dr[title]}</li>\r\n<%/loop%>\r\n</ul>\r\n<div class=\"flickr\">{pagelist}<div> <!--放置页码列表，class=\"flickr\"里面的样式可以修改成不同的23种风格-->\r\n\r\n返回目录八、评论信息调用标签(方法)\r\n\r\n位于DTcms.Web.UI/BasePage.cs类里面的方法，当然啦，如果你是一个程序员，随时可以根据自己的要求添加你自己的方法。\r\n1、返回评论记录总数标签：get_comment_count(channel_id, content_id, strwhere)\r\n\r\nchannel_id：频道ID\r\n\r\ncontent_id：对应的信息ID\r\n\r\nstrwhere：SQL查询条件语句\r\n\r\n频道详细模板页调用示例：\r\n\r\n<%set (int){commentCount}=get_comment_count(channel_id,model.id,\"is_lock=0\")%> 该信息评论总数：{commentCount}\r\n\r\n2、前几条评论调用(重载)标签：get_comment_list(channel_id, content_id, top, strwhere)\r\n\r\nchannel_id：频道ID\r\n\r\ncontent_id：对应的信息ID\r\n\r\ntop：显示的条数\r\n\r\nstrwhere：SQL查询条件语句\r\n\r\n频道详细模板页调用示例：\r\n\r\n<%set (DataTable){comment_list}=get_comment_list(channel_id,model.id,5,\"is_lock=0\")%> <!--取得一个DataTable--> <ul>\r\n	<%loop dr comment_list%>\r\n		<li><span><%datetostr({dr[add_time]},yyyy年MM月dd日)%></span>{dr[content]}</li>\r\n	<%/loop%>\r\n</ul>\r\n\r\n3、评论分页列表调用标签：get_comment_list(channel_id, content_id, page_size, page_index, strwhere, out totalcount)\r\n\r\nchannel_id：频道ID\r\n\r\ncontent_id：对应的信息ID\r\n\r\npage_size：每页显示大小\r\n\r\npage_index：当前页码\r\n\r\nstrwhere：SQL查询条件语句\r\n\r\ntotalcount：查询的总记录数\r\n\r\n调用示例：\r\n\r\n<%set (DataTable){comment_list}=get_comment_list(channel_id, model.id, 10, page, \"is_lock=0\", out totalcount)%> <!--取得一个DataTable--> <%set (string){pagelist}=get_page_link(10, page, totalcount, \"comment_list1\", model.id, \"__id__\")%> <!--取得分页页码列表--> <div class=\"flickr\">{pagelist}<div> <!--放置页码列表，class=\"flickr\"里面的样式可以修改成不同的23种风格--> <ul>\r\n<%loop dr help_list%>\r\n	<li><span><%datetostr({dr[add_time]},yyyy年MM月dd日)%></span>{dr[content]}</li>\r\n<%/loop%>\r\n</ul>\r\n<div class=\"flickr\">{pagelist}<div> <!--放置页码列表，class=\"flickr\"里面的样式可以修改成不同的23种风格-->\r\n\r\ntop版权所有 动力启航 © Copyright 2009 - 2012. dtcms.net. All Rights Reserved. 粤ICP备11064298号\r\n', '0', '2017-09-08 09:48:25', null, 'DTcms V1.0 在线帮助中心.txt');
INSERT INTO `txt` VALUES ('289', '2', 'Easyui 1.2.4+jQuery1.6.4 Dialog控件cache属性失效解决方案.测试过可用.jun.txt', 'Easyui 1.2.4+jQuery1.6.4 Dialog控件cache属性失效解决方案\r\n\r\n博客分类： easyuiJquery\r\n环境：\r\n \r\neasyui 1.2.4\r\njQuery 1.6.4\r\nchrome\r\n \r\n问题：\r\n \r\n做项目中使用到easyui中的dialog，发现他的cache属性即使设置为false仍然还是不会发起服务端请求。\r\n \r\n排查原因：\r\n \r\n网上搜了下，无果，但搜到一个帖子\r\n \r\n \r\nMark ,easyUi缓存问题是执行顺序，(\'#dialog\').html(data)先，再实例化(\'#dialog\').dialog,而juqery dialog不会破坏#dialog\'标签，easyUI会破坏标签，所以存在缓存问题\r\n但是实在看不懂如何解决.\r\n \r\n在调试中发现,使用easyui的dialog事件后你的dialog控件会“不见”，也就是下面代码中的#yourDialog整个DOM节点不见，easyui帮你自动生成一个。可能就是这里出问题。\r\n \r\n \r\nHtml代码  收藏代码\r\n<div style=\"display:none\">  \r\n    <!-- 你的dialog控件-->  \r\n    <div id=\"#yourDialog\" title=\"xxx\"></div>  \r\n</div>  \r\n \r\n解决方法：\r\n \r\n不想修改源码，想了想，可以通过以下方法解决：\r\n \r\n \r\nJs代码  收藏代码\r\n$(\'#yourDialog\').dialog({    \r\n        modal:true,  \r\n        iconCls:\"icon-add\",  \r\n        buttons:[{  \r\n            text:\'提交\',  \r\n            iconCls:\'icon-ok\',  \r\n            handler:function(){  \r\n                submitForm(appId, false);  \r\n            }  \r\n        },{  \r\n            text:\'关闭\',  \r\n            handler:function(){  \r\n                $(\'#yourDialog\').dialog(\'close\');  \r\n            }  \r\n        }],  \r\n        onOpen:function(){  \r\n            $(\'#createAppVersionDialog\').dialog(\'refresh\', \'yourUrl\');  \r\n        }  \r\n    });   \r\n初始不提供href参数，监听onOpen事件，使用refresh参数使dialog读取服务端数据(refresh是panel的method)\r\n', '0', '2017-09-08 09:48:25', null, 'Easyui 1.2.4+jQuery1.6.4 Dialog控件cache属性失效解决方案.测试过可用.jun.txt');
INSERT INTO `txt` VALUES ('290', '2', 'EF4.0和EF5.0增删改查的写法区别及执行Sql的方法.txt', 'EF4.0��EF5.0��ɾ�Ĳ��д������\r\n(from:http://blog.csdn.net/xjn030594/article/details/8946996)\r\npublic T AddEntity(T entity) \r\n{\r\n    //EF4.0��д��  \r\n    ���ʵ��\r\n    //db.CreateObjectSet<T>().AddObject(entity);\r\n    //EF5.0��д��\r\n    db.Entry<T>(entity).State = EntityState.Added;\r\n    //�����д��ͳһ\r\n    db.SaveChanges();\r\n    return entity;\r\n}\r\npublic bool UpdateEntity(T entity)\r\n{\r\n    //EF4.0��д��\r\n    //db.CreateObjectSet<T>().Addach(entity);\r\n    //db.ObjectStateManager.ChangeObjectState(entity, EntityState.Modified);\r\n    //EF5.0��д�� \r\n   db.Set<T>().Attach(entity); \r\n   db.Entry<T>(entity).State = EntityState.Modified; \r\n   return db.SaveChanges() > 0; \r\n}\r\npublic bool DeleteEntity(T entity)\r\n{\r\n    //EF4.0��д�� \r\n   //db.CreateObjectSet<T>().Addach(entity);\r\n   //db.ObjectStateManager.ChangeObjectState(entity, EntityState.Deleted);\r\n   //EF5.0��д��\r\n   db.Set<T>().Attach(entity);\r\n   db.Entry<T>(entity).State = EntityState.Deleted;\r\n   return db.SaveChanges() > 0;\r\n}\r\n\r\npublic IQueryable<T> LoadEntities(Func<T, bool> whereLambda)\r\n{\r\n    //EF4.0��д��\r\n    //return db.CreateObjectSet<T>().Where<T>(whereLambda).AsQueryable();\r\n    //EF5.0��д��\r\n    return db.Set<T>().Where<T>(whereLambda).AsQueryable();\r\n}\r\nִ��SQL���\r\n//EF4.0��д��\r\n//int ExcuteSql(string strSql, ObjectParameter[] parameters);\r\nreturn EFContextFactory.GetCurrentDbContext().ExecuteFunction(strSql, parameters);\r\n//EF5.0��д��\r\n int ExcuteSql(string strSql, DbParameter[] parameters);\r\nreturn DEFContextFactory.GetCurrentDbContext().ExecuteSqlCommand(strSql, parameters);\r\n', '0', '2017-09-08 09:48:25', null, 'EF4.0和EF5.0增删改查的写法区别及执行Sql的方法.txt');
INSERT INTO `txt` VALUES ('291', '2', 'EF5(entity.framework)helper.实体框架.帮助类.txt', 'EF 5.0 帮助类\r\n（from:http://www.cnblogs.com/luomingui/p/3362813.html）\r\n加入命名空间：\r\n\r\nusing System;\r\nusing System.Data;\r\nusing System.Data.Entity;\r\nusing System.Data.Entity.Infrastructure;\r\nusing System.Linq;\r\n接口：\r\n\r\n复制代码\r\npublic interface IEFRepository<TEntity> where TEntity : class\r\n    {\r\n        bool AddEntity(TEntity entity);\r\n        bool UpdateEntity(TEntity entity);\r\n        bool UpdateEntity(IEnumerable<TEntity> entities);\r\n        bool DeleteEntity(int ID);\r\n        bool DeleteEntity(TEntity entity);\r\n        bool DeleteEntity(Expression<Func<TEntity, bool>> predicate);\r\n        bool DeleteEntity(IEnumerable<TEntity> entities);\r\n        IList<TEntity> LoadEntities(Func<TEntity, bool> whereLambda);\r\n        IList<TEntity> LoadEntities(int pageIndex = 1, int pageSize = 30, Func<TEntity, bool> whereLambda = null);\r\n        TEntity FindByID(int ID);\r\n    }\r\n复制代码\r\n具体类：\r\n\r\n复制代码\r\n//EF5.0的写法 \r\n    public class EFRepository<TEntity> : IEFRepository<TEntity> where TEntity : class\r\n    {\r\n        #region 单利模式\r\n        public static EFRepository<TEntity> Instance = new EFRepository<TEntity>();\r\n        public EFRepository( )\r\n        {\r\n            Create();\r\n        }\r\n        #endregion\r\n\r\n        /// <summary>\r\n        /// 获取 当前使用的数据访问上下文对象\r\n        /// </summary>\r\n        public DbContext Context\r\n        {\r\n            get\r\n            {\r\n                return EFDbContextHelper.Context;\r\n            }\r\n        }\r\n        public bool AddEntity(TEntity entity)\r\n        {\r\n            EntityState state = Context.Entry(entity).State;\r\n            if (state == EntityState.Detached)\r\n            {\r\n                Context.Entry(entity).State = EntityState.Added;\r\n            }\r\n            Context.SaveChanges();\r\n            return true;\r\n        }\r\n        public bool UpdateEntity(TEntity entity)\r\n        {\r\n            Context.Set<TEntity>().Attach(entity);\r\n            Context.Entry<TEntity>(entity).State = EntityState.Modified;\r\n            return Context.SaveChanges() > 0;\r\n        }\r\n        public bool UpdateEntity(IEnumerable<TEntity> entities)\r\n        {\r\n            try\r\n            {\r\n                Context.Configuration.AutoDetectChangesEnabled = false;\r\n                foreach (TEntity entity in entities)\r\n                {\r\n                    UpdateEntity(entity);\r\n                }\r\n                return true;\r\n            }\r\n            finally\r\n            {\r\n                Context.Configuration.AutoDetectChangesEnabled = true;\r\n            }\r\n        }\r\n        public bool DeleteEntity(int ID)\r\n        {\r\n            TEntity entity = FindByID(ID);\r\n            return DeleteEntity(entity);\r\n        }\r\n        public bool DeleteEntity(TEntity entity)\r\n        {\r\n            Context.Set<TEntity>().Attach(entity);\r\n            Context.Entry<TEntity>(entity).State = EntityState.Deleted;\r\n            return Context.SaveChanges() > 0;\r\n        }\r\n        public bool DeleteEntity(System.Linq.Expressions.Expression<Func<TEntity, bool>> predicate)\r\n        {\r\n            List<TEntity> entities = Set().Where(predicate).ToList();\r\n            return Context.SaveChanges() > 0;\r\n        }\r\n        public bool DeleteEntity(IEnumerable<TEntity> entities)\r\n        {\r\n            try\r\n            {\r\n                Context.Configuration.AutoDetectChangesEnabled = false;\r\n                foreach (TEntity entity in entities)\r\n                {\r\n                    DeleteEntity(entity);\r\n                }\r\n                return true;\r\n            }\r\n            finally\r\n            {\r\n                Context.Configuration.AutoDetectChangesEnabled = true;\r\n            }\r\n        }\r\n        public IList<TEntity> LoadEntities(Func<TEntity, bool> whereLambda)\r\n        {\r\n            if (whereLambda != null)\r\n                return Context.Set<TEntity>().Where<TEntity>(whereLambda).AsQueryable().ToList();\r\n            else\r\n                return Context.Set<TEntity>().AsQueryable().ToList();\r\n        }\r\n        public IList<TEntity> LoadEntities(int pageIndex = 1, int pageSize = 30, Func<TEntity, bool> whereLambda = null)\r\n        {\r\n            int skinCount = (pageIndex - 1) * pageSize;\r\n            if (whereLambda != null)\r\n                return Set()\r\n                    .Where<TEntity>(whereLambda)\r\n                    .Skip(skinCount)\r\n                    .Take(pageSize)\r\n                    .ToList();\r\n            else\r\n                return Set()\r\n                .Skip(skinCount)\r\n                .Take(pageSize)\r\n                .ToList();\r\n        }\r\n        public DbSet<TEntity> Set( )\r\n        {\r\n            return Context.Set<TEntity>();\r\n        }\r\n        public TEntity FindByID(int ID)\r\n        {\r\n            return Set().Find(ID);\r\n        }\r\n        private TEntity Create( )\r\n        {\r\n            return Context.Set<TEntity>().Create();\r\n        }\r\n    }\r\n复制代码\r\n使用：\r\n\r\n准备实体类\r\n\r\n复制代码\r\n/// <summary> \r\n    /// 实体类楼层管理 。(属性说明自动提取数据库字段的描述信息) \r\n    /// </summary> \r\n    [Serializable]\r\n    public class Floor \r\n    { \r\n        public int ID { get; set; } \r\n        [Category(\"楼层名称\")]\r\n        public string f_Name { get; set; } \r\n        [Category(\"备注\")]\r\n        public string f_Remark { get; set; } \r\n    }\r\n复制代码\r\n使用EF帮助类调用\r\n\r\n复制代码\r\n/// <summary> \r\n    /// 数据上下文 Db3983Context \r\n    /// </summary> \r\n    public class Db3983Context : EFDbContext \r\n    { \r\n        /// <summary> \r\n        /// 构造函数 \r\n        /// </summary> \r\n        public Db3983Context() \r\n            : base(\"3983\") \r\n        { \r\n        } \r\n        /// <summary> \r\n        /// 楼层管理 \r\n        /// </summary> \r\n        public DbSet<Floor> Floor { get; set; } \r\n}\r\n复制代码\r\n复制代码\r\n/// <summary>\r\n        /// 应用程序的主入口点。\r\n        /// </summary>\r\n        [STAThread]\r\n        static void Main( )\r\n        {\r\n            EFDbContextHelper.Context = new Db3983Context();\r\n\r\n            Floor floor = new Floor();\r\n            floor.f_Name = \"罗敏贵\";\r\n            floor.f_Remark = \"我这个人看上去很靠谱，长得也很高有一米八五，也很帅气，千万不要迷恋哥，哥只是传说。\";\r\n\r\n            EFRepository<Floor>.Instance.AddEntity(floor);\r\n        }\r\n复制代码\r\n', '0', '2017-09-08 09:48:25', null, 'EF5(entity.framework)helper.实体框架.帮助类.txt');
