INSERT INTO `txt` VALUES ('301', '2', 'ERP.查询所有顾问（包括在职和离职的）组织和详情.sql.txt', 'SELECT\r\n	d.empid,\r\n	d.empname,\r\n	e.orglv2,\r\n	e.orglv3,\r\n	e.orglv4,\r\n	e.orglv2name,\r\n	e.orglv3name,\r\n	e.orglv4name\r\n  ,d.description\r\nFROM\r\n	(\r\n		SELECT\r\n			empid,\r\n			empname,\r\n			c.description,\r\n			c.organizationunitid\r\n		FROM\r\n			Employees a OUTER apply (\r\n				SELECT\r\n					TOP (1) description,\r\n					reference,\r\n					organizationunitid\r\n				FROM\r\n					HRPPARTYPOSITIONTABLERELAT2226 b\r\n				WHERE\r\n					a.empid = b.reference\r\n				ORDER BY\r\n					VALIDFROMDATETIME DESC,HRMEMPLOYMENTFACTOR desc \r\n			) c\r\n	) d\r\nLEFT JOIN HRMORGTREE e ON d.organizationunitid = e.ORGLV4\r\n', '0', '2017-09-08 09:48:42', null, 'ERP.查询所有顾问（包括在职和离职的）组织和详情.sql.txt');
INSERT INTO `txt` VALUES ('302', '2', 'Everything.文件搜索.search.技巧.支持.布尔表达式.正则表达式.txt', 'Everything��ǿ��֮����֧�ֲ������ʽ��������ʽ��֧��ָ��·���µ����������磺\r\n��ע���������������еġ��ո�������ʾӢ�İ�ǿո�\r\n��1�����Ҫ����������������abc��xyzͬʱ���ֵ��ļ���abc�ո�xyz\r\n\r\n��2�����Ҫ����.jpg��.bmp�ļ��� .jpg | .bmp��\r\n\r\n��3������abc���ǲ�����xyz��ABC�ո�!xyz\r\n\r\n��4�����Ҫ������e��ͷ������g��β���ļ����ļ��У� e*g\r\n\r\n��5�����Ҫ����������չ��Ϊ�����ַ����ļ��� *.??\r\n\r\n��6�����Ҫ����ĳ�������ĳ���ļ�������ļ���d:\\tools\\�ո�abc�ո�xyz\r\n��7��������ʽʹ�þ���1��gray|grey �ܹ�ƥ�� gray��grey��\r\n����  ������ʽʹ�þ���2��gr(a|e)��ʾ��gr��y֮����һ����ĸa��e��gr(a|e)y��ƥ��gray��grey����gray|grey������ͬ��\r\n', '0', '2017-09-08 09:48:42', null, 'Everything.文件搜索.search.技巧.支持.布尔表达式.正则表达式.txt');
INSERT INTO `txt` VALUES ('303', '2', 'example.js.javascript.jquery.文本框输入文字后提示语消失特效-懒人图库.htm.txt', '<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n    <meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\" />\r\n    <title>文本框输入文字后提示语消失特效-懒人图库</title>\r\n    <script src=\"js/jquery-1.3.2.js\" type=\"text/javascript\"></script>\r\n    <script type=\"text/javascript\">\r\n        $(document).ready(function () {\r\n\r\n            $(\"#focus .input_txt\").each(function () {\r\n                var thisVal = $(this).val();\r\n                //判断文本框的值是否为空，有值的情况就隐藏提示语，没有值就显示\r\n                if (thisVal != \"\") {\r\n                    $(this).siblings(\"span\").hide();\r\n                } else {\r\n                    $(this).siblings(\"span\").show();\r\n                }\r\n                //聚焦型输入框验证 \r\n                $(this).focus(function () {\r\n                    $(this).siblings(\"span\").hide();\r\n                }).blur(function () {\r\n                    var val = $(this).val();\r\n                    if (val != \"\") {\r\n                        $(this).siblings(\"span\").hide();\r\n                    } else {\r\n                        $(this).siblings(\"span\").show();\r\n                    }\r\n                });\r\n            })\r\n            $(\"#keydown .input_txt\").each(function () {\r\n                var thisVal = $(this).val();\r\n                //判断文本框的值是否为空，有值的情况就隐藏提示语，没有值就显示\r\n                if (thisVal != \"\") {\r\n                    $(this).siblings(\"span\").hide();\r\n                } else {\r\n                    $(this).siblings(\"span\").show();\r\n                }\r\n                $(this).keyup(function () {\r\n                    var val = $(this).val();\r\n                    $(this).siblings(\"span\").hide();\r\n                }).blur(function () {\r\n                    var val = $(this).val();\r\n                    if (val != \"\") {\r\n                        $(this).siblings(\"span\").hide();\r\n                    } else {\r\n                        $(this).siblings(\"span\").show();\r\n                    }\r\n                })\r\n            })\r\n        })\r\n    </script>\r\n    <style type=\"text/css\">\r\n        form { width: 400px; margin: 10px auto; border: solid 1px #E0DEDE; background: #FCF9EF; padding: 30px; box-shadow: 0 1px 10px rgba(0,0,0,0.1) inset; }\r\n        label { display: block; height: 40px; position: relative; margin: 20px 0; }\r\n        span { position: absolute; float: left; line-height: 40px; left: 10px; color: #BCBCBC; cursor: text; }\r\n        .input_txt { width: 398px; border: solid 1px #ccc; box-shadow: 0 1px 10px rgba(0,0,0,0.1) inset; height: 38px; text-indent: 10px; }\r\n        .input_txt:focus { box-shadow: 0 0 4px rgba(255,153,164,0.8); border: solid 1px #B00000; }\r\n        .border_radius { border-radius: 5px; color: #B00000; }\r\n        h2 { font-family: \"微软雅黑\"; text-shadow: 1px 1px 3px #fff; }\r\n    </style>\r\n</head>\r\n<body>\r\n    <form class=\"border_radius\" id=\"focus\">\r\n    <h2>\r\n        聚焦型提示语消失</h2>\r\n    <label>\r\n        <span>花瓣注册邮箱</span><input type=\"text\" class=\"input_txt border_radius\" /></label>\r\n    <label>\r\n        <span>密码</span><input type=\"text\" class=\"input_txt border_radius\" /></label>\r\n    </form>\r\n    <form class=\"border_radius\" id=\"keydown\">\r\n    <h2>\r\n        输入型提示语消失</h2>\r\n    <label>\r\n        <span>花瓣注册邮箱</span><input type=\"text\" class=\"input_txt border_radius\" /></label>\r\n    <label>\r\n        <span>密码</span><input type=\"text\" class=\"input_txt border_radius\" /></label>\r\n    </form>\r\n    <p>\r\n        查找更多代码，请访问：<a href=\"http://www.lanrentuku.com/\" target=\"_blank\">懒人图库</a></p>\r\n    <p>\r\n        <script type=\"text/javascript\" src=\"http://d.lanrentuku.com/goto/js-preview-728x90.js\"></script>\r\n    </p>\r\n</body>\r\n</html>\r\n', '0', '2017-09-08 09:48:42', null, 'example.js.javascript.jquery.文本框输入文字后提示语消失特效-懒人图库.htm.txt');
INSERT INTO `txt` VALUES ('304', '2', 'example.js.javascript.用JS实现的radio图片选择按钮效果-懒人图库.htm.txt', '<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n    <meta http-equiv=\"Content-Type\" content=\"text/html; charset=gb2312\" />\r\n    <title>��JSʵ�ֵ�radioͼƬѡ��ťЧ��-����ͼ��</title>\r\n    <style type=\"text/css\">\r\n        .lanrentuku img { border: 1px solid #008800; }\r\n    </style>\r\n</head>\r\n<body>\r\n    <script type=\"text/javascript\">\r\n        function myFun(sId) {\r\n            var oImg = document.getElementsByTagName(\'img\');\r\n            for (var i = 0; i < oImg.length; i++) {\r\n                if (oImg[i].id == sId) {\r\n                    oImg[i].previousSibling.previousSibling.checked = true;\r\n                    oImg[i].style.border = \'2px solid #FF6600\';\r\n                } else {\r\n                    oImg[i].style.border = \'2px solid #008800\';\r\n                }\r\n            }\r\n        }\r\n    </script>\r\n    <div class=\"lanrentuku\">\r\n        <input type=\"radio\" value=\"lanrentuku\" id=\"111\" name=\"aaa\" style=\"display: none\" />\r\n        <img id=\"aa\" src=\"1.gif \" onclick=\"myFun(this.id)\" alt=\"ͼƬ1\" />\r\n        <input type=\"radio\" value=\"lanrentuku\" id=\"222\" name=\"aaa\" style=\"display: none\" />\r\n        <img id=\"bb\" src=\"2.gif\" onclick=\"myFun(this.id)\" alt=\"ͼƬ2\" />\r\n        <input type=\"radio\" value=\"lanrentuku\" id=\"333\" name=\"aaa\" style=\"display: none\" />\r\n        <img id=\"cc\" src=\"3.gif\" onclick=\"myFun(this.id)\" alt=\"ͼƬ3\" />\r\n    </div>\r\n</body>\r\n</html>\r\n', '0', '2017-09-08 09:48:42', null, 'example.js.javascript.用JS实现的radio图片选择按钮效果-懒人图库.htm.txt');
INSERT INTO `txt` VALUES ('305', '2', 'FileUpload.txt', '<script runat=\"server\">\r\n//����Click�¼�,ͨ������SaveFile����\r\n    protected void  UploadButton_Click(object sender, EventArgs e)\r\n    {\r\n        // Before attempting to save the file, verify\r\n        // that the FileUpload control contains a file.\r\n        if (FileUpload1.HasFile) \r\n          // Call a helper method routine to save the file.\r\n          SaveFile(FileUpload1.PostedFile);\r\n        else\r\n          // Notify the user that a file was not uploaded.\r\n          UploadStatusLabel.Text = \"You did not specify a file to upload.\";\r\n    }\r\n//����SaveFile����\r\n      void SaveFile(HttpPostedFile file)\r\n      {            \r\n        // Specify the path to save the uploaded file to.\r\n        string savePath = \"c:\\\\temp\\\\uploads\\\\\";\r\n\r\n        // Get the name of the file to upload.\r\n        string fileName = FileUpload1.FileName;\r\n\r\n        // Create the path and file name to check for duplicates.\r\n        string pathToCheck = savePath + fileName;\r\n\r\n        // Create a temporary file name to use for checking duplicates.\r\n        string tempfileName = \"\";\r\n\r\n        // Check to see if a file already exists with the\r\n        // same name as the file to upload.        \r\n        if (System.IO.File.Exists(pathToCheck)) \r\n        {\r\n          int counter = 2;\r\n          while (System.IO.File.Exists(pathToCheck))\r\n          {\r\n            // if a file with this name already exists,\r\n            // prefix the filename with a number.\r\n            tempfileName = counter.ToString() + fileName;\r\n            pathToCheck = savePath + tempfileName;\r\n            counter ++;\r\n          }\r\n\r\n          fileName = tempfileName;\r\n\r\n          // Notify the user that the file name was changed.\r\n          UploadStatusLabel.Text = \"A file with the same name already exists.\" + \r\n              \"<br />Your file was saved as \" + fileName;\r\n        }\r\n        else\r\n        {\r\n          // Notify the user that the file was saved successfully.\r\n          UploadStatusLabel.Text = \"Your file was uploaded successfully.\";\r\n        }\r\n\r\n        // Append the name of the file to upload to the path.\r\n        savePath += fileName;\r\n\r\n        // Call the SaveAs method to save the uploaded\r\n        // file to the specified directory.\r\n        FileUpload1.SaveAs(savePath);\r\n\r\n      }\r\n\r\n</script>\r\n\r\n</head>\r\n<body>\r\n\r\n    <h3>FileUpload.SaveAs Method Example</h3>\r\n\r\n    <form id=\"Form1\" runat=\"server\">\r\n\r\n        <h4>Select a file to upload:</h4>\r\n\r\n        <asp:FileUpload id=\"FileUpload1\"                 \r\n            runat=\"server\">\r\n        </asp:FileUpload>\r\n\r\n        <br /><br />\r\n\r\n        <asp:Button id=\"UploadButton\" \r\n            Text=\"Upload file\"\r\n            OnClick=\"UploadButton_Click\"\r\n            runat=\"server\">\r\n        </asp:Button>      \r\n\r\n        <hr />\r\n\r\n        <asp:Label id=\"UploadStatusLabel\"\r\n            runat=\"server\">\r\n        </asp:Label>   \r\n\r\n    </form>\r\n\r\n</body>\r\n', '0', '2017-09-08 09:48:42', null, 'FileUpload.txt');
INSERT INTO `txt` VALUES ('306', '2', 'FileUtil.cs.txt', '\r\nusing System;\r\nusing System.Collections.Generic;\r\nusing System.Text;\r\n\r\nusing System.IO;\r\nusing System.Collections;\r\n\r\nnamespace BJHouse.Common\r\n{\r\n    /// <summary>\r\n    /// 封装文件相关的函数调用\r\n    /// </summary>\r\n    public class FileUtil\r\n    {\r\n        #region 文件读写\r\n        /// <summary>\r\n        /// 从文件读取字符串\r\n        /// </summary>\r\n        /// <param name=\"sFileName\">文件名</param>\r\n        /// <returns>从文件中读取的字符串</returns>\r\n        public static string ReadStringFromFile(string sFileName)\r\n        {\r\n            return ReadStringFromFile(sFileName, Encoding.Default);\r\n        }\r\n\r\n        /// <summary>\r\n        /// 从文件读取字符串\r\n        /// </summary>\r\n        /// <param name=\"sFileName\">文件名</param>   \r\n        /// <param name=\"encoding\">編碼</param>\r\n        /// <returns>从文件中读取的字符串</returns>\r\n        public static string ReadStringFromFile(string sFileName, Encoding encoding)\r\n        {\r\n            byte[] bsFileContent = ReadBytesFromFile(sFileName);\r\n            string sRet = encoding.GetString(bsFileContent);\r\n\r\n            return sRet;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 从文件读取字符数组\r\n        /// </summary>\r\n        /// <param name=\"sFileName\">文件名</param>\r\n        /// <returns>从文件中读取的字符数组</returns>\r\n        public static byte[] ReadBytesFromFile(string sFileName)\r\n        {\r\n            FileStream fs = File.OpenRead(sFileName);\r\n            int nFileSize = (int)fs.Length;\r\n            byte[] bsFileContent = new byte[nFileSize];\r\n            fs.Read(bsFileContent, 0, nFileSize);\r\n            fs.Close();\r\n\r\n            return bsFileContent;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 从文件中读取字符串数组\r\n        /// </summary>\r\n        /// <param name=\"sFileName\">文件名</param>\r\n        /// <returns>从文件中读取的字符串数组</returns>\r\n        /// <remarks>本函数会跳过空行</remarks>\r\n        public static string[] ReadStringListFromFile(string sFileName)\r\n        {\r\n            return ReadStringListFromFile(sFileName, System.Text.Encoding.Default);\r\n        }\r\n\r\n\r\n        /// <summary>\r\n        /// 从文件中读取字符串数组\r\n        /// </summary>\r\n        /// <param name=\"sFileName\">文件名</param>    \r\n        /// <param name=\"encoding\">讀取的編碼</param>\r\n        /// <returns>从文件中读取的字符串数组</returns>\r\n        /// <remarks>本函数会跳过空行</remarks>\r\n        public static string[] ReadStringListFromFile(string sFileName, System.Text.Encoding encoding)\r\n        {\r\n            //============ 1. 打开文件 ==============\r\n            StreamReader reader;\r\n            reader = new StreamReader(\r\n                         (System.IO.Stream)File.OpenRead(sFileName), encoding);\r\n\r\n            //========== 2. 读取文件 =============\r\n            ArrayList arrReadContent = new ArrayList();\r\n            while (!reader.EndOfStream)\r\n            {\r\n                string sLine = reader.ReadLine();\r\n                if (sLine != \"\")\r\n                    arrReadContent.Add(sLine);\r\n            }\r\n            reader.Close();\r\n\r\n            //=========== 3. 准备返回值 ============\r\n            string[] arrRet = new string[arrReadContent.Count];\r\n\r\n            for (int i = 0; i < arrReadContent.Count; i++)\r\n            {\r\n                string sLine = (string)arrReadContent[i];\r\n                arrRet[i] = sLine;\r\n            }\r\n\r\n            return arrRet;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 写入字符串到文件中。创建一个新的文件，并写入字符串\r\n        /// </summary>\r\n        /// <param name=\"sFileName\">文件名</param>\r\n        /// <param name=\"sString\">待写入的字符串</param>\r\n        public static void WriteStringToFile(string sFileName, string sString)\r\n        {\r\n            WriteStringToFile(sFileName, sString, Encoding.Default);\r\n        }\r\n\r\n\r\n        /// <summary>\r\n        /// 写入字符串到文件中。创建一个新的文件，并写入字符串\r\n        /// </summary>\r\n        /// <param name=\"sFileName\">文件名</param>\r\n        /// <param name=\"sString\">待写入的字符串</param>\r\n        /// <param name=\"encoding\">待写入的字符串使用的编码</param>\r\n        public static void WriteStringToFile(string sFileName, string sString, Encoding encoding)\r\n        {\r\n            byte[] bsFileContent = encoding.GetBytes(sString);\r\n            WriteBytesToFile(sFileName, bsFileContent);\r\n        }\r\n\r\n\r\n        /// <summary>\r\n        /// 写入字节数组到文件中。创建一个新的文件，并写入字节数组。\r\n        /// </summary>\r\n        /// <param name=\"sFileName\">文件名</param>\r\n        /// <param name=\"bsContent\">待写入的字节数组</param>\r\n        public static void WriteBytesToFile(string sFileName, byte[] bsContent)\r\n        {\r\n            FileStream fs = File.Create(sFileName);\r\n            fs.Write(bsContent, 0, bsContent.Length);\r\n            fs.Close();\r\n        }\r\n\r\n\r\n        /// <summary>\r\n        /// 写入字符串列表到文件中。创建一个新的文件，并写入字符串列表。\r\n        /// </summary>\r\n        /// <param name=\"sFileName\">文件名</param>\r\n        /// <param name=\"slContent\">待写入的字符串列表</param> \r\n        public static void WriteStringListToFile(string sFileName, string[] slContent )\r\n        {\r\n            WriteStringListToFile(sFileName, slContent, System.Text.Encoding.Default);\r\n        }\r\n         \r\n\r\n\r\n        /// <summary>\r\n        /// 写入字符串列表到文件中。创建一个新的文件，并写入字符串列表。\r\n        /// </summary>\r\n        /// <param name=\"sFileName\">文件名</param>\r\n        /// <param name=\"slContent\">待写入的字符串列表</param>  \r\n        /// <param name=\"encoding\">待写入編碼</param>\r\n        public static void WriteStringListToFile(string sFileName, string[] slContent, System.Text.Encoding encoding)\r\n        {\r\n            StreamWriter writer;\r\n            writer = new StreamWriter(\r\n                         (System.IO.Stream)File.Create(sFileName), encoding);\r\n\r\n            for (int i = 0; i < slContent.Length; i++)\r\n            {\r\n                string sLine = slContent[i];\r\n                writer.WriteLine(sLine);\r\n            }\r\n\r\n            writer.Close();\r\n        }\r\n\r\n\r\n        /// <summary>\r\n        /// 写入字符串列表到文件中。创建一个新的文件，并写入字符串列表。\r\n        /// </summary>\r\n        /// <param name=\"sFileName\">文件名</param>\r\n        /// <param name=\"slContent\">待写入的字符串列表</param>   \r\n        /// <param name=\"encoding\">待写入的編碼</param>\r\n        public static void WriteStringListToFile(string sFileName, ArrayList slContent, System.Text.Encoding encoding)\r\n        {\r\n            StreamWriter writer;\r\n            writer = new StreamWriter(\r\n                         (System.IO.Stream)File.Create(sFileName),\r\n                         encoding);\r\n\r\n            for (int i = 0; i < slContent.Count; i++)\r\n            {\r\n                string sLine = (string)slContent[i];\r\n                writer.WriteLine(sLine);\r\n            }\r\n\r\n            writer.Close();\r\n        }\r\n\r\n        /// <summary>\r\n        /// 写入字符串列表到文件中。创建一个新的文件，并写入字符串列表。\r\n        /// </summary>\r\n        /// <param name=\"sFileName\">文件名</param>\r\n        /// <param name=\"slContent\">待写入的字符串列表</param>\r\n        public static void WriteStringListToFile(string sFileName, ArrayList slContent)\r\n        {\r\n            WriteStringListToFile(sFileName, slContent, System.Text.Encoding.Default);\r\n        }\r\n        #endregion 文件读写\r\n\r\n        #region 扩展名\r\n        /// <summary>\r\n        /// 改变文件的扩展名\r\n        /// </summary>\r\n        /// <param name=\"sFileName\">文件名</param>\r\n        /// <param name=\"sExtension\">扩展名</param>\r\n        /// <returns>改变过扩展名的文件名</returns>\r\n        public static string ChangeFileExt(string sFileName, string sExtension)\r\n        {\r\n            char[] arrDelimiter = new char[] { \'.\', \'\\\\\', \':\', \'/\' };\r\n            int nPos = sFileName.LastIndexOfAny(arrDelimiter);\r\n            if (nPos == -1 || sFileName[nPos] != \'.\')\r\n                return sFileName + sExtension;\r\n            else\r\n                return sFileName.Substring(0, nPos) + sExtension;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 截掉一个文件的扩展名，返回文件的其余部分\r\n        /// </summary>\r\n        /// <param name=\"sFileName\">文件名</param>\r\n        /// <returns>截掉扩展名的文件名</returns>\r\n        /// <remarks> 如果文件名带路径，则带路径一起返回</remarks>\r\n        public static string CutFileExt(string sFileName)\r\n        {\r\n            char[] arrDelimiter = new char[] { \'.\', \'\\\\\', \':\', \'/\' };\r\n            int nPos = sFileName.LastIndexOfAny(arrDelimiter);\r\n            if (nPos == -1 || sFileName[nPos] != \'.\')\r\n                return sFileName;\r\n            else\r\n                return sFileName.Substring(0, nPos);\r\n        }\r\n\r\n        /// <summary>\r\n        /// 得到文件的扩展名，如无扩展名返回\"\"，扩展名形式为\".xxx\"\r\n        /// </summary>\r\n        /// <param name=\"sFileName\">文件名</param>\r\n        /// <returns>扩展名</returns>\r\n        public static string ExtractFileExt(string sFileName)\r\n        {\r\n            char[] arrDelimiter = new char[] { \'.\', \'\\\\\', \':\', \'/\' };\r\n            int nPos = sFileName.LastIndexOfAny(arrDelimiter);\r\n            if (nPos == -1 || sFileName[nPos] != \'.\')\r\n                return \"\";\r\n            else\r\n                return sFileName.Substring(nPos);\r\n        }\r\n        #endregion 扩展名\r\n\r\n        /// <summary>\r\n        /// 是否绝对路径\r\n        /// </summary>\r\n        /// <param name=\"sPathName\">路径名</param>\r\n        /// <returns>是否绝对路径</returns>\r\n        public static bool IsAbsolutePath(string sPathName)\r\n        {\r\n            if (sPathName.IndexOf(\":\") >= 0)\r\n                return true;\r\n\r\n            if (sPathName[0] == \'\\\\\' || sPathName[0] == \'/\')\r\n                return true;\r\n\r\n            return false;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 去掉字符串尾部的目录分隔符\r\n        /// </summary>\r\n        /// <param name=\"sSrc\">字符串</param>\r\n        /// <returns>去除分隔符以后的字符串</returns>\r\n        public static string ExcludeTrailingSlash(string sSrc)\r\n        {\r\n            string sRet = sSrc;\r\n\r\n            if (IsPathDelimiter(sRet, sRet.Length - 1))\r\n                sRet = sRet.Substring(0, sRet.Length - 1);\r\n\r\n            return sRet;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 一个字符串中的指定位置字符是否是目录分隔符\r\n        /// </summary>\r\n        /// <param name=\"sString\">字符串</param>\r\n        /// <param name=\"nIndex\">指定位置</param>\r\n        /// <returns>是否目录分隔符</returns>\r\n        public static bool IsPathDelimiter(string sString, int nIndex)\r\n        {\r\n            return (nIndex >= 0) && (nIndex < sString.Length)\r\n                    && (sString[nIndex] == \'\\\\\' || sString[nIndex] == \'/\');\r\n        }\r\n\r\n        /// <summary>\r\n        /// 得到一个字符串中最后一个分隔符，分隔符定义在指定字符串中\r\n        /// </summary>\r\n        /// <param name=\"sDelimiters\">分隔符串</param>\r\n        /// <param name=\"sString\">待查找的字符串</param>\r\n        /// <returns>找到的分隔符位置，如果没有找到则返回0</returns>\r\n        public static int LastDelimiter(string sDelimiters, string sString)\r\n        {\r\n            char[] arrDelimiters = new char[sDelimiters.Length];\r\n            for (int i = 0; i < sDelimiters.Length; i++)\r\n            {\r\n                char ch = sDelimiters[i];\r\n                arrDelimiters[i] = ch;\r\n            }\r\n\r\n            return sString.LastIndexOfAny(arrDelimiters);\r\n        }\r\n\r\n        /// <summary>\r\n        /// 得到一个文件名的目录部分\r\n        /// </summary>\r\n        /// <param name=\"sFileName\">文件名</param>\r\n        /// <returns>目录字符串</returns>\r\n        public static string ExtractFilePath(string sFileName)\r\n        {\r\n            int nIndex = LastDelimiter(\"\\\\:/\", sFileName);\r\n\r\n            return sFileName.Substring(0, nIndex + 1);\r\n        }\r\n\r\n        /// <summary>\r\n        /// 得到路径文件名的文件部分\r\n        /// </summary>\r\n        /// <param name=\"sPathName\">路径文件名</param>\r\n        /// <returns>文件部分</returns>\r\n        public static string ExtractFileName(string sPathName)\r\n        {\r\n            int nIndex = LastDelimiter(\"\\\\:/\", sPathName);\r\n\r\n            return sPathName.Substring(nIndex + 1);\r\n        }\r\n    }\r\n}\r\n', '0', '2017-09-08 09:48:42', null, 'FileUtil.cs.txt');
