INSERT INTO `txt` VALUES ('308', '2', 'Flexigrid.cs.txt', 'using System;\r\nusing System.Collections.Generic;\r\nusing System.Reflection;\r\nusing System.Linq;\r\nusing System.ServiceModel;\r\nusing System.Runtime.Serialization;\r\n\r\nnamespace BJHouse.Common\r\n{\r\n\r\n    [DataContract]\r\n    public class FlexigridRow\r\n    {\r\n\r\n\r\n        [DataMember]\r\n        public List<string> cell = new List<string>();\r\n        [OperationContract]\r\n        public static List<string> GetPropertyList(object obj, string[] Cols)\r\n        {\r\n            List<string> propertyList = new List<string>();\r\n            Dictionary<string, string> propertyName = new Dictionary<string, string>();\r\n            Type type = obj.GetType();\r\n            PropertyInfo[] properties = type.GetProperties(BindingFlags.Instance | BindingFlags.Public);\r\n            foreach (PropertyInfo property in properties)\r\n            {\r\n                object o = property.GetValue(obj, null);\r\n                if (!string.IsNullOrEmpty(property.Name) && o != null)\r\n                {\r\n                    propertyName.Add(property.Name, o.ToString());\r\n                }\r\n            }\r\n\r\n            Cols.All(a =>\r\n            {\r\n                if (propertyName.ContainsKey(a))\r\n                {\r\n                    propertyList.Add(propertyName[a]);\r\n                }\r\n                else\r\n                {\r\n                    propertyList.Add(\"\");\r\n                }\r\n                return true;\r\n            });\r\n            return propertyList;\r\n        }\r\n        [OperationContract]\r\n        public static List<string> GetPropertyList(object obj, string[] Cols, int MySeq)\r\n        {\r\n            List<string> propertyList = new List<string>();\r\n            Dictionary<string, string> propertyName = new Dictionary<string, string>();\r\n            Type type = obj.GetType();\r\n            PropertyInfo[] properties = type.GetProperties(BindingFlags.Instance | BindingFlags.Public);\r\n            foreach (PropertyInfo property in properties)\r\n            {\r\n                object o = property.GetValue(obj, null);\r\n                if (!string.IsNullOrEmpty(property.Name) && o != null)\r\n                {\r\n                    propertyName.Add(property.Name, o.ToString());\r\n                }\r\n            }\r\n\r\n            Cols.All(a =>\r\n            {\r\n                if (!string.IsNullOrWhiteSpace(a) && a == \"MySeq\")\r\n                {\r\n                    propertyList.Add(MySeq.ToString());\r\n                    return true;\r\n                }\r\n                if (propertyName.ContainsKey(a))\r\n                {\r\n\r\n                    propertyList.Add(propertyName[a]);\r\n                }\r\n                else\r\n                {\r\n                    propertyList.Add(\"\");\r\n                }\r\n                return true;\r\n            });\r\n            return propertyList;\r\n        }\r\n    }\r\n    /// <summary>\r\n    /// 将数据转换成Flaxigrid认识格式\r\n    /// </summary>\r\n    [DataContract]\r\n    public class FlexigridObject\r\n    {\r\n        [DataMember]\r\n        public FlexigridParam FlexigridPara { get; set; }\r\n        [DataMember]\r\n        public List<FlexigridRow> rows = new List<FlexigridRow>();\r\n        [DataMember]\r\n        public int page = 1;\r\n        [DataMember]\r\n        public int total { get; set; }\r\n        /// <summary>\r\n        /// 数据源\r\n        /// </summary>\r\n        [DataMember]\r\n        public IQueryable<dynamic> Query\r\n        {\r\n            get\r\n            { return null; }\r\n            set\r\n            {\r\n                if (value != null)\r\n                {\r\n                    int MySeq = 1;\r\n                    page = FlexigridPara.Page;\r\n                    if (page > 1)\r\n                    {\r\n                        MySeq = (page - 1) * FlexigridPara.RP + 1;\r\n                    }\r\n\r\n                    foreach (dynamic item in value)\r\n                    {\r\n                        FlexigridRow cell = new FlexigridRow()\r\n                        {\r\n\r\n                            //Id = item.Id.ToString(),\r\n                            cell = FlexigridRow.GetPropertyList(item, FlexigridPara.Cols.Split(\',\'), MySeq)\r\n                        };\r\n                        rows.Add(cell);\r\n                        MySeq++;\r\n                    }\r\n                }\r\n            }\r\n        }\r\n    }\r\n    /// <summary>\r\n    ///  flexigrid与后台传输的对象\r\n    /// </summary>\r\n    [DataContract]\r\n    public class FlexigridParam\r\n    {\r\n        /*  js中 参数使用\r\n         var param = [\r\n					 { name : \'page\', value : p.newp }\r\n					,{ name : \'rp\', value : p.rp }\r\n					,{ name : \'sortname\', value : p.sortname}\r\n					,{ name : \'sortorder\', value : p.sortorder }\r\n					,{ name : \'query\', value : p.query}\r\n					,{ name : \'qtype\', value : p.qtype}\r\n				];							 \r\n         */\r\n        [DataMember]\r\n        public int Page { get; set; }\r\n        [DataMember]\r\n        public int RP { get; set; }\r\n        [DataMember]\r\n        public string SortName { get; set; }\r\n        [DataMember]\r\n        public string SortOrder { get; set; }\r\n        [DataMember]\r\n        public string Query { get; set; }\r\n        [DataMember]\r\n        public string QType { get; set; }\r\n        [DataMember]\r\n        public string Cols { get; set; }\r\n    }\r\n}\r\n', '0', '2017-09-08 09:48:42', null, 'Flexigrid.cs.txt');
INSERT INTO `txt` VALUES ('309', '2', 'FormatToJson.cs.List.To.Json.txt', '/******************************************************************************\r\n *  作者：       tianzh\r\n *  创建时间：   2012/6/6 22:22:20\r\n *\r\n *\r\n ******************************************************************************/\r\nusing System;\r\nusing System.Collections.Generic;\r\nusing System.Linq;\r\nusing System.Web;\r\nusing System.Text;\r\nusing System.Reflection;\r\nusing System.Collections;\r\nusing System.Data;\r\nusing System.Data.Common;\r\nusing System.Web.Script.Serialization;\r\nnamespace TZHSWEET.Common\r\n{\r\n    /// <summary>\r\n    /// 过期版本,以后不使用\r\n    /// </summary>\r\n    public class FormatToJson\r\n    {\r\n        public FormatToJson()\r\n        {\r\n            //\r\n            // TODO: Add constructor logic here\r\n            //\r\n        }\r\n        #region ListToJson\r\n        /// <summary>  \r\n        /// List转成json   \r\n        /// </summary>  \r\n        /// <typeparam name=\"T\"></typeparam>  \r\n        /// <param name=\"jsonName\"></param>  \r\n        /// <param name=\"list\"></param>  \r\n        /// <returns></returns>  \r\n        public static string ListToJson<T>(IList<T> list, string jsonName)\r\n        {\r\n            StringBuilder Json = new StringBuilder();\r\n            if (string.IsNullOrEmpty(jsonName))\r\n                jsonName = list[0].GetType().Name;\r\n            Json.Append(\"{\\\"\" + jsonName + \"\\\":[\");\r\n            if (list.Count > 0)\r\n            {\r\n                for (int i = 0; i < list.Count; i++)\r\n                {\r\n                    T obj = Activator.CreateInstance<T>();\r\n                    PropertyInfo[] pi = obj.GetType().GetProperties();\r\n                    Json.Append(\"{\");\r\n                    for (int j = 0; j < pi.Length; j++)\r\n                    {\r\n                        //注意这里要做非空判断,否则空值会发生异常\r\n                        if (TZHSWEET.Common.Tools.IsNullOrEmpty(pi[j].GetValue(list[i], null)))\r\n                        {\r\n\r\n                            Json.Append(\"\\\"\" + pi[j].Name.ToString() + \"\\\":\" + \"\\\"\\\"\");\r\n                        }\r\n                        else\r\n                        {\r\n                            Type type = pi[j].GetValue(list[i], null).GetType();\r\n                            Json.Append(\"\\\"\" + pi[j].Name.ToString() + \"\\\":\" + StringFormat(pi[j].GetValue(list[i], null).ToString(), type));\r\n                        }\r\n                        if (j < pi.Length - 1)\r\n                        {\r\n                            Json.Append(\",\");\r\n                        }\r\n                    }\r\n                    Json.Append(\"}\");\r\n                    if (i < list.Count - 1)\r\n                    {\r\n                        Json.Append(\",\");\r\n                    }\r\n                }\r\n            }\r\n            Json.Append(\"]}\");\r\n            return Json.ToString();\r\n        }\r\n        /// <summary>  \r\n        /// List转成json (不生成jsonName版本)  \r\n        /// </summary>  \r\n        /// <typeparam name=\"T\"></typeparam>  \r\n        /// <param name=\"list\"></param>  \r\n        /// <returns></returns>  \r\n        public static string ListToJsonNoName<T>(IList<T> list)\r\n        {\r\n            StringBuilder Json = new StringBuilder();\r\n            Json.Append(\"[\");\r\n            if (list.Count > 0)\r\n            {\r\n                for (int i = 0; i < list.Count; i++)\r\n                {\r\n                    T obj = Activator.CreateInstance<T>();\r\n                    PropertyInfo[] pi = obj.GetType().GetProperties();\r\n                    Json.Append(\"{\");\r\n                    for (int j = 0; j < pi.Length; j++)\r\n                    {\r\n                        //注意这里要做非空判断,否则空值会发生异常\r\n                        if (TZHSWEET.Common.Tools.IsNullOrEmpty(pi[j].GetValue(list[i], null)))\r\n                        {\r\n\r\n                            Json.Append(\"\\\"\" + pi[j].Name.ToString() + \"\\\":\" + \"\\\"\\\"\");\r\n                        }\r\n                        else\r\n                        {\r\n                            Type type = pi[j].GetValue(list[i], null).GetType();\r\n                            Json.Append(\"\\\"\" + pi[j].Name.ToString() + \"\\\":\" + StringFormat(pi[j].GetValue(list[i], null).ToString(), type));\r\n                        }\r\n                        if (j < pi.Length - 1)\r\n                        {\r\n                            Json.Append(\",\");\r\n                        }\r\n                    }\r\n                    Json.Append(\"}\");\r\n                    if (i < list.Count - 1)\r\n                    {\r\n                        Json.Append(\",\");\r\n                    }\r\n                }\r\n            }\r\n            Json.Append(\"]\");\r\n            return Json.ToString();\r\n\r\n        }\r\n\r\n        /// <summary>  \r\n        /// List转成json   \r\n        /// </summary>  \r\n        /// <typeparam name=\"T\"></typeparam>  \r\n        /// <param name=\"list\"></param>  \r\n        /// <returns></returns>  \r\n        public static string ListToJson<T>(IList<T> list)\r\n        {\r\n            object obj = list[0];\r\n            return ListToJson<T>(list, obj.GetType().Name);\r\n        } \r\n        #endregion\r\n        /// <summary>\r\n        /// 序列化object类型\r\n        /// </summary>\r\n        /// <param name=\"obj\">object类型</param>\r\n        /// <returns></returns>\r\n        public static string ScriptSerializationToJson(object obj)\r\n        {\r\n            return new JavaScriptSerializer().Serialize(obj);\r\n\r\n        }\r\n        /// <summary>\r\n        /// 解析json\r\n        /// </summary>\r\n        /// <typeparam name=\"T\"></typeparam>\r\n        /// <param name=\"input\"></param>\r\n        /// <returns></returns>\r\n        public static object DeserializeToJson(string input)\r\n        {\r\n            return new JavaScriptSerializer().DeserializeObject(input);\r\n        }\r\n        /// <summary>   \r\n        /// 对象转换为Json字符串   \r\n        /// </summary>   \r\n        /// <param name=\"jsonObject\">对象</param>   \r\n        /// <returns>Json字符串</returns>   \r\n        public static string ToJson(object jsonObject)\r\n        {\r\n            string jsonString = \"{\";\r\n            PropertyInfo[] propertyInfo = jsonObject.GetType().GetProperties();\r\n            for (int i = 0; i < propertyInfo.Length; i++)\r\n            {\r\n                object objectValue = propertyInfo[i].GetGetMethod().Invoke(jsonObject, null);\r\n                string value = string.Empty;\r\n                if (objectValue is DateTime || objectValue is Guid || objectValue is TimeSpan)\r\n                {\r\n                    value = \"\'\" + objectValue.ToString() + \"\'\";\r\n                }\r\n                else if (objectValue is string)\r\n                {\r\n                    value = \"\'\" + ToJson(objectValue.ToString()) + \"\'\";\r\n                }\r\n                else if (objectValue is IEnumerable)\r\n                {\r\n                    value = ToJson((IEnumerable)objectValue);\r\n                }\r\n                else\r\n                {\r\n                    value = ToJson(objectValue.ToString());\r\n                }\r\n                jsonString += \"\\\"\" + ToJson(propertyInfo[i].Name) + \"\\\":\" + value + \",\";\r\n            }\r\n            jsonString.Remove(jsonString.Length - 1, jsonString.Length);\r\n            return jsonString + \"}\";\r\n        }\r\n\r\n        /// <summary>   \r\n        /// 对象集合转换Json   \r\n        /// </summary>   \r\n        /// <param name=\"array\">集合对象</param>   \r\n        /// <returns>Json字符串</returns>   \r\n        public static string ToJson(IEnumerable array)\r\n        {\r\n            string jsonString = \"[\";\r\n            foreach (object item in array)\r\n            {\r\n                jsonString += ToJson(item) + \",\";\r\n            }\r\n            jsonString.Remove(jsonString.Length - 1, jsonString.Length);\r\n            return jsonString + \"]\";\r\n        }\r\n\r\n        /// <summary>   \r\n        /// 普通集合转换Json   \r\n        /// </summary>   \r\n        /// <param name=\"array\">集合对象</param>   \r\n        /// <returns>Json字符串</returns>   \r\n        public static string ToArrayString(IEnumerable array)\r\n        {\r\n            string jsonString = \"[\";\r\n            foreach (object item in array)\r\n            {\r\n                jsonString = ToJson(item.ToString()) + \",\";\r\n            }\r\n            jsonString.Remove(jsonString.Length - 1, jsonString.Length);\r\n            return jsonString + \"]\";\r\n        }\r\n\r\n\r\n\r\n        /// <summary>   \r\n        /// Datatable转换为Json   \r\n        /// </summary>   \r\n        /// <param name=\"table\">Datatable对象</param>   \r\n        /// <returns>Json字符串</returns>   \r\n        public static string ToJson(DataTable dt)\r\n        {\r\n            StringBuilder jsonString = new StringBuilder();\r\n            jsonString.Append(\"[\");\r\n            DataRowCollection drc = dt.Rows;\r\n            for (int i = 0; i < drc.Count; i++)\r\n            {\r\n                jsonString.Append(\"{\");\r\n                for (int j = 0; j < dt.Columns.Count; j++)\r\n                {\r\n                    string strKey = dt.Columns[j].ColumnName;\r\n                    string strValue = drc[i][j].ToString();\r\n                    Type type = dt.Columns[j].DataType;\r\n                    jsonString.Append(\"\\\"\" + strKey + \"\\\":\");\r\n                    strValue = StringFormat(strValue, type);\r\n                    if (j < dt.Columns.Count - 1)\r\n                    {\r\n                        jsonString.Append(strValue + \",\");\r\n                    }\r\n                    else\r\n                    {\r\n                        jsonString.Append(strValue);\r\n                    }\r\n                }\r\n                jsonString.Append(\"},\");\r\n            }\r\n            jsonString.Remove(jsonString.Length - 1, 1);\r\n            jsonString.Append(\"]\");\r\n            return jsonString.ToString();\r\n        }\r\n        #region 为MINIUI生成的json数据转换\r\n        /// <summary>\r\n        /// 为miniui生成json格式(记住,传入数据,包括总行数和数据项的json\r\n        /// </summary>\r\n        /// <param name=\"MiniUi\">数据项的json,比如[{\"id\":1,\"name\":\"tianzh\"},{\"id\":2,\"name\":\"3\"}]</param>\r\n        /// <param name=\"total\">记录总行数</param>\r\n        /// <returns></returns>\r\n        public static string MiniUiToJsonFormat(string MiniUi, int total)\r\n        {\r\n            StringBuilder sb = new StringBuilder();\r\n            sb.Append(\"{\\\"total\\\":\" + total + \",\\\"data\\\":\" + MiniUi + \"}\");\r\n            return sb.ToString();\r\n\r\n        }\r\n        /// <summary>\r\n        /// 专门为muniUi解析树形的json \r\n        /// 大致格式:[{id:\"root\",text:\"总根目录\",expanded:false,children:[{id:\"xxx\",text:\"xx\"},{id:\"xxx\",text:\"xx\"}]}]\r\n        /// </summary>\r\n        /// <param name=\"strMiniUi\">传入参数为[{id:\"xxx\",text:\"xx\"},{id:\"xxx\",text:\"xx\"}]</param>\r\n        /// <param name=\"rootName\">根标题</param>\r\n        /// <returns></returns>\r\n        public static string MiniUiToJsonForTree(string strMiniUi, string rootName)\r\n        {\r\n            StringBuilder sb = new StringBuilder();\r\n            sb.Append(\"[{\\\"id\\\":\\\"-1\\\",\\\"text\\\":\\\"\" + rootName + \"\\\",\\\"children\\\":\" + strMiniUi + \"}]\");\r\n            return sb.ToString();\r\n        }\r\n        /// <summary>\r\n        /// 专门生成为MiniUi生成json数据(List->json)\r\n        /// </summary>\r\n        /// <typeparam name=\"T\">泛型</typeparam>\r\n        /// <param name=\"list\">实现了Ilist接口的list</param>\r\n        /// <param name=\"total\">记录总数</param>\r\n        /// <param name=\"paramMaxMinAvg\">这里放排序的参数例如,string para=\"\\\"maxAge\\\":37,\\\"avgAge\\\":27,\\\"minAge\\\":24\"</param>\r\n        /// <returns></returns>\r\n        public static string MiniUiListToJson<T>(IList<T> list, int total, string paramMaxMinAvg)\r\n        {\r\n            StringBuilder Json = new StringBuilder();\r\n            Json.Append(\"{\\\"total\\\":\" + total + \",\");\r\n            if (!string.IsNullOrEmpty(paramMaxMinAvg))\r\n                Json.Append(paramMaxMinAvg);\r\n            Json.Append(\"\\\"data\\\":[\");\r\n            if (list.Count > 0)\r\n            {\r\n                for (int i = 0; i < list.Count; i++)\r\n                {\r\n                    T obj = Activator.CreateInstance<T>();\r\n                    PropertyInfo[] pi = obj.GetType().GetProperties();\r\n                    Json.Append(\"{\");\r\n                    for (int j = 0; j < pi.Length; j++)\r\n                    {\r\n                        //注意这里要做非空判断,否则空值会发生异常\r\n                        if (TZHSWEET.Common.Tools.IsNullOrEmpty(pi[j].GetValue(list[i], null)))\r\n                        {\r\n\r\n                            Json.Append(\"\\\"\" + pi[j].Name.ToString() + \"\\\":\" + \"\\\"\\\"\");\r\n                        }\r\n                        else\r\n                        {\r\n                            Type type = pi[j].GetValue(list[i], null).GetType();\r\n                            Json.Append(\"\\\"\" + pi[j].Name.ToString() + \"\\\":\" + StringFormat(pi[j].GetValue(list[i], null).ToString(), type));\r\n                        }\r\n\r\n                        if (j < pi.Length - 1)\r\n                        {\r\n                            Json.Append(\",\");\r\n                        }\r\n                    }\r\n                    Json.Append(\"}\");\r\n                    if (i < list.Count - 1)\r\n                    {\r\n                        Json.Append(\",\");\r\n                    }\r\n                }\r\n            }\r\n            Json.Append(\"]}\");\r\n            return Json.ToString();\r\n\r\n        } \r\n        #endregion\r\n        #region 为easyUI生成的json数据\r\n        /// <summary>\r\n        /// 专门生成为EasyUI生成json数据(List->json)\r\n        /// </summary>\r\n        /// <typeparam name=\"T\"></typeparam>\r\n        /// <param name=\"list\"></param>\r\n        /// <param name=\"total\"></param>\r\n        /// <returns></returns>\r\n        public static string EasyUIListToJson<T>(IList<T> list, int total)\r\n        {\r\n            StringBuilder Json = new StringBuilder();\r\n            Json.AppendLine(\"{\\\"total\\\":\" + total + \",\");\r\n            Json.Append(\"\\\"rows\\\":[\");\r\n            if (list.Count > 0)\r\n            {\r\n                for (int i = 0; i < list.Count; i++)\r\n                {\r\n                    T obj = Activator.CreateInstance<T>();\r\n                    PropertyInfo[] pi = obj.GetType().GetProperties();\r\n                    Json.Append(\"{\");\r\n                    for (int j = 0; j < pi.Length; j++)\r\n                    {\r\n                        Type type = pi[j].GetValue(list[i], null).GetType();\r\n                        Json.Append(\"\\\"\" + pi[j].Name.ToString() + \"\\\":\" + StringFormat(pi[j].GetValue(list[i], null).ToString(), type));\r\n\r\n                        if (j < pi.Length - 1)\r\n                        {\r\n                            Json.Append(\",\");\r\n                        }\r\n                    }\r\n                    Json.Append(\"}\");\r\n                    if (i < list.Count - 1)\r\n                    {\r\n                        Json.Append(\",\");\r\n                    }\r\n                }\r\n            }\r\n            Json.Append(\"]}\");\r\n            return Json.ToString();\r\n\r\n        }\r\n        /// <summary>\r\n        /// 专门生成为DataGrid的json数据\r\n        /// </summary>\r\n        /// <param name=\"dt\"></param>\r\n        /// <param name=\"jsonName\"></param>\r\n        /// <returns></returns>\r\n        public static string EasyUIDataGridToJson(DataTable dt, string jsonName, int total)\r\n        {\r\n            StringBuilder Json = new StringBuilder();\r\n            if (string.IsNullOrEmpty(jsonName))\r\n                jsonName = dt.TableName;\r\n            Json.Append(\"{\\\"total\\\":\" + total + \",\\\"\" + jsonName + \"\\\":[\");\r\n            if (dt.Rows.Count > 0)\r\n            {\r\n                for (int i = 0; i < dt.Rows.Count; i++)\r\n                {\r\n                    Json.Append(\"{\");\r\n                    for (int j = 0; j < dt.Columns.Count; j++)\r\n                    {\r\n                        Type type = dt.Rows[i][j].GetType();\r\n                        Json.Append(\"\\\"\" + dt.Columns[j].ColumnName.ToString() + \"\\\":\" + StringFormat(dt.Rows[i][j].ToString(), type));\r\n\r\n                        if (j < dt.Columns.Count - 1)\r\n                        {\r\n                            Json.Append(\",\");\r\n                        }\r\n\r\n                    }\r\n                    //追加编辑\r\n                    Json.Append(\",\\\"opt\\\":\\\"编辑\\\"\");\r\n\r\n\r\n\r\n                    Json.Append(\"}\");\r\n                    if (i < dt.Rows.Count - 1)\r\n                    {\r\n                        Json.Append(\",\");\r\n                    }\r\n                }\r\n            }\r\n            Json.Append(\"]}\");\r\n            return Json.ToString();\r\n        }\r\n        #endregion\r\n        /// <summary>  \r\n        /// DataTable转成Json   \r\n        /// </summary>  \r\n        /// <param name=\"jsonName\"></param>  \r\n        /// <param name=\"dt\"></param>  \r\n        /// <returns></returns>  \r\n        public static string ToJson(DataTable dt, string jsonName)\r\n        {\r\n            StringBuilder Json = new StringBuilder();\r\n            if (string.IsNullOrEmpty(jsonName))\r\n                jsonName = dt.TableName;\r\n            Json.Append(\"{\\\"\" + jsonName + \"\\\":[\");\r\n            if (dt.Rows.Count > 0)\r\n            {\r\n                for (int i = 0; i < dt.Rows.Count; i++)\r\n                {\r\n                    Json.Append(\"{\");\r\n                    for (int j = 0; j < dt.Columns.Count; j++)\r\n                    {\r\n                        Type type = dt.Rows[i][j].GetType();\r\n                        Json.Append(\"\\\"\" + dt.Columns[j].ColumnName.ToString() + \"\\\":\" + StringFormat(dt.Rows[i][j].ToString(), type));\r\n                        if (j < dt.Columns.Count - 1)\r\n                        {\r\n                            Json.Append(\",\");\r\n                        }\r\n                    }\r\n                    Json.Append(\"}\");\r\n                    if (i < dt.Rows.Count - 1)\r\n                    {\r\n                        Json.Append(\",\");\r\n                    }\r\n                }\r\n            }\r\n            Json.Append(\"]}\");\r\n            return Json.ToString();\r\n        }\r\n\r\n        /// <summary>   \r\n        /// DataReader转换为Json   \r\n        /// </summary>   \r\n        /// <param name=\"dataReader\">DataReader对象</param>   \r\n        /// <returns>Json字符串</returns>   \r\n        public static string ToJson(DbDataReader dataReader)\r\n        {\r\n            StringBuilder jsonString = new StringBuilder();\r\n            jsonString.Append(\"[\");\r\n            while (dataReader.Read())\r\n            {\r\n                jsonString.Append(\"{\");\r\n                for (int i = 0; i < dataReader.FieldCount; i++)\r\n                {\r\n                    Type type = dataReader.GetFieldType(i);\r\n                    string strKey = dataReader.GetName(i);\r\n                    string strValue = dataReader[i].ToString();\r\n                    jsonString.Append(\"\\\"\" + strKey + \"\\\":\");\r\n                    strValue = StringFormat(strValue, type);\r\n                    if (i < dataReader.FieldCount - 1)\r\n                    {\r\n                        jsonString.Append(strValue + \",\");\r\n                    }\r\n                    else\r\n                    {\r\n                        jsonString.Append(strValue);\r\n                    }\r\n                }\r\n                jsonString.Append(\"},\");\r\n            }\r\n            dataReader.Close();\r\n            jsonString.Remove(jsonString.Length - 1, 1);\r\n            jsonString.Append(\"]\");\r\n            return jsonString.ToString();\r\n        }\r\n\r\n        /// <summary>   \r\n        /// DataSet转换为Json   \r\n        /// </summary>   \r\n        /// <param name=\"dataSet\">DataSet对象</param>   \r\n        /// <returns>Json字符串</returns>   \r\n        public static string ToJson(DataSet dataSet)\r\n        {\r\n            string jsonString = \"{\";\r\n            foreach (DataTable table in dataSet.Tables)\r\n            {\r\n                jsonString += \"\\\"\" + table.TableName + \"\\\":\" + ToJson(table) + \",\";\r\n            }\r\n            jsonString = jsonString.TrimEnd(\',\');\r\n            return jsonString + \"}\";\r\n        }\r\n\r\n        /// <summary>  \r\n        /// 过滤特殊字符  \r\n        /// </summary>  \r\n        /// <param name=\"s\"></param>  \r\n        /// <returns></returns>  \r\n        private static string String2Json(String s)\r\n        {\r\n            StringBuilder sb = new StringBuilder();\r\n            for (int i = 0; i < s.Length; i++)\r\n            {\r\n                char c = s.ToCharArray()[i];\r\n                switch (c)\r\n                {\r\n                    case \'\\\"\':\r\n                        sb.Append(\"\\\\\\\"\"); break;\r\n                    case \'\\\\\':\r\n                        sb.Append(\"\\\\\\\\\"); break;\r\n                    case \'/\':\r\n                        sb.Append(\"\\\\/\"); break;\r\n                    case \'\\b\':\r\n                        sb.Append(\"\\\\b\"); break;\r\n                    case \'\\f\':\r\n                        sb.Append(\"\\\\f\"); break;\r\n                    case \'\\n\':\r\n                        sb.Append(\"\\\\n\"); break;\r\n                    case \'\\r\':\r\n                        sb.Append(\"\\\\r\"); break;\r\n                    case \'\\t\':\r\n                        sb.Append(\"\\\\t\"); break;\r\n                    default:\r\n                        sb.Append(c); break;\r\n                }\r\n            }\r\n            return sb.ToString();\r\n        }\r\n\r\n        /// <summary>  \r\n        /// 格式化字符型、日期型、布尔型  \r\n        /// </summary>  \r\n        /// <param name=\"str\"></param>  \r\n        /// <param name=\"type\"></param>  \r\n        /// <returns></returns>  \r\n        private static string StringFormat(string str, Type type)\r\n        {\r\n            if (type == typeof(string))\r\n            {\r\n                str = String2Json(str);\r\n                str = \"\\\"\" + str + \"\\\"\";\r\n            }\r\n            else if (type == typeof(DateTime))\r\n            {\r\n                DateTime dt = DateTime.Parse(str);\r\n                str = \"\\\"\" + dt.GetDateTimeFormats(\'s\')[0].ToString() + \"\\\"\";\r\n                // str = + str + ;\r\n            }\r\n            else if (type == typeof(bool))\r\n            {\r\n                str = str.ToLower();\r\n            }\r\n            return str;\r\n        }\r\n    }\r\n\r\n}\r\n', '0', '2017-09-08 09:48:42', null, 'FormatToJson.cs.List.To.Json.txt');
