                          }\r\n                            });\r\n                            // is the \"remove after drop\" checkbox checked?\r\n                            if ($(\'#drop-remove\').is(\':checked\')) {\r\n                                // if so, remove the element from the \"Draggable Events\" list\r\n                                $(this).remove();\r\n                            }\r\n\r\n                        },\r\n                        eventClick: function (calEvent, jsEvent, view) {\r\n                            //alert(calEvent.id);\r\n                            var url = \'/web/calendar/edit/\' + calEvent.id;\r\n                            layer.open({\r\n                                title: \'�༭����\',\r\n                                type: 2,\r\n                                area: [\'1200px\', \'660px\'],\r\n                                fixed: false,\r\n                                maxmin: true,\r\n                                content: url\r\n                            });\r\n                        },\r\n                        events: data\r\n                    });\r\n', '0', '2017-09-08 09:48:42', null, 'fullcalendar.基本使用.中文.汉化.txt');
