INSERT INTO `txt` VALUES ('314', '2', 'Global定时器.定时发送短信.timer.txt', 'using System;\r\nusing System.Collections.Generic;\r\nusing System.Linq;\r\nusing System.Web;\r\nusing System.Web.Security;\r\nusing System.Web.SessionState;\r\nusing System.Timers;\r\n\r\nnamespace WcfService\r\n{\r\n    public class Global : System.Web.HttpApplication\r\n    {\r\n\r\n        private static object LockObject = new Object();\r\n        //�������ݼ��Timer\r\n        private static Timer CheckUpdatetimer = new Timer();\r\n        //��������\r\n        private static int CheckUpDateLock = 0;\r\n\r\n        /// <summary>\r\n        /// �趨���ݼ��Timer����\r\n        /// </summary>\r\n        internal static void GetTimerStart()\r\n        {\r\n            // ѭ�����ʱ��(1����)\r\n            CheckUpdatetimer.Interval = 60000;\r\n            // ����Timerִ��\r\n            CheckUpdatetimer.Enabled = true;\r\n            // ����ص�\r\n            CheckUpdatetimer.Elapsed += new ElapsedEventHandler(CheckUpdatetimer_Elapsed);\r\n            // ������ѭ��\r\n            CheckUpdatetimer.AutoReset = true;\r\n        }\r\n\r\n        /// <summary>\r\n        ///  Timer �¼�\r\n        /// </summary>\r\n        /// <param name=\"sender\"></param>\r\n        /// <param name=\"e\"></param>\r\n        private static void CheckUpdatetimer_Elapsed(object sender, ElapsedEventArgs e)\r\n        {\r\n            // ������������\r\n            lock (LockObject)\r\n            {\r\n                if (CheckUpDateLock == 0) CheckUpDateLock = 1;\r\n                else return;\r\n            }\r\n\r\n            //More code goes here.\r\n            Service1 service1 = new Service1();\r\n            // string nowStr = DateTime.Now.ToString();\r\n            DateTime nowStr = DateTime.Now;\r\n            string nowYear = nowStr.Year.ToString();\r\n            string nowMonth = nowStr.Month.ToString();\r\n            string nowDay = nowStr.Day.ToString();\r\n            string nowHour = nowStr.Hour.ToString();\r\n  