INSERT INTO `txt` VALUES ('315', '2', 'GMT时间转成本地时间.世界时UT即格林尼治平太阳时间.C#.txt', '        /// <summary>  \r\n        /// GMTʱ��ת�ɱ���ʱ��  \r\n        /// </summary>  \r\n        /// <param name=\"gmt\">�ַ�����ʽ��GMTʱ��</param>  \r\n        /// <returns></returns>  \r\n        public static DateTime GMT2Local(this string gmt)\r\n        {\r\n            DateTime dt = DateTime.MinValue;\r\n            try\r\n            {\r\n                string pattern = ;\r\n                if (gmt.IndexOf(+0) != -1)\r\n                {\r\n                    gmt = gmt.Replace(GMT, );\r\n                    pattern = ddd, dd MMM yyyy HH\'\'mm\'\'ss zzz;\r\n                }\r\n                if (gmt.ToUpper().IndexOf(GMT) != -1)\r\n                {\r\n                    pattern = ddd, dd MMM yyyy HH\'\'mm\'\'ss \'GMT\';\r\n                }\r\n                if (pattern != )\r\n                {\r\n                    dt = DateTime.ParseExact(gmt, pattern, System.Globalization.CultureInfo.InvariantCulture, System.Globalization.DateTimeStyles.AdjustToUniversal);\r\n                    dt = dt.ToLocalTime();\r\n                }\r\n                else\r\n                {\r\n                    dt = Convert.ToDateTime(gmt);\r\n                }\r\n            }\r\n            catch\r\n            {\r\n            }\r\n            return dt;\r\n        }\r\n', '0', '2017-09-08 09:48:42', null, 'GMT时间转成本地时间.世界时UT即格林尼治平太阳时间.C#.txt');
