INSERT INTO `txt` VALUES ('323', '2', 'HouseUtil.cs.txt', 'using System;\r\nusing System.Collections.Generic;\r\nusing System.Linq;\r\nusing System.Text;\r\n\r\nnamespace BJHouse.Common\r\n{\r\n    public static class HouseUtil\r\n    {\r\n        /// <summary>\r\n        /// 给每个没有的字段默认值\r\n        /// </summary>\r\n        /// <param name=\"details\"></param>\r\n        /// <returns></returns>\r\n        public static string DefaultDetails(this string details)\r\n        {\r\n            if (details == \"\" || details == null)\r\n            {\r\n                return \"暂无\";\r\n            }\r\n            else\r\n            {\r\n                return details;\r\n            }\r\n        }\r\n        /// <summary>\r\n        /// 获取guid主键\r\n        /// </summary>\r\n        /// <returns></returns>\r\n        public static string GetID()\r\n        {\r\n            return Guid.NewGuid().ToString().Replace(\"-\", \"\");\r\n\r\n        }\r\n    }\r\n}\r\n', '0', '2017-09-08 09:48:42', null, 'HouseUtil.cs.txt');
INSERT INTO `txt` VALUES ('324', '2', 'HRM=Human Resource Manager.人力资源经理.txt', 'HRM=Human Resource Manager.人力资源经理。是近几年在企业中出现的新兴热门职位。也就是cho（人力执行官）的初级阶段。\r\nHRM 是human resource management的缩写，是指人力资源管理（也叫HR管理）。\r\n目录\r\n\r\n    1由来\r\n\r\n    2技术发明\r\n\r\n    3技术内容\r\n\r\n1由来编辑\r\nHRM=Human Resource Manager.人力资源经理\r\nHRM 是human resource management的缩写，是指人力资源管理（也叫HR管理）\r\nHR-Human Resource人力资源,目标是让企业HR更好地进行人力资源的发展和规划。系统重点是实现人力资源部门在员工素质管理、薪资管理、绩效考核等方面的需求。\r\n2技术发明编辑\r\nHR-1和LC Green\r\n\r\nHR-1和LC Green\r\nHRM技术是由美国犹他大学保健科学中心病理学部Carl Wittwer实验室与美国Idaho公司于2003年共同合作开发出来的一种建立在实时荧光定量PCR基础上的技术，并于2006成功生产出世界上第一台商品化的HRM仪器HR-1。其中HRM技术的发明，还得先归功于先发明了饱和荧光染料LC Green，否则不可能实现HRM技术。\r\n3技术内容编辑\r\n1.\r\nCarl Wittwer博士\r\n\r\nCarl Wittwer博士[1]\r\n组织管理\r\n主要实现对公司组织结构及其变更的管理；对职位信息及职位间工作关系的管理，根据职位的空缺进行人员配备；按照组织结构进行人力规划、并对人事成本进行计算和管理，支持生成机构编制表、组织结构图等。\r\n2.人事信息管理\r\n主要实现对员工从试用、转正直至解聘或退休整个过程中各类信息的管理，人员信息的变动管理，提供多种形式、多种角度的查询、统计分析手段。\r\n3.招聘管理\r\n实现从计划招聘岗位、发布招聘信息、采集应聘者简历，按岗位任职资格遴选人员，管理面试结果到通知试用的全过程管理。\r\n4.劳动合同\r\n提供对员工劳动合同的签订、变更、解除、续订、劳动争议、经济补偿的管理。可根据需要设定试用期、合同到期的自动提示。\r\n5.培训管理\r\n根据岗位设置及绩效考核结果，确定必要的培训需求；为员工职业生涯发展制定培训计划；对培训的目标、课程内容、授课教师、时间、地点、设备、预算等进行管理，对培训人员、培训结果、培训费用进行管理。\r\n6.考勤管理\r\n主要提供对员工出勤情况的管理，帮助企业完善作业制度。主要包括各种假期的设置、班别的设置、相关考勤项目的设置，以及调班、加班、公出、请假的管理、迟到早退的统计、出勤情况的统计等。提供与各类考勤机系统的接口，并为薪资管理系统提供相关数据。\r\n7.绩效管理\r\n通过绩效考核可以评价人员配置和培训的效果、对员工进行奖惩激励、为人事决策提供依据。根据不同职位在知识、技能、能力、业绩等方面的要求，系统提供多种考核方法、标准，允许自由设置考核项目，对员工的特征、行为、工作结果等进行定性和定量的考评；\r\n8.福利管理\r\n福利管理系统提供员工的各项福利基金的提取和管理功能。主要包括定义基金类型、设置基金提取的条件，进行基金的日常管理，并提供相应的统计分析，基金的日常管理包括基金定期提取、基金的补缴、转入转出等。此外，提供向相关管理机关报送相关报表的功能。\r\n9.工资管理\r\n工资管理系统适用于各类企业、行政、事业及科研单位，直接集成考勤、绩效考核等数据，主要提供工资核算、工资发放、经费计提、统计分析等功能。支持工资的多次或分次发放；支持代扣税或代缴税；工资发放支持银行代发，提供代发数据的输出功能，同时也支持现金发放，提供分钱清单功能。经费计提的内容和计提的比率可以进行设置。\r\n', '0', '2017-09-08 09:48:42', null, 'HRM=Human Resource Manager.人力资源经理.txt');
INSERT INTO `txt` VALUES ('325', '2', 'HRMJDCONTACTINFO.Career.ERP. 职位统计.sql.Position.Static.DATEDIFF.对比时间.date.time.txt', 'select count(*) from dbo.HRMJDCONTACTINFO where ISUPLOAD=1 and   DATEDIFF (YEAR,CREATEDDATETIME,\'2013\')<0  and DATEDIFF (YEAR,CREATEDDATETIME,\'2015\')>0 \r\n---2014年的职位总数（9516）\r\nselect count(*) from dbo.HRMJDCONTACTINFO where    DATEDIFF (YEAR,CREATEDDATETIME,\'2013\')<0  and DATEDIFF (YEAR,CREATEDDATETIME,\'2015\')>0 \r\n----2013年发布到外网职位\r\nselect CREATEDDATETIME, * from dbo.HRMJDCONTACTINFO where ISUPLOAD=1 and  DATEDIFF (YEAR,CREATEDDATETIME,\'2012\')<0 and DATEDIFF (YEAR,CREATEDDATETIME,\'2014\')>0    \r\n----2013年发布到外网总数（3186）\r\nselect count(*) from dbo.HRMJDCONTACTINFO where ISUPLOAD=1 and  DATEDIFF (YEAR,CREATEDDATETIME,\'2012\')<0 and DATEDIFF (YEAR,CREATEDDATETIME,\'2014\')>0   \r\n----2013年职位总数（10758）\r\nselect count(*) from dbo.HRMJDCONTACTINFO where  DATEDIFF (YEAR,CREATEDDATETIME,\'2012\')<0 and DATEDIFF (YEAR,CREATEDDATETIME,\'2014\')>0  \r\n', '0', '2017-09-08 09:48:42', null, 'HRMJDCONTACTINFO.Career.ERP. 职位统计.sql.Position.Static.DATEDIFF.对比时间.date.time.txt');
