INSERT INTO `txt` VALUES ('368', '2', 'javascript.js.validate.howToUse.txt', '<%@ Page Language=\"C#\" AutoEventWireup=\"true\" CodeBehind=\"DetailInformation.aspx.cs\"\r\n    Inherits=\"WEB.web.user.DetailInformation\" %>\r\n\r\n<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head id=\"Head1\" runat=\"server\">\r\n    <title>��ϸ����</title>\r\n    <meta http-equiv=\"Content-Type\" content=\"text/html; charset=GBK\" />\r\n    <link href=\"../../inc/Style/WebBox.css\" rel=\"stylesheet\" type=\"text/css\" />\r\n    <link href=\"../../inc/Style/WebStyle.css\" rel=\"stylesheet\" type=\"text/css\" />\r\n    <link href=\"../../style/qq.css\" rel=\"stylesheet\" type=\"text/css\" />\r\n    <link href=\"../../style/css.css\" rel=\"stylesheet\" type=\"text/css\" />\r\n    <script src=\"../../inc/Script/jquery-1.4.1.js\" type=\"text/javascript\"></script>\r\n    <script src=\"../../inc/Script/validate.js\" type=\"text/javascript\"></script>\r\n</head>\r\n<body>\r\n    <form id=\"form1\" runat=\"server\">\r\n    <table width=\"790\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n        <tr>\r\n            <td bgcolor=\"#f2f2f2\" height=\"35\">\r\n                <table width=\"770\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n                    <tr>\r\n                        <td width=\"86\" style=\"color: #000000; font-size: 14px\">\r\n                            �û��������\r\n                        </td>\r\n                        <td width=\"684\" align=\"right\">\r\n                            <font color=\"#CC0000\">\r\n                                <%=modelOfUser.UserName %></font>��ӭ����½��Ա���ģ���ǰ���źţ�<font color=\"#CC0000\"><%=new Permission.BLL.QueueRecord().GetQueueRcordCout() %></font>\r\n                            ��ǰ�������źţ�<font color=\"#CC0000\"><%=new Permission.BLL.QueueRecord().GetLineUp(new Permission.BL