INSERT INTO `txt` VALUES ('369', '2', 'javascript.js.validate.script.txt', '//��֤����ö��\r\nvar typeEnumerate = {\r\n    phone: \'phone\',\r\n    telphone: \'telphone\',\r\n    email: \'email\',\r\n    website: \'website\',\r\n    age: \'age\',\r\n    birthday: \'birthday\',\r\n    cardtID: \'cardtID\',\r\n    vstring: \'vstring\',\r\n    tabledefine: \'tabledefine\',\r\n    integer: \'integer\',\r\n    ip: \'ip\',\r\n    float: \'float\',\r\n    postcode: \'postcode\',\r\n    isnull: \"isnull\",\r\n    letterandint: \"letterandint\"\r\n};\r\n\r\n//�����쳣ö��\r\nvar errorEnumerate = {\r\n    phone: \'�ֻ������ʽ����\',\r\n    telphone: \'�绰�����ʽ����\',\r\n    email: \'�����ʽ����\',\r\n    age: \'��������ȷ����\',\r\n    birthday: \'��������ȷ����\',\r\n    cardtID: \'���֤��ʽ����\',\r\n    vstring: \'�������ַ�������\',\r\n    tabledefine: \'��ʽ����ȷ\',\r\n    integer: \'����������\',\r\n    ip: \'IP��ַ����\',\r\n    float: \'��������ȷ��С��\',\r\n    postcode: \'�ʱ����\',\r\n    isnull: \"����Ϊ��\",\r\n    letterandint: \"ֻ��Ϊ���ֺ���ĸ�����\"\r\n};\r\n\r\n//��ȷ��ʾö��\r\nvar succressEnumerate = {\r\n    phone: \"\", //\'�ֻ������ʽ��ȷ\',\r\n    telphone: \"\", // \'�绰�����ʽ��ȷ\',\r\n    email: \"\", //\'�����ʽ��ȷ\',\r\n    age: \"\", //\'������ȷ\',\r\n    birthday: \"\", // \'������ȷ\',\r\n    cardtID: \"\", //\'���֤��ʽ��ȷ\',\r\n    vstring: \"\", // \'��ȷ\',\r\n    tabledefine: \"\", //\'��ȷ\',\r\n    integer: \"\", // \'��ȷ\',\r\n    ip: \"\", //\'IP��ַ��ȷ\',\r\n    float: \"\", // \'��ȷ\',\r\n    postcode: \"\", // \'�ʱ���ȷ\',\r\n    isnull: \"\", // \"��ȷ\",\r\n    letterandint: \"\"//\"��ȷ\"\r\n};\r\n\r\n\r\n//������ʽ\r\nvar regexpEnumerate = {\r\n    phone: /^[1][3|5|8|4|][0-9]{9}$/,\r\n    telphone: /^(\\(\\d{3}\\))?(\\(?(\\d{3}|\\d{4}|\\d{5})\\)?(-?)(\\d+))?((-?)(\\d+))?$/,\r\n    email: /^[0-9a-zA-Z_\\.]+@\\w+\\.\\w+\\.?\\w+$/,\r\n    age: /^[0-9]{1-3}*$/,\r\n    birthday: /^((([1-9]\\d{3})|([1-9]\\d{1}))-(0[1-9]|1[0-2])-(0[1-9]|[1-2]\\d|3[0-1]))?$/,\r\n    cardtID: /(^\\d{15}$)|(^\\d{17}([0-9]|X)$)/,\r\n    vstring: /^([^\'<>]+)?$/,\r\n    tabledefine: /^([A-Za-z0-9|_]{1,18})?$/,\r\n    integer: /^(\\d*)$/,\r\n    ip: /^(([0-9]|[1-9][0-9]|1[0-9][0-9]|2[0-5][0-5])\\.([0-9]|[1-9][0-9]|1[0-9][0-9]|2[0-5][0-5])\\.([0-9]|[1-9][0-9]|1[0-9][0-9]|2[0-5][0-5])\\.([0-9]|[1-9][0-9]|1[0-9][0-9]|2[0-5][0-5]))?$/,\r\n    float: /^(([0-9]\\d?)($|(\\.\\d+$)))?$/,\r\n    postcode: /^(\\d{6})?$/,\r\n    isnull: /^.{1,}$/,\r\n    letterandint: /^[A-Za-z0-9]+$/\r\n};\r\n\r\n/*\r\n�������֤\r\n������\r\nthisControl:����Text���͵�Input�ؼ�\r\n*/\r\nfunction ValidateInput(ControlId, dataType, Message) {\r\n    var flag = true;\r\n    var isTypeData = null;\r\n\r\n    if (flag) {\r\n        isTypeData = ValidateType(ControlId, dataType, Message); //��ȡ���ͷ����ı�\r\n        if (isTypeData != null) {\r\n            SettingLableText(ControlId, isTypeData, \'red\', \'12px\');   //��ֵ����ʾ��\r\n            flag = false;\r\n        }\r\n    }\r\n    if (flag) {\r\n        SettingLableText(ControlId, \'\', \'red\', \'12px\');\r\n        InitializationStyle(ControlId); //��֤�ɹ�����ı���\r\n    }\r\n    else {\r\n        SettingInputStyle(ControlId, \'#FBE2E2\', \'1\', \'#D28C8C\', \'\', \'#D28C8C\'); //���ñ�����ı�����ʽ\r\n    }\r\n    return flag;\r\n}\r\n/*\r\n*function:������֤\r\n*Parameter��thisControl:����Text���͵�Input�ؼ�\r\n*Parameter��dataType:��֤������\r\n*Return��true Or false\r\n*/\r\nfunction Validate(thisControl, dataType) {\r\n    var validateValue = document.getElementById(thisControl).value;\r\n    switch (dataType) {\r\n        case typeEnumerate.phone:\r\n            return Regexp(validateValue, regexpEnumerate.phone) == \"\" ? true : false;\r\n            break;\r\n        case typeEnumerate.telphone:\r\n            return Regexp(validateValue, regexpEnumerate.telphone) == \"\" ? true : false;\r\n            break;\r\n        case typeEnumerate.email:\r\n            return Regexp(validateValue, regexpEnumerate.email) == \"\" ? true : false;\r\n            break;\r\n        case typeEnumerate.age:\r\n            return Regexp(validateValue, regexpEnumerate.age) == \"\" ? true : false;\r\n            break;\r\n        case typeEnumerate.birthday:\r\n            return Regexp(validateValue, regexpEnumerate.birthday) == \"\" ? true : false;\r\n            break;\r\n        case typeEnumerate.cardtID:\r\n            return Regexp(validateValue, regexpEnumerate.cardtID) == \"\" ? true : false;\r\n            break;\r\n        case typeEnumerate.vstring:\r\n            return Regexp(validateValue, regexpEnumerate.vstring) == \"\" ? true : false;\r\n            break;\r\n        case typeEnumerate.tabledefine:\r\n            return Regexp(validateValue, regexpEnumerate.tabledefine) == \"\" ? true : false;\r\n            break;\r\n        case typeEnumerate.integer:\r\n            return Regexp(validateValue, regexpEnumerate.integer) == \"\" ? true : false;\r\n            break;\r\n        case typeEnumerate.ip:\r\n            return Regexp(validateValue, regexpEnumerate.ip) == \"\" ? true : false;\r\n            break;\r\n        case typeEnumerate.float:\r\n            return Regexp(validateValue, regexpEnumerate.float) == \"\" ? true : false;\r\n            break;\r\n        case typeEnumerate.postcode:\r\n            return Regexp(validateValue, regexpEnumerate.postcode) == \"\" ? true : false;\r\n            break;\r\n        case typeEnumerate.isnull:\r\n            return Regexp(validateValue, regexpEnumerate.isnull) == \"\" ? true : false;\r\n            break;\r\n        case typeEnumerate.letterandint:\r\n            return Regexp(validateValue, regexpEnumerate.letterandint) == \"\" ? true : false;\r\n            break;\r\n    }\r\n};\r\n/*\r\n*function:������֤\r\n*Parameter��thisControl:����Text���͵�Input�ؼ�\r\n*Parameter��dataType:��֤������\r\n*Parameter��Message:���ص���Ϣͷ\r\n*Return��������Ϣ����ȷ��Ϣ\r\n*/\r\nfunction ValidateType(thisControl, dataType, Message) {\r\n    var typeMessage = true;\r\n    var errorMessage = null;\r\n    var validateValue = document.getElementById(thisControl).value;\r\n    switch (dataType) {\r\n        case typeEnumerate.phone:\r\n            return Regexp(validateValue, regexpEnumerate.phone) == \"\" ? succressEnumerate.phone : errorEnumerate.phone;\r\n            break;\r\n        case typeEnumerate.telphone:\r\n            return Regexp(validateValue, regexpEnumerate.telphone) == \"\" ? succressEnumerate.telphone : errorEnumerate.telphone;\r\n            break;\r\n        case typeEnumerate.email:\r\n            return Regexp(validateValue, regexpEnumerate.email) == \"\" ? succressEnumerate.email : errorEnumerate.email;\r\n            break;\r\n        case typeEnumerate.age:\r\n            return Regexp(validateValue, regexpEnumerate.age) == \"\" ? succressEnumerate.age : errorEnumerate.age;\r\n            break;\r\n        case typeEnumerate.birthday:\r\n            return Regexp(validateValue, regexpEnumerate.birthday) == \"\" ? succressEnumerate.birthday : errorEnumerate.birthday;\r\n            break;\r\n        case typeEnumerate.cardtID:\r\n            return Regexp(validateValue, regexpEnumerate.cardtID) == \"\" ? succressEnumerate.cardtID : errorEnumerate.cardtID;\r\n            break;\r\n        case typeEnumerate.vstring:\r\n            return Regexp(validateValue, regexpEnumerate.vstring) == \"\" ? succressEnumerate.vstring : errorEnumerate.vstring;\r\n            break;\r\n        case typeEnumerate.tabledefine:\r\n            return Regexp(validateValue, regexpEnumerate.tabledefine) == \"\" ? succressEnumerate.tabledefine : errorEnumerate.tabledefine;\r\n            break;\r\n        case typeEnumerate.integer:\r\n            return Regexp(validateValue, regexpEnumerate.integer) == \"\" ? succressEnumerate.integer : errorEnumerate.integer;\r\n            break;\r\n        case typeEnumerate.ip:\r\n            return Regexp(validateValue, regexpEnumerate.ip) == \"\" ? succressEnumerate.ip : errorEnumerate.ip;\r\n    