sControl:��ǰ��Ҫ�޸ĵĿؼ�\r\nlableColor:������ɫ\r\nlableFontSize:�����С\r\n*/\r\nfunction SettingLableStyle(thisControl, lableColor, lableFontSize) {\r\n    if (lableColor != \"\") {\r\n        thisControl.style.color = lableColor;\r\n    }\r\n\r\n    if (lableFontSize != \"\") {\r\n        thisControl.style.fontSize = lableFontSize;\r\n    }\r\n}\r\n\r\n/*\r\n��ʼ�������ı�\r\n������\r\nthisControl:��ǰ��Ҫ�޸ĵĿؼ�\r\n*/\r\nfunction InitializationStyle(thisControl) {\r\n    document.getElementById(thisControl).style.backgroundColor = \"\";\r\n    document.getElementById(thisControl).style.borderColor = \"\";\r\n    document.getElementById(thisControl).style.fontColor = \"\";\r\n    document.getElementById(thisControl).style.fontSize = \"\";\r\n}\r\n\r\n/*\r\nFrom�����֤\r\n������\r\nthisFrom:���뵱ǰ��From\r\n*/\r\nfunction getFrom(fromName) {\r\n    var fromInfo = document.getElementById(fromName).getElementsByTagName(\"input\");\r\n    var arrFlag = new ArrayList();\r\n    var flag = true;\r\n    for (var i = 0; i < fromInfo.length; i++) {\r\n        if (fromInfo[i].type == \"text\") {\r\n            if (!ValidateInput(fromInfo[i])) {\r\n                arrFlag.add(fromInfo[i].id, false);\r\n            }\r\n        }\r\n    }\r\n\r\n    for (var i = 0; i < arrFlag.content.length; i++) {\r\n        if (!arrFlag.content[i][1]) {\r\n            flag = false;\r\n        }\r\n    }\r\n    return flag;\r\n}\r\n\r\n\r\nfunction ArrayList() {\r\n    this.content = new Array();\r\n    this.add = function (key, value) {\r\n        this.content[this.content.length] = new Array(key, value);\r\n    }\r\n    this.show = function () {\r\n        for (var i = 0; i < this.content.length; i++) {\r\n            alert(this.content[i][0] + \",\" + this.content[i][1]);\r\n        }\r\n    }\r\n}\r\n\r\nfunction SettingLableText(thisControl, LableValue, lableColor, lableSize) {\r\n    var lableControl = document.getElementById(thisControl + \"_lable\");\r\n    if (lableControl != null) {\r\n       