INSERT INTO `txt` VALUES ('396', '2', 'JQquery.调试方法.debug.jun.有待改进.txt', '    function junalert(msg, time) {\r\n        time = time && time * 1000 || 2000;\r\n        if (typeof msg === \"string\") {\r\n            msg = msg.indexOf(\'|\') != -1 && \'<p>\' + msg.split(\'|\').join(\'</p><p>\') || \'<p>\' + msg;\r\n        }\r\n        if (typeof msg === \'object\') {\r\n            var msgs = new Array();\r\n            for (var p in msg) {\r\n                msgs.push(p + \':&nbsp;\' + msg[p]);\r\n            }\r\n            msg = \'<p>\' + msgs.join(\'</p><p>\');\r\n        }\r\n        $(\'body\').prepend(\'<div id=\"debug_dump\"></div>\');\r\n\r\n        $(\'div#debug_dump\')\r\n            .css({\r\n                position: \'absolute\',\r\n                backgroundColor: \'#000\',\r\n                opacity: \'0.65\',\r\n                top: \'0\',\r\n                left: \'0\',\r\n                width: $(\'body\').width(),\r\n                lineHeight: \'28px\',\r\n                zIndex: \'99999\',\r\n                textIndent: \'20px\',\r\n                color: \'#fff\'\r\n            })\r\n            .html(\'�������ǣ� \' + msg + \'</p>\')\r\n            .click(function () {\r\n                $(this).mouseout(function () {\r\n                    setTimeout(function () {\r\n                        $(\'div#debug_dump\').fadeOut(\'slow\').empty().remove();\r\n                    }, time);\r\n                });\r\n            });\r\n    };\r\n\r\n\r\n����\r\n  junalert(\"test\", 3);\r\n\r\n', '0', '2017-09-08 09:48:44', null, 'JQquery.调试方法.debug.jun.有待改进.txt');
