INSERT INTO `txt` VALUES ('407', '2', 'jquery.ajaxSubmit(异步提交表单).txt', 'Jquery �� ajaxSubmitʹ�ý���\r\n1 �����������ű�\r\n<script type=\"text/javascript\" src=\"/js/jquery/jquery.form.js\"></script>   //ajaxForm �����ű�\r\n<script type=\"text/javascript\" src=\"/js/jquery/jquery-1.8.0.min.js\"></script>\r\n<script type=\"text/javascript\" src=\"/js/jquery/jquery.easyui.min.js\"></script>\r\n2 ʹ�÷���\r\n<form name=\"testForm\" id=\"testForm\" >\r\n<input type=\"button\" value=\"submit\"/>\r\n</form>\r\n<script type=\"text/javascript\">\r\nvar ajax_option={\r\n	url:\"login\",//Ĭ����form action\r\n	success:function(data){\r\n	}\r\n}\r\n$(\'#testForm\').ajaxSubmit(ajax_option);\r\n</script>\r\n\r\n3 ajaxSubmit ��ajaxForm����\r\najaxForm�����ύ�������document��ready�����У�ʹ��ajaxForm��ΪAJAX�ύ�������׼�����ύ����������submit��ʼ \r\najaxSubmit ������AJAX���ύ�������������κ�����½��и����ύ�� \r\n4\r\noption�Ĳ��� \r\nvar options = {    \r\n       target:        \'#output1\',   // target element(s) to be updated with server response    \r\n       beforeSubmit:  showRequest,  // pre-submit callback    \r\n       success:       showResponse  // post-submit callback    \r\n       // other available options:    \r\n       //url:       url         // override for form\'s \'action\' attribute    \r\n       //type:      type        // \'get\' or \'post\', override for form\'s \'method\' attribute    \r\n       //dataType:  null        // \'xml\', \'script\', or \'json\' (expected server response type)    \r\n       //clearForm: true        // clear all form fields after successful submit    \r\n       //resetForm: true        // reset the form after successful submit    \r\n       // $.ajax options can be used here too, for example:    \r\n       //timeout:   3000    \r\n   }; \r\n', '0', '2017-09-08 09:48:44', null, 'jquery.ajaxSubmit(异步提交表单).txt');
