INSERT INTO `txt` VALUES ('409', '2', 'jquery.autocomplete.career.candidatesystem.jun.txt', ' =========================================\r\n <script src=\"/Script/jquery.js\" type=\"text/css\"></script>\r\n     <script src=\"/Scripts/jquery-autocomplete/jquery.autocomplete.js\" type=\"text/javascript\"></script>\r\n    <link href=\"/Scripts/jquery-autocomplete/jquery.autocomplete.css\" rel=\"stylesheet\" type=\"text/css\" />\r\n	\r\n $(\"#LatestEducation\").focus().autocomplete(\"/candidate/AutoEducation\", {\r\n            multiple: false,   //是否允许搜索框追加\r\n            dataType: \"json\", //json类型\r\n            matchContains: true,\r\n            autoFill: false,\r\n            delay: 1000,\r\n            parse: function (data) {\r\n                return $.map(data, function (row) {\r\n                    return {\r\n                        data: row,\r\n                        value: row.ChineseName,\r\n                        result: row.ChineseName\r\n                    }\r\n                });\r\n            },\r\n            formatItem: function (item) {\r\n                return item.ChineseName;\r\n            },\r\n            formatResult: function (row, i, max) {\r\n                return row.ChineseName;\r\n            },\r\n            formatMatch: function (row, i, max) {\r\n                return row.ChineseName;\r\n            }\r\n        }).result(function (e, item) {\r\n            alert(item.ChineseName);\r\n        });\r\n		\r\n========================后台=============================\r\n [HttpGet]\r\n        public JsonResult AutoEducation(string q)\r\n        {\r\n            string re = Server.UrlDecode(q);\r\n\r\n            CVDictionaryManager bll = CVDictionaryManager.GetInstance();\r\n            List<CVDictionary> list = bll.GetDictiontry(CVEnum.DictionaryType.degree.ToString(),re);\r\n            JsonResult json = new JsonResult();\r\n            json.Data = list;\r\n            json.JsonRequestBehavior = JsonRequestBehavior.AllowGet;//允