INSERT INTO `txt` VALUES ('410', '2', 'JQuery.blinkTitle.标题闪烁.新消息.message.txt', '<!DOCTYPE HTML PUBLIC \"-//W3C//DTD XHTML 1.0 Strict//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n    <meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\" />\r\n    <title>����Ϣ��˸��ʾDemo</title>\r\n</head>\r\n<body>\r\n    <p style=\"text-align:center;\">\r\n        �뿴��ҳ���⴦Ч����\r\n        <br />��10�����ʾ��ʧ\r\n    </p>\r\n\r\n    <script src=\"js/jquery-1.8.2.js\"></script>\r\n    <script type=\"text/javascript\">\r\n        ; (function ($) {\r\n\r\n            $.extend({\r\n                /**\r\n                 * ���÷����� var timerArr = $.blinkTitle.show();\r\n                 *   $.blinkTitle.clear(timerArr);\r\n                 */\r\n                blinkTitle: {\r\n                    show: function () { //������Ϣʱ��title����˸��ʾ\r\n                        var step = 0, _title = document.title;\r\n\r\n                        var timer = setInterval(function () {\r\n                            step++;\r\n                            if (step == 3) { step = 1 };\r\n                            if (step == 1) { document.title = \"����������\" + _title };\r\n                            if (step == 2) { document.title = \"������Ϣ��\" + _title };\r\n                        }, 500);\r\n\r\n                        return [timer, _title];\r\n                    },\r\n\r\n                    /**\r\n                     * @param timerArr[0], timer���\r\n                     * @param timerArr[1], ��ʼ��title�ı�����\r\n                     */\r\n                    clear: function (timerArr) { //ȥ����˸��ʾ���ָ���ʼtitle�ı�\r\n                        if (timerArr) {\r\n                            clearInterval(timerArr[0]);\r\n                            document.title = timerArr[1];\r\n                        };\r\n                    }\r\n                }\r\n            });\r\n        })(jQuery);\r\n\r\n        jQuery(function ($) {\r\n            var timerArr = $.blinkTitle.show();\r\n\r\n            setTimeout(function () {  //�˴��ǹ�һ��ʱ����Զ���ʧ\r\n                $.blinkTitle.clear(timerArr);\r\n            }, 10000);\r\n\r\n            //����Ϊ������ʧ��ֻ����˵��ã� $.blinkTitle.clear(timerArr);\r\n        });\r\n    </script>\r\n</body>\r\n</html>\r\n', '0', '2017-09-08 09:48:44', null, 'JQuery.blinkTitle.标题闪烁.新消息.message.txt');
