INSERT INTO `txt` VALUES ('414', '2', 'jquery.easy.ui.dialog.jun.txt', '在列表页面点击一条信息，查看详情页面details.aspx  \r\n\r\n<!--Details.Begin-->\r\n    <div id=\"details\" class=\"easyui-dialog\" title=\"职位详情\" closed=\"true\" style=\"width: 1000px; height: 500px;\">\r\n    </div>\r\n    <!--Details.End-->\r\n	\r\n	    //职位详情\r\n        function Details(uid) {\r\n            var href = \'details.aspx?r=\' + new Date().getTime() + \'&uid=\' + uid + \'\'\r\n            $(\"#details\").dialog({\r\n                title: \"职位详情\",\r\n                modal: true,\r\n                iconCls: \'icon-ok\',\r\n                draggable: true,\r\n                resizable: true,\r\n                href: href,\r\n                buttons: [{\r\n                    text: \'确定\',\r\n                    iconCls: \'icon-ok\',\r\n                    handler: function () {\r\n                        $(\'#details\').dialog(\'close\');\r\n                    }\r\n                }, {\r\n                    text: \'关闭\',\r\n                    handler: function () {\r\n                        $(\'#details\').dialog(\'close\');\r\n                    }\r\n                }],\r\n                onOpen: function () {//如果不加这个东东，只能加载第一个href,以后都不会走服务器控件 了，2014年3月21日纠结了半天才弄出来的\r\n                    $(\'#details\').dialog(\'refresh\', href);\r\n                }\r\n            });\r\n            $(\"#details\").dialog(\"open\");\r\n        }\r\n', '0', '2017-09-08 09:48:44', null, 'jquery.easy.ui.dialog.jun.txt');
