INSERT INTO `txt` VALUES ('416', '2', 'jQuery.easyui.datagrid.jun.txt', '自己使用jquery easyui  的datagrid\r\n\r\n <!--列表 start-->\r\n    <form id=\"form_list\" name=\"form_list\" method=\"post\" action=\"\">\r\n        <table id=\"tab_list\">\r\n        </table>\r\n    </form>\r\n    <!--列表 end-->\r\n<script type=\"text/javascript\">\r\n  $(function () {\r\n            InitGird();\r\n        });\r\n  /**\r\n       *初始化表格\r\n       */\r\n        function InitGird() {\r\n            $(\'#tab_list\').datagrid({\r\n                title: \'悬赏职位\', //表格标题\r\n                nowrap: true,//true把数据显示在一行上。设置为true能提高加载性能。\r\n                url: \'/Ajax/BountyHandler.aspx\', //请求数据的页面\r\n                loadMsg: \'正在加载中,请稍后...\',//当加载数据从远程站点,显示一个提示信息。\r\n                singleSelect: false,//True允许选择只有一行。\r\n                checkOnSelect: false,//如果true,复选框checked/ unchecked当用户单击一个行。如果为false,复选框只是checked /unchecked当用户单击正是对复选框。\r\n                selectOnCheck:false,//如果设置为true,点击复选框将永远选择行。如果false选择一个行不会检查复选框。\r\n                sortName: \'JSON_sid\', //排序字段\r\n                idField: \'JSON_sid\', //标识字段,主键\r\n                iconCls: \'\', //标题左边的图标\r\n                width: \'80%\', //宽度  \r\n                height: $(parent.document).find(\"#mainPanle\").height() - 10 > 0 ? $(parent.document).find(\"#mainPanle\").height() - 10 : 500, //高度\r\n                nowrap: false, //是否换行，True 就会把数据显示在一行里\r\n                striped: true, //True 奇偶行使用不同背景色\r\n                collapsible: false, //可折叠\r\n                sortOrder: \'desc\', //排序类型\r\n                remoteSort: true, //定义是否从服务器给数据排序\r\n                frozenColumns: [[//冻结的列，不会随横向滚动轴移动\r\n	                { field: \'cbx\', checkbox: true }\r\n                ]],\r\n                columns: [[\r\n                    {\r\n                        title: \'职位名称\', field: \'JSON_title\', width: 120, formatter: function (value, rowData,rowIndex) {\r\n                            return \'<a href=\"javascript:;\" onclick=\"Details(\' + rowData.JSON_sid + \')\">\' + value + \'</a>\';\r\n                        }\r\n                    },\r\n                    { title: \'工作地点\', field: \'JSON_location_state\', width: 150 },\r\n                    { title: \'职位类型\', field: \'JSON_employmenttype\', width: 120 },\r\n                    { title: \'CI标准职位\', field: \'JSON_ci\', width: 100 },\r\n                    { title: \'预计收费金额\', field: \'JSON_jdid\', width: 150 },\r\n                    { title: \'发布日期\', field: \'JSON_activation_date\', width: 150 },\r\n                    {\r\n                        title: \'操作\', field: \'JSON_sid\', width: 80, formatter: function (value, rec) {\r\n                            return \'<a style=\"color:red\" href=\"javascript:;\" onclick=\"Details(\' + value + \');$(this).parent().click();return false;\">详情</a>\';\r\n                        }\r\n                    }\r\n                ]],\r\n                toolbar: \"#tab_toolbar\",\r\n                queryParams: { \"action\": \"query\" },\r\n                pagination: true, //是否开启分页\r\n                pageNumber: 1, //默认索引页\r\n                pageSize: 10, //默认一页数据条数\r\n                pagePosition: \'bottom\',//定义分页工具栏位置，可选参数有: \'top\',\'bottom\',\'both\'.\r\n                rownumbers: true //行号\r\n            });\r\n        }\r\n</script>\r\n\r\n\r\n\r\n处理请求的服务器代码：\r\n\r\nusing System;\r\nusing System.Web;\r\nusing System.Data;\r\nusing Utility;\r\nusing Post;\r\nusing System.Web.Security;\r\nusing System.Text;\r\nusing Newtonsoft.Json;//解析json\r\nusing System.IO;\r\n\r\nnamespace WikEasyUIDemo.Ajax\r\n{\r\n    public partial class BountyHandler : System.Web.UI.Page\r\n    {\r\n        Post.BLL.User bll = new Post.BLL.User();\r\n        protected void Page_Load(object sender, EventArgs e)\r\n        {\r\n            string action = \"\";\r\n\r\n            if (Request[\"action\"] != \"\")\r\n                action = Request[\"action\"];\r\n            string flag = string.Empty;\r\n\r\n            switch (action)\r\n            {\r\n                case \"query\"://查询数据\r\n                    flag = QueryData();\r\n                    break;\r\n                case \"register\"://注册\r\n                    flag = Register();\r\n                    break;\r\n                case \"login\"://登录\r\n                    flag = Login();\r\n                    break;\r\n                case \"loginout\"://注销\r\n                    flag = Loginout();\r\n                    break;\r\n                case \"islogin\"://是否登录\r\n                    flag = Islogin();\r\n                    break;\r\n                default:\r\n                    break;\r\n            }\r\n            Response.Clear();\r\n            Response.Write(flag);\r\n            Response.End();\r\n        }\r\n\r\n        /// <summary>\r\n        /// 是否登录\r\n        /// </summary>\r\n        /// <returns></returns>\r\n        public string Islogin()\r\n        {\r\n            if (Session[\"login_email\"] != null)\r\n            {\r\n                string email = Session[\"login_email\"].GetString();\r\n                return LoginStateJson(\"yes\", email);\r\n            }\r\n            else\r\n            {\r\n                return LoginStateJson(\"no\", \"\");\r\n            }\r\n        }\r\n        /// <summary>\r\n        /// 注销\r\n        /// </summary>\r\n        private string Loginout()\r\n        {\r\n            HttpCookie cookie = Request.Cookies[FormsAuthentication.FormsCookieName];\r\n            if (cookie != null)\r\n            {\r\n                FormsAuthentication.SignOut();\r\n            }\r\n            Request.Cookies.Clear();\r\n            HttpContext.Current.Response.Cookies[FormsAuthentication.FormsCookieName].Value = null;\r\n            HttpContext.Current.Response.Cookies[FormsAuthentication.FormsCookieName].Expires = DateTime.Now.AddYears(-100);\r\n            if (HttpContext.Current.Session != null)\r\n            {\r\n                HttpContext.Current.Session.Abandon();\r\n            }\r\n            return \"yes\";\r\n        }\r\n\r\n        /// <summary>\r\n        /// 登录\r\n        /// </summary>\r\n        /// <param name=\" \"></param>\r\n        public string Login()\r\n        {\r\n            try\r\n            {\r\n                string email = Request[\"login_email\"].GetString();\r\n                string pwd = Request[\"login_pwd\"].GetString().MD5();\r\n                Post.BLL.RewardUserBLL userbll = new Post.BLL.RewardUserBLL();\r\n                int flag = userbll.Login(email, pwd);\r\n\r\n                if (flag > 0)\r\n                {\r\n                    Session[\"login_email\"] = email;\r\n                    return LoginStateJson(\"yes\", email);\r\n                }\r\n                else\r\n                {\r\n                    return LoginStateJson(\"no\", \"\");\r\n                }\r\n            }\r\n            catch (Exception e)\r\n            {\r\n                Log.Error(\"登录异常\", e);\r\n                return LoginStateJson(\"error\", \"\");\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 登录状态json\r\n        /// </summary>\r\n        /// <param name=\"state\"></param>\r\n        /// <param name=\"user\"></param>\r\n        /// <returns></returns>\r\n        private static string LoginStateJson(string state, string user)\r\n        {\r\n            StringWriter sw = new StringWriter();\r\n            using (JsonWriter writer = new JsonTextWriter(sw))\r\n            {\r\n                writer.Formatting = Formatting.Indented;\r\n                writer.WriteStartObject();\r\n                writer.WritePropertyName(\"state\");\r\n                writer.WriteValue(state);\r\n                writer.WritePropertyName(\"user\");\r\n                writer.WriteValue(user);\r\n                writer.WriteEndObject();\r\n                writer.Flush();\r\n                sw.Close();\r\n            }\r\n            return sw.GetStringBuilder().ToString();\r\n        }\r\n\r\n        /// <summary>\r\n        /// 注册\r\n        /// </summary>\r\n        /// <param name=\" \"></param>\r\n        private string Register()\r\n        {\r\n            //验证验证码\r\n            //waterMark\r\n            string checkCode = Session[\"LoginCode\"].GetString().ToLower();\r\n            string waterMark = Request[\"waterMark\"].GetString().ToLower();\r\n            if (!string.IsNullOrWhiteSpace(waterMark) & (waterMark == checkCode))\r\n            {\r\n                Post.BLL.RewardUserBLL userbll = new Post.BLL.RewardUserBLL();\r\n                Post.Model.RewardUserModel model = new Post.Model.RewardUserModel();\r\n                string email = Request[\"ipt_email\"].GetString();\r\n                if (userbll.ExistEmail(email) > 0)\r\n                {\r\n                    return LoginStateJson(\"repeat\", email);\r\n                }\r\n                else\r\n                {\r\n                    model.Email = email;\r\n                    model.Pwd = Request[\"ipt_pwd\"].GetString().MD5();\r\n                    model.Mobile = Request[\"ipt_mobile\"].GetString();\r\n                    model.BankNo = Request[\"ipt_bank\"].GetString();\r\n                    int uid = userbll.Add(model);\r\n                    if (uid > 0)\r\n                    {\r\n                        Session[\"login_email\"] = email;//todo\r\n                        return LoginStateJson(\"yes\", email);\r\n                    }\r\n                    else\r\n                    {\r\n                        return LoginStateJson(\"no\", \"\");\r\n                    }\r\n                }\r\n            }\r\n            else\r\n            {\r\n                return LoginStateJson(\"codeerror\", \"\");\r\n            }\r\n        }\r\n\r\n\r\n\r\n        /// <summary>\r\n        /// 查询数据\r\n        /// </summary>\r\n        private string QueryData()\r\n        {\r\n            string sessionId = Session[\"login_email\"].GetString();\r\n            int page = Request.Form[\"page\"] != \"\" ? Convert.ToInt32(Request.Form[\"page\"]) : 0;\r\n            int size = Request.Form[\"rows\"] != \"\" ? Convert.ToInt32(Request.Form[\"rows\"]) : 0;\r\n            string sort = Request.Form[\"sort\"] != \"\" ? Request.Form[\"sort\"] : \"\";\r\n            string order = Request.Form[\"order\"] != \"\" ? Request.Form[\"order\"] : \"\";\r\n            if (page < 1)\r\n            {\r\n                return \"\";\r\n            }\r\n            else\r\n            {\r\n                string orderField = sort.Replace(\"JSON_\", \"\");\r\n                Post.BLL.RewardPostBLL postbll = new Post.BLL.RewardPostBLL();\r\n                DataTable dt = postbll.GetPagedList(size, page);\r\n                int count = postbll.GetPagedCount();\r\n                string strJSON = JsonHelper.CreateJsonParameters(dt, true, count);\r\n                return strJSON;\r\n            }\r\n        }\r\n\r\n    }\r\n}\r\n', '0', '2017-09-08 09:48:44', null, 'jQuery.easyui.datagrid.jun.txt');
