       intro: \"��ѡ�˹���ҳ�棬������������ά����ѡ�˵���Ϣ���Ժ�ѡ�˴��ǩ�������͵�ְλ�Ȳ���\",\r\n                        position: \"right\"\r\n                    },\r\n                {\r\n                    //���Ĳ�����\r\n                    element: \" [data-intro=\'mycandidate4\']\",\r\n                    intro: \"ְλ����ҳ�棬������ά�����Լ���ְλ��Ϣ��������ѡ�ˣ��鿴����ְλ����ƥ������ĺ�ѡ�ˡ�\",\r\n                    position: \"right\"\r\n                }, {\r\n                    //���岽����\r\n                    element: \" [data-intro=\'mycandidate5\']\",\r\n                    intro: \"��������ҳ�棬�����������ϼ�¼�ճ����ѡ�\",\r\n                    position: \"left\"\r\n                }, {\r\n                    //����������\r\n                    element: \" [data-intro=\'mycandidate6\']\",\r\n                    intro: \"����ͳ�ƣ��������ڸ�ҳ��鿴��ѡ�˺�ְλ�ĸ��ֱ��档\",\r\n                    position: \"left\"\r\n                }\r\n                ]\r\n                }).oncomplete(function() {\r\n                                     //���������ť��ִ�е��¼�(���ﱣ���Ӧ�İ汾�ŵ�cookie,����������Ч��Ϊ30�죩\r\n                $.cookie(\"intro_cookie_index\", cur_val, { expires: 30\r\n                });\r\n            }).onexit(function () {\r\n                    //���������ť�� ִ�е��¼�\r\n                $.cookie(\"intro_cookie_index\", cur_val, { expires: 30\r\n                });\r\n            }).start();\r\n                    }\r\n$(function(){\r\nintro();\r\n});\r\n\r\n</script>\r\n<div data-intro=\"mycandidate1\">\r\n</div>\r\n<div data-intro=\"mycandidate2\">\r\n</div>v\r\n<div data-intro=\"mycandidate3\">\r\n</div>\r\n<div data-intro=\"mycandidate4\">\r\n</div>\r\n<div data-intro=\"mycandidate5\">\r\n</div>\r\n<div data-intro=\"mycandidate6\">\r\n</div>\r\n', '0', '2017-09-08 09:48:44', null, 'Jquery.intro（ 页面引导简单用法 分享）.txt');
INSERT INTO `txt` VALUES ('422', '2', 'jquery.js.javascript.picture.hover图片展示.鼠标移上去显示大图片.txt', '<!DOCTYPE html>\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n    <meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\" />\r\n    <title></title>\r\n    <style type=\"text/css\">\r\n        .tooltip {\r\n            width:500px;\r\n            height:600px;\r\n            border:1px solid red;\r\n            background-color:gray;\r\n            position:absolute;\r\n        }\r\n    </style>\r\n    <script src=\"Scripts/jquery-1.7.1.js\"></script>\r\n    <script type=\"text/javascript\">\r\n\r\n        var imgs = {\r\n            \"01.jpg\": { \"src\": \"images/01.jpg\", \"title\": \"图片1\" },\r\n            \"02.jpg\": { \"src\": \"images/02.jpg\", \"title\": \"图片2\" },\r\n            \"03.jpg\": { \"src\": \"images/03.jpg\", \"title\": \"图片3\" }\r\n        };\r\n        $(function () {\r\n            $.each(imgs, function () {\r\n                var img = $(\"<img width=\'200px\'/>\");\r\n                img.attr(\"src\", this.src);\r\n                img.css(\"margin-left\", \"10px\");\r\n                img.attr(\"title\",this.title);\r\n                $(\"#imgs\").append(img);\r\n                //生成详细层\r\n                img.hover(function (e) {\r\n                    var $div = $(\"<div id=\'d1\' class=\'tooltip\'></div>\");\r\n                    $div.html(\"<p>\" + $(this).attr(\"title\") + \"</p>\");\r\n                    $div.css(\"top\",e.pageY).css(\"left\",e.pageX);\r\n                    var $bigImg = $(\"<img src=\'\" + $(this).attr(\"src\") + \"\' width=\'400px\'/>\");\r\n                    $div.append($bigImg);\r\n                    $(\"#imgs\").append($div);\r\n                }, function () {\r\n                    var $d = $(\"#d1\");\r\n                    if ($d.length>0) {\r\n                        $d.remove();\r\n                    }\r\n                });\r\n            });\r\n        });\r\n\r\n    </script>\r\n</head>\r\n<body>\r\n    <div id=\"imgs\"></div>\r\n</body>\r\n</html>\r\n', '0', '2017-09-08 09:48:44', null, 'jquery.js.javascript.picture.hover图片展示.鼠标移上去显示大图片.txt');
INSERT INTO `txt` VALUES ('423', '2', 'jquery.js.javascript.picture.hover图片展示.鼠标移上去显示大图片.占位符.jun改良.txt', '<!DOCTYPE html>\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n    <meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\" />\r\n    <title></title>\r\n    <style type=\"text/css\">\r\n        .tooltip {\r\n            width: 500px;\r\n            height: 600px;\r\n            border: 1px solid red;\r\n            background-color: gray;\r\n            position: absolute;\r\n        }\r\n    </style>\r\n    <script src=\"Scripts/jquery-1.7.1.js\"></script>\r\n\r\n\r\n    <script type=\"text/javascript\">\r\n        /**\r\n        *js实现用自串替换占位符{0} {1} {2} 等\r\n        *用法：\"http://{0}/{1}/{2}\".format(\"www.jd.com\", \"web\", \"abc.htm\")\r\n        *输出：\"http://www.jd.com/web/abc.htm\"\r\n        */\r\n        String.prototype.format = function () {\r\n            if (arguments.length == 0) return this;\r\n            for (var s = this, i = 0; i < arguments.length; i++)\r\n                s = s.replace(new RegExp(\"\\\\{\" + i + \"\\\\}\", \"g\"), arguments[i]);\r\n            return s;\r\n        };\r\n    </script>\r\n    <script type=\"text/javascript\">\r\n        //图片源json\r\n        var imgs = {\r\n            \"01.jpg\": { \"src\": \"images/01.jpg\", \"title\": \"图片1\" },\r\n            \"02.jpg\": { \"src\": \"images/02.jpg\", \"title\": \"图片2\" },\r\n            \"03.jpg\": { \"src\": \"images/03.jpg\", \"title\": \"图片3\" }\r\n        };\r\n        $(function () {\r\n            //遍历图片json源\r\n            $.each(imgs, function () {\r\n                var src = this.src;\r\n                var title = this.title;\r\n                var $img = $(\"<img width=\'200px\' src=\'{0}\' margin-left:\'10px\' title=\'{1}\'/>\".format(src, title));\r\n                $(\"#imgs\").append($img);\r\n                //生成详细层\r\n                $img.hover(function (e) {\r\n                    var divBig = \"<div id=\'d1\' class=\'tooltip\' style=\'top:{0}px;left:{1}px\'><p>{2}</p><img src=\'{3}\' width=\'400px;\'/></div>\".format(e.pageY, e.pageX, title, src);\r\n                    $(\"#imgs\").append(divBig);\r\n                }, function () {\r\n                    var divBig = $(\"#d1\");\r\n                    if (divBig.length > 0) {\r\n                        divBig.remove();\r\n                    }\r\n                });\r\n            });\r\n        });\r\n\r\n    </script>\r\n</head>\r\n<body>\r\n    <div id=\"imgs\"></div>\r\n    <div style=\"top: 10px; left: 25px;\"></div>\r\n</body>\r\n</html>\r\n', '0', '2017-09-08 09:48:44', null, 'jquery.js.javascript.picture.hover图片展示.鼠标移上去显示大图片.占位符.jun改良.txt');
