INSERT INTO `txt` VALUES ('425', '2', 'jquery.raty.评分.默认参数.txt', '$.fn.raty.defaults = {\r\n    		cancel			: false,\r\n    		cancelHint		: \'cancel this rating!\',\r\n    		cancelOff		: \'cancel-off.png\',\r\n    		cancelOn		: \'cancel-on.png\',\r\n    		cancelPlace		: \'left\',\r\n    		click			: undefined,\r\n    		half			: false,\r\n    		halfShow		: true,\r\n    		hints			: [\'bad\', \'poor\', \'regular\', \'good\', \'gorgeous\'],\r\n    		iconRange		: undefined,\r\n    		mouseover		: undefined,\r\n    		noRatedMsg		: \'not rated yet\',\r\n    		number			: 5,\r\n    		path			: \'img/\',\r\n    		precision		: false,\r\n    		round			: { down: .25, full: .6, up: .76 },\r\n    		readOnly		: false,\r\n    		score			: undefined,\r\n    		scoreName		: \'score\',\r\n    		single			: false,\r\n    		size			: 16,\r\n    		space			: true,\r\n    		starHalf		: \'star-half.png\',\r\n    		starOff			: \'star-off.png\',\r\n    		starOn			: \'star-on.png\',\r\n    		target			: undefined,\r\n    		targetFormat	: \'{score}\',\r\n    		targetKeep		: false,\r\n    		targetText		: \'\',\r\n    		targetType		: \'hint\',\r\n    		width			: undefined\r\n	};\r\n', '0', '2017-09-08 09:49:06', null, 'jquery.raty.评分.默认参数.txt');
INSERT INTO `txt` VALUES ('426', '2', 'jquery.Validate 验证总结.jun.txt', 'jquery.Validate 验证总结.jun\r\n1.引用\r\n<script src=\"/jquery.js\" type=\"text/javascript\"></script>\r\n<script src=\"/jquery.validate.js\" type=\"text/javascript\"></script>\r\n<script src=\"/jquery.metadata.js\" type=\"text/javascript\"></script>\r\n2.使用\r\n    <script type=\"text/javascript\">\r\n   $(document).ready(function () {\r\n            $(\"form\").validate();\r\n        });\r\n	</script>\r\n	<form class=\"cmxform\" id=\"commentForm\" method=\"post\" action=\"\">\r\n	<input id=\"cemail\" name=\"email\" class=\"{validate:{required:true, email:true, messages:{required:\'Please enter your email address\', email:\'Please enter a valid email address\'}}}\"/>\r\n	<input class=\"submit\" type=\"submit\" value=\"Submit\"/>\r\n    </form>\r\n	\r\n	\r\n	//messages可以写到一个js里(message.js)，class里就不用写messages了\r\njQuery.extend(jQuery.validator.messages, {\r\n    required: \"不能为空\",\r\n    remote: \"请修正该字段\",\r\n    email: \"请输入正确格式的电子邮件\",\r\n    url: \"请输入合法的网址\",\r\n    date: \"请输入合法的日期\",\r\n    dateISO: \"请输入合法的日期 (ISO).\",\r\n    number: \"请输入合法的数字\",\r\n    digits: \"只能输入整数\",\r\n    creditcard: \"请输入合法的信用卡号\",\r\n    equalTo: \"请再次输入相同的值\",\r\n    accept: \"请输入拥有合法后缀名的字符串\",\r\n    maxlength: jQuery.validator.format(\"请输入一个 长度最多是 {0} 的字符串\"),\r\n    minlength: jQuery.validator.format(\"请输入一个 长度最少是 {0} 的字符串\"),\r\n    rangelength: jQuery.validator.format(\"请输入 一个长度介于 {0} 和 {1} 之间的字符串\"),\r\n    range: jQuery.validator.format(\"请输入一个介于 {0} 和 {1} 之间的值\"),\r\n    max: jQuery.validator.format(\"请输入一个最大为{0} 的值\"),\r\n    min: jQuery.validator.format(\"请输入一个最小为{0} 的值\")\r\n});\r\n	\r\n3.修改提示样式\r\n\r\n<style type=\"text/css\">\r\n*\r\n{\r\nfont-size: 14px;\r\n}\r\n#signupForm label.error\r\n{\r\ncolor:Red;\r\nfont-size:13px;\r\nmargin-left:5px;\r\npadding-left:16px;\r\nbackground:url(\"error.png\") left no-repeat;\r\n}\r\n</style> \r\n', '0', '2017-09-08 09:49:06', null, 'jquery.Validate 验证总结.jun.txt');
INSERT INTO `txt` VALUES ('427', '2', 'jquery.validate.extendmethod.js.自己写的扩展验证方便.jun.txt', '$(function () {\r\n    // 判断整数value是否等于0 \r\n    jQuery.validator.addMethod(\"isIntEqZero\", function (value, element) {\r\n        value = parseInt(value);\r\n        return this.optional(element) || value == 0;\r\n    }, \"整数必须为0\");\r\n\r\n    // 判断整数value是否大于0\r\n    jQuery.validator.addMethod(\"isIntGtZero\", function (value, element) {\r\n        value = parseInt(value);\r\n        return this.optional(element) || value > 0;\r\n    }, \"整数必须大于0\");\r\n\r\n    // 判断整数value是否大于或等于0\r\n    jQuery.validator.addMethod(\"isIntGteZero\", function (value, element) {\r\n        value = parseInt(value);\r\n        return this.optional(element) || value >= 0;\r\n    }, \"整数必须大于或等于0\");\r\n\r\n    // 判断整数value是否不等于0 \r\n    jQuery.validator.addMethod(\"isIntNEqZero\", function (value, element) {\r\n        value = parseInt(value);\r\n        return this.optional(element) || value != 0;\r\n    }, \"整数必须不等于0\");\r\n\r\n    // 判断整数value是否小于0 \r\n    jQuery.validator.addMethod(\"isIntLtZero\", function (value, element) {\r\n        value = parseInt(value);\r\n        return this.optional(element) || value < 0;\r\n    }, \"整数必须小于0\");\r\n\r\n    // 判断整数value是否小于或等于0 \r\n    jQuery.validator.addMethod(\"isIntLteZero\", function (value, element) {\r\n        value = parseInt(value);\r\n        return this.optional(element) || value <= 0;\r\n    }, \"整数必须小于或等于0\");\r\n\r\n    // 判断浮点数value是否等于0 \r\n    jQuery.validator.addMethod(\"isFloatEqZero\", function (value, element) {\r\n        value = parseFloat(value);\r\n        return this.optional(element) || value == 0;\r\n    }, \"浮点数必须为0\");\r\n\r\n    // 判断浮点数value是否大于0\r\n    jQuery.validator.addMethod(\"isFloatGtZero\", function (value, element) {\r\n        value = parseFloat(value);\r\n        return this.optional(element) || value > 0;\r\n    }, \"浮点数必须大于0\");\r\n\r\n    // 判断浮点数value是否大于或等于0\r\n    jQuery.validator.addMethod(\"isFloatGteZero\", function (value, element) {\r\n        value = parseFloat(value);\r\n        return this.optional(element) || value >= 0;\r\n    }, \"浮点数必须大于或等于0\");\r\n\r\n    // 判断浮点数value是否不等于0 \r\n    jQuery.validator.addMethod(\"isFloatNEqZero\", function (value, element) {\r\n        value = parseFloat(value);\r\n        return this.optional(element) || value != 0;\r\n    }, \"浮点数必须不等于0\");\r\n\r\n    // 判断浮点数value是否小于0 \r\n    jQuery.validator.addMethod(\"isFloatLtZero\", function (value, element) {\r\n        value = parseFloat(value);\r\n        return this.optional(element) || value < 0;\r\n    }, \"浮点数必须小于0\");\r\n\r\n    // 判断浮点数value是否小于或等于0 \r\n    jQuery.validator.addMethod(\"isFloatLteZero\", function (value, element) {\r\n        value = parseFloat(value);\r\n        return this.optional(element) || value <= 0;\r\n    }, \"浮点数必须小于或等于0\");\r\n\r\n    // 判断浮点型  \r\n    jQuery.validator.addMethod(\"isFloat\", function (value, element) {\r\n        return this.optional(element) || /^[-\\+]?\\d+(\\.\\d+)?$/.test(value);\r\n    }, \"只能包含数字、小数点等字符\");\r\n\r\n    // 匹配integer\r\n    jQuery.validator.addMethod(\"isInteger\", function (value, element) {\r\n        return this.optional(element) || (/^[-\\+]?\\d+$/.test(value) && parseInt(value) >= 0);\r\n    }, \"匹配integer\");\r\n\r\n    // 判断数值类型，包括整数和浮点数\r\n    jQuery.validator.addMethod(\"isNumber\", function (value, element) {\r\n        return this.optional(element) || /^[-\\+]?\\d+$/.test(value) || /^[-\\+]?\\d+(\\.\\d+)?$/.test(value);\r\n    }, \"匹配数值类型，包括整数和浮点数\");\r\n\r\n    // 只能输入[0-9]数字\r\n    jQuery.validator.addMethod(\"isDigits\", function (value, element) {\r\n        return this.optional(element) || /^\\d+$/.test(value);\r\n    }, \"只能输入0-9数字\");\r\n\r\n    // 判断中文字符 \r\n    jQuery.validator.addMethod(\"isChinese\", function (value, element) {\r\n        return this.optional(element) || /^[\\u0391-\\uFFE5]+$/.test(value);\r\n    }, \"只能包含中文字符。\");\r\n\r\n    // 判断英文字符 \r\n    jQuery.validator.addMethod(\"isEnglish\", function (value, element) {\r\n        return this.optional(element) || /^[A-Za-z]+$/.test(value);\r\n    }, \"只能包含英文字符。\");\r\n\r\n    // 手机号码验证    \r\n    jQuery.validator.addMethod(\"isMobile\", function (value, element) {\r\n        var length = value.length;\r\n        return this.optional(element) || (length == 11 && /^(((13[0-9]{1})|(15[0-9]{1})|(18[0-9]{1}))+\\d{8})$/.test(value));\r\n    }, \"请正确填写您的手机号码。\");\r\n\r\n    // 电话号码验证    \r\n    jQuery.validator.addMethod(\"isPhone\", function (value, element) {\r\n        var tel = /^(\\d{3,4}-?)?\\d{7,9}$/g;\r\n        return this.optional(element) || (tel.test(value));\r\n    }, \"请正确填写您的电话号码。\");\r\n\r\n    // 联系电话(手机/电话皆可)验证   \r\n    jQuery.validator.addMethod(\"isTel\", function (value, element) {\r\n        var length = value.length;\r\n        var mobile = /^(((13[0-9]{1})|(15[0-9]{1})|(18[0-9]{1}))+\\d{8})$/;\r\n        var tel = /^(\\d{3,4}-?)?\\d{7,9}$/g;\r\n        return this.optional(element) || tel.test(value) || (length == 11 && mobile.test(value));\r\n    }, \"请正确填写您的联系方式\");\r\n\r\n    // 匹配qq      \r\n    jQuery.validator.addMethod(\"isQq\", function (value, element) {\r\n        return this.optional(element) || /^[1-9]\\d{4,12}$/;\r\n    }, \"匹配QQ\");\r\n\r\n    // 邮政编码验证    \r\n    jQuery.validator.addMethod(\"isZipCode\", function (value, element) {\r\n        var zip = /^[0-9]{6}$/;\r\n        return this.optional(element) || (zip.test(value));\r\n    }, \"请正确填写您的邮政编码。\");\r\n\r\n    // 匹配密码，以字母开头，长度在6-12之间，只能包含字符、数字和下划线。      \r\n    jQuery.validator.addMethod(\"isPwd\", function (value, element) {\r\n        return this.optional(element) || /^[a-zA-Z]\\\\w{6,12}$/.test(value);\r\n    }, \"以字母开头，长度在6-12之间，只能包含字符、数字和下划线。\");\r\n\r\n    // 身份证号码验证\r\n    jQuery.validator.addMethod(\"isIdCardNo\", function (value, element) {\r\n        //var idCard = /^(\\d{6})()?(\\d{4})(\\d{2})(\\d{2})(\\d{3})(\\w)$/;   \r\n        return this.optional(element) || isIdCardNo(value);