\r\n         value=parseFloat(value);      \r\n         return this.optional(element) || value==0;       \r\n    }, \"浮点数必须为0\"); \r\n      \r\n    // 判断浮点数value是否大于0\r\n    jQuery.validator.addMethod(\"isFloatGtZero\", function(value, element) { \r\n         value=parseFloat(value);      \r\n         return this.optional(element) || value>0;       \r\n    }, \"浮点数必须大于0\"); \r\n      \r\n    // 判断浮点数value是否大于或等于0\r\n    jQuery.validator.addMethod(\"isFloatGteZero\", function(value, element) { \r\n         value=parseFloat(value);      \r\n         return this.optional(element) || value>=0;       \r\n    }, \"浮点数必须大于或等于0\");   \r\n    \r\n    // 判断浮点数value是否不等于0 \r\n    jQuery.validator.addMethod(\"isFloatNEqZero\", function(value, element) { \r\n         value=parseFloat(value);      \r\n         return this.optional(element) || value!=0;       \r\n    }, \"浮点数必须不等于0\");  \r\n    \r\n    // 判断浮点数value是否小于0 \r\n    jQuery.validator.addMethod(\"isFloatLtZero\", function(value, element) { \r\n         value=parseFloat(value);      \r\n         return this.optional(element) || value<0;       \r\n    }, \"浮点数必须小于0\");  \r\n    \r\n    // 判断浮点数value是否小于或等于0 \r\n    jQuery.validator.addMethod(\"isFloatLteZero\", function(value, element) { \r\n         value=parseFloat(value);      \r\n         return this.optional(element) || value<=0;       \r\n    }, \"浮点数必须小于或等于0\");  \r\n    \r\n    // 判断浮点型  \r\n    jQuery.validator.addMethod(\"isFloat\", function(value, element) {       \r\n         return this.optional(element) || /^[-\\+]?\\d+(\\.\\d+)?$/.test(value);       \r\n    }, \"只能包含数字、小数点等字符\"); \r\n     \r\n    // 匹配integer\r\n    jQuery.validator.addMethod(\"isInteger\", function(value, element) {       \r\n         return this.optional(element) || (/^[-\\+]?\\d+$/.test(value) && parseInt(value)>=0);       \r\n    }, \"匹配integer\");  \r\n     \r\n    // 判断数值类型，包括整数和浮点数\r\n    jQuery.validator.addMethod(\"isNumber\", function(value, element) {       \r\n         return this.optional(element) || /^[-\\+]?\\d+$/.test(value) || /^[-\\+]?\\d+(\\.\\d+)?$/.test(value);       \r\n    }, \"匹配数值类型，包括整数和浮点数\");  \r\n    \r\n    // 只能输入[0-9]数字\r\n    jQuery.validator.addMethod(\"isDigits\", function(value, element) {       \r\n         return this.optional(element) || /^\\d+$/.test(value);       \r\n    }, \"只能输入0-9数字\");  \r\n    \r\n    // 判断中文字符 \r\n    jQuery.validator.addMethod(\"isChinese\", function(value, element) {       \r\n         return this.optional(element) || /^[\\u0391-\\uFFE5]+$/.test(value);       \r\n    }, \"只能包含中文字符。\");   \r\n \r\n    // 判断英文字符 \r\n    jQuery.validator.addMethod(\"isEnglish\", function(value, element) {       \r\n         return this.optional(element) || /^[A-Za-z]+$/.test(value);       \r\n    }, \"只能包含英文字符。\");   \r\n \r\n     // 手机号码验证    \r\n    jQuery.validator.addMethod(\"isMobile\", function(value, element) {    \r\n      var length = value.length;    \r\n      return this.optional(element) || (length == 11 && /^(((13[0-9]{1})|(15[0-9]{1})|(18[0-9]{1}))+\\d{8})$/.test(value));    \r\n    }, \"请正确填写您的手机号码。\");\r\n\r\n    // 电话号码验证    \r\n    jQuery.validator.addMethod(\"isPhone\", function(value, element) {    \r\n      var tel = /^(\\d{3,4}-?)?\\d{7,9}$/g;    \r\n      return this.optional(element) || (tel.test(value));    \r\n    }, \"请正确填写您的电话号码。\");\r\n\r\n    // 联系电话(手机/电话皆可)验证   \r\n    jQuery.validator.addMethod(\"isTel\", function(value,element) {   \r\n        var length = value.length;   \r\n        var mobile = /^(((13[0-9]{1})|(15[0-9]{1})|(18[0-9]{1}))+\\d{8})$/;   \r\n        var tel = /^(\\d{3,4}-?)?\\d{7,9}$/g;       \r\n        return this.optional(element) || tel.test(value) || (length==11 && mobile.test(value));   \r\n    }, \"请正确填写您的联系方式\"); \r\n \r\n     // 匹配qq      \r\n    jQuery.validator.addMethod(\"isQq\", function(value, element) {       \r\n         return this.optional(element) || /^[1-9]\\d{4,12}$/;       \r\n    }, \"匹配QQ\");   \r\n \r\n     // 邮政编码验证    \r\n    jQuery.validator.addMethod(\"isZipCode\", function(value, element) {    \r\n      var zip = /^[0-9]{6}$/;    \r\n      return this.optional(element) || (zip.test(value));    \r\n    }, \"请正确填写您的邮政编码。\");  \r\n    \r\n    // 匹配密码，以字母开头，长度在6-12之间，只能包含字符、数字和下划线。      \r\n    jQuery.validator.addMethod(\"isPwd\", function(value, element) {       \r\n         return this.optional(element) || /^[a-zA-Z]\\\\w{6,12}$/.test(value);       \r\n    }, \"以字母开头，长度在6-12之间，只能包含字符、数字和下划线。\");  \r\n    \r\n    // 身份证号码验证\r\n    jQuery.validator.addMethod(\"isIdCardNo\", function(value, element) { \r\n      //var idCard = /^(\\d{6})()?(\\d{4})(\\d{2})(\\d{2})(\\d{3})(\\w)$/;   \r\n      return this.optional(element) || isIdCardNo(value);    \r\n    }, \"请输入正确的身份证号码。\"); \r\n\r\n    // IP地址验证   \r\n    jQuery.validator.addMethod(\"ip\", function(value, element) {    \r\n      return this.optional(element) || /^(([1-9]|([1-9]\\d)|(1\\d\\d)|(2([0-4]\\d|5[0-5])))\\.)(([1-9]|([1-9]\\d)|(1\\d\\d)|(2([0-4]\\d|5[0-5])))\\.){2}([1-9]|([1-9]\\d)|(1\\d\\d)|(2([0-4]\\d|5[0-5])))$/.test(value);    \r\n    }, \"请填写正确的IP地址。\");\r\n   \r\n    // 字符验证，只能包含中文、英文、数字、下划线等字符。    \r\n    jQuery.validator.addMethod(\"stringCheck\", function(value, element) {       \r\n         return this.optional(element) || /^[a-zA-Z0-9\\u4e00-\\u9fa5-_]+$/.test(value);       \r\n    }, \"只能包含中文、英文、数字、下划线等字符\");   \r\n   \r\n    // 匹配english  \r\n    jQuery.validator.addMethod(\"isEnglish\", function(value, element) {       \r\n         return this.optional(element) || /^[A-Za-z]+$/.test(value);       \r\n    }, \"匹配english\");   \r\n    \r\n    // 匹配汉字  \r\n    jQuery.validator.addMethod(\"isChinese\", function(value, element) {       \r\n         return this.optional(element) || /^[\\u4e00-\\u9fa5]+$/.test(value);       \r\n    }, \"匹配汉字\");   \r\n    \r\n    // 匹配中文(包括汉字和字符) \r\n    jQuery.validator.addMethod(\"isChineseChar\", function(value, element) {       \r\n         return this.optional(element) || /^[\\u0391-\\uFFE5]+$/.test(value);       \r\n    }, \"匹配中文(包括汉字和字符) \"); \r\n      \r\n    // 判断是否为合法字符(a-zA-Z0-9-_)\r\n    jQuery.validator.addMethod(\"isRightfulString\", function(value, element) {       \r\n         return this.optional(element) || /^[A-Za-z0-9_-]+$/.test(value);       \r\n    }, \"判断是否为合法字符(a-zA-Z0-9-_)\");   \r\n    \r\n    // 判断是否包含中英文特殊字符，除英文\"-_\"字符外\r\n    jQuery.validator.addMethod(\"isContainsSpecialChar\", function(value, element) {  \r\n         var reg = RegExp(/[(\\ )(\\`)(\\~)(\\!)(\\@)(\\#)(\\$)(\\%)(\\^)(\\&)(\\*)(\\()(\\))(\\+)(\\=)(\\|)(\\{)(\\})(\\\')(\\:)(\\;)(\\\')(\',)(\\[)(\\])(\\.)(\\<)(\\>)(\\/)(\\?)(\\~)(\\！)(\\@)(\\#)(\\￥)(\\%)(\\…)(\\&)(\\*)(\\（)(\\）)(\\—)(\\+)(\\|)(\\{)(\\})(\\【)(\\】)(\\‘)(\\；)(\\：)(\\”)(\\“)(\\’)(\\。)(\\，)(\\、)(\\？)]+/);   \r\n         return this.optional(element) || !reg.test(value);       \r\n    }, \"含有中英文特殊字符\");   \r\n   \r\n\r\n    //身份证号码的验证规则\r\n    function isIdCardNo(num){ \r\n    　   //if (isNaN(num)) {alert(\"输入的不是数字！\"); return false;} \r\n    　　 var len = num.length, re; \r\n    　　 if (len == 15) \r\n    　　 re = new RegExp(/^(\\d{6})()?(\\d{2})(\\d{2})(\\d{2})(\\d{2})(\\w)$/); \r\n    　　 else if (len == 18) \r\n    　　 re = new RegExp(/^(\\d{6})()?(\\d{4})(\\d{2})(\\d{2})(\\d{3})(\\w)$/); \r\n    　　 else {\r\n            //alert(\"输入的数字位数不对。\"); \r\n            return false;\r\n        } \r\n    　　 var a = num.match(re); \r\n    　　 if (a != null) \r\n    　　 { \r\n    　　 if (len==15) \r\n    　　 { \r\n    　　 var D = new Date(\"19\"+a[3]+\"/\"+a[4]+\"/\"+a[5]); \r\n    　　 var B = D.getYear()==a[3]&&(D.getMonth()+1)==a[4]&&D.getDate()==a[5]; \r\n    　　 } \r\n    　　 else \r\n    　　 { \r\n    　　 var D = new Date(a[3]+\"/\"+a[4]+\"/\"+a[5]); \r\n    　　 var B = D.getFullYear()==a[3]&&(D.getMonth()+1)==a[4]&&D.getDate()==a[5]; \r\n    　　 } \r\n    　　 if (!B) {\r\n            //alert(\"输入的身份证号 \"+ a[0] +\" 里出生日期不对。\"); \r\n            return false;\r\n        } \r\n    　　 } \r\n    　　 if(!re.test(num)){\r\n            //alert(\"身份证最后一位只能是数字和字母。\");\r\n            return false;\r\n        }\r\n    　　 return true; \r\n    } \r\n\r\n});\r\n\r\n复制代码\r\n\r\n二、测试-validate-methods.jsp\r\n复制代码\r\n\r\n<%@ page language=\"java\" import=\"java.util.*\" pageEncoding=\"UTF-8\"%>\r\n<%\r\n    String path = request.getContextPath();\r\n    String basePath = request.getScheme() + \"://\" + request.getServerName() + \":\"\r\n            + request.getServerPort() + path + \"/\";\r\n%>\r\n\r\n<!DOCTYPE HTML PUBLIC \"-//W3C//DTD HTML 4.01 Transitional//EN\">\r\n<html>\r\n    <head>\r\n        <base href=\"<%=basePath%>\">\r\n\r\n        <title>jQuery Validate扩展验证方法</title>\r\n\r\n        <meta http-equiv=\"pragma\" content=\"no-cache\">\r\n        <meta http-equiv=\"cache-control\" content=\"no-cache\">\r\n        <meta http-equiv=\"expires\" content=\"0\">\r\n        <meta http-equiv=\"keywords\" content=\"keyword1,keyword2,keyword3\">\r\n        <meta http-equiv=\"description\" content=\"This is my page\">\r\n        <script type=\"text/javascript\" src=\"<%=request.getContextPath()%>/validate/jquery-1.6.2.min.js\"></script>\r\n        <script type=\"text/javascript\" src=\"<%=request.getContextPath()%>/validate/jquery.validate.min.js\"></script>\r\n        <script type=\"text/javascript\" src=\"<%=request.getContextPath()%>/validate/jquery.metadata.min.js\"></script>\r\n        <script type=\"text/javascript\" src=\"<%=request.getContextPath()%>/validate/validate-methods.js\"></script>\r\n        <script type=\"text/javascript\" src=\"<%=request.getContextPath()%>/validate/messages_zh.js\"></script>\r\n        <script type=\"text/javascript\">\r\n        $(function(){\r\n            var validate = $(\"#myform\").validate({\r\n                debug: true, //调试模式取消submit的默认提交功能    \r\n                submitHandler: function(form){   //表单提交句柄,为一回调函数，带一个参数：form   \r\n                    alert(\"提交表单\");   \r\n                    form.submit();   //提交表单   \r\n                },   \r\n                \r\n                rules:{\r\n                    zero:{\r\n                        //isIntEqZero:true\r\n                        //isFloat:true\r\n                        //isInteger:true\r\n                        //isNumber:true\r\n                        //isMobile:true\r\n                        //isPhone:true\r\n                        //isTel:true\r\n                        //isIdCardNo:true\r\n                        //isRightfulString:true\r\n                        isContainsSpecialChar:true\r\n                    }                \r\n                },\r\n                //如果验证控件没有message，将调用默认的信息\r\n                messages:{\r\n                    zero:{\r\n                        //isIntEqZero:\"请输入0\"\r\n                        //isFloat:\"请输入浮点数\"\r\n                        //isInteger:\"请输入整数\"\r\n                    }\r\n                                                     \r\n                }\r\n                          \r\n            });    \r\n        \r\n        });\r\n        </script>\r\n    </head>\r\n\r\n    <body>\r\n        <form id=\"myform\" method=\"post\" action=\"\">\r\n            <p>\r\n                <label for=\"zero\">输入框：</label>\r\n                <!-- id和name最好同时写上 -->\r\n                <input id=\"zero\" name=\"zero\" />\r\n            </p>\r\n         \r\n            <p>\r\n                <input class=\"submit\" type=\"submit\" value=\"验证\" />\r\n            </p>\r\n        </form>\r\n    </body>\r\n</html>\r\n', '0', '2017-09-08 09:49:06', null, 'jQuery.封装.Validate.扩展验证方法.fromcnblogs.txt');
INSERT INTO `txt` VALUES ('432', '2', 'jquery.插件.右下角弹窗.弹框.txt', '@{\r\n    Layout = null;\r\n}\r\n<html>\r\n<head>\r\n    <title>test</title>\r\n    <script src=\"~/Scripts/jquery-1.8.2.min.js\"></script>\r\n</head>\r\n<body>\r\n    <script type=\"text/javascript\">\r\n        //����:���B(www.0379zd.com����)\r\n        //ʱ��2009��6��30��9:02:51���޸ģ�\r\n        //�汾:v 1.1.1.2(�ڶ����汾)�����ڹ���������ʱ����Ϣ�����¹�����BUG\r\n\r\n        (function ($) {\r\n            $.fn.extend({\r\n                Show: function (widht, height) {\r\n                    var TopY = 0;//��ʼ��Ԫ�ؾุԪ�صľ���\r\n                    $(this).css(\"width\", widht + \"px\").css(\"height\", height + \"px\");//������Ϣ��Ĵ�С\r\n                    $(this).slideDown(1000);//����\r\n                    $(\"#messageTool\").css(\"margin-top\", -height);//Ϊ���ݲ��ִ����߶�  ���\r\n                    $(\"#message_close\").click(function () {//������رհ�ť��ʱ��\r\n                        if (TopY == 0) {\r\n                            $(\"#message\").slideUp(1000);//����֮������slideUp��Ϊ�˼���Firefox�����\r\n                        }\r\n                        else {\r\n                            $(\"#message\").animate({ top: TopY + height }, \"slow\", function () { $(\"#message\").hide(); });//��TopY������0ʱ  ie�º�FirefoxЧ��һ��\r\n                        }\r\n                    });\r\n                    $(window).scroll(function () {\r\n                        $(\"#message\").css(\"top\", $(window).scrollTop() + $(window).height() - $(\"#message\").height());//��������������ʱ��ʼ������Ļ�����½�\r\n                        TopY = $(\"#message\").offset().top;//��������������ʱ����ʱ����Ԫ�ؾุԭ�ؾ���\r\n                    });\r\n                }\r\n            })\r\n        }\r\n       )(jQuery);\r\n\r\n        $(document).ready(function () {\r\n            $(\"#message\").Show(256, 185);\r\n        });\r\n\r\n    </script>\r\n\r\n    <div id=\"testad\"></div>\r\n    <script>\r\n        $(function () {\r\n         