INSERT INTO `txt` VALUES ('438', '2', 'jquery.调试方法.debug.jun.有待改进.txt', '    function junalert(msg, time) {\r\n        time = time && time * 1000 || 2000;\r\n        if (typeof msg === \"string\") {\r\n            msg = msg.indexOf(\'|\') != -1 && \'<p>\' + msg.split(\'|\').join(\'</p><p>\') || \'<p>\' + msg;\r\n        }\r\n        if (typeof msg === \'object\') {\r\n            var msgs = new Array();\r\n            for (var p in msg) {\r\n                msgs.push(p + \':&nbsp;\' + msg[p]);\r\n            }\r\n            msg = \'<p>\' + msgs.join(\'</p><p>\');\r\n        }\r\n        $(\'body\').prepend(\'<div id=\"debug_dump\"></div>\');\r\n\r\n        $(\'div#debug_dump\')\r\n            .css({\r\n                position: \'absolute\',\r\n                backgroundColor: \'#000\',\r\n                opacity: \'0.65\',\r\n                top: \'0\',\r\n                left: \'0\',\r\n                width: $(\'body\').width(),\r\n                lineHeight: \'28px\',\r\n                zIndex: \'99999\',\r\n                textIndent: \'20px\',\r\n                color: \'#fff\'\r\n            })\r\n            .html(\'�������ǣ� \' + msg + \'</p>\')\r\n            .click(function () {\r\n                $(this).mouseout(function () {\r\n                    setTimeout(function () {\r\n                        $(\'div#debug_dump\').fadeOut(\'slow\').empty().remove();\r\n                    }, time);\r\n                });\r\n            });\r\n    };\r\n\r\n\r\n����\r\n  junalert(\"test\", 3);\r\n\r\n', '0', '2017-09-08 09:49:06', null, 'jquery.调试方法.debug.jun.有待改进.txt');
INSERT INTO `txt` VALUES ('439', '2', 'jQuery取得select选择的文本与值.txt', ' jQueryȡ��selectѡ����ı���ֵ\r\n2015-03-09 14:31 79591���Ķ� ����(0) �ղ� �ٱ�\r\n ���ࣺ JS���ࣨ6��  \r\n��Ȩ����������Ϊ����ԭ�����£�δ�������������ת�ء�\r\n\r\njquery��ȡselectѡ����ı���ֵ\r\n��ȡselect ��\r\n��ȡselect ѡ�е� text :\r\n    $(\"#ddlregtype\").find(\"option:selected\").text();\r\n\r\n��ȡselectѡ�е� value:\r\n    $(\"#ddlregtype \").val();\r\n\r\n��ȡselectѡ�е�����:\r\n    $(\"#ddlregtype \").get(0).selectedindex;\r\n\r\n����select:\r\n����select ѡ�е�������\r\n    $(\"#ddlregtype \").get(0).selectedindex=index;//indexΪ����ֵ\r\n\r\n����select ѡ�е�value��\r\n    $(\"#ddlregtype \").attr(\"value\",\"normal��);\r\n    $(\"#ddlregtype \").val(\"normal\");\r\n    $(\"#ddlregtype \").get(0).value = value;\r\n\r\n����select ѡ�е�text:\r\n\r\n    var count=$(\"#ddlregtype option\").length;\r\n      for(var i=0;i<count;i++)\r\n         {           if($(\"#ddlregtype \").get(0).options[i].text == text)\r\n            {\r\n                $(\"#ddlregtype \").get(0).options[i].selected = true;\r\n                break;\r\n            }\r\n        }\r\n    $(\"#select_id option[text=\'jquery\']\").attr(\"selected\", true);\r\n\r\n����select option��:\r\n\r\n    $(\"#select_id\").append(\"<option value=\'value\'>text</option>\");  //���һ��option\r\n    $(\"#select_id\").prepend(\"<option value=\'0\'>��ѡ��</option>\"); //��ǰ�����һ��option\r\n    $(\"#select_id option:last\").remove(); //ɾ������ֵ����option\r\n    $(\"#select_id option[index=\'0\']\").remove();//ɾ������ֵΪ0��option\r\n    $(\"#select_id option[value=\'3\']\").remove(); //ɾ��ֵΪ3��option\r\n    $(\"#select_id option[text=\'4\']\").remove(); //ɾ��textֵΪ4��option\r\n\r\n��� select:\r\n\r\n    $(\"#ddlregtype \").empty();\r\n\r\n������Ҫ��Ҫ�����������е�ֵ����ͼ��\r\n\r\n��λ�ô����ѡ�����ӵ��ұ�ѡ����е�ֵ��������������ҳ��Ч���Ի�ã��������˱Ƚ����е�jquery��\r\njs�������£�\r\n\r\n    //��ȡ��������ֵ var item = $(\"#select1\").val();\r\n    $(function(){\r\n      $(\'#select1\').each(  //���select1������ֵ\r\n         function(){\r\n            $(\'button\').click(function(){\r\n                alert($(\'#select2\').val());  //���select2�е�select1ֵ\r\n            });\r\n         });\r\n    })\r\n    </script>\r\n\r\nֵ��ע����ǣ�����ֱ��д��\r\n\r\n    $(function(){\r\n      $(\'#select2\').each(  //���select1������ֵ����Ϊǰ�潲ѡ��������ӵ��ұߣ�jquery��ʵ��û��������ֵ����ߴ����ұߡ�\r\n         function(){\r\n            $(\'button\').click(function(){\r\n                alert($(this).val());  //���select2�е�select1ֵ\r\n            });\r\n         });\r\n    })\r\n\r\nhtml��\r\n    <div class=\"centent\">\r\n            <select multiple=\"multiple\" id=\"select1\" name=\"dd\" style=\"width:100px;height:160px;\">\r\n                <option value=\"1\">ѡ��1</option>\r\n                <option value=\"2\">ѡ��2</option>\r\n                <option value=\"3\">ѡ��3</option>\r\n                <option value=\"4\">ѡ��4</option>\r\n                <option value=\"5\">ѡ��5</option>\r\n                <option value=\"6\">ѡ��6</option>\r\n                <option value=\"7\">ѡ��7</option>\r\n            </select>\r\n            <div>\r\n                <span id=\"add\" >ѡ����ӵ��ұ�&gt;&gt;</span>\r\n                <span id=\"add_all\" >ȫ����ӵ��ұ�&gt;&gt;</span>\r\n            </div>\r\n        </div>\r\n        <div class=\"centent\">\r\n            <select multiple=\"multiple\" id=\"select2\" name=\"sel\" style=\"width: 100px;height:160px;\">\r\n            </select>\r\n            <div>\r\n                <span id=\"remove\">&lt;&lt;ѡ��ɾ�������</span>\r\n                <span id=\"remove_all\">&lt;&lt;ȫ��ɾ�������</span>\r\n            </div>\r\n        </div>\r\n\r\nʹ��JQuery��Ajax���ö�̬���Select��optionѡ��\r\n\r\n    //��ClassLevel1�����¼�\r\n        $(\"#ClassLevel1\").change(function () {\r\n            var id = $(\"#ClassLevel1\").val();\r\n            var level2 = $(\"#ClassLevel2\");\r\n            level2.empty();\r\n            $(\"#ClassLevel3\").hide();\r\n            $.ajax({\r\n                url: \"./askCommon.ashx?action=getclasslevel&pid=\" + id,\r\n                data: { \"type\": \"ajax\" },\r\n                datatype: \"json\",\r\n                type: \"get\",\r\n                success: function (data) {\r\n                    var json = eval_r(data);\r\n                    for (var ind in json) {\r\n                        level2.append($(\"<option value=\'\" + json[ind].id + \"\'>\" + json[ind].typename + \"</option>\"));\r\n                    }\r\n    \r\n                }\r\n            });\r\n        })\r\n', '0', '2017-09-08 09:49:06', null, 'jQuery取得select选择的文本与值.txt');
INSERT INTO `txt` VALUES ('440', '2', 'JQuery和document之间相互转换.txt', '<script src=\"jquery-2/jquery.js\"></script>\r\n<script type=\"text/javascript\">\r\n    $(document).ready(function(){\r\n        //Ҳ������$(function(){})\r\n\r\n//�õ�jquery����\r\n        var $te1=$(\"#t4\");\r\n\r\n//��ʾ����\r\n    alert($te1.html());\r\n    var t2=document.getElementById(\"t2\");\r\n\r\n\r\n    alert(t2.innerHTML);\r\n\r\n//תdoucment\r\n    var te11=$te1.get(0);\r\n    alert(te11.innerHTML);\r\n\r\n//תjquery\r\n    var $tt2=$(t2);\r\n    alert($tt2.html());\r\n        });\r\n\r\n</script>\r\n<body>\r\n<div id=\"tt\">\r\n<h1 id=\"t1\" name=\"t1\">1</h1>\r\n<h1 id=\"t2\">22</h1>\r\n<h1 id=\"t3\">333</h1>\r\n<h1 id=\"t4\" name=\"nn\">4444</h1>\r\n</div>\r\n\r\n</body>\r\n', '0', '2017-09-08 09:49:06', null, 'JQuery和document之间相互转换.txt');
INSERT INTO `txt` VALUES ('441', '2', 'jquery如何判断checkbox（复选框）是否被选中.txt', ' jquery如何判断checkbox（复选框）是否被选中\r\n谁都知道 在html 如果一个复选框被选中 是 checked=\"checked\"。\r\n\r\n但是我们如果用jquery alert($(\"#id\").attr(\"checked\")) 会提示您是true而不是checked\r\n\r\n所以很多朋友判断  if($(\"#id\").attr(\"checked\")==\"true\") 这个是错误的，其实应该是 if($(\"#id\").attr(\"checked\")==true)\r\n\r\n例子里面包括了一下几个功能。\r\n\r\n   <input type=\"button\" id=\"btn1\" value=\"全选\">\r\n   <input type=\"button\" id=\"btn2\" value=\"取消全选\">\r\n   <input type=\"button\" id=\"btn3\" value=\"选中所有奇数\">\r\n   <input type=\"button\" id=\"btn4\" value=\"反选\">\r\n   <input type=\"button\" id=\"btn5\" value=\"获得选中的所有值\">\r\n\r\n代码\r\n\r\n \r\n\r\n<!DOCTYPE HTML PUBLIC \"-//W3C//DTD HTML 4.0 Transitional//EN\">\r\n<HTML>\r\n <HEAD>\r\n  <TITLE> New Document </TITLE>\r\n  <meta http-equiv=\"Content-Type\" content=\"text/html; charset=UTF-8\" />\r\n    <SCRIPT LANGUAGE=\"JavaScript\" src=\"http://www.cnjquery.com/demo/jquery.js\"></script>\r\n  <SCRIPT LANGUAGE=\"JavaScript\">\r\n  <!--\r\n   $(\"document\").ready(function(){\r\n    \r\n    $(\"#btn1\").click(function(){\r\n     \r\n    $(\"[name=\'checkbox\']\").attr(\"checked\",\'true\');//全选\r\n  \r\n    })\r\n       $(\"#btn2\").click(function(){\r\n     \r\n    $(\"[name=\'checkbox\']\").removeAttr(\"checked\");//取消全选\r\n  \r\n    })\r\n    $(\"#btn3\").click(function(){\r\n     \r\n    $(\"[name=\'checkbox\']:even\").attr(\"checked\",\'true\');//选中所有奇数\r\n  \r\n    })\r\n    $(\"#btn4\").click(function(){\r\n     \r\n    $(\"[name=\'checkbox\']\").each(function(){\r\n     \r\n   \r\n     if($(this).attr(\"checked\"))\r\n   {\r\n    $(this).removeAttr(\"checked\");\r\n    \r\n   }\r\n   else\r\n   {\r\n    $(this).attr(\"checked\",\'true\');\r\n    \r\n   }\r\n   \r\n    })\r\n  \r\n    })\r\n     $(\"#btn5\").click(function(){\r\n    var str=\"\";\r\n    $(\"[name=\'checkbox\'][checked]\").each(function(){\r\n     str+=$(this).val()+\"\"r\"n\";\r\n   //alert($(this).val());\r\n    })\r\n   alert(str);\r\n    })\r\n   })\r\n  //-->\r\n  </SCRIPT>\r\n  \r\n </HEAD>\r\n\r\n <BODY>\r\n <form name=\"form1\" method=\"post\" action=\"\">\r\n   <input type=\"button\" id=\"btn1\" value=\"全选\">\r\n   <input type=\"button\" id=\"btn2\" value=\"取消全选\">\r\n   <input type=\"button\" id=\"btn3\" value=\"选中所有奇数\">\r\n   <input type=\"button\" id=\"btn4\" value=\"反选\">\r\n   <input type=\"button\" id=\"btn5\" value=\"获得选中的所有值\">\r\n   <br>\r\n   <input type=\"checkbox\" name=\"checkbox\" value=\"checkbox1\">\r\n   checkbox1\r\n   <input type=\"checkbox\" name=\"checkbox\" value=\"checkbox2\">\r\n   checkbox2\r\n   <input type=\"checkbox\" name=\"checkbox\" value=\"checkbox3\">\r\n   checkbox3\r\n   <input type=\"checkbox\" name=\"checkbox\" value=\"checkbox4\">\r\n   checkbox4\r\n   <input type=\"checkbox\" name=\"checkbox\" value=\"checkbox5\">\r\n   checkbox5\r\n   <input type=\"checkbox\" name=\"checkbox\" value=\"checkbox6\">\r\n   checkbox6\r\n   <input type=\"checkbox\" name=\"checkbox\" value=\"checkbox7\">\r\n   checkbox7\r\n   <input type=\"checkbox\" name=\"checkbox\" value=\"checkbox8\">\r\n checkbox8\r\n </form>\r\n', '0', '2017-09-08 09:49:06', null, 'jquery如何判断checkbox（复选框）是否被选中.txt');
INSERT INTO `txt` VALUES ('442', '2', 'jQuery如何获取选中单选按钮radio的值.txt', 'jQuery��λ�ȡѡ�е�ѡ��ťradio��ֵ\r\n\r\n<div id=\"wrap\">\r\n    <input type=\"radio\" name=\"payMethod\" value=\"1\" />֧����\r\n    <input type=\"radio\" name=\"payMethod\" value=\"2\" />����ͨ\r\n</div>\r\n����\r\n������ȡһ�鵥ѡ��ť����var obj_payPlatform = $(\'#wrap input[name=\"payMethod\"]\');\r\n������ȡ��ѡ�а�ť��ֵ ��var val_payPlatform = $(\'#wrap input[name=\"payMethod\"]:checked \').val();\r\n', '0', '2017-09-08 09:49:06', null, 'jQuery如何获取选中单选按钮radio的值.txt');
INSERT INTO `txt` VALUES ('443', '2', 'jquery子窗体操作父窗体中的元素.txt', 'jquery子窗体操作父窗体中的元素  \r\n\r\n1.在父窗口中操作子窗口中的元素，如(其中 iframe1是iframe的ID）\r\n1) 选中IFRAME中的所有单选钮\r\n  $(window.frames[\"iframe1\"].document).find(\"input[@type=\'radio\']\").attr(\"checked\",\"true\");\r\n2).去掉子窗口中类名为top_menu元素的css属性类on\r\n $(window.frames[\"iframe1\"].document).find(\'.top_menu\').removeClass(\"on\");\r\n\r\n2.在IFRAME中操作父窗口的元素\r\n1) 选中父窗口中的所有单选钮\r\n$(window.parent.document).find(\"input[@type=\'radio\']\").attr(\"checked\",\"true\");\r\n2父窗口元素ID为_M1408加上CSS类on\r\n$(window.parent.document).find(\"#_M1408\").addClass(\"on\");\r\n\r\niframe框架的：<iframe src=\"http://chenxinwen2006.blog.163.com/blog/test.html\" id=\"iframe1\" width=\"700\" height=\"300\" frameborder=\"0\" scrolling=\"auto\"></iframe>\r\n\r\n好像也可以用$(\"#myid\", top.document);\r\n\r\n父窗体调用IFrame中的方法（right)为ifram的id\r\ndocument.frames[\"right\"].expand();  \r\n父窗体：parent;\r\n\r\n子窗体：opener;(window.opener.reload();)\r\n\r\n$(window.parent.document).find(\"#alimamaifrm\").attr(\"src\",\"http://www.163.com\");\r\n', '0', '2017-09-08 09:49:06', null, 'jquery子窗体操作父窗体中的元素.txt');
INSERT INTO `txt` VALUES ('444', '2', 'jquery怎么判断checkbox是否选中 .txt', 'jquery怎么判断checkbox是否选中？\r\n2011-08-15 17:37 xiaowei2627956 | 分类：JavaScript | 浏览82148次\r\n\r\n代码如下：<SCRIPT type=\"text/javascript\">\r\n   		$(document).ready(function(){\r\n   			$(\"#checkbox2\").click(){\r\n   			if($(\"#checkbox2\").attr(\"checked\")==true){\r\n   				alert(\"ddd\");\r\n   			};\r\n   			};\r\n   		});\r\n    </SCRIPT>\r\nhtml:\r\n<s:iterator var=\"event\" value=\"events\">\r\n  	<tr>\r\n  		<td><input type=\"checkbox\" name=\"check2\" id=\"checkbox2\"/> </td>\r\n  		<td><s:property value=\"#event.enent\"/></td>\r\n  		<td><s:property value=\"#event.isfinished\"/></td>\r\n  		<td><s:property value=\"#event.createdate\"/></td>\r\n  		<td><s:property value=\"#event.finishdate\"/></td>\r\n  	</tr>\r\n', '0', '2017-09-08 09:49:06', null, 'jquery怎么判断checkbox是否选中 .txt');
INSERT INTO `txt` VALUES ('445', '2', 'Jquery怎么获取select选中项 自定义属性的值.txt', 'Jquery��λ�ȡselectѡ���� �Զ������Ե�ֵ��\r\n\r\n------�������--------------------------------------------------------\r\n$(\"#ddl\").find(\"option:selected\").attr(\"emoney\");\r\n\r\n--------------------------------------------------------------------------------------------------\r\nHTML code\r\n\r\n<select id=\"ddl\" onchange=\"ddl_change(this)\"\">\r\n   <option value=\"100\" emoney=\"12\" gmoney=\"12\">�տ�</option>\r\n   <option value=\"102\" emoney=\"58\" gmoney=\"58\">�¿�</option>\r\n   <option value=\"103\" emoney=\"518\" gmoney=\"518\">�꿨</option>\r\n<select>\r\n\r\n\r\n\r\nJScript code\r\n\r\nfunction ddl_change(obj){\r\n   alert($(\"#ddl\").attr(\"emoney\"));\r\n}\r\n\r\n\r\n\r\nJquery��λ�ȡselectѡ���� �Զ������Ե�ֵ��\r\n�ó�����undefined!!\r\n��ô��ȡselectѡ�����е��Զ������ԡ�emoney����ֵ������\r\n------�������--------------------------------------------------------\r\n$(\"#ddl\").find(\"option:selected\").attr(\"emoney\");\r\n\r\n \r\n\r\n<!DOCTYPE html>\r\n<html lang=\"en\">\r\n<head>\r\n<meta charset=\"UTF-8\">\r\n<title>Document</title>\r\n<script type=\"text/javascript\" src=\"jquery-1.8.1.min.js\"></script>\r\n<script type=\"text/javascript\">\r\n$(function(){\r\n$(\'#lev\').change(function(){\r\n\r\n$(\'#lev2\').html(\'<option value=\"1\" lev2=\"1\">1</option><option value=\"2\" lev2=\"2\">2</option><option value=\"3\" lev2=\"3\">3</option><option value=\"4\" lev2=\"4\">4</option><option value=\"5\" lev2=\"5\">5</option>\');\r\nalert($(\'#lev\').find(\'option:selected\').attr(\'lev\'));\r\n$(\'#lev\').find(\'option:selected\').attr(\'selected\', \'selected\');\r\n});\r\n$(\'#lev2\').change(function(){\r\nalert($(\'#lev2\').find(\'option:selected\').attr(\'lev2\'));\r\n$(\'#lev2\').find(\'option:selected\').attr(\'selected\', \'selected\');\r\n});\r\n\r\n})\r\n</script>\r\n</head>\r\n<body>\r\n<select id=\"lev\">\r\n<option value=\"1\" lev=\"1\">1</option>\r\n<option value=\"2\" lev=\"2\">2</option>\r\n<option value=\"3\" lev=\"3\">3</option>\r\n<option value=\"4\" lev=\"4\">4</option>\r\n<option value=\"5\" lev=\"5\">5</option>\r\n</select>\r\n<select id=\"lev2\">\r\n\r\n</select>\r\n</body>\r\n</html>\r\n', '0', '2017-09-08 09:49:06', null, 'Jquery怎么获取select选中项 自定义属性的值.txt');
INSERT INTO `txt` VALUES ('446', '2', 'jQuery打造动态渐变按钮.txt', 'from:http://www.cnblogs.com/yc-755909659/archive/2013/04/23/3038274.html\r\n<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\" />\r\n<title>jQuery���춯̬���䰴ť</title>\r\n<style type=\"text/css\" >\r\n.button\r\n{\r\n    background: url(\"http://y.photo.qq.com/img?s=uDgIxsWYI&l=y.jpg\") top no-repeat;\r\n    display: block;\r\n    height: 80px;\r\n    width: 200px;\r\n    text-indent: -9999px;\r\n}\r\n/*����Ҫ������:hover����ʽ����������span.hover����ʽ*/\r\n.button span.hover\r\n{  \r\n    position: absolute;\r\n    display: block;\r\n    width: 200px;\r\n    height: 80px;\r\n    text-indent: -9999px;\r\n    background: url(\"http://y.photo.qq.com/img?s=uDgIxsWYI&l=y.jpg\") bottom no-repeat;\r\n}\r\n.content\r\n{\r\n    margin: 0 auto;\r\n    width: 666px;\r\n}\r\n.jsbutton\r\n{\r\n    background: #F00;\r\n    display: block;\r\n    height: 80px;\r\n    width: 200px;\r\n    text-indent: -9999px;\r\n}\r\n/*����Ҫ������:hover����ʽ����������span.hover����ʽ*/\r\n.jsbutton span.hover\r\n{   /*ע��Ҫʹ�þ��Զ�λ*/\r\n    position: absolute;\r\n    display: block;\r\n    width: 200px;\r\n    height: 80px;\r\n    text-indent: -9999px;\r\n    background: #00F;\r\n}\r\n</style>\r\n<script type=\"application/javascript\" src=\"http://files.cnblogs.com/yc-755909659/jquery-1.9.1.min.js\"></script>\r\n<script type=\"application/javascript\">\r\n$(document).ready(function() {\r\n    //���ı�������<span>Ԫ���У��ٸ��ӵ�.jsbutton��\r\n    $(\".jsbutton,.button\").wrapInner(\'<span class=\"hover\"></span>\').css(\'textIndex\',\'0\').each(function() {\r\n         //������<span>Ԫ����ȫ͸��������������ͣ�¼�\r\n         $(\"span.hover\").css(\'opacity\',0).hover(function() {\r\n            $(this).stop().fadeTo(650,1);  //��������͸��\r\n        },function(){\r\n            $(this).fadeTo(650,0);//������ȫ͸��\r\n            });\r\n    });\r\n});\r\n</script>\r\n</head>\r\n\r\n<body>\r\n<div class=\"content\">\r\n  <div>����Ч��</div>\r\n  <div><a class=\"button\">���֮·</a></div>\r\n  <br />\r\n  <br />\r\n  <div>��ɫ����</div>\r\n  <div><a class=\"jsbutton\">���֮·</a></div>\r\n</div>\r\n</body>\r\n</html>\r\n', '0', '2017-09-08 09:49:06', null, 'jQuery打造动态渐变按钮.txt');
INSERT INTO `txt` VALUES ('447', '2', 'js.change.table.color.隔行变色.olcc.html.txt', '<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n    <title></title>\r\n    <script src=\"js/jquery.js\" type=\"text/javascript\"></script>\r\n    <script type=\"text/javascript\">\r\n        /**\r\n        *�����б�ɫ\r\n        *@param  {String}  Table ��Id\r\n        */\r\n        function ChangeColor(iTableId) {\r\n            var oTable = document.getElementById(iTableId);\r\n            var aTr = oTable.getElementsByTagName(\'tr\');\r\n            for (var i = 0, j = aTr.length; i < j; i++) {\r\n                if (i % 2) {\r\n                    aTr[i].style.backgroundColor = \'red\';\r\n                }\r\n            }\r\n        }\r\n        /**\r\n        *ҳ������ʱ����:��ʼ�������б�ɫ\r\n        */\r\n        window.onload = function () {\r\n            ChangeColor(\'TableViewToolbar\');\r\n        };\r\n    </script>\r\n</head>\r\n<body>\r\n    <table id=\"TableViewToolbar\">\r\n        <tr>\r\n            <td>\r\n                a\r\n            </td>\r\n            <td>\r\n                b\r\n            </td>\r\n            <td>\r\n                c\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td>\r\n                a2\r\n            </td>\r\n            <td>\r\n                b2\r\n            </td>\r\n            <td>\r\n                c2\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td>\r\n                a3\r\n            </td>\r\n            <td>\r\n                b3\r\n            </td>\r\n            <td>\r\n                c3\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td>\r\n                a4\r\n            </td>\r\n            <td>\r\n                b4\r\n            </td>\r\n            <td>\r\n                c4\r\n            </td>\r\n        </tr>\r\n    </table>\r\n</body>\r\n</html>\r\n', '0', '2017-09-08 09:49:07', null, 'js.change.table.color.隔行变色.olcc.html.txt');
INSERT INTO `txt` VALUES ('448', '2', 'js.console.log.公共调试方法.common.method.txt', '    function debug(message) {\r\n        if (window.console && window.console.log) {\r\n            window.console.log(message);\r\n        } else {\r\n            alert(message);\r\n        }\r\n    };\r\n', '0', '2017-09-08 09:49:07', null, 'js.console.log.公共调试方法.common.method.txt');
INSERT INTO `txt` VALUES ('449', '2', 'js.hasClass.怎么使用jquery判断一个元素是否含有一个指定的类（class）.txt', '��jQuery�п���ʹ��2�ַ������ж�һ��Ԫ���Ƿ����һ��ȷ������(class)�����ַ���������ͬ�Ĺ��ܡ�2�ַ������£�\r\n1.           is(��.classname��)\r\n2.           hasClass(��classname��)\r\n������һ��divԪ���Ƿ����һ��redColor�����ӣ�\r\n1. ʹ��is(��.classname��)�ķ���\r\n$(\'div\').is(\'.redColor\')\r\n2. ʹ��hasClass(��classname��)�ķ���(ע��jquery�ĵͰ汾������hasClass(��.classname��))\r\n$(\'div\').hasClass(\'redColor\')\r\n�����Ǽ��һ��Ԫ���Ƿ���һ��redColor������ӣ�����ʱ����������ΪblueColor��\r\n<html>\r\n<head>\r\n<styletype=\"text/css\">\r\n  .redColor { \r\n        background:red;\r\n  }\r\n  .blueColor { \r\n        background:blue;\r\n  }\r\n</style>\r\n<scripttype=\"text/javascript\"src=\"jquery-1.3.2.min.js\"></script>\r\n</head>\r\n<body>\r\n  <h1>jQuery check if an element has a certain class</h1>\r\n \r\n  <divclass=\"redColor\">This is a div tag with class name of \"redColor\"</div>\r\n \r\n  <p>\r\n  <buttonid=\"isTest\">is(\'.redColor\')</button>\r\n  <buttonid=\"hasClassTest\">hasClass(\'.redColor\')</button>\r\n  <buttonid=\"reset\">reset</button>\r\n  </p>\r\n<scripttype=\"text/javascript\">\r\n \r\n    $(\"#isTest\").click(function () {\r\n \r\n          if($(\'div\').is(\'.redColor\')){\r\n                $(\'div\').addClass(\'blueColor\');\r\n          }\r\n \r\n    });\r\n \r\n    $(\"#hasClassTest\").click(function () {\r\n \r\n          if($(\'div\').hasClass(\'redColor\')){\r\n                $(\'div\').addClass(\'blueColor\');\r\n          }\r\n \r\n    });\r\n \r\n        $(\"#reset\").click(function () {\r\n          location.reload();\r\n    });\r\n \r\n \r\n</script>\r\n</body>\r\n</html>\r\n ��ʼЧ����\r\n\r\n���is(\'.redColor\')���Ч����\r\n\r\n���hasClass(\'redColor\')��Ч������is(\'.redColor\')���Ч����ͬ�����reset��Ч�����ʼЧ����ͬ��\r\n', '0', '2017-09-08 09:49:07', null, 'js.hasClass.怎么使用jquery判断一个元素是否含有一个指定的类（class）.txt');
INSERT INTO `txt` VALUES ('450', '2', 'js.javascript.ajax.原版.miaoV.diy.js.txt', '//==================================================ajax.js.Begin=================================================\r\n// 一共四步\r\n//    1.创建Ajax对象\r\n//    2.连接服务器\r\n//    3.发送请求\r\n//    3.接收返回\r\n\r\n/**\r\n* 调用:ajax(\'TextFile.txt\', \'GET\', function (str) { alert(str); }, function () { alert(\'failure\'); }, true);\r\n*sUrl:请求的地址\r\n*sMethod:请求的方法(GET或POST)\r\n*fnSuccess:成功调用的方法\r\n*fnFailure:失败调用的方法\r\n*sParameters:发送的参数\r\n*bAsynchronous:是否异步(ture异步/false同步)\r\n*/\r\nfunction ajax(sUrl, sMethod, fnSuccess, fnFailure, sParameters, bAsynchronous) {\r\n    //1.创建Ajax对象asynchronous synchronization\r\n    var oAjax = null;\r\n    if (window.XMLHttpRequest) {//写成window下的一个属性,避免因不存在而报错\r\n        oAjax = new XMLHttpRequest(); //除ie6以外的浏览器\r\n        if (oAjax.overrideMimeType) {\r\n            oAjax.overrideMimeType(\'text/html\');\r\n        }\r\n    } else {\r\n        new ActiveXObject(\"Microsoft.XMLHTTP\"); //ie6下有这个插件\r\n    }\r\n\r\n    //2.连接服务器\r\n    //open(GET/POST,url,是否异步);\r\n    var sync = !bAsynchronous ? true : bAsynchronous;\r\n    oAjax.open(sMethod, sUrl, sync);\r\n    oAjax.setRequestHeader(\"Content-type\", \"application/x-www-form-urlencoded\");\r\n    oAjax.setRequestHeader(\"Content-length\", sParameters.length);\r\n    oAjax.setRequestHeader(\"Connection\", \"close\");\r\n    //3.发送请求\r\n    oAjax.send(sParameters);\r\n    //4.接收返回\r\n    oAjax.onreadystatechange = function () {\r\n        // alert(oAjax.readyState);\r\n        if (oAjax.readyState == 4) {//和服务器交互结束\r\n            if (oAjax.status == 200) {//交互成功\r\n                fnSuccess(oAjax.responseText);\r\n            } else {\r\n                if (fnFaild) {\r\n                    fnFailure();\r\n                }\r\n            }\r\n        }\r\n    };\r\n    //用一个不存在的变量 : 报错\r\n    //用一个不存在的属性: undefined\r\n    //例如:所以判断的时候if (XMLHttpRequest) 浏览器会报错,所有把变量写成window下的一个属性,就不会报错了\r\n\r\n    //readyState属性:请求状态\r\n    //0(未初始化)还没有调用的状态\r\n    //1\r\n};\r\n//==================================================ajax.js.End=================================================\r\n//==================================================Invoke.aspx.Begin=================================================\r\n<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head runat=\"server\">\r\n    <title></title>\r\n    <script src=\"ajax.js\" type=\"text/javascript\"></script>\r\n    <script type=\"text/javascript\">\r\n        var params = \"txt1=\" + escape(encodeURI(\'abcde\'))\r\n+ \"&txt2=\" + escape(encodeURI(\'我4515个\')); //escape编码  encode也是编码\r\n        var url = \'Handler.ashx\';\r\n        function fnSuccess(str) {\r\n            var _str = unescape(decodeURI(str)); //解码\r\n            alert(_str);\r\n        }\r\n        function fnFailure() {\r\n            alert(\'failure\');\r\n        }\r\n        ajax(url, \'POST\', fnSuccess, fnFailure, params, true);\r\n    </script>\r\n</head>\r\n<body>\r\n</body>\r\n</html>\r\n//==================================================Invoke.aspx.End=================================================\r\n//==================================================服务器的处理.ashx.Begin=================================================\r\n    string str = context.Request[\"txt1\"] as string;\r\n    string str2 = context.Request[\"txt2\"] as string;\r\n    context.Response.Write(str+\"\\r\\n\"+str2);\r\n//==================================================服务器的处理.ashx.End=================================================\r\n', '0', '2017-09-08 09:49:07', null, 'js.javascript.ajax.原版.miaoV.diy.js.txt');
