INSERT INTO `txt` VALUES ('454', '2', 'js.javascript.checkAll.checkbox.全选.MiaoV.htm.txt', '<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n    <title>ȫѡ</title>\r\n    <script type=\"text/javascript\">\r\n        window.onload = function () {\r\n            var oBtn = document.getElementById(\'btn\');\r\n            var oCheckAll = document.getElementById(\'checkAll\');\r\n            var aInput = oCheckAll.getElementsByTagName(\'input\');\r\n            oBtn.onclick = function () {\r\n                for (var i = 0, j = aInput.length; i < j; i++) {\r\n                    aInput[i].checked = true;\r\n                }\r\n            };\r\n        }\r\n    </script>\r\n</head>\r\n<body>\r\n    <div id=\"checkAll\">\r\n        <p id=\"btn\">\r\n            ȫѡ</p>\r\n        <input type=\"checkbox\" /><br />\r\n        <input type=\"checkbox\" /><br />\r\n        <input type=\"checkbox\" /><br />\r\n        <input type=\"checkbox\" /><br />\r\n        <input type=\"checkbox\" /><br />\r\n        <input type=\"checkbox\" /><br />\r\n        <input type=\"checkbox\" /><br />\r\n        <input type=\"checkbox\" /><br />\r\n    </div>\r\n</body>\r\n</html>\r\n', '0', '2017-09-08 09:49:07', null, 'js.javascript.checkAll.checkbox.全选.MiaoV.htm.txt');
INSERT INTO `txt` VALUES ('455', '2', 'js.javascript.checkall.全选.olcc.html.txt', '<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n    <title></title>\r\n    <style type=\"text/css\">\r\n        table, tr, td { border: 1px solid red; border-collapse: collapse; }\r\n    </style>\r\n    <script src=\"js/jquery.js\" type=\"text/javascript\"></script>\r\n    <script type=\"text/javascript\">\r\n        //ȫѡ��ť�¼�\r\n		\r\n        function allSelectClick() {\r\n            if ($(\"#allSelected\").attr(\"checked\") == \"checked\") {\r\n                $(\"#TableViewToolbar\").find(\"tr\").each(function () {\r\n                    $(this).find(\"input[type=\'checkbox\']\").attr(\"checked\", true);\r\n                })\r\n            }\r\n            else {\r\n                $(\"#TableViewToolbar\").find(\"tr\").each(function () {\r\n                    $(this).find(\"input[type=\'checkbox\']\").attr(\"checked\", false);\r\n                })\r\n            }\r\n        }\r\n\r\n        //��checkbox�ı��ʱ��ѡ����ͷ��ȫѡ��\r\n        function CkChange() {\r\n            var i = 0;\r\n            var j = 0;\r\n            var flag = true;\r\n            $(\"#TableViewToolbar\").find(\"tr\").each(function () {\r\n                if ($(this).find(\"input[type=\'checkbox\']\").attr(\"name\") == \"datarowck\") {\r\n                    ++i;\r\n                }\r\n            });\r\n            $(\"#TableViewToolbar\").find(\"tr\").each(function () {\r\n                if ($(this).find(\"input[type=\'checkbox\']\").attr(\"checked\") == \"checked\" && $(this).find(\"input[type=\'checkbox\']\").attr(\"name\") == \"datarowck\") {\r\n                    ++j;\r\n                }\r\n            });\r\n            if (i == j) {\r\n                $(\"#allSelected\").attr(\"checked\", true);\r\n            } else if (i > j) {\r\n                $(\"#all