INSERT INTO `txt` VALUES ('456', '2', 'js.javascript.childNodes.nodeType.children.DOM.子节点.txt', '<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n    <title>DOM</title>\r\n    <script type=\"text/javascript\">\r\n        //childNodes�ӽڵ�\r\n        window.onload = function () {\r\n            var oUl = document.getElementById(\'ul1\');\r\n\r\n            for (var i = 0; i < oUl.childNodes.length; i++) {\r\n                if (oUl.childNodes[i].nodeType == 1) {//childNodes[1].nodeTypeΪ1����ʾԪ�ؽڵ㣬3�ı��ڵ�//���������仰��firefox���������ɫ\r\n                    oUl.childNodes[i].style.background = \'red\';\r\n                }\r\n            }\r\n            //childred�ӽڵ㣬�൱��childNodes�ļ��ݰ棬firefox��ie�����ж�nodeType\r\n            var oUl2 = document.getElementById(\'ul2\');\r\n            for (var i = 0; i < oUl2.children.length; i++) {\r\n                oUl2.children[i].style.background = \'blue\';\r\n            }\r\n        }\r\n    </script>\r\n</head>\r\n<body>\r\n    <ul id=\'ul1\'>\r\n        <li>111</li>\r\n        <li>222</li>\r\n        <li>333</li>\r\n    </ul>\r\n    <ul id=\'ul2\'>\r\n        <li>555</li>\r\n        <li>666</li>\r\n        <li>777</li>\r\n    </ul>\r\n</body>\r\n</html>\r\n', '0', '2017-09-08 09:49:07', null, 'js.javascript.childNodes.nodeType.children.DOM.子节点.txt');
INSERT INTO `txt` VALUES ('457', '2', 'Js.javascript.Comment.注释.规范.txt', 'Comments in JavaScript\r\nContents | JavaScript FAQ | Basic Syntax & General Questions 	 	\r\n\r\nQuestion: How do I insert comments in JavaScript code?\r\n\r\nAnswer: JavaScript supports three different types of comments:\r\n\r\n    Multiple-line C-style comments. Everything between /* and */ is a comment, for example:\r\n\r\n    /* This is a comment */\r\n    /* C-style comments can span\r\n    as many lines as you like,\r\n    as shown in this example */\r\n\r\n    One-line comments of C++ style. These comments begin with // and continue up to the next line break:\r\n\r\n    // This is a one-line comment\r\n\r\n    One-line comments with the HTML comment-opening sequence (<!--). Note that the JavaScript interpreter ignores the closing characters of HTML comments (-->). Consider this example:\r\n\r\n    <!-- This is treated as a one-line JS comment\r\n    <!-- It works just like a comment beginning with //\r\n    <!-- --> This is also a one-line JS comment\r\n    <!-- --> because JS ignores the closing characters\r\n    <!-- --> of HTML-style comments\r\n\r\n    HTML-style comments are not usually found in the middle of JavaScript code. (The // comments are simpler and easier to read.) However, they are useful for hiding JavaScript code from old browsers. \r\n//=================================================================================================================================\r\n��VS2010����Ը�JS������Ӵ�����ʾ\\ע��\r\n\r\n    ������дJS�����ѳ�������Ϊ����д���ˣ���һ��ʱ��ͻ������˺�������;�����ߺ�������Ĳ���������������ʱ������뵽VS�������ǿ����ʾ���ܣ���ϣ��Ҳ����JS��ʵ��ѽ�������㣬����뷨�����Ƕ��ѣ�VS2010�Ծ������ǽ����������⣬���濴�¾�������ô�����ģ�\r\n \r\n\r\n    function SayHi(name, age) {\r\n        /// <summary>���ҽ������ֺ�����ĺ���</summary>\r\n        /// <param name=\"name\" type=\"String\">Ҫ���ܵ�����</param>\r\n        /// <param name=\"age\" type=\"int\">Ҫ���ܵ�����</param>\r\n	\r\n        var str = \"��Һ�,�ҵ����ֽ�:\" + name + \",�ҽ���\" + age + \"��\";\r\n        alert(str);\r\n    }\r\n\r\nע�⵽����Ĵ�������summary��paramӢ�����壬��summary���Ǽ�¼�ú��������ã�param���Ƿ�����Ĳ���,name���ǲ�����,type���ǲ���������><�м�ľ��ǲ�������ؽ����ˡ�\r\n���������ڸ���һ��ʱ����ٻ���ʹ����δ���ʱ������ͨ����Щ��¼�����ʾ����⺯���ˣ�����ȥ�ܵ���������Ĵ����￴���졣\r\n���濴����ʾЧ������ĺܲ����\r\n//=============================================================\r\nfunction getArea(radius)\r\n  {\r\n      /// <summary>Determines the area of a circle that has the specified radius parameter.</summary>\r\n      /// <param name=\"radius\" type=\"Number\">The radius of the circle.</param>\r\n      /// <returns type=\"Number\">The area.</returns>\r\n      var areaVal;\r\n      areaVal = Math.PI * radius * radius;\r\n      return areaVal;\r\n  }\r\n', '0', '2017-09-08 09:49:07', null, 'Js.javascript.Comment.注释.规范.txt');
