INSERT INTO `txt` VALUES ('459', '2', 'Js.javascript.cookie结合拖拽.MiaoV.txt', '<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n    <style type=\"text/css\">\r\n        #div1\r\n        {\r\n            width: 100px;\r\n            height: 100px;\r\n            background: red;\r\n            position: absolute;\r\n        }\r\n    </style>\r\n    <meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\" />\r\n    <title>cookie�����ק</title>\r\n    <script type=\"text/javascript\">\r\n        function setCookie(name, value, iDay) {\r\n            var oDate = new Date();\r\n\r\n            oDate.setDate(oDate.getDate() + iDay);\r\n\r\n            document.cookie = name + \'=\' + value + \';expires=\' + oDate;\r\n        }\r\n\r\n        function getCookie(name) {\r\n            //\'username=abc; password=123456; aaa=123; bbb=4r4er\'\r\n            var arr = document.cookie.split(\'; \');\r\n            var i = 0;\r\n\r\n            //arr->[\'username=abc\', \'password=123456\', ...]\r\n\r\n            for (i = 0; i < arr.length; i++) {\r\n                //arr2->[\'username\', \'abc\']\r\n                var arr2 = arr[i].split(\'=\');\r\n\r\n                if (arr2[0] == name) {\r\n                    return arr2[1];\r\n                }\r\n            }\r\n\r\n            return \'\';\r\n        }\r\n\r\n        function removeCookie(name) {\r\n            setCookie(name, \'1\', -1);\r\n        }\r\n\r\n        window.onload = function () {\r\n\r\n            var oDiv = document.getElementById(\'div1\');\r\n            var disX = 0;\r\n            var disY = 0;\r\n\r\n            var x = getCookie(\'x\');\r\n            var y = getCookie(\'y\');\r\n\r\n            if (x) {\r\n                oDiv.style.left = x + \'px\';\r\n                oDiv.style.top = y + \'px\';\r\n            }\r\n\r\n            oDiv.onmousedown = function (ev) {\r\n                var oEvent = ev || event;\r\n\r\n                disX = oEvent.clientX - oDiv.offsetLeft;\r\n                disY = oEvent.clientY - oDiv.offsetTop;\r\n\r\n                document.onmousemove = function (ev) {\r\n                    var oEvent = ev || event;\r\n\r\n                    oDiv.style.left = oEvent.clientX - disX + \'px\';\r\n                    oDiv.style.top = oEvent.clientY - disY + \'px\';\r\n                };\r\n\r\n                document.onmouseup = function () {\r\n                    document.onmousemove = null;\r\n                    document.onmouseup = null;\r\n\r\n                    setCookie(\'x\', oDiv.offsetLeft, 5);\r\n                    setCookie(\'y\', oDiv.offsetTop, 5);\r\n                };\r\n\r\n                return false;\r\n            };\r\n        };\r\n    </script>\r\n</head>\r\n<body>\r\n    <div id=\"div1\">\r\n    </div>\r\n</body>\r\n</html>\r\n', '0', '2017-09-08 09:49:07', null, 'Js.javascript.cookie结合拖拽.MiaoV.txt');
