INSERT INTO `txt` VALUES ('471', '2', 'js.javascript.move.div变宽.多物体运动.txt', '<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n    <style type=\"text/css\">\r\n        div\r\n        {\r\n            width: 100px;\r\n            height: 50px;\r\n            background: red;\r\n            margin-top: 15px;\r\n        }\r\n    </style>\r\n    <meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\" />\r\n    <title>div���(�������˶�)</title>\r\n    <script type=\"text/javascript\">\r\n        window.onload = function () {\r\n            var aDiv = document.getElementsByTagName(\'div\');\r\n            var i = 0;\r\n            for (var i = 0; i < aDiv.length; i++) {\r\n                aDiv[i].onmouseover = function () {\r\n                    startMove(this, 300);\r\n                }\r\n                aDiv[i].onmouseout = function () {\r\n                    startMove(this, 100);\r\n                }\r\n            }\r\n        }\r\n        function startMove(oDiv, iTarget) {\r\n            clearInterval(oDiv.timer); //�ر��Լ��Ķ�ʱ��(�����ڵ������˶�)\r\n            oDiv.timer = setInterval(function () {//ÿ���˶����Լ��Ķ�ʱ��(�����ڵ������˶�)\r\n                var iSpeed = (iTarget - oDiv.offsetWidth) / 8\r\n                iSpeed = iSpeed > 0 ? Math.ceil(iSpeed) : Math.floor(iSpeed);\r\n                if (oDiv.offsetWidth == iTarget) {\r\n                    clearInterval(oDiv.timer); //�ر��Լ��Ķ�ʱ��(�����ڵ������˶�)\r\n                } else {\r\n                    oDiv.style.width = oDiv.offsetWidth + iSpeed + \'px\';\r\n                }\r\n            }, 30);\r\n        }\r\n    </script>\r\n</head>\r\n<body>\r\n    <div>\r\n    </div>\r\n    <div>\r\n    </div>\r\n    <div>\r\n    </div>\r\n</body>\r\n</html>\r\n', '0', '2017-09-08 09:49:07', null, 'js.javascript.move.div变宽.多物体运动.txt');
INSERT INTO `txt` VALUES ('472', '2', 'js.javascript.move.opacity.filter.匀速运动.淡入淡出.图片.miaoV.txt', '<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n    <title>���뵭����ͼƬ</title>\r\n    <style type=\'text/css\'>\r\n        #img1\r\n        {\r\n            filter: alpha(opacity:30);\r\n            opacity: 0.3;\r\n        }\r\n    </style>\r\n    <script type=\"text/javascript\">\r\n\r\n        window.onload = function () {\r\n            var timer = null;\r\n            var oImg = document.getElementById(\'img1\');\r\n            var alpha = 30;\r\n            function startMove(iTarget) {\r\n                clearInterval(timer); //�����ʱ��\r\n                timer = setInterval(function () {//��ʱ��\r\n                    var iSpeed = 10; //�˶��ٶ�\r\n                    iSpeed = (alpha < iTarget) ? iSpeed : -iSpeed;\r\n                    if (alpha == iTarget) {\r\n                        clearInterval(timer);\r\n                    } else {\r\n                        alpha += iSpeed;\r\n                        oImg.style.filter = \'alpha(opacity:\' + alpha + \')\';\r\n                        oImg.style.opacity = alpha / 100;\r\n                    }\r\n                }, 30);\r\n            }\r\n            oImg.onmouseover = function () {\r\n                startMove(100);\r\n            }\r\n            oImg.onmouseout = function () {\r\n                startMove(30);\r\n            }\r\n        };\r\n    </script>\r\n</head>\r\n<body>\r\n    <img id=\"img1\" src=\"beauty.jpg\" alt=\"beauty\" />\r\n</body>\r\n</html>\r\n', '0', '2017-09-08 09:49:07', null, 'js.javascript.move.opacity.filter.匀速运动.淡入淡出.图片.miaoV.txt');
INSERT INTO `txt` VALUES ('473', '2', 'Js.javascript.move.匀速运动.分享到.侧边栏广告.MiaoV.txt', '<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n    <title>�����,��������</title>\r\n    <style type=\"text/css\">\r\n        #div1\r\n        {\r\n            width: 100px;\r\n            height: 200px;\r\n            background: #CCC;\r\n            position: absolute;\r\n            left: -100px;\r\n        }\r\n        #div1 span\r\n        {\r\n            width: 20px;\r\n            height: 60px;\r\n            line-height: 20px;\r\n            text-align: center;\r\n            background: yellow;\r\n            position: absolute;\r\n            left: 100px;\r\n            top: 70px;\r\n        }\r\n    </style>\r\n    <script type=\"text/javascript\">\r\n        window.onload = function () {\r\n            var oDiv = document.getElementById(\"div1\"); //��ʼ��ʱ��,div��leftΪ-100(����״̬)\r\n            oDiv.onmouseover = function () {\r\n                startMove(0); //����ƹ�:Ŀ���--div��leftΪ0\r\n            }\r\n            oDiv.onmouseout = function () {\r\n                startMove(-100); //����Ƴ�:Ŀ���-100,�ص���ʼ״̬(����״̬)\r\n            }\r\n        }\r\n        var timer = null;\r\n        function startMove(iTarget) {\r\n            var oDiv = document.getElementById(\'div1\');\r\n            clearInterval(timer);\r\n            timer = setInterval(function () {\r\n                var iSpeed = 10; //�˶��ٶ�\r\n                //��������offsetLeft��Ŀ������:�ٶ�Ϊ����,�����Ŀ���ұ�:�ٶ�Ϊ��\r\n                iSpeed = (oDiv.offsetLeft < iTarget) ? iSpeed : -iSpeed;\r\n                if (oDiv.offsetLeft == iTarget) {//(�˶��ٶȱ����ܱ�300����, Ҫ��Ȼ�͵�����Ŀ��)\r\n                    clearInterval(timer);\r\n                } else {\r\n                    oDiv.style.left = oDiv.offsetLeft + iSpeed + \'px\';\r\n                }\r\n            }, 30);\r\n        }      \r\n    </script>\r\n</head>\r\n<body>\r\n    <div id=\"div1\">\r\n        <span>�����</span>\r\n    </div>\r\n</body>\r\n</html>\r\n', '0', '2017-09-08 09:49:07', null, 'Js.javascript.move.匀速运动.分享到.侧边栏广告.MiaoV.txt');
INSERT INTO `txt` VALUES ('474', '2', 'js.javascript.move.多个div淡入淡出.txt', '<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n    <style type=\"text/css\">\r\n        div\r\n        {\r\n            width: 100px;\r\n            height: 100px;\r\n            background: red;\r\n            margin: 10px;\r\n            filter: alpha(opacity:30);\r\n            opacity: 0.3;\r\n        }\r\n    </style>\r\n    <meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\" />\r\n    <title>���div���뵭��</title>\r\n    <script type=\"text/javascript\">\r\n        window.onload = function () {\r\n            //�������˶��Ĺؼ�:���еĶ��������ܹ���\r\n            var aDiv = document.getElementsByTagName(\'div\');\r\n            var i = 0;\r\n\r\n            for (i = 0; i < aDiv.length; i++) {\r\n                aDiv[i].alpha = 30;\r\n                aDiv[i].onmouseover = function () {\r\n                    startMove(this, 100);\r\n                }\r\n\r\n                aDiv[i].onmouseout = function () {\r\n                    startMove(this, 30);\r\n                }\r\n            }\r\n        }\r\n        //var alpha=30;\r\n\r\n        function startMove(obj, iTarget) {\r\n            clearInterval(obj.timer);\r\n            obj.timer = setInterval(function () {\r\n                var iSpeed = (iTarget - obj.alpha) / 8;\r\n                iSpeed = iSpeed > 0 ? Math.ceil(iSpeed) : Math.floor(iSpeed);\r\n\r\n                if (obj.alpha == iTarget) {\r\n                    clearInterval(obj.timer);\r\n                }\r\n                else {\r\n                    obj.alpha += iSpeed;\r\n\r\n                    obj.style.filter = \'alpha(opacity:\' + obj.alpha + \')\';\r\n                    obj.style.opacity = obj.alpha / 100;\r\n                }\r\n            }, 30);\r\n        }\r\n\r\n    </script>\r\n</head>\r\n<body>\r\n    <div>\r\n    </div>\r\n    <div>\r\n    </div>\r\n    <div>\r\n    </div>\r\n    <div>\r\n    </div>\r\n</body>\r\n</html>\r\n', '0', '2017-09-08 09:49:07', null, 'js.javascript.move.多个div淡入淡出.txt');
INSERT INTO `txt` VALUES ('475', '2', 'js.javascript.move.弹性运动+摩擦运动.txt', '<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n    <style type=\"text/css\">\r\n        #div1\r\n        {\r\n            width: 100px;\r\n            height: 100px;\r\n            background: red;\r\n            position: absolute;\r\n            top: 50px;\r\n            left: 0;\r\n        }\r\n    </style>\r\n    <meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\" />\r\n    <title>弹性+摩擦运动</title>\r\n    <script type=\"text/javascript\">\r\n        var iSpeed = 0;\r\n        function startMove() {\r\n            var oDiv = document.getElementById(\'div1\');\r\n            setInterval(function () {\r\n                iSpeed += (300 - oDiv.offsetLeft) / 5; //弹性\r\n                iSpeed *= 0.7;                                     //摩擦\r\n                oDiv.style.left = oDiv.offsetLeft + iSpeed + \'px\';\r\n            }, 30);\r\n        }\r\n    </script>\r\n</head>\r\n<body>\r\n    <input type=\"button\" value=\"开始运动\" onclick=\"startMove()\" />\r\n    <div id=\"div1\">\r\n    </div>\r\n    <div style=\"position: absolute; width: 1px; height: 300px; left: 300px; top: 0; background: black;\">\r\n    </div>\r\n</body>\r\n</html>\r\n', '0', '2017-09-08 09:49:07', null, 'js.javascript.move.弹性运动+摩擦运动.txt');
INSERT INTO `txt` VALUES ('476', '2', 'js.javascript.move.弹性运动.txt', '<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n    <style>\r\n        #div1\r\n        {\r\n            width: 100px;\r\n            height: 100px;\r\n            background: red;\r\n            position: absolute;\r\n            top: 50px;\r\n            left: 0;\r\n        }\r\n    </style>\r\n    <meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\" />\r\n    <title>弹性运动</title>\r\n    <script type=\"text/javascript\">\r\n        var iSpeed = 0;\r\n        function startMove() {\r\n            var oDiv = document.getElementById(\'div1\');\r\n            setInterval(function () {\r\n                iSpeed += (300 - oDiv.offsetLeft) / 50;\r\n                oDiv.style.left = oDiv.offsetLeft + iSpeed + \'px\';\r\n            }, 30);\r\n        }\r\n    </script>\r\n</head>\r\n<body>\r\n    <input type=\"button\" value=\"开始运动\" onclick=\"startMove()\" />\r\n    <div id=\"div1\">\r\n    </div>\r\n    <div style=\"position: absolute; width: 1px; height: 300px; left: 300px; top: 0; background: black;\">\r\n    </div>\r\n</body>\r\n</html>\r\n', '0', '2017-09-08 09:49:07', null, 'js.javascript.move.弹性运动.txt');
INSERT INTO `txt` VALUES ('477', '2', 'js.javascript.move.浮动.客服.QQ.侧边栏.缓冲运动.miaoV.txt', '<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n    <title>����.�ͷ�.QQ.�����.�����˶�</title>\r\n    <style type=\"text/css\">\r\n        #div1\r\n        {\r\n            width: 100px;\r\n            height: 100px;\r\n            background: red;\r\n            position: absolute;\r\n            right: 0;\r\n        }\r\n    </style>\r\n    <script type=\"text/javascript\">\r\n        window.onload = window.onscroll = function () {\r\n            var oDiv = document.getElementById(\'div1\');\r\n\r\n            var scrollTop = document.documentElement.scrollTop || document.body.scrollTop; //�����������ĸ߶�\r\n            var clientHeight = document.documentElement.clientHeight; //�������ĸ߶�\r\n            //!!!!!!!!!!!!ֻҪ�г����ͻ���С�����ֵĿ���\r\n            //�����С������,�����˶���С�˶���λ��1����,\r\n            //���л���������֮�䶶��\r\n            //�������:ȡ��.����:startMove(parseInt(styleTop));\r\n            var clientCenter = (clientHeight - oDiv.offsetHeight) / 2; //���������м�ĸ߶�\r\n            var styleTop = scrollTop + clientCenter; //����:�������м�(���������ҳ��)��Topֵ\r\n            //  oDiv.style.top = scrollTop + clientCenter + \'px\'; //��������˶�,���ϸı�topֵ,��һֱ����\r\n            startMove(parseInt(styleTop));\r\n\r\n        }\r\n        var timer = null;\r\n        function startMove(iTarget) {\r\n            var oDiv = document.getElementById(\'div1\');\r\n            clearInterval(timer);\r\n            timer = setInterval(function () {\r\n                var iSpeed = (iTarget - oDiv.offsetTop) / 8;\r\n                iSpeed = (iSpeed > 0) ? Math.ceil(iSpeed) : Math.floor(iSpeed);\r\n                if (oDiv.offsetTop == iTarget) {\r\n                    clearInterval(timer);\r\n                } else {\r\n                    oDiv.style.top = oDiv.offsetTop + iSpeed + \'px\';\r\n                }\r\n            }, 30);\r\n        }\r\n    </script>\r\n</head>\r\n<body style=\"height: 2000px;\">\r\n    <div id=\"div1\">\r\n    </div>\r\n</body>\r\n</html>\r\n', '0', '2017-09-08 09:49:07', null, 'js.javascript.move.浮动.客服.QQ.侧边栏.缓冲运动.miaoV.txt');
INSERT INTO `txt` VALUES ('478', '2', 'js.javascript.move.缓冲运动.miaoV.txt', '<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n    <style type=\"text/css\">\r\n        #div1\r\n        {\r\n            width: 100px;\r\n            height: 100px;\r\n            position: absolute;\r\n            background: red;\r\n            left: 0px;\r\n            top: 50px;\r\n        }\r\n    </style>\r\n    <meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\" />\r\n    <title>无标题文档</title>\r\n    <script type=\"text/javascript\">\r\n        var timer = null;\r\n        //缓冲运动\r\n        //越接近终点, 速度越小\r\n        //距离越小, 速度越小\r\n        //距离越大, 速度越大\r\n        //速度处理:速度=(目标点-左坐标)/缩放系数>>>>例如: var iSpeed = (iTarget - oDiv.offsetLeft) / 8;\r\n        function startMove(iTarget) {\r\n            var oDiv = document.getElementById(\'div1\');\r\n            clearInterval(timer);\r\n            timer = setInterval(function () {\r\n                //越来越小,到最后速度为小于1的小数\r\n                //oDiv.offsetLeft + iSpeed,像素最小单位是1\r\n                //所以,到运动的最后的部分(iSpeed<1)时就会被舍去\r\n                //oDiv.offsetLeft就永远达不到目标点\r\n                var iSpeed = (iTarget - oDiv.offsetLeft) / 8;\r\n                //解决方案:\r\n                //如果运动的物体在目标点左边(速度大于零),向上取整,不到1的时候就取到1,这样就不会被忽略了\r\n                // iSpeed = Math.ceil(iSpeed);\r\n                //如果运动的物体在目标点右边(速度小于零),向下取整\r\n                //iSpeed = Math.floor(iSpeed);\r\n                iSpeed = (iSpeed > 0) ? Math.ceil(iSpeed) : Math.floor(iSpeed);\r\n                //是否到达终点\r\n                //要用(==),不能用(>=),因为用(>=),运动物体在目标点右边时就不会运动了\r\n                if (oDiv.offsetLeft == iTarget) {\r\n                    clearInterval(timer); //到达终点\r\n                }\r\n                else {\r\n                    oDiv.style.left = oDiv.offsetLeft + iSpeed + \'px\'; //到达之前\r\n                }\r\n            }, 30);\r\n        }\r\n\r\n    </script>\r\n</head>\r\n<body>\r\n    <input type=\"button\" value=\"开始运动\" onclick=\"startMove(300)\" />\r\n    <div id=\"div1\">\r\n    </div>\r\n    <span style=\"width: 1px; height: 300px; background: black; position: absolute; left: 300px;\r\n        top: 0;\"></span>\r\n</body>\r\n</html>\r\n', '0', '2017-09-08 09:49:07', null, 'js.javascript.move.缓冲运动.miaoV.txt');
