INSERT INTO `txt` VALUES ('480', '2', 'js.javascript.null.undefined.typeof.txt', '   <script type=\"text/javascript\">\r\n       //<![CDATA[\r\n        var a;\r\n        var b = null;\r\n        document.write(a); // undefined\r\n        document.write(b); //null\r\n        document.write(undefined == true); //false\r\n        document.write(undefined == \"\"); //false\r\n        document.write(undefined == 0); //false\r\n        document.write(undefined == -0); //false\r\n        document.write(undefined == NaN); //false\r\n        document.write(undefined == null); //true\r\n        document.write(undefined === null); //false\r\n        document.write(typeof (undefined === null)); //boolean��typeod()�ж����ͣ�\r\n        document.write(1985 === \"1985\"); //false(�������ںţ�ֵ�����Ͷ�Ҫ���)\r\n        document.write(1985 == \"1985\"); //true\r\n         //]]>\r\n    </script>\r\n', '0', '2017-09-08 09:49:07', null, 'js.javascript.null.undefined.typeof.txt');
INSERT INTO `txt` VALUES ('481', '2', 'js.javascript.oop.prototype.inherit.继承.面向对象..jicheng.miaoV.html.txt', '<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n    <meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\" />\r\n    <title>继承</title>\r\n    <script type=\"text/javascript\">\r\n        //------------------------父级构造函数-----------\r\n        function Person(name, sex) {\r\n            this.name = name;\r\n            this.sex = sex;\r\n        }\r\n        Person.prototype.showName = function () {\r\n            alert(this.name);\r\n        };\r\n        Person.prototype.showSex = function () {\r\n            alert(this.sex);\r\n        };\r\n        //-----------------------实现继承-----------------------\r\n        //1、《构造函数的伪装》 调用父级的构造函数---为了继承属性\r\n        function Worker(name, sex, job) {\r\n            //《构造函数的伪装》 调用父级的构造函数---为了继承属性\r\n            Person.call(this, name, sex); //this指的是Worker实例化的一个对象\r\n            //添加新属性\r\n            this.job = job;\r\n        }\r\n        //2、《原型链》通过原型来继承父级的方法\r\n        //Worker.prototype = Person.prototype;//引用：弊端（子类的属性修改会改变父级的属性）\r\n        //不用引用，而是把父级的属性copy到子类\r\n        for (var i in Person.prototype) {\r\n            Worker.prototype[i] = Person.prototype[i];\r\n        };\r\n        //添加子类自己的属性\r\n        Worker.prototype.showJob = function () {\r\n            alert(this.job);\r\n        };\r\n        //========================invoke=====================\r\n        //----父类实例------\r\n        var oP = new Person(\'blue\', \'man\');\r\n        oP.showName();\r\n        oP.showSex();\r\n        //-----子类实例------\r\n        var oW = new Worker(\'blue\', \'man\', \'worker\');\r\n        oW.showName();\r\n        oW.showSex();\r\n        oW.showJob();\r\n    </script>\r\n</head>\r\n<body>\r\n</body>\r\n</html>\r\n', '0', '2017-09-08 09:49:07', null, 'js.javascript.oop.prototype.inherit.继承.面向对象..jicheng.miaoV.html.txt');
INSERT INTO `txt` VALUES ('482', '2', 'js.javascript.oop.prototype.拖拽.tuozhuai.MiaoV.html.txt', '<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n    <style type=\"text/css\">\r\n        #div1 { width: 100px; height: 100px; background: red; position: absolute; }\r\n        #div2 { width: 100px; height: 100px; background: yellow; position: absolute; }\r\n    </style>\r\n    <meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\" />\r\n    <title>��ק</title>\r\n    <script type=\"text/javascript\">\r\n        window.onload = function () {\r\n            //invoke\r\n            new Drag(\'div1\');\r\n            new Drag(\'div2\');\r\n        };\r\n\r\n        //��ק����\r\n        function Drag(id) {\r\n            var _this = this;\r\n\r\n            this.disX = 0;\r\n            this.disY = 0;\r\n            this.oDiv = document.getElementById(id);\r\n            this.oDiv.onmousedown = function () {\r\n                _this.fnDown();\r\n            };\r\n        }\r\n\r\n        Drag.prototype.fnDown = function (ev) {\r\n            var _this = this;\r\n            var oEvent = ev || event;\r\n            this.disX = oEvent.clientX - this.oDiv.offsetLeft;\r\n            this.disY = oEvent.clientY - this.oDiv.offsetTop;\r\n            document.onmousemove = function () {\r\n                _this.fnMove();\r\n            };\r\n            document.onmouseup = function () {\r\n                _this.fnUp();\r\n            };\r\n        };\r\n\r\n        Drag.prototype.fnMove = function (ev) {\r\n            var oEvent = ev || event;\r\n            this.oDiv.style.left = oEvent.clientX - this.disX + \'px\';\r\n            this.oDiv.style.top = oEvent.clientY - this.disY + \'px\';\r\n        };\r\n\r\n        Drag.prototype.fnUp = function () {\r\n            document.onmousemove = null;\r\n            document.onmouseup = null;\r\n        };\r\n    </script>\r\n</head>\r\n<body>\r\n    <div id=\"div1\">\r\n        Div1\r\n    </div>\r\n    <div id=\"div2\">\r\n        Div2\r\n    </div>\r\n</body>\r\n</html>\r\n', '0', '2017-09-08 09:49:07', null, 'js.javascript.oop.prototype.拖拽.tuozhuai.MiaoV.html.txt');
INSERT INTO `txt` VALUES ('483', '2', 'js.javascript.oop.构造函数.miaoV.txt', '   /**\r\n        *构造函数(首字母大写)\r\n        */\r\n        function CreatePerson(name, sex) {\r\n            //不同的东西写到构造函数中\r\n            this.name = name;//属性\r\n            this.sex = sex;//方法\r\n        }\r\n        //相同的东西写到原型里\r\n        //利用原型给构造函数添加方法\r\n        createPerson.prototype.showName = function () {\r\n            alert(\'我的名字叫：\' + this.name);\r\n        }\r\n        //利用原型给构造函数添加方法\r\n        createPerson.prototype.showSex = function () {\r\n            alert(\'我是：\' + this.sex + \"的\");\r\n        }\r\n        \r\n        var p1 =new CreatePerson(\'blue\', \'男\');\r\n        p1.showName();\r\n', '0', '2017-09-08 09:49:07', null, 'js.javascript.oop.构造函数.miaoV.txt');
INSERT INTO `txt` VALUES ('484', '2', 'js.javascript.parentNode.DOM.父节点.txt', '<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n    <title>DOM</title>\r\n    <script type=\"text/javascript\">\r\n        //childNodes�ӽڵ�\r\n        window.onload = function () {\r\n            var aA = document.getElementsByTagName(\'a\');\r\n            var i = 0;\r\n            for (i = 0; i < aA.length; i++) {\r\n                aA[i].onclick = function () {\r\n                    this.parentNode.style.display = \'none\'; //parentNode���ڵ�\r\n                }\r\n            }\r\n        }\r\n    </script>\r\n</head>\r\n<body>\r\n    <ul>\r\n        <li>aaaaaaa<a href=\"javascript:void(0);\">����</a></li>\r\n        <li>bbbbbb<a href=\"javascript:void(0);\">����</a></li>\r\n        <li>ccccccc<a href=\"javascript:;\">����</a></li>\r\n    </ul>\r\n</body>\r\n</html>\r\n', '0', '2017-09-08 09:49:07', null, 'js.javascript.parentNode.DOM.父节点.txt');
INSERT INTO `txt` VALUES ('485', '2', 'js.javascript.random.获得随即数.txt', 'using System;\r\nusing System.Collections.Generic;\r\nusing System.Linq;\r\nusing System.Text;\r\n\r\nnamespace OLCC.Common\r\n{\r\n    /// <summary>\r\n    /// ��ȡ��ˮ��\r\n    /// </summary>\r\n    public static class GetRandomCode\r\n    {\r\n        #region ��ȡ23λ��ˮ��\r\n        public static string getCurDateTimeWithMillion()\r\n        {\r\n            string taskid = DateTime.Now.ToString(\"yyyyMMddHHmmssfff\");\r\n            Random r = new Random(GetRandomSeed());\r\n            String append = r.Next(100000, 899999).ToString();\r\n            taskid = taskid + append;\r\n            return taskid;\r\n        }\r\n        #endregion\r\n\r\n        /// <summary>\r\n        /// GetRandomSeed\r\n        /// </summary>\r\n        /// <returns></returns>\r\n        private static int GetRandomSeed()\r\n        {\r\n            byte[] bytes = new byte[4];\r\n            System.Security.Cryptography.RNGCryptoServiceProvider rng = new System.Security.Cryptography.RNGCryptoServiceProvider();\r\n            rng.GetBytes(bytes);\r\n            return BitConverter.ToInt32(bytes, 0);\r\n        }\r\n    }\r\n}\r\n', '0', '2017-09-08 09:49:07', null, 'js.javascript.random.获得随即数.txt');
INSERT INTO `txt` VALUES ('486', '2', 'js.javascript.scroll.div.move.侧边栏广告.miaoV.txt', '<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n    <title>��������</title>\r\n    <style type=\"text/css\">\r\n        #div1\r\n        {\r\n            width: 100px;\r\n            height: 100px;\r\n            background: red;\r\n            position: absolute;\r\n            right: 0;\r\n            border: 1px solid red;\r\n        }\r\n    </style>\r\n    <script src=\"move.js\" type=\"text/javascript\"></script>\r\n    <script type=\"text/javascript\">\r\n        window.onload = window.onresize = window.onscroll = function () {\r\n            var oDiv = document.getElementById(\'div1\');\r\n            var scrollTop = document.documentElement.scrollTop || document.body.scrollTop; //�����������ľ���(���ݰ�)\r\n            var t = (document.documentElement.clientHeight - oDiv.offsetHeight) / 2; //(���������-div�ĸ�)/2\r\n            // oDiv.style.top = scrollTop + t + \'px\';//�ᶶ��,����Ѷ�λ��Ϊ�̶���λ����,����ie6������\r\n            startMove(oDiv, { top: scrollTop + t }); //�����˶�����������\r\n        }\r\n    </script>\r\n</head>\r\n<body style=\"height: 3000px;\">\r\n    <div id=\"div1\">\r\n        ��������\r\n    </div>\r\n</body>\r\n</html>\r\n', '0', '2017-09-08 09:49:07', null, 'js.javascript.scroll.div.move.侧边栏广告.miaoV.txt');
INSERT INTO `txt` VALUES ('487', '2', 'js.javascript.select.option.todo.txt', '\r\nJavascript ����select�ؼ���ȫ���������޸ġ�ɾ����ѡ�С���ա��жϴ��ڵȣ�\r\nPosted on 2007-08-08 14:56 ���һ �Ķ�(92208) ����(41) �༭ �ղ�\r\n 1�ж�selectѡ���� �Ƿ����Value=\"paraValue\"��Item \r\n 2��selectѡ���� ����һ��Item \r\n 3��selectѡ���� ɾ��һ��Item \r\n 4ɾ��select��ѡ�е��� \r\n 5�޸�selectѡ���� value=\"paraValue\"��textΪ\"paraText\" \r\n 6����select��text=\"paraText\"�ĵ�һ��ItemΪѡ�� \r\n 7����select��value=\"paraValue\"��ItemΪѡ�� \r\n 8�õ�select�ĵ�ǰѡ�����value \r\n 9�õ�select�ĵ�ǰѡ�����text \r\n10�õ�select�ĵ�ǰѡ�����Index \r\n11���select���� \r\n\r\njs ����\r\n// 1.�ж�selectѡ���� �Ƿ����Value=\"paraValue\"��Item        \r\nfunction jsSelectIsExitItem(objSelect, objItemValue) {        \r\n    var isExit = false;        \r\n    for (var i = 0; i < objSelect.options.length; i++) {        \r\n        if (objSelect.options[i].value == objItemValue) {        \r\n            isExit = true;        \r\n            break;        \r\n        }        \r\n    }        \r\n    return isExit;        \r\n}         \r\n   \r\n// 2.��selectѡ���� ����һ��Item        \r\nfunction jsAddItemToSelect(objSelect, objItemText, objItemValue) {        \r\n    //�ж��Ƿ����        \r\n    if (jsSelectIsExitItem(objSelect, objItemValue)) {        \r\n        alert(\"��Item��Valueֵ�Ѿ�����\");        \r\n    } else {        \r\n        var varItem = new Option(objItemText, objItemValue);      \r\n        objSelect.options.add(varItem);     \r\n        alert(\"�ɹ�����\");     \r\n    }        \r\n}        \r\n   \r\n// 3.��selectѡ���� ɾ��һ��Item        \r\nfunction jsRemoveItemFromSelect(objSelect, objItemValue) {        \r\n    //�ж��Ƿ����        \r\n    if (jsSelectIsExitItem(objSelect, objItemValue)) {        \r\n        for (var i = 0; i < objSelect.options.length; i++) {        \r\n            if (objSelect.options[i].value == objItemValue) {        \r\n                objSelect.options.remove(i);        \r\n                break;        \r\n            }        \r\n        }        \r\n        alert(\"�ɹ�ɾ��\");        \r\n    } else {        \r\n        alert(\"��select�� �����ڸ���\");        \r\n    }        \r\n}    \r\n   \r\n   \r\n// 4.ɾ��select��ѡ�е���    \r\nfunction jsRemoveSelectedItemFromSelect(objSelect) {        \r\n    var length = objSelect.options.length - 1;    \r\n    for(var i = length; i >= 0; i--){    \r\n        if(objSelect[i].selected == true){    \r\n            objSelect.options[i] = null;    \r\n        }    \r\n    }    \r\n}      \r\n   \r\n// 5.�޸�selectѡ���� value=\"paraValue\"��textΪ\"paraText\"        \r\nfunction jsUpdateItemToSelect(objSelect, objItemText, objItemValue) {        \r\n    //�ж��Ƿ����        \r\n    if (jsSelectIsExitItem(objSelect, objItemValue)) {        \r\n        for (var i = 0; i < objSelect.options.length; i++) {        \r\n            if (objSelect.options[i].value == objItemValue) {        \r\n                objSelect.options[i].text = objItemText;        \r\n                break;        \r\n            }        \r\n        }        \r\n        alert(\"�ɹ��޸�\");        \r\n    } else {        \r\n        alert(\"��select�� �����ڸ���\");        \r\n    }        \r\n}        \r\n   \r\n// 6.����select��text=\"paraText\"�ĵ�һ��ItemΪѡ��        \r\nfunction jsSelectItemByValue(objSelect, objItemText) {            \r\n    //�ж��Ƿ����        \r\n    var isExit = false;        \r\n    for (var i = 0; i < objSelect.options.length; i++) {        \r\n        if (objSelect.options[i].text == objItemText) {        \r\n            objSelect.options[i].selected = true;        \r\n            isExit = true;        \r\n            break;        \r\n        }        \r\n    }              \r\n    //Show�����        \r\n    if (isExit) {        \r\n        alert(\"�ɹ�ѡ��\");        \r\n    } else {        \r\n        alert(\"��select�� �����ڸ���\");        \r\n    }        \r\n}        \r\n   \r\n// 7.����select��value=\"paraValue\"��ItemΪѡ��    \r\ndocument.all.objSelect.value = objItemValue;    \r\n       \r\n// 8.�õ�select�ĵ�ǰѡ�����value    \r\nvar currSelectValue = document.all.objSelect.value;    \r\n       \r\n// 9.�õ�select�ĵ�ǰѡ�����text    \r\nvar currSelectText = document.all.objSelect.options[document.all.objSelect.selectedIndex].text;    \r\n       \r\n// 10.�õ�select�ĵ�ǰѡ�����Index    \r\nvar currSelectIndex = document.all.objSelect.selectedIndex;    \r\n       \r\n// 11.���select����    \r\ndocument.all.objSelect.options.length = 0; \r\n', '0', '2017-09-08 09:49:07', null, 'js.javascript.select.option.todo.txt');
INSERT INTO `txt` VALUES ('488', '2', 'js.javascript.setAttribute.设置属性.txt', '  \r\n <select id=\"REPLYTYPE\" name=\'REPLYTYPE\' style=\'width: 152px;\'>\r\n                        <option value=\'\'>ȫ������</option>\r\n                        <option value=\'10\'>�绰�ظ�</option>\r\n                        <option value=\'20\'>�ʼ��ظ�</option>\r\n                        <option value=\'30\'>���Żظ�</option>\r\n                    </select>\r\n<script type=\"text/javascript\">\r\n(function(){\r\n             var taskType=\"10\";//ȡ����ֵ\r\n            var oSelect=document.getElementById(\'REPLYTYPE\');\r\n            var aOption=oSelect.getElementsByTagName(\'option\');\r\n            for (var i = 0; i < aOption.length; i++) {\r\n                if (aOption[i].value==taskType) {\r\n                alert(i);\r\n                aOption[i].setAttribute(\'selected\',\'selected\');//����Ϊѡ��״̬\r\n                }\r\n            }\r\n    })();\r\n</script>\r\n', '0', '2017-09-08 09:49:07', null, 'js.javascript.setAttribute.设置属性.txt');
INSERT INTO `txt` VALUES ('489', '2', 'js.javascript.setcookie.getcookie. 设置获取cookie.txt', '<html>\r\n <head>\r\n  <title>cookie的生存期</title>\r\n  <script language=\"javascript\" type=\"text/javascript\">\r\n  <!--\r\n   if(document.cookie==\"\")\r\n   {\r\n    document.write(\"当前没有cookie\");\r\n   }\r\n   else\r\n   {\r\n    document.write(\"cookie文件中的内容为：<br>\");\r\n    document.write(document.cookie,\"<br>\");\r\n    document.write(\"cookieName的值为：\",getCookieValue(\"cookieName\"),\"<br>\");\r\n    document.write(\"cookiePassword的值为：\", getCookieValue(\"cookiePassword\"),\"<br>\");\r\n   }\r\n   \r\n   function setCookie()\r\n   {\r\n    var cookie1 = \"cookieName = \" + escape(document.myForm.myName.value);\r\n    var cookie2 = \"cookiePassword = \" + escape(document.myForm.myPassword.value);\r\n    if (myForm.cookieDate.value!=\"0\")\r\n    {\r\n     //获取当前日期\r\n     var now = new Date();\r\n\r\n     //在当前日期上加上Cookie的存活时间与路径\r\n     now.setDate(now.getDate() + parseInt(myForm.cookieDate.value));\r\n     cookie1 = cookie1+\";expires=\" + now.toGMTString()+ \";path=/\";;\r\n     cookie2 = cookie2+\";expires=\" + now.toGMTString()+ \";path=/\";\r\n    }\r\n    document.cookie = cookie1;\r\n    document.cookie = cookie2;\r\n    alert(\"cookie设置完毕\");\r\n   }\r\n   \r\n   function getCookieValue(cookieName)\r\n   {\r\n    var cookies = unescape(document.cookie);\r\n    var cookieIndex = cookies.indexOf(cookieName+\"=\");\r\n    if (cookieIndex!=-1)\r\n    {\r\n     var cookieValueStart = cookieIndex + cookieName.length + 1;\r\n     var cookieValueEnd = cookies.indexOf(\";\",cookieValueStart);\r\n     if (cookieValueEnd == -1)\r\n     {\r\n      cookieValueEnd = cookies.length;\r\n     }\r\n     var cookieValue = cookies.substring(cookieValueStart,cookieValueEnd);\r\n     return cookieValue;\r\n    }\r\n   }\r\n  -->\r\n  </script>\r\n </head>\r\n <body>\r\n  <form name=\"myForm\">\r\n   姓名：<input type=\"text\" name=\"myName\"><br>\r\n   密码：<input type=\"password\" name=\"myPassword\"><br>\r\n   选择cookie的保留时间：\r\n   <select name=\"cookieDate\">\r\n    <option value=\"1\">1天</option>\r\n    <option value=\"30\">30天</option>\r\n    <option value=\"365\">365天</option>\r\n    <option value=\"0\" selected>不保留</option>\r\n   </select><br>\r\n   <input type=\"button\" value=\"确定\" onclick=\"setCookie()\">\r\n  </form>\r\n </body>\r\n</html> \r\n', '0', '2017-09-08 09:49:07', null, 'js.javascript.setcookie.getcookie. 设置获取cookie.txt');
