INSERT INTO `txt` VALUES ('496', '2', 'js.javascript.w3c.HTML.DOM.setTimeout().txt', 'HTML DOM setTimeout() ����\r\n\r\nHTML DOM Window ����ο��ֲ�\r\n������÷�\r\n\r\nsetTimeout() ����������ָ���ĺ���������ú����������ʽ��\r\n�﷨\r\n\r\nsetTimeout(code,millisec)\r\n\r\n���� 	����\r\ncode 	���衣Ҫ���õĺ�����Ҫִ�е� JavaScript ���봮��\r\nmillisec 	���衣��ִ�д���ǰ��ȴ��ĺ�������\r\n��ʾ��ע��\r\n\r\n��ʾ��setTimeout() ִֻ�� code һ�Ρ����Ҫ��ε��ã���ʹ�� setInterval() ������ code �����ٴε��� setTimeout()��\r\nʵ��\r\n\r\n<html>\r\n<head>\r\n<script type=\"text/javascript\">\r\nfunction timedMsg()\r\n{\r\nvar t=setTimeout(\"alert(\'5 seconds!\')\",5000)\r\n}\r\n</script>\r\n</head>\r\n\r\n<body>\r\n<form>\r\n<input type=\"button\" value=\"Display timed alertbox!\"\r\nonClick=\"timedMsg()\">\r\n</form>\r\n<p>Click on the button above. An alert box will be\r\ndisplayed after 5 seconds.</p>\r\n</body>\r\n\r\n</html>\r\n\r\n', '0', '2017-09-08 09:49:07', null, 'js.javascript.w3c.HTML.DOM.setTimeout().txt');
