INSERT INTO `txt` VALUES ('499', '2', 'js.javascript.判断浏览器.浏览器版本.Using the navigator object to detect client\'s browser.html.txt', '<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n    <title>�ж�������Լ�������İ汾</title>\r\n</head>\r\n<body>\r\n    <script type=\"text/javascript\">\r\n        //=====================���ж������===============================\r\n        //���ж������\r\n        if (navigator.userAgent.indexOf(\"MSIE\") > 0) {\r\n            document.write(\"ie\");\r\n        } else if (navigator.userAgent.indexOf(\"Firefox\") > 0) {\r\n            //firefox\r\n            document.write(\"firefox\");\r\n        } else if (navigator.userAgent.indexOf(\"Chrome\") > 0) {\r\n            //chrome\r\n            document.write(\"chrome\");\r\n        } else if (navigator.userAgent.indexOf(\"Safari\") > 0) {\r\n            //safari\r\n            document.write(\"safari\");\r\n        } else {\r\n            document.write(\"Other\");\r\n            //this part can be used as opera area\r\n        }\r\n        //========================��ϸ�ж�������Լ���汾===========================\r\n        //�ж�IE������Լ�������İ汾\r\n        if (/MSIE (\\d+\\.\\d+);/.test(navigator.userAgent)) { //test for MSIE x.x;\r\n            var ieversion = new Number(RegExp.$1) // capture x.x portion and store as a number\r\n            if (ieversion >= 9)\r\n                document.write(\"You\'re using IE9 or above\")\r\n            else if (ieversion >= 8)\r\n                document.write(\"You\'re using IE8 or above\")\r\n            else if (ieversion >= 7)\r\n                document.write(\"You\'re using IE7.x\")\r\n            else if (ieversion >= 6)\r\n                document.write(\"You\'re using IE6.x\")\r\n            else if (ieversion >= 5)\r\n                document.write(\"You\'re using IE5.x\")\r\n        }\r\n        //�жϻ������