INSERT INTO `txt` VALUES ('530', '2', 'js.判断字符串长度.真实长度.汉字算两个，英文占一个.txt', 'Ŀ�ģ������ַ�������(Ӣ��ռ1���ַ������ĺ���ռ2���ַ�)\r\n\r\n����һ��\r\n\r\n\r\n���ƴ��� ��������:\r\n\r\n\r\nString.prototype.gblen = function() {  \r\n  var len = 0;  \r\n  for (var i=0; i<this.length; i++) {  \r\n    if (this.charCodeAt(i)>127 || this.charCodeAt(i)==94) {  \r\n       len += 2;  \r\n     } else {  \r\n       len ++;  \r\n     }  \r\n   }  \r\n  return len;  \r\n}\r\n\r\n��������\r\n\r\n���ƴ��� ��������:\r\n\r\nfunction strlen(str){\r\n    var len = 0;\r\n    for (var i=0; i<str.length; i++) { \r\n     var c = str.charCodeAt(i); \r\n    //���ֽڼ�1 \r\n     if ((c >= 0x0001 && c <= 0x007e) || (0xff60<=c && c<=0xff9f)) { \r\n       len++; \r\n     } \r\n     else { \r\n      len+=2; \r\n     } \r\n    } \r\n    return len;\r\n}\r\n\r\n��������\r\n\r\n���ƴ��� ��������:\r\n\r\nvar jmz = {};\r\njmz.GetLength = function(str) {\r\n  ///<summary>����ַ���ʵ�ʳ��ȣ�����2��Ӣ��1</summary>\r\n  ///<param name=\"str\">Ҫ��ó��ȵ��ַ���</param>\r\n  var realLength = 0, len = str.length, charCode = -1;\r\n  for (var i = 0; i < len; i++) {\r\n    charCode = str.charCodeAt(i);\r\n    if (charCode >= 0 && charCode <= 128) realLength += 1;\r\n    else realLength += 2;\r\n  }\r\n  return realLength;\r\n};\r\n\r\n�����ģ�\r\n\r\n���ƴ��� ��������:\r\n\r\nvar l = str.length; \r\nvar blen = 0; \r\nfor(i=0; i<l; i++) { \r\nif ((str.charCodeAt(i) & 0xff00) != 0) { \r\nblen ++; \r\n} \r\nblen ++; \r\n}\r\n\r\n�����壺\r\n��˫�ֽڵ��滻���������ֽڵ�Ȼ���ٻ�ó��� \r\n\r\n���ƴ��� ��������:\r\n\r\ngetBLen = function(str) {\r\n  if (str == null) return 0;\r\n  if (typeof str != \"string\"){\r\n    str += \"\";\r\n  }\r\n  return str.replace(/[^\\x00-\\xff]/g,\"01\").length;\r\n}\r\n', '0', '2017-09-08 09:49:08', null, 'js.判断字符串长度.真实长度.汉字算两个，英文占一个.txt');
INSERT INTO `txt` VALUES ('531', '2', 'js.图片预加载demo.txt', '<!DOCTYPE html> \r\n<html xmlns=\"http://www.w3.org/1999/xhtml\"> \r\n<head> \r\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\" /> \r\n<title>img ready</title> \r\n<script> \r\nvar imgReady = function (url, callback, error) {\r\n	var width, height, intervalId, check, div,\r\n		img = new Image(),\r\n		body = document.body;\r\n		\r\n	img.src = url;\r\n	\r\n	// �ӻ����ж�ȡ\r\n	if (img.complete) {\r\n		return callback(img.width, img.height);\r\n	};\r\n	\r\n	// ͨ��ռλ��ǰ��ȡͼƬͷ������\r\n	if (body) {\r\n		div = document.createElement(\'div\');\r\n		div.style.cssText = \'visibility:hidden;position:absolute;left:0;top:0;width:1px;height:1px;overflow:hidden\';\r\n		div.appendChild(img)\r\n		body.appendChild(div);\r\n		width = img.offsetWidth;\r\n		height = img.offsetHeight;\r\n		\r\n		check = function () {\r\n			if (img.offsetWidth !== width || img.offsetHeight !== height) {\r\n				clearInterval(intervalId);\r\n				callback(img.offsetWidth, img.clientHeight);\r\n				img.onload = null;\r\n				div.innerHTML = \'\';\r\n				div.parentNode.removeChild(div);\r\n			};\r\n		};\r\n		\r\n		intervalId = setInterval(check, 150);\r\n	};\r\n	\r\n	// ������Ϻ�ʽ��ȡ\r\n	img.onload = function () {\r\n		callback(img.width, img.height);\r\n		img.onload = img.onerror = null;\r\n		clearInterval(intervalId);\r\n		body && img.parentNode.removeChild(img);\r\n	};\r\n	\r\n	// ͼƬ���ش���\r\n	img.onerror = function () {\r\n		error && error();\r\n		clearInterval(intervalId);\r\n		body && img.parentNode.removeChild(img);\r\n	};\r\n	\r\n};\r\n</script> \r\n \r\n<script> \r\n/* demo script */\r\nwindow.onload = function () {\r\nvar imgUrl = \'http://www.planeart.cn/demo/imgReady/vistas24.jpg?\',\r\n	testReadyBtn = document.getElementById(\'testReadyBtn\'),\r\n	clsCacheBtn = document.getElementById(\'clsCacheBtn\'),\r\n	status = document.getElementById(\'status\'),\r\n	statusReady = document.getElementById(\'statusReady\'),\r\n	statusLoad = document.getElementById(\'statusLoad\'),\r\n	imgWrap = document.getElementById(\'imgWrap\');\r\n	\r\nvar imgLoad = function (url, callback) {\r\n	var img = new Image();\r\n \r\n	img.src = url;\r\n	if (img.complete) {\r\n		callback(img.width, img.height);\r\n	} else {\r\n		img.onload = function () {\r\n			callback(img.width, img.height);\r\n			img.onload = null;\r\n		};\r\n	};\r\n \r\n};\r\n \r\ntestReadyBtn.onclick = function () {\r\n	var that = this;\r\n \r\n	that.disabled = true;\r\n	status.style.display = \'block\';\r\n	statusLoad.innerHTML = statusReady.innerHTML = \'Loading...\';\r\n	imgWrap.innerHTML = \'<img src=\"\' + imgUrl + \'\" />\';\r\n	\r\n	// ʹ��ռλ��ʽ���ٻ�ȡ��С\r\n	imgReady(imgUrl, function (width, height) {\r\n		statusReady.innerHTML = \'width:\' + width + \'; height:\' + height;\r\n	}, function () {\r\n		statusReady.innerHTML = \'Img Error!\';\r\n	});\r\n	\r\n	// ʹ�ô�ͳ��ʽ��ȡ��С\r\n	imgLoad(imgUrl, function (width, height) {\r\n		statusLoad.innerHTML = \'width:\' + width + \'; height:\' + height;\r\n		that.disabled = false;\r\n	}, function () {\r\n		statusLoad.innerHTML = \'Img Error!\';\r\n		that.disabled = false;\r\n	});\r\n	\r\n};\r\n \r\nclsCacheBtn.onclick = function () {\r\n	imgUrl += new Date().getTime();\r\n	status.style.display = \'none\';\r\n	imgWrap.innerHTML = \'\';\r\n};\r\n \r\n};\r\n</script> \r\n</head> \r\n \r\n<body> \r\n<p>���£���<a href=\"http://www.planeart.cn/?p=1121\">��̸javascriptͼƬԤ���ؼ���</a>��</p> \r\n<p><button id=\"testReadyBtn\">��ʼ����ͼƬ</button> <button id=\"clsCacheBtn\">��ջ���</button>(���ͼƬ���ع���������Ỻ��)</p> \r\n<div id=\"status\" style=\"display:none\"> \r\n	<p><strong>imgReady:</strong><p> \r\n	<p id=\"statusReady\"></p> \r\n	<p><strong>imgLoad:</strong></p> \r\n	<p id=\"statusLoad\"><p> \r\n</div> \r\n<div id=\"imgWrap\"></div> \r\n<div style=\"display:none\"><script src=\"http://s86.cnzz.com/stat.php?id=1581115&web_id=1581115\" charset=\"gb2312\"></script></div> \r\n</body> \r\n</html> \r\n', '0', '20