INSERT INTO `txt` VALUES ('534', '2', 'js.日期时间带星期农历.rili.nongli.txt', '<%@ Page Language=\"C#\" AutoEventWireup=\"true\" CodeFile=\"DataTimeDemo.aspx.cs\" Inherits=\"DataTimeDemo\" %>\r\n\r\n<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n<head>\r\n    <meta http-equiv=\"Content-Type\" content=\"text/html; charset=gb2312\" />\r\n    <title>����ʱ�������ũ��js����-���˽�վ</title>\r\n    <meta name=\"keywords\" content=\"jquery��Ч,JS����,js��Ч�����ȫ,js��Ч������,�����˵�����\" />\r\n    <meta name=\"description\" content=\"���˽�վΪ���ṩ-JS���룬js��Ч�����ȫ��js��Ч�����룬�����˵��������˵����룬�����˵�����ͻ���jquery�ĸ�����Ч��jquery�����\" />\r\n</head>\r\n<body>\r\n    <script>\r\n        var CalendarData = new Array(20);\r\n        var madd = new Array(12);\r\n        var TheDate = new Date();\r\n        var tgString = \"���ұ����켺�����ɹ�\";\r\n        var dzString = \"�ӳ���î������δ�����纥\";\r\n        var numString = \"һ�����������߰˾�ʮ\";\r\n        var monString = \"�������������߰˾�ʮ����\";\r\n        var weekString = \"��һ����������\";\r\n        var sx = \"��ţ������������Ｆ����\";\r\n        var cYear;\r\n        var cMonth;\r\n        var cDay;\r\n        var cHour;\r\n        var cDateString;\r\n        var DateString;\r\n        var Browser = navigator.appName;\r\n\r\n        function init() {\r\n            CalendarData[0] = 0x41A95;\r\n            CalendarData[1] = 0xD4A;\r\n            CalendarData[2] = 0xDA5;\r\n            CalendarData[3] = 0x20B55;\r\n            CalendarData[4] = 0x56A;\r\n            CalendarData[5] = 0x7155B;\r\n            CalendarData[6] = 0x25D;\r\n            CalendarData[7] = 0x92D;\r\n            CalendarData[8] = 0x5192B;\r\n            CalendarData[9] = 0xA95;\r\n            CalendarData[10] = 0xB4A;\r\n            CalendarData[11] = 0x416AA;\r\n            CalendarData[12] = 0xAD5;\r\n            CalendarData[13] = 0x90AB5;\r\n            CalendarData[14] = 0x4BA;\r\n            CalendarData[15] = 0xA5B;\r\n            CalendarData[16] = 0x60A57;\r\n            CalendarData[17] = 0x52B;\r\n            CalendarData[18] = 0xA93;\r\n            CalendarData[19] = 0x40E95;\r\n            madd[0] = 0;\r\n            madd[1] = 31;\r\n            madd[2] = 59;\r\n            madd[3] = 90;\r\n            madd[4] = 120;\r\n            madd[5] = 151;\r\n            madd[6] = 181;\r\n            madd[7] = 212;\r\n            madd[8] = 243;\r\n            madd[9] = 273;\r\n            madd[10] = 304;\r\n            madd[11] = 334;\r\n        }\r\n\r\n        function GetBit(m, n) {\r\n            return (m >> n) & 1;\r\n        }\r\n\r\n        function e2c() {\r\n            var total, m, n, k;\r\n            var isEnd = false;\r\n            var tmp = TheDate.getYear();\r\n            if (tmp < 1900) tmp += 1900;\r\n            total = (tmp - 2001) * 365\r\n          + Math.floor((tmp - 2001) / 4)\r\n          + madd[TheDate.getMonth()]\r\n          + TheDate.getDate()\r\n          - 23;\r\n            if (TheDate.getYear() % 4 == 0 && TheDate.getMonth() > 1)\r\n                total++;\r\n            for (m = 0; ; m++) {\r\n                k = (CalendarData[m] < 0xfff) ? 11 : 12;\r\n                for (n = k; n >= 0; n--) {\r\n                    if (total <= 29 + GetBit(CalendarData[m], n)) {\r\n                        isEnd = true;\r\n                        break;\r\n                    }\r\n                    total = total - 29 - GetBit(CalendarData[m], n);\r\n                }\r\n                if (isEnd) break;\r\n            }\r\n            cYear = 2001 + m;\r\n            cMonth = k - n + 1;\r\n            cDay = total;\r\n            if (k == 12) {\r\n                if (cMonth == Math.floor(CalendarData[m] / 0x10000) + 1)\r\n                    cMonth = 1 - cMonth;\r\n                if (cMonth > Math.floor(CalendarData[m] / 0x10000) + 1)\r\n                    cMonth--;\r\n            }\r\n            cHour = Math.floor((TheDate.getHours() + 3) / 2);\r\n        }\r\n\r\n        function GetcDateString() {\r\n            var tmp = \"\";\r\n            tmp += tgString.charAt((cYear - 4) % 10);       //���  \r\n            tmp += dzString.charAt((cYear - 4) % 12);       //��֧  \r\n            tmp += \"��(\";\r\n            tmp += sx.charAt((cYear - 4) % 12);\r\n            tmp += \")   \";\r\n            if (cMonth < 1) {\r\n                tmp += \"��\";\r\n                tmp += monString.charAt(-cMonth - 1);\r\n            }\r\n            else\r\n                tmp += monString.charAt(cMonth - 1);\r\n            tmp += \"��\";\r\n            tmp += (cDay < 11) ? \"��\" : ((cDay < 20) ? \"ʮ\" : ((cDay < 30) ? \"إ\" : \"ئ\"));\r\n            if (cDay % 10 != 0 || cDay == 10)\r\n                tmp += numString.charAt((cDay - 1) % 10);\r\n            tmp += \"    \";\r\n            if (cHour == 13) tmp += \"ҹ\";\r\n            tmp += dzString.charAt((cHour - 1) % 12);\r\n            tmp += \"ʱ\";\r\n            cDateString = tmp;\r\n            return tmp;\r\n        }\r\n\r\n        function GetDateString() {\r\n            var tmp = \"\";\r\n            var t1 = TheDate.getYear();\r\n            if (t1 < 1900) t1 += 1900;\r\n            tmp += t1\r\n                + \"��\"\r\n                + (TheDate.getMonth() + 1) + \"��\"\r\n                + TheDate.getDate() + \"��   \"\r\n                + TheDate.getHours() + \":\"\r\n                + ((TheDate.getMinutes() < 10) ? \"0\" : \"\")\r\n                + TheDate.getMinutes()\r\n                + \"   ����\" + weekString.charAt(TheDate.getDay());\r\n            DateString = tmp;\r\n            return tmp;\r\n        }\r\n\r\n        init();\r\n        e2c();\r\n        GetDateString();\r\n        GetcDateString();\r\n        document.write(DateString, \"ũ��\", cDateString);  \r\n    </script>\r\n</body>\r\n</html>\r\n', '0', '2017-09-08 09:49:08', null, 'js.日期时间带星期农历.rili.non