INSERT INTO `txt` VALUES ('537', '2', 'js.获取时间.当前时间.date.datetime.txt', 'Js��ȡ��ǰ����ʱ�估��������\r\n\r\nvar myDate = new Date();\r\nmyDate.getYear();        //��ȡ��ǰ���(2λ)\r\nmyDate.getFullYear();    //��ȡ���������(4λ,1970-????)\r\nmyDate.getMonth();       //��ȡ��ǰ�·�(0-11,0����1��)\r\nmyDate.getDate();        //��ȡ��ǰ��(1-31)\r\nmyDate.getDay();         //��ȡ��ǰ����X(0-6,0����������)\r\nmyDate.getTime();        //��ȡ��ǰʱ��(��1970.1.1��ʼ�ĺ�����)\r\nmyDate.getHours();       //��ȡ��ǰСʱ��(0-23)\r\nmyDate.getMinutes();     //��ȡ��ǰ������(0-59)\r\nmyDate.getSeconds();     //��ȡ��ǰ����(0-59)\r\nmyDate.getMilliseconds();    //��ȡ��ǰ������(0-999)\r\nmyDate.toLocaleDateString();     //��ȡ��ǰ����\r\nvar mytime=myDate.toLocaleTimeString();     //��ȡ��ǰʱ��\r\nmyDate.toLocaleString( );        //��ȡ������ʱ��\r\n\r\n \r\n\r\n����ʱ��ű��ⷽ���б�\r\n\r\nDate.prototype.isLeapYear �ж�����\r\nDate.prototype.Format ���ڸ�ʽ��\r\nDate.prototype.DateAdd ���ڼ���\r\nDate.prototype.DateDiff �Ƚ����ڲ�\r\nDate.prototype.toString ����ת�ַ���\r\nDate.prototype.toArray ���ڷָ�Ϊ����\r\nDate.prototype.DatePart ȡ���ڵĲ�����Ϣ\r\nDate.prototype.MaxDayOfDate ȡ���������µ��������\r\nDate.prototype.WeekNumOfYear �ж�����������ĵڼ���\r\nStringToDate �ַ���ת������\r\nIsValidDate ��֤������Ч��\r\nCheckDateTime ��������ʱ����\r\ndaysBetween ����������\r\n\r\njs���룺\r\n\r\n//---------------------------------------------------  \r\n// �ж�����  \r\n//---------------------------------------------------  \r\nDate.prototype.isLeapYear = function()   \r\n{   \r\n    return (0==this.getYear()%4&&((this.getYear()%100!=0)||(this.getYear()%400==0)));   \r\n}   \r\n  \r\n//---------------------------------------------------  \r\n// ���ڸ�ʽ��  \r\n// ��ʽ YYYY/yyyy/YY/yy ��ʾ���  \r\n// MM/M �·�  \r\n// W/w ����  \r\n// dd/DD/d/D ����  \r\n// hh/HH/h/H ʱ��  \r\n// mm/m ����  \r\n// ss/SS/s/S ��  \r\n//---------------------------------------------------  \r\nDate.prototype.Format = function(formatStr)   \r\n{   \r\n    var str = formatStr;   \r\n    var Week = [\'��\',\'һ\',\'��\',\'��\',\'��\',\'��\',\'��\'];  \r\n  \r\n    str=str.replace(/yyyy|YYYY/,this.getFullYear());   \r\n    str=str.replace(/yy|YY/,(this.getYear() % 100)>9?(this.getYear() % 100).toString():\'0\' + (this.getYear() % 100));   \r\n  \r\n    str=str.replace(/MM/,this.getMonth()>9?this.getMonth().toString():\'0\' + this.getMonth());   \r\n    str=str.replace(/M/g,this.getMonth());   \r\n  \r\n    str=str.replace(/w|W/g,Week[this.getDay()]);   \r\n  \r\n    str=str.replace(/dd|DD/,this.getDate()>9?this.getDate().toString():\'0\' + this.getDate());   \r\n    str=str.replace(/d|D/g,this.getDate());   \r\n  \r\n    str=str.replace(/hh|HH/,this.getHours()>9?this.getHours().toString():\'0\' + this.getHours());   \r\n    str=str.replace(/h|H/g,this.getHours());   \r\n    str=str.replace(/mm/,this.getMinutes()>9?this.getMinutes().toString():\'0\' + this.getMinutes());   \r\n    str=str.replace(/m/g,this.getMinutes());   \r\n  \r\n    str=str.replace(/ss|SS/,this.getSeconds()>9?this.getSeconds().toString():\'0\' + this.getSeconds());   \r\n    str=str.replace(/s|S/g,this.getSeconds());   \r\n  \r\n    return str;   \r\n}   \r\n  \r\n//+---------------------------------------------------  \r\n//| ������ʱ��������� ���ڸ�ʽΪ YYYY-MM-dd   \r\n//+---------------------------------------------------  \r\nfunction daysBetween(DateOne,DateTwo)  \r\n{   \r\n    var OneMonth = DateOne.substring(5,DateOne.lastIndexOf (\'-\'));  \r\n    var OneDay = DateOne.substring(DateOne.length,DateOne.lastIndexOf (\'-\')+1);  \r\n    var OneYear = DateOne.substring(0,DateOne.indexOf (\'-\'));  \r\n  \r\n    var TwoMonth = DateTwo.substring(5,DateTwo.lastIndexOf (\'-\'));  \r\n    var TwoDay = DateTwo.substring(DateTwo.length,DateTwo.lastIndexOf (\'-\')+1);  \r\n    var TwoYear = DateTwo.substring(0,DateTwo.indexOf (\'-\'));  \r\n  \r\n    var cha=((Date.parse(OneMonth+\'/\'+OneDay+\'/\'+OneYear)- Date.parse(TwoMonth+\'/\'+TwoDay+\'/\'+TwoYear))/86400000);   \r\n    return Math.abs(cha);  \r\n}  \r\n  \r\n  \r\n//+---------------------------------------------------  \r\n//| ���ڼ���  \r\n//+---------------------------------------------------  \r\nDate.prototype.DateAdd = function(strInterval, Number) {   \r\n    var dtTmp = this;  \r\n    switch (strInterval) {   \r\n        case \'s\' :return new Date(Date.parse(dtTmp) + (1000 * Number));  \r\n        case \'n\' :return new Date(Date.parse(dtTmp) + (60000 * Number));  \r\n        case \'h\' :return new Date(Date.parse(dtTmp) + (3600000 * Number));  \r\n        case \'d\' :return new Date(Date.parse(dtTmp) + (86400000 * Number));  \r\n        case \'w\' :return new Date(Date.parse(dtTmp) + ((86400000 * 7) * Number));  \r\n        case \'q\' :return new Date(dtTmp.getFullYear(), (dtTmp.getMonth()) + Number*3, dtTmp.getDate(), dtTmp.getHours(), dtTmp.getMinutes(), dtTmp.getSeconds());  \r\n        case \'m\' :return new Date(dtTmp.getFullYear(), (dtTmp.getMonth()) + Number, dtTmp.getDate(), dtTmp.getHours(), dtTmp.getMinutes(), dtTmp.getSeconds());  \r\n        case \'y\' :return new Date((dtTmp.getFullYear() + Number), dtTmp.getMonth(), dtTmp.getDate(), dtTmp.getHours(), dtTmp.getMinutes(), dtTmp.getSeconds());  \r\n    }  \r\n}  \r\n  \r\n//+---------------------------------------------------  \r\n//| �Ƚ����ڲ� dtEnd ��ʽΪ�����ͻ�����Ч���ڸ�ʽ�ַ���  \r\n//+---------------------------------------------------  \r\nDate.prototype.DateDiff = function(strInterval, dtEnd) {   \r\n    var dtStart = this;  \r\n    if (typeof dtEnd == \'string\' )//������ַ���ת��Ϊ������  \r\n    {   \r\n        dtEnd = StringToDate(dtEnd);  \r\n    }  \r\n    switch (strInterval) {   \r\n        case \'s\' :return parseInt((dtEnd - dtStart) / 1000);  \r\n        case \'n\' :return parseInt((dtEnd - dtStart) / 60000);  \r\n        case \'h\' :return parseInt((dtEnd - dtStart) / 3600000);  \r\n        case \'d\' :return parseInt((dtEnd - dtStart) / 86400000);  \r\n        case \'w\' :return parseInt((dtEnd - dtStart) / (86400000 * 7));  \r\n        case \'m\' :return (dtEnd.getMonth()+1)+((dtEnd.getFullYear()-dtStart.getFullYear())*12) - (dtStart.getMonth()+1);  \r\n        case \'y\' :return dtEnd.getFullYear() - dtStart.getFullYear();  \r\n    }  \r\n}  \r\n  \r\n//+---------------------------------------------------  \r\n//| ��������ַ�����������ϵͳ��toString����  \r\n//+---------------------------------------------------  \r\nDate.prototype.toString = function(showWeek)  \r\n{   \r\n    var myDate= this;  \r\n    var str = myDate.toLocaleDateString();  \r\n    if (showWeek)  \r\n    {   \r\n        var Week = [\'��\',\'һ\',\'��\',\'��\',\'��\',\'��\',\'��\'];  \r\n        str += \' ����\' + Week[myDate.getDay()];  \r\n    }  \r\n    return str;  \r\n}  \r\n  \r\n//+---------------------------------------------------  \r\n//| ���ںϷ�����֤  \r\n//| ��ʽΪ��YYYY-MM-DD��YYYY/MM/DD  \r\n//+---------------------------------------------------  \r\nfunction IsValidDate(DateStr)   \r\n{   \r\n    var sDate=DateStr.replace(/(^\\s+|\\s+$)/g,\'\'); //ȥ���߿ո�;   \r\n    if(sDate==\'\') return true;   \r\n    //�����ʽ����YYYY-(/)MM-(/)DD��YYYY-(/)M-(/)DD��YYYY-(/)M-(/)D��YYYY-(/)MM-(/)D���滻Ϊ\'\'   \r\n    //���ݿ��У��Ϸ����ڿ�����:YYYY-MM/DD(2003-3/21),���ݿ���Զ�ת��ΪYYYY-MM-DD��ʽ   \r\n    var s = sDate.replace(/[\\d]{ 4,4 }[\\-/]{ 1 }[\\d]{ 1,2 }[\\-/]{ 1 }[\\d]{ 1,2 }/g,\'\');   \r\n    if (s==\'\') //˵����ʽ����YYYY-MM-DD��YYYY-M-DD��YYYY-M-D��YYYY-MM-D   \r\n    {   \r\n        var t=new Date(sDate.replace(/\\-/g,\'/\'));   \r\n        var ar = sDate.split(/[-/:]/);   \r\n        if(ar[0] != t.getYear() || ar[1] != t.getMonth()+1 || ar[2] != t.getDate())   \r\n        {   \r\n            //alert(\'��������ڸ�ʽ����ʽΪ��YYYY-MM-DD��YYYY/MM/DD��ע�����ꡣ\');   \r\n            return false;   \r\n        }   \r\n    }   \r\n    else   \r\n    {   \r\n        //alert(\'��������ڸ�ʽ����ʽΪ��YYYY-MM-DD��YYYY/MM/DD��ע�����ꡣ\');   \r\n        return false;   \r\n    }   \r\n    return true;   \r\n}   \r\n  \r\n//+---------------------------------------------------  \r\n//| ����ʱ����  \r\n//| ��ʽΪ��YYYY-MM-DD HH:MM:SS  \r\n//+---------------------------------------------------  \r\nfunction CheckDateTime(str)  \r\n{   \r\n    var reg = /^(\\d+)-(\\d{ 1,2 })-(\\d{ 1,2 }) (\\d{ 1,2 }):(\\d{ 1,2 }):(\\d{ 1,2 })$/;   \r\n    var r = str.match(reg);   \r\n    if(r==null)return false;   \r\n    r[2]=r[2]-1;   \r\n    var d= new Date(r[1],r[2],r[3],r[4],r[5],r[6]);   \r\n    if(d.getFullYear()!=r[1])return false;   \r\n    if(d.getMonth()!=r[2])return false;   \r\n    if(d.getDate()!=r[3])return false;   \r\n    if(d.getHours()!=r[4])return false;   \r\n    if(d.getMinutes()!=r[5])return false;   \r\n    if(d.getSeconds()!=r[6])return false;   \r\n    return true;   \r\n}   \r\n  \r\n//+---------------------------------------------------  \r\n//| �����ڷָ������  \r\n//+---------------------------------------------------  \r\nDate.prototype.toArray = function()  \r\n{   \r\n    var myDate = this;  \r\n    var myArray = Array();  \r\n    myArray[0] = myDate.getFullYear();  \r\n    myArray[1] = myDate.getMonth();  \r\n    myArray[2] = myDate.getDate();  \r\n    myArray[3] = myDate.getHours();  \r\n    myArray[4] = myDate.getMinutes();  \r\n    myArray[5] = myDate.getSeconds();  \r\n    return myArray;  \r\n}  \r\n  \r\n//+---------------------------------------------------  \r\n//| ȡ������������Ϣ  \r\n//| ���� interval ��ʾ��������  \r\n//| y �� m�� d�� w���� ww�� hʱ n�� s��  \r\n//+---------------------------------------------------  \r\nDate.prototype.DatePart = function(interval)  \r\n{   \r\n    var myDate = this;  \r\n    var partStr=\'\';  \r\n    var Week = [\'��\',\'һ\',\'��\',\'��\',\'��\',\'��\',\'��\'];  \r\n    switch (interval)  \r\n    {   \r\n        case \'y\' :partStr = myDate.getFullYear();break;  \r\n        case \'m\' :partStr = myDate.getMonth()+1;break;  \r\n        case \'d\' :partStr = myDate.getDate();break;  \r\n        case \'w\' :partStr = Week[myDate.getDay()];break;  \r\n        case \'ww\' :partStr = myDate.WeekNumOfYear();break;  \r\n        case \'h\' :partStr = myDate.getHours();break;  \r\n        case \'n\' :partStr = myDate.getMinutes();break;  \r\n        case \'s\' :partStr = myDate.getSeconds();break;  \r\n    }  \r\n    return partStr;  \r\n}  \r\n  \r\n//+---------------------------------------------------  \r\n//| ȡ�õ�ǰ���������µ��������  \r\n//+---------------------------------------------------  \r\nDate.prototype.MaxDayOfDate = function()  \r\n{   \r\n    var myDate = this;  \r\n    var ary = myDate.toArray();  \r\n    var date1 = (new Date(ary[0],ary[1]+1,1));  \r\n    var date2 = date1.dateAdd(1,\'m\',1);  \r\n    var result = dateDiff(date1.Format(\'yyyy-MM-dd\'),date2.Format(\'yyyy-MM-dd\'));  \r\n    return result;  \r\n}  \r\n  \r\n//+---------------------------------------------------  \r\n//| ȡ�õ�ǰ������������һ���еĵڼ���  \r\n//+---------------------------------------------------  \r\nDate.prototype.WeekNumOfYear = function()  \r\n{   \r\n    var myDate = this;  \r\n    var ary = myDate.toArray();  \r\n    var year = ary[0];  \r\n    var month = ary[1]+1;  \r\n    var day = ary[2];  \r\n    document.write(\'< script language=VBScript\\> \\n\');  \r\n    document.write(\'myDate = Datue(\'\'+month+\'-\'+day+\'-\'+year+\'\') \\n\');  \r\n    document.write(\'result = DatePart(\'ww\', myDate) \\n\');  \r\n    document.write(\' \\n\');  \r\n    return result;  \r\n}  \r\n  \r\n//+---------------------------------------------------  \r\n//| �ַ���ת����������   \r\n//| ��ʽ MM/dd/YYYY MM-dd-YYYY YYYY/MM/dd YYYY-MM-dd  \r\n//+---------------------------------------------------  \r\nfunction StringToDate(DateStr)  \r\n{   \r\n  \r\n    var converted = Date.parse(DateStr);  \r\n    var myDate = new Date(converted);  \r\n    if (isNaN(myDate))  \r\n    {   \r\n        //var delimCahar = DateStr.indexOf(\'/\')!=-1?\'/\':\'-\';  \r\n        var arys= DateStr.split(\'-\');  \r\n        myDate = new Date(arys[0],--arys[1],arys[2]);  \r\n    }  \r\n    return myDate;  \r\n}  \r\n\r\n \r\n\r\n��Ҫ��ʾ:��ǰ���ڼ�ʱ��(��:2009-06-12 12:00)\r\n\r\nfunction CurentTime()\r\n    { \r\n        var now = new Date();\r\n       \r\n        var year = now.getFullYear();       //��\r\n        var month = now.getMonth() + 1;     //��\r\n        var day = now.getDate();            //��\r\n       \r\n        var hh = now.getHours();            //ʱ\r\n        var mm = now.getMinutes();          //��\r\n       \r\n        var clock = year + \"-\";\r\n       \r\n        if(month < 10)\r\n            clock += \"0\";\r\n       \r\n        clock += month + \"-\";\r\n       \r\n        if(day < 10)\r\n            clock += \"0\";\r\n           \r\n        clock += day + \" \";\r\n       \r\n        if(hh < 10)\r\n            clock += \"0\";\r\n           \r\n        clock += hh + \":\";\r\n        if (mm < 10) clock += \'0\'; \r\n        clock += mm; \r\n        return(clock); \r\n    } \r\n', '0', '2017-09-08 09:49:08', null, 'js.获取时间.当前时间.date.datetime.txt');
INSERT INTO `txt` VALUES ('538', '2', 'Js.获取本周、上周、本月、上月的开始日期、结束日期.txt', 'Js��ȡ���ܡ����ܡ����¡����µĿ�ʼ���ڡ���������Js��ȡ���ܡ����ܡ����¡����µĿ�ʼ���ڡ��������� (2015-08-06 13:21:35)ת�ب�\r\n��ǩ�� js��ȡ���ܡ����ܡ���	���ࣺ JS��jQuery��Ajax\r\n \r\n    var now = new Date();                    //��ǰ����\r\n    var nowDayOfWeek = now.getDay();         //���챾�ܵĵڼ���\r\n    var nowDay = now.getDate();              //��ǰ��\r\n    var nowMonth = now.getMonth();           //��ǰ��\r\n    var nowYear = now.getYear();             //��ǰ��\r\n    nowYear += (nowYear < 2000) ? 1900 : 0;  //\r\n\r\n    var lastMonthDate = new Date();  //��������\r\n    lastMonthDate.setDate(1);\r\n    lastMonthDate.setMonth(lastMonthDate.getMonth()-1);\r\n    var lastYear = lastMonthDate.getYear();\r\n    var lastMonth = lastMonthDate.getMonth();\r\n\r\n    //��ʽ�����ڣ�yyyy-MM-dd\r\n    function formatDate(date) {\r\n        var myyear = date.getFullYear();\r\n        var mymonth = date.getMonth()+1;\r\n        var myweekday = date.getDate();\r\n\r\n        if(mymonth < 10){\r\n            mymonth = \"0\" + mymonth;\r\n        }\r\n        if(myweekday < 10){\r\n            myweekday = \"0\" + myweekday;\r\n        }\r\n        return (myyear+\"-\"+mymonth + \"-\" + myweekday);\r\n    }\r\n\r\n    //���ĳ�µ�����\r\n    function getMonthDays(myMonth){\r\n        var monthStartDate = new Date(nowYear, myMonth, 1);\r\n        var monthEndDate = new Date(nowYear, myMonth + 1, 1);\r\n        var   days   =   (monthEndDate   -   monthStartDate)/(1000   *   60   *   60   *   24);\r\n        return   days;\r\n    }\r\n\r\n    //��ñ����ȵĿ�ʼ�·�\r\n    function getQuarterStartMonth(){\r\n        var quarterStartMonth = 0;\r\n        if(nowMonth<3){\r\n            quarterStartMonth = 0;\r\n        }\r\n        if(2<6){\r\n            quarterStartMonth = 3;\r\n        }\r\n        if(5<9){\r\n            quarterStartMonth = 6;\r\n        }\r\n        if(nowMonth>8){\r\n            quarterStartMonth = 9;\r\n        }\r\n        return quarterStartMonth;\r\n    }\r\n\r\n    //����\r\n    var getCurrentDate = new Date(nowYear, nowMonth, nowDay);\r\n    var getCurrentDate  = formatDate(getCurrentDate)\r\n\r\n    //����\r\n    var getYesterdayDate = new Date(nowYear, nowMonth, nowDay - 1);\r\n    var getYesterdayDate =  formatDate(getYesterdayDate);\r\n\r\n    //��ñ��ܵĿ�ʼ����\r\n    var getWeekStartDate = new Date(nowYear, nowMonth, nowDay - nowDayOfWeek);\r\n    var getWeekStartDate =  formatDate(getWeekStartDate);\r\n    //��ñ��ܵĽ�������\r\n    var getWeekEndDate = new Date(nowYear, nowMonth, nowDay + (6 - nowDayOfWeek));\r\n    var getWeekEndDate =  formatDate(getWeekEndDate);\r\n\r\n\r\n    //������ܵĿ�ʼ����\r\n    var getUpWeekStartDate = new Date(nowYear, nowMonth, nowDay - nowDayOfWeek -7);\r\n    var getUpWeekStartDate =  formatDate(getUpWeekStartDate);\r\n\r\n    //������ܵĽ�������\r\n    var getUpWeekEndDate = new Date(nowYear, nowMonth, nowDay + (6 - nowDayOfWeek - 7));\r\n    var getUpWeekEndDate =  formatDate(getUpWeekEndDate);\r\n\r\n\r\n    //��ñ��µĿ�ʼ����\r\n    var getMonthStartDate = new Date(nowYear, nowMonth, 1);\r\n    var getMonthStartDate =  formatDate(getMonthStartDate);\r\n\r\n    //��ñ��µĽ�������\r\n    var getMonthEndDate = new Date(nowYear, nowMonth, getMonthDays(nowMonth));\r\n    var getMonthEndDate =  formatDate(getMonthEndDate);\r\n\r\n    //������¿�ʼʱ��\r\n    var getLastMonthStartDate = new Date(nowYear, lastMonth, 1);\r\n    var getLastMonthStartDate = formatDate(getLastMonthStartDate);\r\n\r\n    //������½���ʱ��\r\n    var getLastMonthEndDate = new Date(nowYear, lastMonth, getMonthDays(lastMonth));\r\n    var getLastMonthEndDate = formatDate(getLastMonthEndDate);\r\n', '0', '2017-09-08 09:49:08', null, 'Js.获取本周、上周、本月、上月的开始日期、结束日期.txt');
INSERT INTO `txt` VALUES ('539', '2', 'Json.txt', '//==================http://www.json.org/js.html===============\r\n	\r\nJSON in JavaScript\r\n\r\nJavaScript is a general purpose programming language that was introduced as the page scripting language for Netscape Navigator. It is still widely believed to be a subset of Java, but it is not. It is a Scheme-like language with C-like syntax and soft objects. JavaScript was standardized in the ECMAScript Language Specification, Third Edition.\r\n\r\nJSON is a subset of the object literal notation of JavaScript. Since JSON is a subset of JavaScript, it can be used in the language with no muss or fuss.\r\n\r\nvar myJSONObject = {\"bindings\": [\r\n        {\"ircEvent\": \"PRIVMSG\", \"method\": \"newURI\", \"regex\": \"^http://.*\"},\r\n        {\"ircEvent\": \"PRIVMSG\", \"method\": \"deleteURI\", \"regex\": \"^delete.*\"},\r\n        {\"ircEvent\": \"PRIVMSG\", \"method\": \"randomURI\", \"regex\": \"^random.*\"}\r\n    ]\r\n};\r\n\r\nIn this example, an object is created containing a single member \"bindings\", which contains an array containing three objects, each containing \"ircEvent\", \"method\", and \"regex\" members.\r\n\r\nMembers can be retrieved using dot or subscript operators.\r\n\r\nmyJSONObject.bindings[0].method    // \"newURI\"\r\n\r\nTo convert a JSON text into an object, you can use the eval() function. eval() invokes the JavaScript compiler. Since JSON is a proper subset of JavaScript, the compiler will correctly parse the text and produce an object structure. The text must be wrapped in parens to avoid tripping on an ambiguity in JavaScript\'s syntax.\r\n\r\nvar myObject = eval(\'(\' + myJSONtext + \')\');\r\n\r\nThe eval function is very fast. However, it can compile and execute any JavaScript program, so there can be security issues. The use of eval is indicated when the source is trusted and competent. It is much safer to use a JSON parser. In web applications over XMLHttpRequest, communication is permitted only to the same origin that provide that page, so it is trusted. But it might not be competent. If the server is not rigorous in its JSON encoding, or if it does not scrupulously validate all of its inputs, then it could deliver invalid JSON text that could be carrying dangerous script. The eval function would execute the script, unleashing its malice.\r\n\r\nTo defend against this, a JSON parser should be used. A JSON parser will recognize only JSON text, rejecting all scripts. In browsers that provide native JSON support, JSON parsers are also much faster than eval. It is expected that native JSON support will be included in the next ECMAScript standard.\r\n\r\nvar myObject = JSON.parse(myJSONtext, reviver);\r\n\r\nThe optional reviver parameter is a function that will be called for every key and value at every level of the final result. Each value will be replaced by the result of the reviver function. This can be used to reform generic objects into instances of pseudoclasses, or to transform date strings into Date objects.\r\n\r\nmyData = JSON.parse(text, function (key, value) {\r\n    var type;\r\n    if (value && typeof value === \'object\') {\r\n        type = value.type;\r\n        if (typeof type === \'string\' && typeof window[type] === \'function\') {\r\n            return new (window[type])(value);\r\n        }\r\n    }\r\n    return value;\r\n});\r\n\r\nA JSON stringifier goes in the opposite direction, converting JavaScript data structures into JSON text. JSON does not support cyclic data structures, so be careful to not give cyclical structures to the JSON stringifier.\r\n\r\nvar myJSONText = JSON.stringify(myObject, replacer);\r\n\r\nIf the stringify method sees an object that contains a toJSON method, it calls that method, and stringifies the value returned. This allows an object to determine its own JSON representation.\r\n\r\nThe stringifier method can take an optional array of strings. These strings are used to select the properties that will be included in the JSON text.\r\n\r\nThe stringifier method can take an optional replacer function. It will be called after the toJSON method (if there is one) on each of the values in the structure. It will be passed each key and value as parameters, and this will be bound to object holding the key. The value returned will be stringified.\r\n\r\nValues that do not have a representation in JSON (such as functions and undefined) are excluded.\r\n\r\nNonfinite numbers are replaced with null. To substitute other values, you could use a replacer function like this:\r\n\r\nfunction replacer(key, value) {\r\n    if (typeof value === \'number\' && !isFinite(value)) {\r\n        return String(value);\r\n    }\r\n    return value;\r\n}\r\n\r\nGiving a corresponding reviver to JSON.parse can undo that.\r\n\r\nThe open source code of a JSON parser and JSON stringifier is available. When minified it is less than 2.5K.\r\n', '0', '2017-09-08 09:49:08', null, 'Json.txt');
INSERT INTO `txt` VALUES ('540', '2', 'JSONConvert.cs.txt', 'using System;\r\nusing System.Collections.Generic;\r\nusing System.Linq;\r\nusing System.Text;\r\nusing System.Text.RegularExpressions;\r\n\r\nnamespace BJHouse.Common\r\n{ \r\n    /// <summary>   \r\n    /// 类  名：JSONConvert   \r\n    /// 描  述：JSON解析类    \r\n    /// </summary>   \r\n    public static class JSONConvert\r\n    {\r\n        #region 全局变量\r\n\r\n        private static JSONObject _json = new JSONObject();//寄存器   \r\n        private static readonly string _SEMICOLON = \"@semicolon\";//分号转义符   \r\n        private static readonly string _COMMA = \"@comma\"; //逗号转义符 \r\n\r\n        #endregion\r\n\r\n        #region 字符串转义\r\n        /// <summary>   \r\n        /// 字符串转义,将双引号内的:和,分别转成_SEMICOLON和_COMMA   \r\n        /// </summary>   \r\n        /// <param name=\"text\"></param>   \r\n        /// <returns></returns>   \r\n        private static string StrEncode(string text)\r\n        {\r\n            MatchCollection matches = Regex.Matches(text, \"//\\\"[^//\\\"] //\\\"\");\r\n            foreach (Match match in matches)\r\n            {\r\n                text = text.Replace(match.Value, match.Value.Replace(\":\", _SEMICOLON).Replace(\",\", _COMMA));\r\n            }\r\n\r\n            return text;\r\n        }\r\n\r\n        /// <summary>   \r\n        /// 字符串转义,将_SEMICOLON和_COMMA分别转成:和,   \r\n        /// </summary>   \r\n        /// <param name=\"text\"></param>   \r\n        /// <returns></returns>   \r\n        private static string StrDecode(string text)\r\n        {\r\n            return text.Replace(_SEMICOLON, \":\").Replace(_COMMA, \",\");\r\n        }\r\n\r\n        #endregion\r\n\r\n        #region JSON最小单元解析\r\n\r\n        /// <summary>   \r\n        /// 最小对象转为JSONObject   \r\n        /// </summary>   \r\n        /// <param name=\"text\"></param>   \r\n        /// <returns></returns>   \r\n        private static JSONObject DeserializeSingletonObject(string text)\r\n        {\r\n            JSONObject jsonObject = new JSONObject();\r\n\r\n            MatchCollection matches = Regex.Matches(text, \"(//\\\"(?<key>[^//\\\"] )//\\\"://\\\"(?<value>[^,//\\\"] )//\\\")|(//\\\"(?<key>[^//\\\"] )//\\\":(?<value>[^,//\\\"//}] ))\");\r\n            foreach (Match match in matches)\r\n            {\r\n                string value = match.Groups[\"value\"].Value;\r\n                jsonObject.Add(match.Groups[\"key\"].Value, _json.ContainsKey(value) ? _json[value] : StrDecode(value));\r\n            }\r\n\r\n            return jsonObject;\r\n        }\r\n\r\n        /// <summary>   \r\n        /// 最小数组转为JSONArray   \r\n        /// </summary>   \r\n        /// <param name=\"text\"></param>   \r\n        /// <returns></returns>   \r\n        private static JSONArray DeserializeSingletonArray(string text)\r\n        {\r\n            JSONArray jsonArray = new JSONArray();\r\n\r\n            MatchCollection matches = Regex.Matches(text, \"(//\\\"(?<value>[^,//\\\"] )\\\")|(?<value>[^,//[//]] )\");\r\n            foreach (Match match in matches)\r\n            {\r\n                string value = match.Groups[\"value\"].Value;\r\n                jsonArray.Add(_json.ContainsKey(value) ? _json[value] : StrDecode(value));\r\n            }\r\n\r\n            return jsonArray;\r\n        }\r\n\r\n        /// <summary>   \r\n        /// 反序列化   \r\n        /// </summary>   \r\n        /// <param name=\"text\"></param>   \r\n        /// <returns></returns>   \r\n        private static string Deserialize(string text)\r\n        {\r\n            text = StrEncode(text);//转义;和,   \r\n\r\n            int count = 0;\r\n            string key = string.Empty;\r\n            string pattern = \"(//{[^//[//]//{//}] //})|(//[[^//[//]//{//}] //])\";\r\n\r\n            while (Regex.IsMatch(text, pattern))\r\n            {\r\n                MatchCollection matches = Regex.Matches(text, pattern);\r\n                foreach (Match match in matches)\r\n                {\r\n                    key = \"___key\" + count + \"___\";\r\n\r\n                    if (match.Value.Substring(0, 1) == \"{\")\r\n                        _json.Add(key, DeserializeSingletonObject(match.Value));\r\n                    else\r\n                        _json.Add(key, DeserializeSingletonArray(match.Value));\r\n\r\n                    text = text.Replace(match.Value, key);\r\n\r\n                    count++;\r\n                }\r\n            }\r\n            return text;\r\n        }\r\n\r\n        #endregion\r\n\r\n        #region 公共接口\r\n\r\n        /// <summary>   \r\n        /// 序列化JSONObject对象   \r\n        /// </summary>   \r\n        /// <param name=\"text\"></param>   \r\n        /// <returns></returns>   \r\n        public static JSONObject DeserializeObject(string text)\r\n        {\r\n            return _json[Deserialize(text)] as JSONObject;\r\n        }\r\n\r\n        /// <summary>   \r\n        /// 序列化JSONArray对象   \r\n        /// </summary>   \r\n        /// <param name=\"text\"></param>   \r\n        /// <returns></returns>   \r\n        public static JSONArray DeserializeArray(string text)\r\n        {\r\n            return _json[Deserialize(text)] as JSONArray;\r\n        }\r\n\r\n        /// <summary>   \r\n        /// 反序列化JSONObject对象   \r\n        /// </summary>   \r\n        /// <param name=\"jsonObject\"></param>   \r\n        /// <returns></returns>   \r\n        public static string SerializeObject(JSONObject jsonObject)\r\n        {\r\n            StringBuilder sb = new StringBuilder();\r\n            sb.Append(\"{\");\r\n            foreach (KeyValuePair<string, object> kvp in jsonObject)\r\n            {\r\n                if (kvp.Value is JSONObject)\r\n                {\r\n                    sb.Append(string.Format(\"\\\"{0}\\\":{1},\", kvp.Key, SerializeObject((JSONObject)kvp.Value)));\r\n                }\r\n                else if (kvp.Value is JSONArray)\r\n                {\r\n                    sb.Append(string.Format(\"\\\"{0}\\\":{1},\", kvp.Key, SerializeArray((JSONArray)kvp.Value)));\r\n                }\r\n                else if (kvp.Value is String)\r\n                {\r\n                    sb.Append(string.Format(\"\\\"{0}\\\":\\\"{1}\\\",\", kvp.Key, kvp.Value));\r\n                }\r\n                else\r\n                {\r\n                    sb.Append(string.Format(\"\\\"{0}\\\":\\\"{1}\\\",\", kvp.Key, \"\"));\r\n                }\r\n            }\r\n            if (sb.Length > 1)\r\n                sb.Remove(sb.Length - 1, 1);\r\n            sb.Append(\"}\");\r\n            return sb.ToString();\r\n        }\r\n\r\n        /// <summary>   \r\n        /// 反序列化JSONArray对象   \r\n        /// </summary>   \r\n        /// <param name=\"jsonArray\"></param>   \r\n        /// <returns></returns>   \r\n        public static string SerializeArray(JSONArray jsonArray)\r\n        {\r\n            StringBuilder sb = new StringBuilder();\r\n            sb.Append(\"[\");\r\n            for (int i = 0; i < jsonArray.Count; i++)\r\n            {\r\n                if (jsonArray[i] is JSONObject)\r\n                {\r\n                    sb.Append(string.Format(\"{0},\", SerializeObject((JSONObject)jsonArray[i])));\r\n                }\r\n                else if (jsonArray[i] is JSONArray)\r\n                {\r\n                    sb.Append(string.Format(\"{0},\", SerializeArray((JSONArray)jsonArray[i])));\r\n                }\r\n                else if (jsonArray[i] is String)\r\n                {\r\n                    sb.Append(string.Format(\"\\\"{0}\\\",\", jsonArray[i]));\r\n                }\r\n                else\r\n                {\r\n                    sb.Append(string.Format(\"\\\"{0}\\\",\", \"\"));\r\n                }\r\n\r\n            }\r\n            if (sb.Length > 1)\r\n                sb.Remove(sb.Length - 1, 1);\r\n            sb.Append(\"]\");\r\n            return sb.ToString();\r\n        }\r\n        #endregion\r\n\r\n        //IList<ParamsKeyList> 转josn\r\n        public static string ToJosn(IList<ParamsKeyList> list)\r\n        {\r\n            StringBuilder build = new StringBuilder();\r\n            build.Append(\"[\");\r\n            for (int i = 0; i < list.Count; i++)\r\n            {\r\n                ParamsKeyList item = list[i];\r\n                if (i != 0)\r\n                {\r\n                    build.Append(\",\");\r\n                }\r\n                build.Append(item.ToJosn());\r\n            }\r\n            build.Append(\"]\");\r\n            return build.ToString();\r\n        }\r\n    }\r\n\r\n    /// <summary>   \r\n    /// 类  名：JSONObject   \r\n    /// 描  述：JSON对象类   \r\n    /// 版  本：1.1.0   \r\n    /// 更新历史：   \r\n    ///     2010-01-06  继承Dictionary<TKey, TValue>代替this[]   \r\n    /// </summary>   \r\n    public class JSONObject : Dictionary<string, object>\r\n    { }\r\n\r\n    /// <summary>   \r\n    /// 类  名：JSONArray   \r\n    /// 描  述：JSON数组类   \r\n    /// 版  本：1.1.0   \r\n    /// 更新历史：   \r\n    ///     2010-01-06  继承List<T>代替this[]   \r\n    /// </summary>   \r\n    public class JSONArray : List<object>\r\n    { }\r\n\r\n\r\n    //嗯，json写完了，下面来看下使用的方法：\r\n\r\n    ////序列化   \r\n    //JSONArray jsonArray = new JSONArray();   \r\n    //jsonArray.Add(\"2006\");   \r\n    //jsonArray.Add(\"2007\");   \r\n    //jsonArray.Add(\"2008\");   \r\n    //jsonArray.Add(\"2009\");   \r\n    //jsonArray.Add(\"2010\");   \r\n\r\n    //JSONObject jsonObject = new JSONObject();   \r\n    //jsonObject.Add(\"domain\", \"mzwu.com\");   \r\n    //jsonObject.Add(\"years\", jsonArray);   \r\n\r\n    //Console.WriteLine(JSONConvert.SerializeObject(jsonObject));   \r\n\r\n    ////反序列化  \"{\\\"domain\\\":\\\"mzwu.com\\\",\\\"years\\\":[2006,2007,2008,2009,2010]}\"   \r\n    //JSONObject json = JSONConvert.DeserializeObject(JSONConvert.SerializeObject(jsonObject));   \r\n    //if (json != null)   \r\n    //{   \r\n    //    Console.WriteLine(json[\"domain\"]);   \r\n    //    Console.WriteLine(((JSONArray)json[\"years\"])[3]);   \r\n    //}   \r\n\r\n}\r\n', '0', '2017-09-08 09:49:08', null, 'JSONConvert.cs.txt');
INSERT INTO `txt` VALUES ('541', '2', 'JsonHelper.jun.cs.txt', 'using System;\r\nusing System.Collections;\r\nusing System.Collections.Generic;\r\nusing System.Data;\r\nusing System.Globalization;\r\nusing System.Linq;\r\nusing System.Reflection;\r\nusing System.Text;\r\nusing System.Web;\r\n\r\nnamespace WebApplication1\r\n{\r\n    /// <summary>\r\n    /// JSON帮助类\r\n    /// </summary>\r\n    public class JsonHelper\r\n    {\r\n        /// <summary>\r\n        /// 生成表单编辑赋值 JSON格式\r\n        /// </summary>\r\n        /// <param name=\"dt\"></param>\r\n        /// <param name=\"displayCount\"></param>\r\n        /// <returns></returns>\r\n        public static string CreateJsonOne(DataTable dt, bool displayCount)\r\n        {\r\n            StringBuilder JsonString = new StringBuilder();\r\n            //Exception Handling       \r\n            if (dt != null && dt.Rows.Count > 0)\r\n            {\r\n\r\n                for (int i = 0; i < dt.Rows.Count; i++)\r\n                {\r\n                    JsonString.Append(\"{ \");\r\n                    for (int j = 0; j < dt.Columns.Count; j++)\r\n                    {\r\n                        if (j < dt.Columns.Count - 1)\r\n                        {\r\n                            JsonString.Append(\"\" + dt.Columns[j].ColumnName.ToString().ToLower() + \":\" + \"\\\"\" + dt.Rows[i][j].ToString() + \"\\\",\");\r\n                        }\r\n                        else if (j == dt.Columns.Count - 1)\r\n                        {\r\n                            JsonString.Append(\"\" + dt.Columns[j].ColumnName.ToString().ToLower() + \":\" + \"\\\"\" + dt.Rows[i][j].ToString() + \"\\\"\");\r\n                        }\r\n                    }\r\n\r\n                    if (i == dt.Rows.Count - 1)\r\n                    {\r\n                        JsonString.Append(\"} \");\r\n                    }\r\n                    else\r\n                    {\r\n                        JsonString.Append(\"}, \");\r\n                    }\r\n                }\r\n\r\n                return JsonString.ToString();\r\n            }\r\n            else\r\n            {\r\n                return null;\r\n            }\r\n\r\n        }\r\n\r\n\r\n        /// <summary>\r\n        /// 将DataTable中的数据转换成JSON格式\r\n        /// </summary>\r\n        /// <param name=\"dt\">数据源DataTable</param>\r\n        /// <param name=\"displayCount\">是否输出数据总条数</param>\r\n        /// <returns></returns>\r\n        public static string CreateJsonParameters(DataTable dt, bool displayCount)\r\n        {\r\n            return CreateJsonParameters(dt, displayCount, dt.Rows.Count);\r\n        }\r\n        /// <summary>\r\n        /// 将DataTable中的数据转换成JSON格式\r\n        /// </summary>\r\n        /// <param name=\"dt\">数据源DataTable</param>\r\n        /// <returns></returns>\r\n        public static string CreateJsonParameters(DataTable dt)\r\n        {\r\n            return CreateJsonParameters(dt, true);\r\n        }\r\n        /// <summary>\r\n        /// 将DataTable中的数据转换成JSON格式\r\n        /// </summary>\r\n        /// <param name=\"dt\">数据源DataTable</param>\r\n        /// <param name=\"displayCount\">是否输出数据总条数</param>\r\n        /// <param name=\"totalcount\">JSON中显示的数据总条数</param>\r\n        /// <returns></returns>\r\n        public static string CreateJsonParameters(DataTable dt, bool displayCount, int totalcount)\r\n        {\r\n            StringBuilder JsonString = new StringBuilder();\r\n            //Exception Handling       \r\n\r\n            if (dt != null)\r\n            {\r\n                JsonString.Append(\"{ \");\r\n                JsonString.Append(\"\\\"rows\\\":[ \");\r\n                for (int i = 0; i < dt.Rows.Count; i++)\r\n                {\r\n                    JsonString.Append(\"{ \");\r\n                    for (int j = 0; j < dt.Columns.Count; j++)\r\n                    {\r\n                        if (j < dt.Columns.Count - 1)\r\n                        {\r\n                            //if (dt.Rows[i][j] == DBNull.Value) continue;\r\n                            if (dt.Columns[j].DataType == typeof(bool))\r\n                            {\r\n                                JsonString.Append(\"\\\"\" + dt.Columns[j].ColumnName.ToLower() + \"\\\":\" +\r\n                                                  dt.Rows[i][j].ToString().ToLower() + \",\");\r\n                            }\r\n                            else if (dt.Columns[j].DataType == typeof(string))\r\n                            {\r\n                                JsonString.Append(\"\\\"\" + dt.Columns[j].ColumnName.ToLower() + \"\\\":\" + \"\\\"\" +\r\n                                                  dt.Rows[i][j].ToString().Replace(\"\\\"\", \"\\\\\\\"\") + \"\\\",\");\r\n                            }\r\n                            else\r\n                            {\r\n                                JsonString.Append(\"\\\"\" + dt.Columns[j].ColumnName.ToLower() + \"\\\":\" + \"\\\"\" + dt.Rows[i][j] + \"\\\",\");\r\n                            }\r\n                        }\r\n                        else if (j == dt.Columns.Count - 1)\r\n                        {\r\n                            //if (dt.Rows[i][j] == DBNull.Value) continue;\r\n                            if (dt.Columns[j].DataType == typeof(bool))\r\n                            {\r\n                                JsonString.Append(\"\\\"JSON_\" + dt.Columns[j].ColumnName.ToLower() + \"\\\":\" +\r\n                                                  dt.Rows[i][j].ToString().ToLower());\r\n                            }\r\n                            else if (dt.Columns[j].DataType == typeof(string))\r\n                            {\r\n                                JsonString.Append(\"\\\"JSON_\" + dt.Columns[j].ColumnName.ToLower() + \"\\\":\" + \"\\\"\" +\r\n                                                  dt.Rows[i][j].ToString().Replace(\"\\\"\", \"\\\\\\\"\") + \"\\\"\");\r\n                            }\r\n                            else\r\n                            {\r\n                                JsonString.Append(\"\\\"JSON_\" + dt.Columns[j].ColumnName.ToLower() + \"\\\":\" + \"\\\"\" + dt.Rows[i][j] + \"\\\"\");\r\n                            }\r\n                        }\r\n                    }\r\n                    /*end Of String*/\r\n                    if (i == dt.Rows.Count - 1)\r\n                    {\r\n                        JsonString.Append(\"} \");\r\n                    }\r\n                    else\r\n                    {\r\n                        JsonString.Append(\"}, \");\r\n                    }\r\n                }\r\n                JsonString.Append(\"]\");\r\n\r\n                if (displayCount)\r\n                {\r\n                    JsonString.Append(\",\");\r\n\r\n                    JsonString.Append(\"\\\"total\\\":\");\r\n                    JsonString.Append(totalcount);\r\n                }\r\n\r\n                JsonString.Append(\"}\");\r\n                return JsonString.ToString().Replace(\"\\n\", \"\");\r\n            }\r\n            else\r\n            {\r\n                return null;\r\n            }\r\n        }\r\n\r\n        #region object 2 json\r\n\r\n        private static void WriteDataRow(StringBuilder sb, DataRow row)\r\n        {\r\n            sb.Append(\"{\");\r\n            foreach (DataColumn column in row.Table.Columns)\r\n            {\r\n                sb.AppendFormat(\"\\\"{0}\\\":\", column.ColumnName);\r\n                WriteValue(sb, row[column]);\r\n                sb.Append(\",\");\r\n            }\r\n            // Remove the trailing comma.\r\n            if (row.Table.Columns.Count > 0)\r\n            {\r\n                --sb.Length;\r\n            }\r\n            sb.Append(\"}\");\r\n        }\r\n\r\n        private static void WriteDataSet(StringBuilder sb, DataSet ds)\r\n        {\r\n            sb.Append(\"{\\\"Tables\\\":{\");\r\n            foreach (DataTable table in ds.Tables)\r\n            {\r\n                sb.AppendFormat(\"\\\"{0}\\\":\", table.TableName);\r\n                WriteDataTable(sb, table);\r\n                sb.Append(\",\");\r\n            }\r\n            // Remove the trailing comma.\r\n            if (ds.Tables.Count > 0)\r\n            {\r\n                --sb.Length;\r\n            }\r\n            sb.Append(\"}}\");\r\n        }\r\n\r\n        private static void WriteDataTable(StringBuilder sb, DataTable table)\r\n        {\r\n            sb.Append(\"{\\\"Rows\\\":[\");\r\n            foreach (DataRow row in table.Rows)\r\n            {\r\n                WriteDataRow(sb, row);\r\n                sb.Append(\",\");\r\n            }\r\n            // Remove the trailing comma.\r\n            if (table.Rows.Count > 0)\r\n            {\r\n                --sb.Length;\r\n            }\r\n            sb.Append(\"]}\");\r\n        }\r\n\r\n        private static void WriteEnumerable(StringBuilder sb, IEnumerable e)\r\n        {\r\n            bool hasItems = false;\r\n            sb.Append(\"[\");\r\n            foreach (object val in e)\r\n            {\r\n                WriteValue(sb, val);\r\n                sb.Append(\",\");\r\n                hasItems = true;\r\n            }\r\n            // Remove the trailing comma.\r\n            if (hasItems)\r\n            {\r\n                --sb.Length;\r\n            }\r\n            sb.Append(\"]\");\r\n        }\r\n\r\n        private static void WriteHashtable(StringBuilder sb, IDictionary e)\r\n        {\r\n            bool hasItems = false;\r\n            sb.Append(\"{\");\r\n            foreach (string key in e.Keys)\r\n            {\r\n                sb.AppendFormat(\"\\\"{0}\\\":\", key.ToLower());\r\n                WriteValue(sb, e[key]);\r\n                sb.Append(\",\");\r\n                hasItems = true;\r\n            }\r\n            // Remove the trailing comma.\r\n            if (hasItems)\r\n            {\r\n                --sb.Length;\r\n            }\r\n            sb.Append(\"}\");\r\n        }\r\n\r\n        private static void WriteObject(StringBuilder sb, object o)\r\n        {\r\n            MemberInfo[] members = o.GetType().GetMembers(BindingFlags.Instance | BindingFlags.Public);\r\n            sb.Append(\"{\");\r\n            bool hasMembers = false;\r\n            foreach (MemberInfo member in members)\r\n            {\r\n                bool hasValue = false;\r\n                object val = null;\r\n                if ((member.MemberType & MemberTypes.Field) == MemberTypes.Field)\r\n                {\r\n                    FieldInfo field = (FieldInfo)member;\r\n                    val = field.GetValue(o);\r\n                    hasValue = true;\r\n                }\r\n                else if ((member.MemberType & MemberTypes.Property) == MemberTypes.Property)\r\n                {\r\n                    PropertyInfo property = (PropertyInfo)member;\r\n                    if (property.CanRead && property.GetIndexParameters().Length == 0)\r\n                    {\r\n                        val = property.GetValue(o, null);\r\n                        hasValue = true;\r\n                    }\r\n                }\r\n                if (hasValue)\r\n                {\r\n                    sb.Append(\"\\\"\");\r\n                    sb.Append(member.Name);\r\n                    sb.Append(\"\\\":\");\r\n                    WriteValue(sb, val);\r\n                    sb.Append(\",\");\r\n                    hasMembers = true;\r\n                }\r\n            }\r\n            if (hasMembers)\r\n            {\r\n                --sb.Length;\r\n            }\r\n            sb.Append(\"}\");\r\n        }\r\n\r\n        private static void WriteString(StringBuilder sb, IEnumerable s)\r\n        {\r\n            sb.Append(\"\\\"\");\r\n            foreach (char c in s)\r\n            {\r\n                switch (c)\r\n                {\r\n                    case \'\\\"\':\r\n                        sb.Append(\"\\\\\\\"\");\r\n                        break;\r\n                    case \'\\\\\':\r\n                        sb.Append(\"\\\\\\\\\");\r\n                        break;\r\n                    case \'\\b\':\r\n                        sb.Append(\"\\\\b\");\r\n                        break;\r\n                    case \'\\f\':\r\n                        sb.Append(\"\\\\f\");\r\n                        break;\r\n                    case \'\\n\':\r\n                        sb.Append(\"\\\\n\");\r\n                        break;\r\n                    case \'\\r\':\r\n                        sb.Append(\"\\\\r\");\r\n                        break;\r\n                    case \'\\t\':\r\n                        sb.Append(\"\\\\t\");\r\n                        break;\r\n                    default:\r\n                        int i = c;\r\n                        if (i < 32 || i > 127)\r\n                        {\r\n                            sb.AppendFormat(\"\\\\u{0:X04}\", i);\r\n                        }\r\n                        else\r\n                        {\r\n                            sb.Append(c);\r\n                        }\r\n                        break;\r\n                }\r\n            }\r\n            sb.Append(\"\\\"\");\r\n        }\r\n\r\n        public static void WriteValue(StringBuilder sb, object val)\r\n        {\r\n            if (val == null || val == DBNull.Value)\r\n            {\r\n                sb.Append(\"null\");\r\n            }\r\n            else if (val is string || val is Guid)\r\n            {\r\n                WriteString(sb, val.ToString());\r\n            }\r\n            else if (val is bool)\r\n            {\r\n                sb.Append(val.ToString().ToLower());\r\n            }\r\n            else if (val is double ||\r\n                     val is float ||\r\n                     val is long ||\r\n                     val is int ||\r\n                     val is short ||\r\n                     val is byte ||\r\n                     val is decimal)\r\n            {\r\n                sb.AppendFormat(CultureInfo.InvariantCulture.NumberFormat, \"{0}\", val);\r\n            }\r\n            else if (val.GetType().IsEnum)\r\n            {\r\n                sb.Append((int)val);\r\n            }\r\n            else if (val is DateTime)\r\n            {\r\n                sb.Append(\"new Date(\\\"\");\r\n                sb.Append(((DateTime)val).ToString(\"MMMM, d yyyy HH:mm:ss\",\r\n                                                    new CultureInfo(\"en-US\", false).DateTimeFormat));\r\n                sb.Append(\"\\\")\");\r\n            }\r\n            else if (val is DataSet)\r\n            {\r\n                WriteDataSet(sb, val as DataSet);\r\n            }\r\n            else if (val is DataTable)\r\n            {\r\n                WriteDataTable(sb, val as DataTable);\r\n            }\r\n            else if (val is DataRow)\r\n            {\r\n                WriteDataRow(sb, val as DataRow);\r\n            }\r\n            else if (val is Hashtable)\r\n            {\r\n                WriteHashtable(sb, val as Hashtable);\r\n            }\r\n            else if (val is IEnumerable)\r\n            {\r\n                WriteEnumerable(sb, val as IEnumerable);\r\n            }\r\n            else\r\n            {\r\n                WriteObject(sb, val);\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        ///\r\n        /// </summary>\r\n        /// <param name=\"o\"></param>\r\n        /// <returns></returns>\r\n        public static string Convert2Json(object o)\r\n        {\r\n            StringBuilder sb = new StringBuilder();\r\n            WriteValue(sb, o);\r\n            return sb.ToString();\r\n        }\r\n\r\n        #endregion\r\n    }\r\n\r\n}\r\n', '0', '2017-09-08 09:49:08', null, 'JsonHelper.jun.cs.txt');
INSERT INTO `txt` VALUES ('542', '2', 'JSONhelper.疯狂秀才授权系统.中的common类里面的.cs.txt', 'using System;\r\nusing System.Collections.Generic;\r\nusing System.Text;\r\n\r\nusing System.Data;\r\nusing System.IO;\r\nusing System.Reflection;\r\nusing Newtonsoft.Json;\r\nusing Newtonsoft.Json.Converters;\r\nusing System.Collections;\r\n\r\nnamespace Common\r\n{\r\n    public class JSONhelper\r\n    {\r\n        /// <summary>\r\n        /// 泛型列表转换为JSON\r\n        /// </summary>\r\n        /// <typeparam name=\"T\">数据类型</typeparam>\r\n        /// <param name=\"list\">数据列表</param>\r\n        /// <param name=\"ClassName\">属性名称</param>\r\n        /// <returns></returns>\r\n        public static string IListToJSON<T>(IList<T> list, string ClassName)\r\n        {\r\n            StringBuilder sb = new StringBuilder();\r\n            sb.Append(\"{\\\"\" + ClassName + \"\\\":[\");\r\n            foreach (T t in list)\r\n            {\r\n                sb.Append(ModelToJSON(t) + \",\");\r\n            }\r\n\r\n            string _temp = sb.ToString().TrimEnd(\',\');\r\n            _temp += \"]}\";\r\n            return _temp;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 泛型列表转换为JSON\r\n        /// </summary>\r\n        /// <typeparam name=\"T\">数据类型</typeparam>\r\n        /// <param name=\"list\">数据列表</param>\r\n        /// <returns></returns>\r\n        public static string IList2JSON<T>(IList<T> list)\r\n        {\r\n            StringBuilder sb = new StringBuilder();\r\n            sb.Append(\"[\");\r\n            foreach (T t in list)\r\n            {\r\n                sb.Append(ModelToJSON(t) + \",\");\r\n            }\r\n\r\n            string _temp = sb.ToString().TrimEnd(\',\');\r\n            _temp += \"]\";\r\n            return _temp;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 数据实体类转换为JSON\r\n        /// </summary>\r\n        /// <typeparam name=\"T\">数据类型</typeparam>\r\n        /// <param name=\"t\">数据实体类</param>\r\n        /// <returns></returns>\r\n        public static string ModelToJSON<T>(T t)\r\n        {\r\n            StringBuilder sb = new StringBuilder();\r\n            string json = \"\";\r\n            if (t != null)\r\n            {\r\n                sb.Append(\"{\");\r\n                PropertyInfo[] properties = t.GetType().GetProperties();\r\n                foreach (PropertyInfo pi in properties)\r\n                {\r\n                    sb.Append(\"\\\"\" + pi.Name.ToString().ToLower() + \"\\\"\");\r\n                    sb.Append(\":\");\r\n                    sb.Append(\"\\\"\" + pi.GetValue(t, null).ToString().Replace(\"\\\"\", \"“\").Replace(\"\'\", \"‘\").Replace(\"\\r\", \"\\\\r\").Replace(\"\\n\", \"\\\\n\").Replace(\"<\", \"＜\").Replace(\">\", \"＞\") + \"\\\"\");\r\n                    sb.Append(\",\");\r\n                }\r\n\r\n                json = sb.ToString().TrimEnd(\',\');\r\n                json += \"}\";\r\n            }\r\n\r\n            return json;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 生成压缩的json 字符串\r\n        /// </summary>\r\n        /// <param name=\"obj\">生成json的对象</param>\r\n        /// <returns></returns>\r\n        public static string ToJson(object obj)\r\n        {\r\n            return ToJson(obj, false);\r\n        }\r\n        /// <summary>\r\n        /// 生成JSON字符串\r\n        /// </summary>\r\n        /// <param name=\"obj\">生成json的对象</param>\r\n        /// <param name=\"formatjson\">是否格式化</param>\r\n        /// <returns></returns>\r\n        public static string ToJson(object obj, bool formatjson)\r\n        {\r\n            StringBuilder sb = new StringBuilder();\r\n            StringWriter sw = new StringWriter(sb);\r\n\r\n            IsoDateTimeConverter idtc = new IsoDateTimeConverter();\r\n            idtc.DateTimeFormat = \"yyyy-MM-dd HH:mm:ss\";\r\n\r\n            JsonSerializer serializer = new JsonSerializer();\r\n            serializer.Converters.Add(idtc);\r\n            JsonWriter jw = new JsonTextWriter(sw);\r\n\r\n            if (formatjson)\r\n            {\r\n                jw.Formatting = Formatting.Indented;\r\n            }\r\n\r\n            serializer.Serialize(jw, obj);\r\n\r\n            //JsonConvert.SerializeObject(dt, idtc).ToString();\r\n\r\n            return sb.ToString();\r\n        }\r\n\r\n\r\n        /// <summary>\r\n        /// 将指定串值转换JSON格式\r\n        /// </summary>\r\n        /// <param name=\"name\">Key值</param>\r\n        /// <param name=\"value\">value值</param>\r\n        /// <returns></returns>\r\n        public static string StringToJson(string name, string value)\r\n        {\r\n            return \"{\\\"\" + name + \":\\\"\" + value + \"\\\"}\";\r\n        }\r\n\r\n        /// <summary>\r\n        /// 将指定串值转换JSON格式\r\n        /// </summary>\r\n        /// <param name=\"name\">Key值</param>\r\n        /// <param name=\"value\">value值</param>\r\n        /// <returns></returns>\r\n        public static string StringToJson(string name, int value)\r\n        {\r\n            return \"{\\\"\" + name + \"\\\":\" + value.ToString() + \"}\";\r\n        }\r\n\r\n        /// <summary>\r\n        /// 将数组转换为JSON格式的字符串\r\n        /// </summary>\r\n        /// <typeparam name=\"T\">数据类型，如string,int ...</typeparam>\r\n        /// <param name=\"list\">泛型list</param>\r\n        /// <param name=\"propertyname\">JSON的类名</param>\r\n        /// <returns></returns>\r\n        public static string ArrayToJSON<T>(List<T> list, string propertyname)\r\n        {\r\n            StringBuilder sb = new StringBuilder();\r\n            if (list.Count > 0)\r\n            {\r\n                sb.Append(\"[{\\\"\");\r\n                sb.Append(propertyname);\r\n                sb.Append(\"\\\":[\");\r\n\r\n                foreach (T t in list)\r\n                {\r\n                    sb.Append(\"\\\"\");\r\n                    sb.Append(t.ToString());\r\n                    sb.Append(\"\\\",\");\r\n                }\r\n\r\n                string _temp = sb.ToString();\r\n                _temp = _temp.TrimEnd(\',\');\r\n\r\n                _temp += \"]}]\";\r\n\r\n                return _temp;\r\n            }\r\n            else\r\n                return \"\";\r\n        }\r\n\r\n        public static string DataTableToJSON(DataTable dt, string dtName)\r\n        {\r\n            string s = DataTableToJSONJquery(dt);\r\n            s = \"{\\\"\" + dtName + \"\\\":\" + s + \"}\";\r\n            return s;\r\n        }\r\n\r\n        public static string DataTableToJSONJquery(DataTable dt)\r\n        {\r\n            StringBuilder sb = new StringBuilder();\r\n            if (dt.Rows.Count > 0)\r\n            {\r\n                for (int i = 0; i < dt.Rows.Count; i++)\r\n                {\r\n                    sb.Append(\"{\");\r\n                    for (int n = 0; n < dt.Columns.Count; n++)\r\n                    {\r\n                        sb.AppendFormat(\"\\\"{0}\\\":\\\"{1}\\\",\", dt.Columns[n].ColumnName.ToLower(), dt.Rows[i][n].ToString().Replace(\"\\\"\", \"“\").Replace(\"\'\", \"‘\").Replace(\"\\r\", \"\\\\r\").Replace(\"\\n\", \"\\\\n\").Replace(\"<\", \"＜\").Replace(\">\", \"＞\"));\r\n                    }\r\n                    sb.Remove(sb.Length - 1, 1);\r\n                    sb.Append(\"},\");\r\n                }\r\n                sb.Remove(sb.Length - 1, 1).Insert(0, \"[\").Append(\"]\");\r\n                return sb.ToString();\r\n            }\r\n            return \"\";\r\n        }\r\n\r\n        public static string DataRowToJSON(DataRow row)\r\n        {\r\n            StringBuilder sb = new StringBuilder();\r\n            if (row == null)\r\n                return \"\";\r\n            sb.Append(\"{\");\r\n            for (int n = 0; n < row.Table.Columns.Count; n++)\r\n            {\r\n                sb.AppendFormat(\"\\\"{0}\\\":\\\"{1}\\\",\", row.Table.Columns[n].ColumnName.ToLower(), row[n].ToString().Replace(\"\\\"\", \"“\").Replace(\"\'\", \"‘\").Replace(\"\\r\", \"\\\\r\").Replace(\"\\n\", \"\\\\n\").Replace(\"<\", \"＜\").Replace(\">\", \"＞\"));\r\n            }\r\n            sb.Remove(sb.Length - 1, 1);\r\n            sb.Append(\"}\");\r\n            return sb.ToString();\r\n        }\r\n\r\n        public static string ArrayToJSON(string[] strs)\r\n        {\r\n            StringBuilder sb = new StringBuilder();\r\n            for (int i = 0; i < strs.Length; i++)\r\n            {\r\n                sb.AppendFormat(\"\'{0}\':\'{1}\',\", i + 1, strs[i]);\r\n            }\r\n            if (sb.Length > 0)\r\n                return \"{\" + sb.ToString().TrimEnd(\',\') + \"}\";\r\n            return \"\";\r\n        }\r\n\r\n\r\n\r\n        public static string ArrayToJSON(string[] strs, string var)\r\n        {\r\n            return var + \"=\" + ArrayToJSON(strs);\r\n        }\r\n\r\n        public static DateTime JsonToDateTime(string jsonDate)\r\n        {\r\n            string value = jsonDate.Substring(6, jsonDate.Length - 8);\r\n            DateTimeKind kind = DateTimeKind.Utc;\r\n            int index = value.IndexOf(\'+\', 1);\r\n            if (index == -1)\r\n                index = value.IndexOf(\'-\', 1);\r\n            if (index != -1)\r\n            {\r\n                kind = DateTimeKind.Local;\r\n                value = value.Substring(0, index);\r\n            }\r\n            long javaScriptTicks = long.Parse(value, System.Globalization.NumberStyles.Integer, System.Globalization.CultureInfo.InvariantCulture);\r\n            long InitialJavaScriptDateTicks = (new DateTime(1970, 1, 1, 0, 0, 0, DateTimeKind.Utc)).Ticks;\r\n            DateTime utcDateTime = new DateTime((javaScriptTicks * 10000) + InitialJavaScriptDateTicks, DateTimeKind.Utc);\r\n            DateTime dateTime;\r\n            switch (kind)\r\n            {\r\n                case DateTimeKind.Unspecified:\r\n                    dateTime = DateTime.SpecifyKind(utcDateTime.ToLocalTime(), DateTimeKind.Unspecified);\r\n                    break;\r\n                case DateTimeKind.Local:\r\n                    dateTime = utcDateTime.ToLocalTime();\r\n                    break;\r\n                default:\r\n                    dateTime = utcDateTime;\r\n                    break;\r\n            }\r\n            return dateTime;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 获取指定表的数据，并转换为jqgrid 的JSON格式。适用于sql2005 以上版本\r\n        /// </summary>\r\n        /// <param name=\"pageindex\">当前第几页</param>\r\n        /// <param name=\"pagesize\">每页记录条数</param>\r\n        /// <param name=\"order\">排序</param>\r\n        /// <param name=\"where\">条件</param>\r\n        /// <param name=\"tbname\">表名或视图名</param>\r\n        /// <returns></returns>\r\n        public static string GetJsonforjQgrid(int pageindex, int pagesize, string order, string where, string tbname)\r\n        {\r\n            return GetJsonforjQgrid(\"*\", pageindex, pagesize, order, where, tbname);\r\n        }\r\n\r\n        /// <summary>\r\n        /// 获取指定表的数据，并转换为jqgrid 的JSON格式。适用于sql2005 以上版本\r\n        /// </summary>\r\n        /// <param name=\"fields\">要选取的列，以逗号隔开</param>\r\n        /// <param name=\"pageindex\">当前第几页</param>\r\n        /// <param name=\"pagesize\">每页记录条数</param>\r\n        /// <param name=\"order\">排序</param>\r\n        /// <param name=\"where\">条件</param>\r\n        /// <param name=\"tbname\">表名或视图名</param>\r\n        /// <returns></returns>\r\n        public static string GetJsonforjQgrid(string fields, int pageindex, int pagesize, string order, string where, string tbname)\r\n        {\r\n            int recordcount = SqlEasy.GetRecordCount(tbname, where);\r\n            int pagecount = SqlEasy.GetDataPages(pagesize, recordcount);\r\n\r\n            DataTable dt = SqlEasy.GetDataByPager2005(fields, tbname, where, order, pageindex, pagesize);\r\n\r\n            string json = Common.JSONhelper.DataTableToJSON(dt, \"griddata\");\r\n\r\n            string s = \"\\\"totalpages\\\":\\\"\" + pagecount.ToString() + \"\\\",\\\"currpage\\\":\\\"\" + pageindex.ToString() + \"\\\",\\\"totalrecords\\\":\\\"\" + recordcount.ToString() + \"\\\",\";\r\n\r\n            json = json.Insert(1, s);\r\n\r\n            return json;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 获取指定表的数据，并转换为jqgrid 的JSON格式。适用于sql2000 以上版本\r\n        /// </summary>\r\n        /// <param name=\"pageindex\">当前第几页</param>\r\n        /// <param name=\"pagesize\">每页记录条数</param>\r\n        /// <param name=\"orderfield\">排序字段 如：id asc,name desc</param>\r\n        /// <param name=\"key\">主键</param>\r\n        /// <param name=\"where\">筛选条件</param>\r\n        /// <param name=\"tbname\">表或视图名</param>\r\n        /// <returns></returns>\r\n        public static string GetJsonforjQgrid(int pageindex, int pagesize, string orderfield, string key, string where, string tbname)\r\n        {\r\n            return GetJsonforjQgrid(\"*\", pageindex, pagesize, orderfield, key, where, tbname);\r\n        }\r\n\r\n        /// <summary>\r\n        /// 获取指定表的数据，并转换为jqgrid 的JSON格式。适用于sql2000 以上版本\r\n        /// </summary>\r\n        /// <param name=\"fields\">要选取的列，以逗号隔开</param>\r\n        /// <param name=\"pageindex\">当前第几页</param>\r\n        /// <param name=\"pagesize\">每页记录条数</param>\r\n        /// <param name=\"orderfield\">排序</param>\r\n        /// <param name=\"key\">关键字</param>\r\n        /// <param name=\"where\">条件</param>\r\n        /// <param name=\"tbname\">表名或视图名</param>\r\n        /// <returns></returns>\r\n        public static string GetJsonforjQgrid(string fields, int pageindex, int pagesize, string orderfield, string key, string where, string tbname)\r\n        {\r\n            int recordcount = 0;\r\n\r\n\r\n            DataTable dt = SqlEasy.GetDataByPager2000(fields, tbname, where, orderfield, key, pageindex, pagesize, out recordcount);\r\n            int pagecount = SqlEasy.GetDataPages(pagesize, recordcount);\r\n            //string json = DataTableToJSONJquery(dt);\r\n            string json = ToJson(dt);\r\n\r\n            string s = \"{\\\"totalpages\\\":\\\"\" + pagecount.ToString() + \"\\\",\\\"currpage\\\":\\\"\" + pageindex.ToString() + \"\\\",\\\"totalrecords\\\":\\\"\" + recordcount.ToString() + \"\\\",\\\"griddata\\\":}\";\r\n\r\n            json = s.Insert(s.Length - 1, json);\r\n\r\n            return json;\r\n        }\r\n\r\n        public static string FormatJSONForJQgrid(int totalpages, int pageindex, int recordcount, IEnumerable rows)\r\n        {\r\n            string json = string.Format(\"{\\\"total\\\":\\\"{0}\\\",\\\"page\\\":\\\"{1}\\\",\\\"records\\\":\\\"{2}\\\",\\\"rows\\\":{3}}\",\r\n                        totalpages.ToString(),\r\n                        pageindex.ToString(),\r\n                        recordcount.ToString(),\r\n                        ToJson(rows));\r\n\r\n            return json;\r\n        }\r\n\r\n\r\n        /// <summary>\r\n        /// 获取easyui datagrid 所需要的JSON数据\r\n        /// </summary>\r\n        /// <param name=\"pageindex\">第几页</param>\r\n        /// <param name=\"pagesize\">每页记录数</param>\r\n        /// <param name=\"keyfield\">主键字段名</param>\r\n        /// <param name=\"where\">条件</param>\r\n        /// <param name=\"sort\">排序字段</param>\r\n        /// <param name=\"tablename\">表名</param>\r\n        /// <returns></returns>\r\n        public static string GetJsonForEasyuiDatagrid(int pageindex, int pagesize, string keyfield, string where, string sort, string tablename)\r\n        {\r\n            return GetJsonForEasyuiDatagrid(\"*\", pageindex, pagesize, keyfield, where, sort, tablename);\r\n        }\r\n\r\n        /// <summary>\r\n        /// 获取easyui datagrid 所需要的JSON数据\r\n        /// </summary>\r\n        /// <param name=\"fields\">字段列表，以逗号隔开</param>\r\n        /// <param name=\"pageindex\">第几页</param>\r\n        /// <param name=\"pagesize\">每页记录数</param>\r\n        /// <param name=\"keyfield\">主键字段名</param>\r\n        /// <param name=\"where\">条件</param>\r\n        /// <param name=\"sort\">排序字段</param>\r\n        /// <param name=\"tablename\">表名</param>\r\n        /// <returns></returns>\r\n        public static string GetJsonForEasyuiDatagrid(string fields, int pageindex, int pagesize, string keyfield, string where, string sort, string tablename)\r\n        {\r\n            int recordcount = 0;\r\n\r\n            DataTable dt = SqlEasy.GetDataByPager2000(fields, tablename, where, sort, keyfield, pageindex, pagesize, out recordcount);\r\n\r\n            string s = \"{\\\"total\\\":\" + recordcount.ToString() + \",\\\"rows\\\":\" + ToJson(dt) + \"}\";\r\n            return s;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 格式化EASYUI DATAGRID JSON\r\n        /// </summary>\r\n        /// <param name=\"recordcount\">总记录数</param>\r\n        /// <param name=\"rows\">每页记录的JSON格式</param>\r\n        /// <returns></returns>\r\n        public static string FormatJSONForEasyuiDataGrid(int recordcount, string rows)\r\n        {\r\n            return \"{\\\"total\\\":\" + recordcount.ToString() + \",\\\"rows\\\":\" + rows + \"}\";\r\n        }\r\n\r\n    }\r\n}\r\n', '0', '2017-09-08 09:49:08', null, 'JSONhelper.疯狂秀才授权系统.中的common类里面的.cs.txt');
INSERT INTO `txt` VALUES ('543', '2', 'JsonResult.Paged.把分页的信息都包含在Json中.txt', '=========MVC��JsonResut�а����б���Ϣ�����з�ҳ��Ϣ��============= \r\n       [HttpPost]\r\n        public JsonResult GetCVWorkExperienceJson(string id)\r\n        {\r\n            CVWorkExperienceManager bll = CVWorkExperienceManager.GetInstance();\r\n            List<CVWorkExperience> list = bll.GetListByPid(id);\r\n            JsonResult json = new JsonResult();\r\n            Paged model = new Paged() { \r\n            	PageSize=10,\r\n            	TotalItemCount=100,\r\n            	list=list\r\n            };\r\n            json.Data = model;\r\n            return json;\r\n        }\r\n\r\n---ǰ̨����-----------------\r\n function getWork() {\r\n            var option = {\r\n                type: \"post\",\r\n                url: \"/candidate/GetCVWorkExperienceJson\",\r\n                dataType: \"json\",\r\n                data: { id: \"@id\" },\r\n                success: function (json) {\r\n                    var PageSize = json.PageSize;\r\n                    var TotalItemCount = json.TotalItemCount;\r\n                    var list = json.list;\r\n                    var htmlStr = getWorkHtml(list);\r\n                    $(\"#CVWorkDiv\").html(htmlStr);\r\n                }\r\n            };\r\n            $.ajax(option);\r\n        }\r\n        function getWorkHtml(json) {\r\n            var html = [];\r\n            html.push(\" <div style=\\\"clear: left;\\\"></div>\");\r\n            $.each(json, function (i, item) {\r\n                html.push(\"<div style=\\\"display: none; position: absolute; margin-top: -3px; margin-left: -10px;\\\"><a class=\\\"edit-icon\\\" href=\\\"javascript:;\\\" onclick=\\\"doDetailsEdit(\'/Candidate/candidateEditCVWork/\" + item.ID + \"\',\'Edit\')\\\">Edit</a>\");\r\n                html.push(\"</div>\");\r\n                html.push(\"  <div ondblclick=\\\"DetailsInitEdit(this)\\\" style=\\\"margin-top: 15px;\\\"  class=\\\"bordiv\\\">\");\r\n   