INSERT INTO `txt` VALUES ('548', '2', 'js向url中添加参数.add.param.txt', ' js��url����Ӳ���\r\n��ǩ�� javaScripturl��Ӳ���\r\n2015-03-17 18:09 6689���Ķ� ����(0) �ղ� �ٱ�\r\n ���ࣺ Javascript��10��   webǰ�ˣ�15��  \r\n��Ȩ����������Ϊ����ԭ�����£�δ�������������ת�ء�\r\n\r\n//��url�м����������ʱ�������Ǵ������滻��������\r\nfunction addUrlPara(name, value) {\r\n	var currentUrl = window.location.href.split(\'#\')[0];\r\n	if (/\\?/g.test(currentUrl)) {\r\n		if (/name=[-\\w]{4,25}/g.test(currentUrl)) {\r\n			currentUrl = currentUrl.replace(/name=[-\\w]{4,25}/g, name + \"=\" + value);\r\n		} else {\r\n			currentUrl += \"&\" + name + \"=\" + value;\r\n		}\r\n	} else {\r\n		currentUrl += \"?\" + name + \"=\" + value;\r\n	}\r\n	if (window.location.href.split(\'#\')[1]) {\r\n		window.location.href = currentUrl + \'#\' + window.location.href.split(\'#\')[1];\r\n	} else {\r\n		window.location.href = currentUrl;\r\n	}\r\n}\r\n', '0', '2017-09-08 09:49:08', null, 'js向url中添加参数.add.param.txt');
