INSERT INTO `txt` VALUES ('571', '2', 'MD5.cs.txt', 'using System;\r\nusing System.Data;\r\nusing System.Configuration;\r\nusing System.Web;\r\nusing System.Web.Security;\r\nusing System.Web.UI;\r\nusing System.Web.UI.WebControls;\r\nusing System.Web.UI.WebControls.WebParts;\r\nusing System.Web.UI.HtmlControls;\r\nusing System.Security.Cryptography;\r\n\r\n/// <summary>\r\n/// MD5 的摘要说明\r\n/// </summary>\r\npublic class MD5\r\n{\r\n	public MD5()\r\n	{\r\n		//\r\n		// TODO: 在此处添加构造函数逻辑\r\n		//\r\n    }\r\n    public static string MD5JM(string strText)\r\n    {\r\n        MD5CryptoServiceProvider x = new MD5CryptoServiceProvider();\r\n        byte[] bs = System.Text.Encoding.Default.GetBytes(strText);\r\n        bs = x.ComputeHash(bs);\r\n        System.Text.StringBuilder s = new System.Text.StringBuilder();\r\n        foreach (byte b in bs)\r\n        {\r\n            s.Append(b.ToString(\"x2\").ToLower());\r\n        }\r\n        return s.ToString();\r\n    }\r\n}\r\n', '0', '2017-09-08 09:49:09', null, 'MD5.cs.txt');
INSERT INTO `txt` VALUES ('572', '2', 'MD5.加密.DES.解密.Security.Encryption.jiami.jiemi.txt', 'using System;\r\nusing System.Data;\r\nusing System.Configuration;\r\nusing System.Text;\r\nusing System.Security.Cryptography;\r\nusing System.IO;\r\nusing System.Diagnostics;\r\nusing System.Net;\r\n\r\nnamespace Permission.Lib\r\n{\r\n    public class SecurityEncryption\r\n    {\r\n        #region MD5�����㷨\r\n\r\n        /// <summary>\r\n        /// MD5�����㷨\r\n        /// </summary>\r\n        /// <param name=\"str\">�ַ���</param>\r\n        /// <param name=\"code\">���ܷ�ʽ,16��32</param>\r\n        /// <returns></returns>\r\n        public static string MD5(string str, int code)\r\n        {\r\n            if (code == 16) //16λMD5���ܣ�ȡ32λ���ܵ�9~25�ַ��� \r\n            {\r\n                return System.Web.Security.FormsAuthentication.HashPasswordForStoringInConfigFile(str, \"MD5\").ToUpper().Substring(8, 16);\r\n            }\r\n            else//32λ���� \r\n            {\r\n                return System.Web.Security.FormsAuthentication.HashPasswordForStoringInConfigFile(str, \"MD5\").ToUpper();\r\n            }\r\n        }\r\n\r\n        #endregion\r\n\r\n        #region DES ����/����\r\n\r\n        private static byte[] key = ASCIIEncoding.ASCII.GetBytes(\"caikelun\");\r\n        private static byte[] iv = ASCIIEncoding.ASCII.GetBytes(\"12345678\");\r\n\r\n        /// <summary>\r\n        /// DES���ܡ�\r\n        /// </summary>\r\n        /// <param name=\"inputString\">�����ַ�����</param>\r\n        /// <returns>���ܺ���ַ�����</returns>\r\n        public static string DESEncrypt(string inputString)\r\n        {\r\n            MemoryStream ms = null;\r\n            CryptoStream cs = null;\r\n            StreamWriter sw = null;\r\n\r\n            DESCryptoServiceProvider des = new DESCryptoServiceProvider();\r\n            try\r\n            {\r\n                ms = new MemoryStream();\r\n                cs = new CryptoStream(ms, des.CreateEncryptor(key, iv), CryptoStreamMode.Write);\r\n                sw = new StreamWriter(cs);\r\n                sw.Write(inputString);\r\n                sw.Flush();\r\n                cs.FlushFinalBlock();\r\n                return Convert.ToBase64String(ms.GetBuffer(), 0, (int)ms.Length);\r\n            }\r\n            finally\r\n            {\r\n                if (sw != null) sw.Close();\r\n                if (cs != null) cs.Close();\r\n                if (ms != null) ms.Close();\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// DES���ܡ�\r\n        /// </summary>\r\n        /// <param name=\"inputString\">�����ַ�����</param>\r\n        /// <returns>���ܺ���ַ�����</returns>\r\n        public static string DESDecrypt(string inputString)\r\n        {\r\n            MemoryStream ms = null;\r\n            CryptoStream cs = null;\r\n            StreamReader sr = null;\r\n\r\n            DESCryptoServiceProvider des = new DESCryptoServiceProvider();\r\n            try\r\n            {\r\n                ms = new MemoryStream(Convert.FromBase64String(inputString));\r\n                cs = new CryptoStream(ms, des.CreateDecryptor(key, iv), CryptoStreamMode.Read);\r\n                sr = new StreamReader(cs);\r\n                return sr.ReadToEnd();\r\n            }\r\n            finally\r\n            {\r\n                if (sr != null) sr.Close();\r\n                if (cs != null) cs.Close();\r\n                if (ms != null) ms.Close();\r\n            }\r\n        }\r\n\r\n        #endregion\r\n    }\r\n}\r\n', '0', '2017-09-08 09:49:09', null, 'MD5.加密.DES.解密.Security.Encryption.jiami.jiemi.txt');
INSERT INTO `txt` VALUES ('573', '2', 'Memcached.ClientLibrary.缓存第三方类库.txt', '1. 将Commons.dll，ICSharpCode.SharpZipLib.dll，log4net.dll，Memcached.ClientLibrary.dll放到bin目录\r\n2. 引用Memcached.ClientLibrary.dll\r\n3. 类中引用using Memcached.ClientLibrary;\r\n4. 代码\r\n\r\nusing System;\r\nusing System.Collections.Generic;\r\nusing System.Linq;\r\nusing System.Text;\r\n\r\nusing Memcached.ClientLibrary;\r\n\r\nnamespace TestMemcachedApp\r\n{\r\n    class Program\r\n    {\r\n        [STAThread]\r\n        public static void Main(String[] args)\r\n        {\r\n            string[] serverlist = { \"192.168.1.11:11211\",\"192.168.1.12:11211\",\"192.168.1.13:11211\" };//服务器可以是多个\r\n\r\n            //初始化池\r\n            SockIOPool pool = SockIOPool.GetInstance();\r\n            pool.SetServers(serverlist);//设置连接池可用的cache服务器列表，server的构成形式是IP:PORT（如：127.0.0.1:11211）\r\n            pool.InitConnections = 3;//初始连接数\r\n            pool.MinConnections = 3;//最小连接数\r\n            pool.MaxConnections = 5;//最大连接数\r\n            pool.SocketConnectTimeout = 1000;//设置连接的套接字超时\r\n            pool.SocketTimeout = 3000;//设置套接字超时读取\r\n            pool.MaintenanceSleep = 30;//设置维护线程运行的睡眠时间。如果设置为0，那么维护线程将不会启动,30就是每隔30秒醒来一次\r\n\r\n            //获取或设置池的故障标志。\r\n            //如果这个标志被设置为true则socket连接失败，将试图从另一台服务器返回一个套接字如果存在的话。\r\n            //如果设置为false，则得到一个套接字如果存在的话。否则返回NULL，如果它无法连接到请求的服务器。\r\n            pool.Failover = true;\r\n\r\n          pool.Nagle = false;//如果为false，对所有创建的套接字关闭Nagle的算法\r\n            pool.Initialize();\r\n\r\n            // 获得客户端实例\r\n            MemcachedClient mc = new MemcachedClient();\r\n            mc.EnableCompression = false;\r\n\r\n            Console.WriteLine(\"------------测  试-----------\");\r\n            mc.Set(\"test\", \"my value\");  //存储数据到缓存服务器，这里将字符串\"my value\"缓存，key 是\"test\"\r\n\r\n            if (mc.KeyExists(\"test\"))   //测试缓存存在key为test的项目\r\n            {\r\n                Console.WriteLine(\"test is Exists\");\r\n                Console.WriteLine(mc.Get(\"test\").ToString());  //在缓存中获取key为test的项目\r\n            }\r\n            else\r\n            {\r\n                Console.WriteLine(\"test not Exists\");\r\n            }\r\n\r\n            \r\n            mc.Delete(\"test\");  //移除缓存中key为test的项目\r\n\r\n            if (mc.KeyExists(\"test\"))\r\n            {\r\n                Console.WriteLine(\"test is Exists\");\r\n                Console.WriteLine(mc.Get(\"test\").ToString());\r\n            }\r\n            else\r\n            {\r\n                Console.WriteLine(\"test not Exists\");\r\n            }\r\n            Console.ReadLine();\r\n\r\n            SockIOPool.GetInstance().Shutdown();  //关闭池， 关闭sockets\r\n        }\r\n    }\r\n}\r\n', '0', '2017-09-08 09:49:09', null, 'Memcached.ClientLibrary.缓存第三方类库.txt');
